INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Public Accounting Practice', '610', 'ACC', 'This course will enhance students¿ technical and communication skills that they have developed through the application and integration of their knowledge in various types of cases. Students will use these skills extensively in their careers as financial professionals.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('External Reporting with Integration', '611', 'ACC', 'This course emphasizes financial reporting standards, in conjunction with assurance and tax, and their application through the use of scenarios and simulations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations of Venture Creation', '625', 'ACC', 'This course provides students with an introduction to the processes involved in moving an idea for a new venture from concept through to launch. The theoretical knowledge and practical skills needed to create a successful entrepreneurial enterprise are developed. Topics include definition and evaluation of entrepreneurial opportunities, business planning, funding strategies and early-stage revenue models, legal issues and intellectual property protection.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('IT Assurance and Computer-Assisted Audit Techniques', '626', 'ACC', 'This course will address audit considerations and other assurance services in computer-based information systems. (Course offering will be internet-based.)');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Business Process Enablement and Project Management', '627', 'ACC', 'This course will cover methods of improving business processes and managing related organizational change. (Course offering will be internet-based.)');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Assurance and Governance', '650', 'ACC', 'This course considers the role of risk in the context of assurance and the clients risk management process and addresses the impact of risk on an assurance providers professional practice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Tax Policy', '662', 'ACC', 'This course examines the economic, political, legal and administrative aspects of selected contemporary issues in Canadian tax policy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Systems and Analysis for Management Decision-making', '680', 'ACC', 'This course reviews and integrates theory, analytical approaches, and processes for those intending to pursue certification as a professional accountant. It provides insight into the problems facing management and executives using cases designed to expose students to real world situations requiring qualitative and quantitative analysis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Understanding and Managing Organizational Change', '681', 'ACC', 'This course is designed for individuals who are interested in a deeper understanding of the process of change from a senior management perspective.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Accounting', '690', 'ACC', 'One or more one-term courses will be offered at different times as announced.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Financial Accounting Research Seminar', '701', 'ACC', 'Current research topics in financial accounting including applications of agency theory, capital markets theory and human information processing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Management Accounting Research Seminar', '702', 'ACC', 'This course provides an in-depth look at the major research efforts that characterize contemporary management accounting and cost management systems. Particular emphasis is placed on field-based research techniques.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Auditing Research Seminar', '750', 'ACC', 'A broad survey of current auditing research covering: the socio-economic role of auditing in society; the agency relationships between shareholders, managers and auditors; the factors influencing the quality of auditing, in particular, experimental research into key attributes of the audit judgment process, and technological innovations such as statistical sampling, regression, and expert systems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Taxation Research Seminar', '760', 'ACC', 'A survey of non-legal tax research in accounting. Tax research in related areas, especially economics, will also be reviewed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finance 1', '770', 'ACC', 'The course introduces options and other derivative securities in different asset classes. The main focus is on methods of pricing in a multi-period setting, but continuous-time models are also discussed. Topics may include no-arbitrage pricing theory, the fundamental theory of asset pricing, complete and incomplete markets,and pricing of complex financial instruments.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finance 1', '970', 'ACTSC', 'The course introduces options and other derivative securities in different asset classes. The main focus is on methods of pricing in a multi-period setting, but continuous-time models are also discussed. Topics may include no-arbitrage pricing theory, the fundamental theory of asset pricing, complete and incomplete markets,and pricing of complex financial instruments.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finance 2', '771', 'ACC', 'The course discusses methods and tools for modeling of financial derivatives in the continuous-time setting. Both theory and practical applications are discussed. The first part covers methods of pricing and hedging of derivatives under different assumptions about the dynamics of the underlying economic factors. Topics normally include currency derivatives, American and exotic options, futures contracts, stochastic volatility models and mean-variance hedging. The second part deals with modeling and pricing of interest-rate products. Topics may include short interest rate models, the Heath-Jarrow-Morton Framework, and Libor and swap market models.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finance 2', '971', 'ACTSC', 'The course discusses methods and tools for modeling of financial derivatives in the continuous-time setting. Both theory and practical applications are discussed. The first part covers methods of pricing and hedging of derivatives under different assumptions about the dynamics of the underlying economic factors. Topics normally include currency derivatives, American and exotic options, futures contracts, stochastic volatility models and mean-variance hedging. The second part deals with modeling and pricing of interest-rate products. Topics may include short interest rate models, the Heath-Jarrow-Morton Framework, and Libor and swap market models.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finance 3', '772', 'ACC', 'The course will cover selected and advanced topics in quantitative finance and risk management, with a particular focus on current developments. Topics may include robust and Bayesian portfolio optimization, limits to arbitrage, derivatives pricing under model uncertainty, credit risk models, and models of systematic risk.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finance 3', '972', 'ACTSC', 'The course will cover selected and advanced topics in quantitative finance and risk management, with a particular focus on current developments. Topics may include robust and Bayesian portfolio optimization, limits to arbitrage, derivatives pricing under model uncertainty, credit risk models, and models of systematic risk.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Accounting Research', '781', 'ACC', 'ACC 781 provides an introduction to academic research in Accounting and Finance.  It covers elements of scientific inference, experimental and quasi-experimental design, and various threats to valid inference.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Accounting Research', '784', 'ACC', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Life Contingencies 3', '855', 'ACTSC', 'Profit testing for traditional and non-traditional life insurance. Pricing and valuation of embedded options in life insurance products. Defined benefit and defined contribution pension plan design. Theory and practice of unit credit methods for pension plan funding and valuation for final average salary, career average earnings, and career average revalued earnings pension plans; post-retirement health benefits.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mathematical Methods of Loss Reserving', '961', 'ACTSC', 'Macro methods of runoff analysis: chain-ladder, least squares, separation, payment per claim incurred. Stochastic methods: Reids method, see-saw, payment per unit of risk, autoregressive models, Kalman filter.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Actuarial Science', '991', 'ACTSC', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Seminar in Actuarial Science', '992', 'ACTSC', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Functional Analysis', '731', 'AMATH', 'Basic concepts of functional analysis. Topics include: theory of linear operators, nonlinear operators and the Frechet derivative, fixed point theorems, approximate solution of operator equations, Hilbert space, spectral theory. Applications from various areas will be used to motivate and illustrate the theory. A previous undergraduate course in real analysis is strongly recommended.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Ordinary Differential Equations', '751', 'AMATH', 'Qualitative theory of systems of ODEs. Topics include: existence/uniqueness of solutions, comparison principle, iterative techniques, stability and boundedness, Lyapunov method, periodic solutions, Floquet theory and Poincare maps, hyperbolicity, stable, unstable and center manifolds, structural stability and bifurcation. Applications from various areas will be used to motivate and illustrate the theory. A previous course in ordinary differential equations at the undergraduate level is strongly recommended.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Partial Differential Equations', '753', 'AMATH', 'The main themes are well-posedness of problems, Hilbert space methods, variational principles and integral equation methods. Topics include: first-order nonlinear partial differential equations, quasilinear hyperbolic systems, potential theory, eigenfunctions and eigenvalues, semi-groups, and power series solutions. Applications from various areas will be used to motivate and illustrate the theory. A previous course in partial differential equations at the undergraduate level is strongly recommended.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Systems Analysis and Control', '855', 'AMATH', 'The main theme is the extension of control theory beyond systems modelled by linear ordinary differential equations. Topics include: advanced systems theory, control of nonlinear systems, control of partial differential equations and delay equations. Students should have completed an introductory undergraduate course in control theory.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Hydrodynamic Stability and Turbulence', '863', 'AMATH', 'Mathematical methods, stability of parellel flows for unstratified and stratified fluids, Rayleigh-Taylor instability, centrifugal instability, barotropic and baroclinic instabilities, the effects of viscosity and the Orr-Sommerfeld equation, transition to turbulence, averaged equations, closure problem, homogeneous isotropic turbulence, turbulent boundary layers, effects of stratification. Students should have completed an introductory undergraduate course in fluid mechanics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Dispersive and Nonlinear Waves', '867', 'AMATH', 'Dispersive waves, propagation of dispersive waves in an inhomogeneous medium (WKB theory). Nonlinear resonant interactions. Solitons: completely integrable nonlinear wave equations (e.g., the KdV equation, nonlinear Schrodinger equations) and the inverse Scattering Transform. Applications to water waves and nonlinear optics. Introducation to weakly nonlocal solitary waves and beyond-all-orders asymptotics. Completion of an upper year course in partial differential equations is strongly recommended.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Quantum Field Theory', '873', 'AMATH', 'Review of relativistic quantum mechanics and classical field theory. Quantization of free quantum fields (the particle interpretation of field quanta). Canonical quantization of interacting fields (Feynman rules). Application of the formalism of interacting quantum fields to lowest¿order quantum electrodynamic processes. Radiative corrections and renormalization.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Quantum Field Theory', '703', 'PHYS', 'Review of relativistic quantum mechanics and classical field theory. Quantization of free quantum fields (the particle interpretation of field quanta). Canonical quantization of interacting fields (Feynman rules). Application of the formalism of interacting quantum fields to lowest¿order quantum electrodynamic processes. Radiative corrections and renormalization.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations of Quantum Theory', '877', 'AMATH', 'Review mathematical formulation of operational quantum theory; theory of measurements and decoherence; quantum-classical contrast; review of historical perspectives on interpretation, including EPR paradox; Bells theorem, non-locality and contextuality; PBR theorem; selected topics including overviews of current interpretations of quantum mechanics and critical experiments in quantum foundations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fisheries Biology', '602', 'BIOL', 'The literature and methods of Fisheries Biology. Examination and discussion of selected topics of interest to the class. Emphasis will be primarily, but not exclusively on the ecology, habitats, and management issues related to temperature freshwater fish.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Animal Physiology', '605', 'BIOL', 'An advanced study of the physiological processes used by animals to respond to changes in environmental conditions. The study will focus on adaptation strategies used by animals to changes in temperature and other rate controlling environmental factors.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Aquatic Ecology', '606', 'BIOL', 'Advanced Aquatic Ecology provides opportunity for deeper study of new knowledge fronts and current issues in the field.  Topics selection varies according to developments in the field and the interests of course participants, but is designed to provide broad coverage of the diversity of theoretical and methodological questions arising in modern aquatic ecology.  The course is intended to be accessible to students with interests and background in ecology, environmental science, environmental engineering, or related fields.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Bacterial Genetics', '608', 'BIOL', 'Genetic aspects of the control of gene expression in bacteria and bacteriophages will be stressed. Recently published works will serve as the focal points for discussion. The specific content of any one set of topics to be analyzed in a particular term will be determined in consultation with the participants.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Topics in Evolution and Diversity', '610', 'BIOL', 'A critical evaluation of selected research topics in evolution and the diversity of life and practical applications.  Topics may include macroevolution, population genetics, evolution of behaviours, ecological evolution, phylogenetic reconstruction and/or phylogeny of the eukaryotic and prokaryotic organisms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Bioinformatics and Genomics', '614', 'BIOL', 'This course will cover current topics in bioinformatics and genomics, including genome assembly, annotation, sequence alignment, phylogentics, transcriptomics, and comparative genomics.  Students will perform bioinformatics and computational analyses with an emphasis on topics relevant to their thesis work.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Topics in Environmental Toxicology', '617', 'BIOL', 'A critical evaluation of current research topics in environmental toxicology will be undertaken. Emphasis will be placed on the ecosystem approach to toxicology including cycling of toxicants, routes by which toxicants are removed from the environment and the impact of toxicants on species interaction within communities. Consideration will also be given to new methods for toxic hazard prediction and evaluation, as well as to biotic and abiotic factors that modify toxicant impact.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Microbial Physiology', '618', 'BIOL', 'Discussion of current advances in selected topics in physiology of prokaryotic and/or eukaryotic microorganisms. Recently published research results will be the central points for the discussion. The topics might include but are not limited to microbiol growth behaviour under extreme conditions, microbial roles in the cycles of specific elements, processes of energy conservation, and properties and functions of key enzymes in microbial metabolic pathways.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Selected Topics in Plant Physiology', '622', 'BIOL', 'Discussions of selected topics not covered in other courses. These may include juvenility, dormancy, senescence, plant response to environmental stress, morphogenesis, photosynthesis and biochemistry.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Biogeochemistry', '624', 'EARTH', 'The influence of physical, chemical and microbiological processes on groundwater geochemistry are examined. Background concepts in microbial ecology and organic geochemistry are developed and related to subsurface environments. Treatment is given to biodegradation of organic pollutants, microbially-mediated redox reactions and organic-metal interactions. EARTH 439 is strongly recommended.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Biogeochemistry', '624', 'BIOL', 'The influence of physical, chemical and microbiological processes on groundwater geochemistry are examined. Background concepts in microbial ecology and organic geochemistry are developed and related to subsurface environments. Treatment is given to biodegradation of organic pollutants, microbially-mediated redox reactions and organic-metal interactions. EARTH 439 is strongly recommended.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Applied and Industrial Microbiology', '627', 'BIOL', 'One or more topics will be addressed in detail through review of literature, including patents, and current practices in industrial microbiology. The topics might include but are not limited to Bacillus subtilis: a workhorse in industrial fermentations; production of microbiol lipids; Aspergillus fermentations. Each student will present a seminar on an assigned topic and the class will work as a group to prepare an up-to-date review of publication quality.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cell Growth and Differentiation', '629', 'BIOL', 'Discussion of literature relating to the cell cycle and cell differentiation. Topics may include: the cell cycle, DNA replication, mitosis, stem cells, cell proliferation and differentiation, cancer progression, epigenetics, chromatin remodeling, and extracellular matrix function. A strong undergraduate background in cell biology and molecular biology is required.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Statistical Methods in Ecology', '631', 'BIOL', 'This course introduces statistical methods commonly used in ecology. Topics covered will include Experimental design, ANOVA, regression, general linear models, clustering, ordination, and some multivariate analyses of variance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Community Ecology', '633', 'BIOL', 'Community ecology seeks to understand the processes by which communities assemble and the key biotic and abiotic factors that influence their structure and function. This seminar covers diverse ecological theories relevant to communities, applying concepts of selection, drift, speciation, and dispersal through readings, discussion, and a term paper that tests a community ecology hypothesis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Immunology', '636', 'BIOL', 'Discussion of current advances in selected topics in immunology. The areas to be covered will include cell-mediated immunity, humoral immunity, comparative immunology, and other topics of current interest.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Current Topics in Biotechnology', '642', 'BIOL', 'Recent developments in biotechnology, both applied and basic, will be emphasized. The course will cover such topics as the development of vectors for the stable transformation of plant cells, the isolation, characterization and manipulation of plant, animal and microbial genes, and the use of microorganisms to develop new products and processes.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Recent  Advances in Microbial Ecology', '645', 'BIOL', 'Recent advances in selected topics of microbial ecology will be examined. Topics will be selected from soil, fresh water or other ecosystems with an important microbial component.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Bio-Molecular Tools', '650', 'BIOL', 'The ability to determine the three¿dimensional structure of a biomolecule and correlate that structure to molecular function is essential to understanding its role in Biology. In BIOL 650 we will explore the use of biophysical and structural approaches that can be used to establish the structure and function of protein-based biomolecules. The primary emphasis of the course will be upon establishing structure¿function relationships in enzymes. Upon completion of the course, students will have gained a practical understanding of the use and limitations of the tools of steady¿state enzyme kinetics and x¿ray crystallography.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Animal Molecular Biology', '667', 'BIOL', 'Selected topics in molecular biology will be presented at an advanced level with the aim of evaluating recent contributions and developments. Basic concepts and organismic interrelationships will be emphasized. The topics will concentrate on mechanisms of replication, transcription and (or) translation. Specific themes for discussion will be arranged each time the course is presented.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Plant Molecular Biology', '669', 'BIOL', 'Critical discussion of current developments in plant biology. The course will cover such topics as the structure, organization, replication and expression of plant and algal genetic material. Emphasis will be placed on understanding basic molecular mechanisms and processes.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Topics in Animal Behaviour', '675', 'BIOL', 'This course will deal intensively with a subject area in the field of animal behaviour. The particular topic for a given term will be determined by the interests of the participating members of the class. Suggested topics include: mating systems in the animal kingdom; patterns of parental care; cost/benefit analyses of social living. Recommended: a basic knowledge of animal behaviour and a devoted interest.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Specialized Studies of Selected Research Procedures, Strategies or Topics', '680', 'BIOL', 'Critical evaluation and discussion of topics and procedures in biology that are not covered in existing graduate courses. The course must be specially arranged with a faculty member. Students may take only one BIOL 680 course for credit. (Offered to Masters students only.)');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Specialized Studies of Selected Research Procedures, Strategies or Topics', '681', 'BIOL', 'Critical evaluation and discussion of topics and procedures in biology that are not covered in existing courses. The course must be specially arranged with a faculty member. Students may take only one BIOL 681 course for credit. (Offered to PhD students only.)');
