INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations in Spatial Data Handling', '600', 'GEOG', 'This seminar class will provide a foundational analysis and overview of concepts and methodologies in
several aspects of spatial data handling: cartography, geographic information systems, remote sensing,
spatial statistics, including analysis of spatial data sets. *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Remote Sensing of Cold Regions', '602', 'GEOG', 'This course focuses on the extraction of hydrologically-relevant parameters from remote sensing that are germane to cold regions such as snowfall, snow cover, glaciers & ice caps, lake and river ice, permafrost, and seasonally frozen ground.  The determination of hydrological parameters in the liquid and vapour phases such precipitiation, soild moisture, surface water, and evaporation/evapotranspiration from remote sensing, as well as the integration of remote sensing data into hydrological models are also covered.  Prerequisites: UW undergraduates: GEOG 271, 371, 471 or undergraduate Geomatics degree.  Undergraduate Geography degree with suitable remote sensing experience. Students with no remote sensing experience but strong GIS experience are required to take a 675 Directed Reading course entitled "Remote Sensing Analysis" prior to enrolment in 602. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Remote Sensing and Earth System Science', '603', 'GEOG', 'This course will explore how remote sensing is used and in concert with empirical or physically-based models to help us better understand Earth system processes from the local to global scales.  Students will learn how remote sensing measurements are coupled with radiative transfer models, energy and mass balance models and spatial analytical models from different components of the Earth system.  Examples will be drawn from the hydrologic and carbon cycles from the human built enviornment.  Prerequisites: UW undergraduates: Geog 271, 371, 471 or undergraduate Geomatics degree.  Undergraduate degree with suitable remote sensing experience.  Students with no remote sensing experience but strong GIS experience are required to take a 675 Directed Reading course entitled  "Remote Sensing Analysis" prior to enrolment in 603. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Statistics', '604', 'GEOG', 'An overview of spatial statistics and their use in geographic studies. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Scientific Data Wrangling', '606', 'GEOG', 'This course covers the data science skills comprising data visualization, data wrangling (cleaning,
combining, modelling, etc.), and methodological and statistical design, which are an important part of
the scientific method. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Geographic Information Systems', '607', 'GEOG', 'Introduces the fundamentals of GIS at a graduate level, including cartographic best practices, map projections and coordinate systems, georeferencing data, quantitative mapping, web mapping, and introductory spatial analysis, including clustering, network, and multicriteria analysis. Intended for students without significant experience in GIS. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Multivariate Statistics', '616', 'GEOG', 'Theory and application of multivariate statistics, regression and correlation analysis, factor analysis, discriminant analysis and grouping analysis, with emphasis on the use of the computer. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Multivariate Statistics', '616', 'PLAN', 'Theory and application of multivariate statistics, regression and correlation analysis, factor analysis, discriminant analysis and grouping analysis, with emphasis on the use of the computer. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Analysis', '618', 'GEOG', 'Presentation of analytical and simulation models useful in analyzing geographic phenomena such as spatial interaction, spatial dependence, spatial equilibria and locational optimization; consideration is given to both theoretical and empirical components. *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Analysis', '618', 'PLAN', 'Presentation of analytical and simulation models useful in analyzing geographic phenomena such as spatial interaction, spatial dependence, spatial equilibria and locational optimization; consideration is given to both theoretical and empirical components. *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations in Human Geography', '620', 'GEOG', 'This seminar class will provide a foundational analysis of the interdisciplinary scope of Human
Geography by evaluating key concepts and theoretical approaches that have defined the evolution of
the sub-discipline in the contemporary period. Students will assess how their research interests will
contribute to ongoing conceptual developments in Human Geography.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Activity and Travel Behaviour', '624', 'GEOG', 'An exploration of observed patterns of human activity and travel, underlying decision-making processes, data collection methodologies, and associated impacts on environment and health.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Qualitative Methods in Geography', '625', 'GEOG', 'This course is designed to investigate the range of qualitative research methods (research tools) employed by human geographers and explore the methodological justifications (philosophical or theoretical underpinnings) for using this type of research approach.  Qualitative methods attempt to interpret meaning as opposed to purely measuring phenomena.  The focus of qualitative research is not descriptive measurement and prediction of phenomena, as offered by statistical description/analysis, but is more attuned to examining subjective understandings and the interpretation of meaning (hermeneutics).  This course will examine and evaluate the range of research tools comprising qualitative methods including: various interviewing methods, participant observation, ethnography, case study methods and dicourse analysis.  It will also engage with the theoretical debates and philosophical approaches that underpin qualitative research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Development: Theories and Practice', '635', 'GEOG', 'This course emphasizes both theoretical and conceptual frameworks, techniques, practices and methods for analysis of development, focusing in particular on the development - environment interface and questions of sustainability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Development: Theories and Practice', '602', 'INDEV', 'This course emphasizes both theoretical and conceptual frameworks, techniques, practices and methods for analysis of development, focusing in particular on the development - environment interface and questions of sustainability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations in Environmental Science', '640', 'GEOG', 'This seminar course examines basic concepts related to temporal and spatial scales together with
variability as they directly affect data collection and analysis. Leading-edge research related to
Environmental Science will be critically discussed. *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Micrometeorology', '642', 'GEOG', 'Advanced study of boundary-layer meteorology. Energy balance regimes for complex surfaces are examined. The theory of turbulent and radiant transfer is studied using field data collected during the course. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Dynamic Geomorphology', '643', 'GEOG', 'An overview of current issues and methodologies in geomorphology with special emphasis on processes operating in fluvial, glacial, periglacial or coastal environments. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Geomorphology', '644', 'GEOG', 'An examination of the way in which human activities modify landscapes and the impacts of government policy and planning with respect to landscape change. The role of the geomorphologist in landscape rehabilitation. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Hydrology', '646', 'GEOG', 'Overview of hydrological processes with emphasis on Canadian examples related to the hydrologic cycle, surface hydrology, groundwater hydrology, and measurement techniques. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Recent Advances in Wetland Studies', '647', 'GEOG', 'Selected topics on the distribution, classification, functional dynamics, and ecosystem structure of wetlands. A scientific basis for wetlands management is emphasized. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Studies in Hydrology and the Environment 2', '661B', 'GEOG', 'This course is a continuation of GEOG 661A/PLAN661A. *eligible for MES.');
