INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Political Ecology: Nature, Society and Sustainability', '664', 'GEOG', 'Readings, discussions and case study analysis draw attention to the intersection of political-economy and ecological analysis, and contested understandings of environmental change, livelihoods and sustainability.  Topics may include communities and conservation, knowledge systems and power, ecological change, property rights and institutions, social movements, and methods. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Perspectives on Tourism', '601', 'TOUR', 'This course will introduce participants to a variety of topics and research methods through presentations made by active researchers from Canada and abroad.  *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Ecology of Stressed Environments', '672', 'GEOG', 'An examination of socioeconomic-biophysical system interactions emphasizing multi- and transdisciplinary perspectives such as human ecology, health, ecosystems, and complexity.  Students normally focus on one particular problem and region to illustrate concepts and methods. *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Perspectives on Resource and Environmental Management', '673', 'GEOG', 'An overview of issues, problems and concepts in international resource and environmental management.  The complex nature of ecological/biophysical, economic, and sociocultural aspects of resource management will be explored, with particular emphasis placed on tracing challenges experienced in developing countries.  *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
