INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Innovation and Economic Development in Cities and Regions', '606', 'ECDEV', 'This course offers a critical examination of the innovation, knowledge creation and circulation, and governance processes that underpin contemporary economic development in cities and regions.  Strategies, policies and programs that are intended to support and promote innovation and economic development in urban and regional economies are analyzed.');
