INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Community Economic Development', '615', 'ECDEV', 'Community Economic Development is a field of theory, process and practice that is concerned with understanding the forces shaping communities and finding sustainable local solutions to economic needs.  This seminar course will examine topics such as capacity-building, asset-based strategies, social capital, poverty-alleviation, social enterprises and co-operatives, and comprehensive community initiatives, using international and local examples and case studies.');
