INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Clinical Applications of Physics in Medicine', '751', 'PHYS', 'This course provides an overview of the application of physics to medicine. The physical concepts underlying the diagnosis and treatment of disease will be explored. Topics will include general imaging principles such as resolution, intensity and contrast; x-ray imaging and computed tomography; radioisotopes and nuclear medicine, SPECT and PET; magnetic resonance imaging; ultrasound imaging and radiation therapy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Molecular Biophysics', '752', 'PHYS', 'Physical methods of determining macromolecular structure: energetics, intramolecular and intermolecular forces, with applications to lamellar structures, information storage, DNA and RNA, recognition and rejection of foreign molecules.');
