INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Astrophysics', '781', 'PHYS', 'Multi-wavelength astronomy: radio, infrared, optical and x-ray observations.  Radiative Processes: macroscopic description, thermal and non-thermal emission, scattering, line transitions and plasma effects.  Gravitational Dynamics: potential and orbits, self-gravitating systems, the Collisionless Boltzmann Equation, gravitational encounters.  Fluid Mechanics: simple fluids, soundwaves and shocks, instabilities and transport mechanisms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cosmology', '787', 'PHYS', 'Friedmann-Robertson-Walker metric and dynamics; big bang thermodynamics; nucleosynthesis; recombination; perturbation theory and structure formation; anisotropies in the Cosmic Microwave Background; statistics of cosmological density and velocity fields; galaxy formation; inflation.');
