INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Project', '499A', 'BIOL', 'A senior-year research project. [Note:  Normally, only students attaining either a 73% or better cumulative major average or a 78% or better major average in their two most recent terms (normally 3A and 3B) will be accepted into this course. May only be taken with the permission of the BIOL 499 co-ordinator. Consult the BIOL 499 manual for details. A final grade for BIOL 499A will be submitted only after completion of 499B. Normally, BIOL 499A and 499B may not be taken concurrently without prior permission of the BIOL 499 co-ordinator]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Project', '499B', 'BIOL', 'A senior-year research project.  [Note:  Normally, only students attaining either a 73% or better cumulative major average or a 78% or better major average in their two most recent terms (normally 3A and 3B) will be accepted into this course. May only be taken with the permission of the BIOL co-ordinator. Consult the BIOL 499 manual for details. A final grade for BIOL 499A will be submitted only after completion of 499B. Normally, BIOL 499A and 499B may not be taken concurrently without prior permission of the BIOL 499 co-ordinator]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Optimization (Non-Specialist Level)', '227', 'CO', 'A broad introduction to the field of optimization, discussing applications, and solution techniques. Mathematical models for real life applications; algorithms: simplex, cutting plane, and branch & bound; linear programming duality. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Deterministic OR Models (Non-Specialist Level)', '327', 'CO', 'An applications-oriented course that illustrates how various mathematical models and methods of optimization can be used to solve problems arising in business, industry, and science. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Combinatorial Enumeration', '330', 'CO', 'The algebra of formal power series. The combinatorics of the ordinary and exponential generating series. Lagranges implicit function theorem, applications to the enumeration of permutations, functions, trees and graphs. Integer partitions, geometric methods, enumerating linear transformations. Introduction to the pattern algebra, applications to the enumeration of strings. Lattice paths, Wiener-Hopf factorization. Enumeration under symmetries. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Coding Theory', '331', 'CO', 'A first course in error-correcting codes. Linear block codes, Hamming-Golay codes, and multiple error-correcting BCH codes are studied. Various encoding and decoding schemes are considered. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Graph Theory', '342', 'CO', 'An introduction to some of the key topics in graph theory: connectivity, planarity, and matchings. Connectivity: Mengers theorem, 3-connected graphs. Planarity: Kuratowskis theorem, uniqueness of planar embeddings. Matchings: Review of Konigs theorem, Tuttes theorem. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Optimization', '250', 'CO', 'A broad introduction to the field of optimization, discussing applications, and solution techniques. Mathematical models for real life applications; algorithms; aspects of computational complexity; geometry; linear programming duality, focusing on the development of algorithms. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Network Flow Theory', '351', 'CO', 'Review of linear programming. Shortest path problems. The max-flow min-cut theorem and applications.  Minimum cost flow problems.  Network simplex and primal-dual algorithms.  Applications to problems of transportation, distribution, job assignments, and critical-path planning. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Optimization (Advanced Level)', '255', 'CO', 'Linear optimization: feasibility theorems, duality, the simplex algorithm. Discrete optimization: integer linear programming, cutting planes, network flows. Continuous optimization: local and global optima, feasible directions, convexity, necessary optimality conditions. [Note: CO 255 may be substituted for CO 250 whenever the latter is a requirement in an Honours plan. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Nonlinear Optimization', '367', 'CO', 'A course on the fundamentals of nonlinear optimization, including both the mathematical and the computational aspects.  Necessary and sufficient optimality conditions for unconstrained and  constrained problems. Convexity and its applications. Computational  techniques and their analysis. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Deterministic OR Models', '370', 'CO', 'An applications-oriented course that illustrates how various mathematical models and methods of optimization can be used to solve problems arising in business, industry, and science. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mathematical Discovery and Invention', '380', 'CO', 'A course in problem solving. 100 problems are studied. Problems are taken mainly from the elementary parts of algebra, geometry, number theory, combinatorics, and probability. [Note: Offered in the spring term of even years.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algebraic Enumeration', '430', 'CO', 'The Lagrange Implicit Function Theorem, the MacMahon Master Theorem. Enumeration of planar triangulations. The transfer matrix method. Sieve methods, inclusion/exclusion, Mobius inversion. Polya enumeration, Enumeration of trees. Basic hypergeometric series, q-analogues, Rogers-Ramanujan identities. Asymptotic methods.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Combinatorial Designs', '434', 'CO', 'Pairwise orthogonal latin squares. Transversal designs and finite planes. Balanced incomplete block designs, group divisible designs, and pairwise balanced designs. Symmetric designs and Hadamard matrices. Recursive constructions. Wilsons fundamental construction.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Combinatorics', '439', 'CO', 'An undergraduate seminar in combinatorics. The primary objective is to study current work in specific areas of combinatorics. Course content may vary from term to term.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Graph Theory', '440', 'CO', 'An in-depth study of one or two topics in graph theory. Course content may vary from term to term. Topics may include planar graphs, extremal graph theory, directed graphs, enumeration, algebraic graph theory, probabilistic graph theory, connectivity, graph embedding, colouring problems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Graph Theory', '442', 'CO', 'Connectivity (Mengers theorem, ear decomposition, and Tuttes wheels theorem) and matchings (Halls theorem and Tuttes theorem). Flows: integer and group-valued flows, the flow polynomial, the 6-flow theorem. Ramsey theory: upper and lower bounds, explicit constructions. External graph theory: Turans theorem, the Erdos-Gallai theorem. Probabilistic methods. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algebraic Graph Theory', '444', 'CO', 'An introduction to the methods of and some interesting current topics in algebraic graph theory.  Topics covered will include vertex-transitive graphs, eigenvalue methods, strongly regular graphs and may include graph homomorphisms, Laplacians or knot and link invariants.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Combinatorial Optimization', '450', 'CO', 'Characterizations of optimal solutions and efficient algorithms for optimization problems over discrete structures. Topics include network flows, optimal matchings, T-joins and postman tours, matroid optimization. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Integer Programming', '452', 'CO', 'Formulation of problems as integer linear programs. Solution by branch-and-bound and cutting plane algorithms. Introduction to the theory of valid inequalities and polyhedral combinatorics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Scheduling', '454', 'CO', 'An overview of practical optimization problems that can be posed as scheduling problems. Characterizations of optimal schedules. Simple and efficient combinatorial algorithms for easy problems. A brief overview of computational complexity, definition of P, NP, NP-complete and NP-hard. Integer programming formulations, the traveling salesman problem, heuristics, dynamic programming, and branch-and-bound approaches. Polynomial-time approximation algorithms. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Game Theory', '456', 'CO', 'A broad introduction to game theory and its applications to the modeling of competition and cooperation in business, economics, and society. Two-person games in strategic form and Nash equilibria. Extensive form games, including multi-stage games. Coalition games and the core. Bayesian games, mechanism design, and auctions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Continuous Optimization', '466', 'CO', 'Numerical algorithms for nonlinear optimization. Newton, variable-metric, quasi-Newton and conjugate gradient methods. Obtaining derivatives. Convexity. Trust region methods. Constrained optimization including optimality conditions, sequential quadratic programming, interior point, and active set strategies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Mathematics', '480', 'CO', 'An in-depth examination of the origins of mathematics, beginning with examples of Babylonian mathematics. Topics may include Pythagorean triples, solution of equations, estimation of pi, duplication of the cube, trisection of an angle, the Fibonacci sequence, the origins of calculus. [Note: Offered in the spring term of  odd years.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reading in Combinatorics and Optimization', '499', 'CO', 'Reading course as announced by the department.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Issues in Contemporary Indigenous Communities in Canada', '272', 'ANTH', 'An examination of First Nations and Métis cultures and cultural development from the perspective of local Indigenous communities. The course will feature lectures, discussions, and occasional guest speakers representative of the wider Indigenous community.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Issues in Contemporary Indigenous Communities in Canada', '272', 'INDG', 'An examination of First Nations and Métis cultures and cultural development from the perspective of local Indigenous communities. The course will feature lectures, discussions, and occasional guest speakers representative of the wider Indigenous community.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Equilibrium Stage Operations', '200', 'CHE', 'Equilibrium between phases; the equilibrium stage concept. Cascades of stages with and without reflux; group methods and stage-by-stage approaches; graphical solutions. Applications in the separation of components by distillation, absorption, stripping, extraction and leaching. [Offered:  F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Process Data Analysis', '220', 'CHE', 'Introduction to statistical methods for analyzing and interpreting process data.  Introduction to statistical ideas, probability theory, distribution theory, sampling theory, confidence intervals and significance tests. Introduction to regression analysis. Introduction to design of experiments and statistical quality control. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Chemistry 1', '230', 'CHE', 'Thermodynamics: work and heat as forms of energy. First law, internal energy and enthalpy. Heats of chemical and physical changes. Cycles and the second law, entropy. Spontaneity and equilibrium, free energies. Systems of variable composition, chemical equilibrium. Phase equilibrium and the phase rule. Ideal solutions, colligative properties.  [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluid Mechanics', '211', 'CHE', 'Fundamentals of fluid flow. Conservation laws for mass, momentum and mechanical energy. Flow of fluids in conduits. Flow past immersed bodies. Flow through beds of solids, fluidization. Transportation and metering of fluids. Dimensional analysis. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Chemistry 2', '231', 'CHE', 'Thermodynamics: ideal solutions; non-ideal solutions, non-electrolytic and electrolytic solutions, phase equilibrium and phase diagrams, reaction equilibrium.  Surface phenomena: surface tension, capillarity, properties of small particles, adsorption.  Chemical kinetics: rate laws, reaction rates, mechanisms, catalysis, heterogeneous reactions. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Bioprocess Engineering', '361', 'CHE', 'Review of elementary aspects of microbiology, biochemistry, molecular biology, and genetic engineering. Introduction of biological systems for the production of commercial goods and services, e.g., foods, pharmaceuticals, chemicals, fuels, diagnostics, waste treatment, and biomaterials. Introduction to design of bioprocess systems, including biosafety and sustainability. Development of reaction kinetics associated with biological systems. Quantification of metabolism. Development of material balances for key constituents in bioreactors operated in different modes, e.g., batch, fed-batch, continuous stirred-tank reactor (CSTR), perfusion, recycle. Introduction to mass and heat transfer considerations for bioreactors. Dynamic simulation of cultures defined by ordinary differential equations. Introduction of downstream processes associated with biological systems and recovery of biological products. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Engineering Thermodynamics', '330', 'CHE', 'Review of fundamentals, including 2nd law and concepts of equilibrium, phase and reaction equilibria, fugacity, exergy. Thermodynamics applied to practical situations. Examples chosen from: fluid flow; power generation; refrigeration; air conditioning and water cooling; liquefaction of gases; equilibria in complex chemical reactions and separation processes; surface phenomena; electrochemical reactions; biological processes. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Reaction Engineering', '314', 'CHE', 'Review of stoichiometry and chemical kinetics. Homogeneous reactors: isothermal operation; batch; semi-batch; continuous tank; plug flow reactor design. Continuous stirred-tank reactors (CSTRs) in series; plug flow reactor with recycle. Multiple reactions in reactor networks. Temperature effects in adiabatic and non-isothermal reactors. Yield, selectivity and optimal operation of reactors. Heterogeneous catalysis and effectiveness factors in two-phase reactors. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electrochemical Engineering', '331', 'CHE', 'Topics and applications of electrochemistry and electrochemical engineering. Industrial process examples. Environmental aspects. Ionic equilibria. Laws of electrolysis. Theory of electrolytes. Transport properties of electrolytes. Reversible cell potentials. Irreversible electrode processes. Thermodynamic and kinetic aspects of corrosion. Common examples of corrosion. Electrochemical energy conversion and storage. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Research Project', '498', 'CHE', 'Directed research project under the supervision of faculty members.  Participation will give students experience in advanced research techniques, with valuable training for those potentially interested in graduate school or industrial research careers.  Taken over and above normal course load.  Good standing and permission of department required for registration.  [Note:  This course graded as CR/NCR.  Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Group Design Project and Symposium', '483', 'CHE', 'Completion of the design project cycle started in CHE 482 and communication of the engineering design work. Submission of a written final report. Lecture-style technical presentation by group members. Poster-style technical presentation with group members available to discuss the project. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elective Research Project', '499', 'CHE', 'A major undergraduate research project carried out as a technical elective (TE) under the supervision of a faculty member. An oral presentation of results and a written report are the minimum requirements. Other requirements may be set by the faculty supervisor or department. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Engineering Concepts 1', '100', 'CHE', 'Introduction to basic methods and principles in chemical engineering. The fundamentals of engineering calculations (units and dimensions), behaviour of fluids, mass balances, processes, and process variables. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Engineering Concepts 2', '101', 'CHE', 'Development of chemical process analysis skills. Introduction to single- and multi-phase physical equilibria. Material and energy balances in reactive and non-reactive systems. Introduction to investigation and technical communication through laboratory experiments illustrating the physical principles discussed. (In the Winter term only: brief review of co-op fundamentals.) [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemistry for Engineers', '102', 'CHE', 'Chemical principles with applications in engineering. Stoichiometric calculations, properties of gases, properties of liquids and solutions, gas phase chemical equilibrium, ionic equilibrium in aqueous solution, oxidation-reduction reactions, chemical kinetics. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Petroleum Production', '514', 'CHE', 'Fundamentals of surface chemistry, capillary pressure, and wettability. Petrophysics, measurement, and interpretation of electrical, capillary, and flow properties of reservoir rock. Hydrostatic pressure regimes and estimation of oil and gas reserves. Darcys law and modelling of steady-state and transient incompressible and compressible single-phase flow through porous media. Thermodynamics of petroleum fluids. Material balance for oil and gas reservoirs: subsurface withdrawal and primary production mechanisms. Oil well testing. Two-phase flow in oil reservoirs, relative permeability, Buckley-Leverett theory of linear water flooding and sweep efficiency. Introduction to enhanced oil recovery. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Process Dynamics and Control', '522', 'CHE', 'State space methods. Sampled-data systems. Discrete systems. Transform methods. Multivariable control. Computer control. Closed-loop analysis. Design of controllers. Control of complex chemical systems. [Offered:  W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Process Control Laboratory', '524', 'CHE', 'Experiments on process dynamics, control, and simulation of processes. Time constant; step and frequency response; controller tuning; multivariable control strategies. Implementation using simulation systems, mainframe computer control, microcomputers. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Bioprocess Engineering', '562', 'CHE', 'Application of process engineering principles to the design and operation of fermentation reactors which are widely used in the pharmaceutical, food, brewing, and waste treatment industries. Aspects of mass transfer, heat transfer, mixing, and rheology with biochemical and biological constraints. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Food Process Engineering', '564', 'CHE', 'Applications of unsteady and steady state heat and/or mass transfer operations to processing natural and texturized foods. Design and analysis of sterilization, low temperature preservation, concentration, separation and purification processes. Effects of formulation, additives and processing on organoleptic and nutritional quality. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Air Pollution Control', '572', 'CHE', 'Nature and sources of air pollutants.  Transport of pollutants and dispersion modeling for regulatory purposes. Design of industrial particulate capture systems using cyclones, electrostatic precipitators, filters, scrubbers.  Design of organic compound emissions control using incineration, biofiltration, adsorption and absorption.  Overview of NOx and SOx control.  Indoor air quality assessment techniques.  [Offered:  W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Industrial Wastewater Pollution Control', '574', 'CHE', 'Primary focus is on the control and treatment of inorganic aqueous waste from chemical process industries.  Waste minimization methods with specific examples such as rinsewater circuit design.  Principles and design of treatment methods:  chemical treatment, precipitation, coagulation and flocculation, ion exchange and membrane separation.  Treatment of organic aqueous waste.  [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry for Engineering', '262', 'CHEM', 'Bonding, structure, and nomenclature in organic chemistry. Physical properties and simple reactions associated with the important functional groups. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry Laboratory for Engineering Students', '262L', 'CHEM', 'Selected experiments for engineering students taking CHEM 262. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('General Chemistry 1', '120', 'CHEM', 'The stoichiometry of compounds and chemical reactions. Properties of gases. Periodicity and chemical bonding. Energy changes in chemical systems. Electronic structure of atoms and molecules; correlation with the chemical reactivity of common elements, inorganic and organic compounds. [Note: Science students must also take CHEM 120L. Successful completion of Grade 12 U Calculus and Vectors and Grade 12 U Chemistry or equivalent courses is recommended; Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('General Chemistry Laboratory 1', '120L', 'CHEM', 'Selected experiments based on introductory-level chemistry topics. This course is an introduction to the chemistry laboratory environment and focuses on the development of basic lab skills. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical and Chemical Properties of Matter', '121', 'CHEM', 'A first-year Chemistry course for students in Chemistry-based programs. The stoichiometry of compounds and chemical reactions. Properties of gases. Periodicity and chemical bonding. Energy changes in chemical systems. Electronic structure of atoms and molecules; correlation with the chemical reactivity of common elements, inorganic and organic compounds. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Reaction Laboratory 1', '121L', 'CHEM', 'Selected experiments for students taking CHEM 121. This course is an introduction to the chemistry laboratory environment and focuses on the development of basic lab skills. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('General Chemistry 2', '123', 'CHEM', 'Properties of liquids and solutions. Introduction to chemical equilibria. Principles of acid-base equilibria, solubility and electrochemical processes. Chemical kinetics. [Note: Science students must also take CHEM 123L; Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('General Chemistry Laboratory 2', '123L', 'CHEM', 'Selected experiments based on introductory-level chemistry topics. This course is a continuation of CHEM 120L, with increased emphasis on assessment of experimental design. [Note: Students who are not taking, or who have not previously taken CHEM 123, will be removed from CHEM 123L; Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Reactions, Equilibria and Kinetics', '125', 'CHEM', 'A first-year chemistry course for students in chemistry-based programs. Properties of liquids and solutions. Introduction to chemical equilibria. Principles of acid-base equilibria, solubility, and electrochemical processes. Chemical kinetics. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Reaction Laboratory 2', '125L', 'CHEM', 'Selected experiments for student taking CHEM 125. This course is a continuation of CHEM 121L, with increased emphasis on assessment of experimental design. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structure and Bonding', '212', 'CHEM', 'An introduction to the principles of chemical structure and bonding, with emphasis on their application to inorganic systems. Topics include atoms, orbitals, and periodicity; localized bonding models; symmetry and group theory; and molecular orbital theory. The subjects treated in this course are foundational components for advanced studies in all areas of chemistry. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intro Analytical Chemistry', '220', 'CHEM', 'Quantitative and analytical chemistry including ionic equilibria, classical and more recent methods. Emphasis on planning and decision-making in the analytical process.  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Quantitative Chemical Analysis Laboratory', '220L', 'CHEM', 'Selected experiments for students taking CHEM 220. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Multi-Component Analysis', '221', 'CHEM', 'Instrumental analytical chemistry, including traditional and more recent methods.  Emphasis on planning and decision-making in the analytical process.  [Offered W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Analytical Instrumentation Laboratory', '224L', 'CHEM', 'Extensive lab experience for students who have taken CHEM 220. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Biochemistry', '233', 'CHEM', 'Chemistry of amino acids, carbohydrates, lipids and nucleic acids, with special emphasis on representative proteins and enzymes, including hemoglobin, cytochrome c and chymotrypsin.  [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Biochemistry', '237', 'CHEM', 'An introduction to the chemistry of amino acids, carbohydrates, lipids and nucleic acids. Structure and properties of proteins and enzymes. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Biochemistry Laboratory', '237L', 'CHEM', 'Selected experiments for students taking CHEM 237. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Chemistry Laboratory 1', '250L', 'CHEM', 'This laboratory provides an introduction to physical chemistry experimentation. The experiments are designed to illustrate the principles and theories of thermodynamics and employ a variety of important techniques, including spectroscopy, conductivity, and calorimetry. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Chemical Thermodynamics', '254', 'CHEM', 'An introduction to the first, second, and third laws of thermodynamics and the application of these laws to ideal systems, mixtures, and chemical reactions. Thermodynamic principles are used to study changes in state, including phase changes, and to establish the link between the equilibrium constant and the properties of the substances involved in a chemical reaction. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Quantum Mechanics', '356', 'CHEM', 'Historical background; the differential equation approach to quantum mechanics; treatments of solvable problems such as the particle-in-a-box, harmonic oscillator, rigid rotator and the hydrogen atom; introduction to approximation methods for more complicated systems. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry 1', '264', 'CHEM', 'Structure and bonding in organic chemistry. Isomerism and stereoisomerism in organic compounds. Acidity of organic compounds and substituent effects on acidity. Reaction mechanisms and energetics. Chemistry of alkanes, haloalkanes, alcohols and ethers, alkenes and alkynes. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry 2', '265', 'CHEM', 'Nucleophilic addition and substitution at CO carbon. Aromaticity and simple MO theory of conjugated systems. Electrophilic and nucleophilic aromatic substitution reactions. Applications of spectroscopic techniques in organic chemistry. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry Laboratory 1', '265L', 'CHEM', 'Selected experiments for students taking CHEM 265. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Basic Organic Chemistry 1', '266', 'CHEM', 'Structure, nomenclature, and reactions of important classes of organic compounds.  Stereochemistry and its role in reaction mechanisms. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry Laboratory', '266L', 'CHEM', 'Selected experiments for students taking (or who have taken) CHEM 266. [Note: Lab alternate weeks. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Basic Organic Chemistry 2', '267', 'CHEM', 'A continuation of the concepts of CHEM 266, including material on amines, aromatics, enols and enolates, and nucleophilic addition and substitution at the carbonyl group. Introduction to nuclear magnetic resonance and infrared spectroscopy. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry Laboratory', '267L', 'CHEM', 'Selected experiments for students taking CHEM 267. [Note:  Lab alternate weeks. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transition Element Compounds and Inorganic Materials', '310', 'CHEM', 'The inorganic, organic, and solid state chemistry of the d-block elements. The structure and physical properties of co-ordination compounds and transition metal containing solids. The role of transition metal organometallics in catalysis. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Inorganic Chemistry Laboratory 2', '310L', 'CHEM', 'Synthesis of transition and non-transition metal compounds. Characterization of compounds using IR, UV-VIS, and NMR spectroscopy. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Main Group and Solid State Chemistry', '313', 'CHEM', 'This course provides a detailed examination of the structure and bonding in main group and solid state compounds, including valence bond and molecular orbital theory for describing electronic structures, Hueckel and extended Hueckel approximations.  Structures of simple solids, including close packing of spheres and derived ionic lattice types; aspects of chemical crystallography, Bravais lattices, point groups, space groups, crystal planes, and X-ray diffraction;  Ionic interactions in gases and solution;  the thermodynamics of acid-base interactions; descriptive chemistry and characterization of  main group element compounds. [Offered:  W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Analytical Instrumentation', '323', 'CHEM', 'Detailed study of selected instruments and instrumental methods. Introduction to chemometrics and to computer interfacing. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Metabolism 1', '333', 'CHEM', 'Metabolism of carbohydrates, lipids and amino acids. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Biochemistry Laboratory', '335L', 'CHEM', 'Selected experiments for students having completed or concurrently taking CHEM 331.  Topics to be covered include:  NMR, allostery, enzymology, electrophoresis, carbohydrates, lipids, photosynthesis, and respiration. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Kinetics and Statistical Mechanics', '350', 'CHEM', 'Basic chemical kinetics; treatment of kinetic data; complex reaction mechanisms; fast reactions; the canonical ensemble and the canonical partition function; statistical mechanics applied to chemistry; statistical theory of reaction rates. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Chemistry Laboratory 2', '350L', 'CHEM', 'This advanced laboratory builds upon fundamental knowledge of physical chemistry gained in the introductory physical chemistry laboratory, CHEM 250L. The experiments are designed to illustrate the principles and theories of thermodynamics, kinetics, spectroscopy, and quantum mechanics. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Biochemistry', '357', 'CHEM', 'The use of diffusion, ultracentrifugation, osmotic pressure, electrophoresis and X-ray diffraction to study the properties of biopolymers. Hyperbolic and allosteric enzyme kinetics, inhibition, and regulation. Some spectroscopies important to the life sciences. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Chemistry 3', '360', 'CHEM', 'Enolate alkylation, condensation, conjugate addition and pericyclic reactions. FMO theory, organometallics and transition metal-catalyzed C-C bond formation. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Organic Chemistry Laboratory', '360L', 'CHEM', 'Selected microscale synthetic experiments for students in Year Three Chemistry and Biochemistry programs, including spectroscopic identification of organic compounds. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organic Process Chemistry', '363', 'CHEM', 'This course is an introduction to the important aspects of Process Chemistry for the industrial production of pharmaceutically active organic compounds. Topics covered include: industrial organic synthesis and process design, scaling to kilogram quantities, green chemistry principles, process safety and hazard identification, industrial separation and purification, and meeting quality guidelines and regulations.  [Offered: F or W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Project 1', '392A', 'CHEM', 'This course is only for exchange students wishing to carry out a research project.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Project 2', '392B', 'CHEM', 'This course is only for exchange students wishing to carry out a research project.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physicochemical Aspects of Natural Waters', '404', 'CHEM', 'Properties of water; chemicals in the environment; environmental fate of inorganic and organic pollutants; basic phenomena affecting the fate of water pollutants (vapor pressure; activity, solubility, partitioning, diffusion, sorption); acids and bases in water; dissolved carbon dioxide; trace metals in water. [Offered: F or W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Metabolism 2', '432', 'CHEM', 'Properties and metabolism of porphyrins, purines, pyrimidines and biogenic amines. Biosynthesis and mode of action of selected cofactors. Structure-function relationships of enzymes. Regulation of enzyme activity. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Biochemistry', '433', 'CHEM', 'Nitrogen fixation. Assimilation of nitrogen. Amino acid metabolism. Metabolic regulation. Proteolytic enzymes, ubiquitin. Blood coagulation. Signal transduction and amplification. Biochemistry of nitric oxide. Biochemistry of vision. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Biochemistry', '430', 'CHEM', 'For a current list of offerings see the Undergraduate Officer. [Note: Instructor may elect to use the third lecture hour for a tutorial or not at all; Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spectroscopy in Organic Chemistry', '464', 'CHEM', 'Elucidation and identification of organic structures by contemporary spectroscopic techniques. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Polymer Science', '370', 'CHEM', 'Basic definitions and polymer nomenclature, molecular weight distributions and averages, molecular weight measurements, step-growth and free radical chain-growth polymerization reactions, chain conformations, glass transition, crystallization, and mechanical properties of polymers. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Project', '494A', 'CHEM', 'Laboratory work on a senior year research project. Enrolment into this course requires permission of the CHEM 494 co-ordinator.  See the CHEM 494 coordinator for course details.  No credit or grade will be provided for this course until the two-term sequence CHEM 494A/B has been completed.  CHEM 494A/B may not be taken concurrently without prior permission of the CHEM 494 co-ordinator.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First-Year Chinese 1', '101R', 'CHINA', 'An introductory course for students who have little or no prior background in writing, speaking, or understanding any dialect of the Chinese language to develop basic listening, speaking, reading, and writing skills. Practical oral and written exercises provide a firm grammatical foundation for further study.  [Note: CHINA 101R is not open to speakers of any Chinese dialect.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First-Year Chinese 2', '102R', 'CHINA', 'With the completion of the study of the rudiments of phonetics (as provided in CHINA 101R), the emphasis in this course shifts to grammar and character writing.  Vocabulary will be expanded to between 500 and 700 words. [Note: CHINA 102R is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Second-Year Chinese 1', '201R', 'CHINA', 'Development of speaking, writing, reading, and listening skills. This course and its follow-up (CHINA 202R) include a survey of grammar, complex sentences, and logical stress.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Second-Year Chinese 2', '202R', 'CHINA', 'The study of Chinese characters receives more emphasis. Grammar instruction includes comparisons and different kinds of complements.  The course includes topics of interest to students illustrating cultural differences between China and the West.  Upon completion of CHINA 201R and 202R, the student should have a reading vocabulary of 1,600 Chinese characters and have learned about 300 key sentence patterns.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Solid Mechanics 1', '204', 'CIVE', 'Three-dimensional force systems, moments, couples, and resultants. Three-dimensional equilibrium problems. Shear stresses in beams. Plastic bending. Beam deflection. Torsion of shafts and thin-walled closed sections. Shear, bending moment, and deflection diagrams for beams. Compound stress and stress transformations. Design concepts.  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Solid Mechanics 2', '205', 'CIVE', 'Frames, arches and suspended structures. Stress and strain transformations. Strain energy. Energy methods. Virtual work. Buckling of columns. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Calculus', '221', 'CIVE', 'Calculus of functions of several variables. Differentiation; partial derivatives of implicit and explicit functions, applications including optimizations. Integration; multiple integrals in various co-ordinate systems with applications. Vector calculus; vector fields, line integrals, surface integrals, and applications. Numerical integration and differentiation. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Calculus', '221', 'GEOE', 'Calculus of functions of several variables. Differentiation; partial derivatives of implicit and explicit functions, applications including optimizations. Integration; multiple integrals in various co-ordinate systems with applications. Vector calculus; vector fields, line integrals, surface integrals, and applications. Numerical integration and differentiation. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Differential Equations', '222', 'CIVE', 'An introduction to linear and partial differential equations. Standard methods of solution, applications to physical and engineering problems, linear equations with constant coefficients, basic systems of differential equations, partial differential equations. Applications. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Probability and Statistics', '224', 'CIVE', 'Role of probability in engineering and decision-making under uncertainty. Data analysis. Basic probability concepts. Probability distributions. Functions of random variables. Estimation theory. Empirical determination of distribution models. Regression analysis. Introduction to risk. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structure and Properties of Materials', '265', 'CIVE', 'A basic course in structure, behaviour, and uses of engineering materials. Topics include monotonic and cyclic stress-strain behaviour of metals. Phase diagrams. Diffusion, nucleation and growth of grains. Metallurgy and mechanical properties of irons and steels. Structure and mechanical properties of wood, cements, and concrete. Fracture, fatigue, and corrosion. Three lab sessions. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluid Mechanics', '280', 'CIVE', 'An introduction to fluid mechanics. Fluid properties. Review of fluid statics. Buoyancy. Bernoulli equation. The momentum equation and applications. Laminar and turbulent flow. Dimensionless numbers. Closed conduit flow including friction losses. Pipe network analysis. Pump systems. Four lab sessions. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structural Analysis', '303', 'CIVE', 'Analysis of statically indeterminate structures using force and displacement methods. Influence lines for indeterminate structures. Introduction to the matrix stiffness method. Computer applications using commercial structural analysis software. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Solid Mechanics 3', '306', 'CIVE', 'Membrane stresses in shells. Buckling. Beams on elastic foundations. Plane elasticity. Torsion of non-circular sections. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geotechnical Engineering 1', '353', 'CIVE', 'An introduction to geologic processes. Subsurface exploration. Classification systems. Weight-volume relationships. Soil mechanics principles including state of stress, ground water flow, consolidation and shear strength. Four lab sessions. [Offered as: CIVE 353 (W), GEOE 353 (S)]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geotechnical Engineering 1', '353', 'GEOE', 'An introduction to geologic processes. Subsurface exploration. Classification systems. Weight-volume relationships. Soil mechanics principles including state of stress, ground water flow, consolidation and shear strength. Four lab sessions. [Offered as: CIVE 353 (W), GEOE 353 (S)]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geotechnical Engineering 2', '354', 'GEOE', 'Foundation engineering. Earth pressure theories. Retaining walls. Anchors. Shallow and deep foundations. Braced trenches and excavations. Slope stability. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geotechnical Engineering 2', '354', 'CIVE', 'Foundation engineering. Earth pressure theories. Retaining walls. Anchors. Shallow and deep foundations. Braced trenches and excavations. Slope stability. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Engineering Principles', '375', 'CIVE', 'Water quality, air pollution, fate and transport of contaminants in natural and engineered systems, and pollution prevention. Solid and hazardous waste management. Water and wastewater treatment systems and design principles. Four lab sessions. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Civil Engineering Design Project 1', '400', 'CIVE', 'Students must undertake an independent civil engineering design project during the last two terms of their plan. The purpose of the project is to demonstrate students abilities to practise in a civil engineering capacity in their chosen area of expertise, using knowledge gained from their academic and employment experiences. The first part of the project (CIVE 400) will include problem identification, generation and selection of solutions, and time management. Incorporation of technical, ecological, social, political, and economic issues in the solution for the project will be required. A basic requirement of the proposed solution is that it must be compatible with the principles of sustainability. Requirements include proposal, progress report, oral presentation, and a final report containing recommendations for part two of the project, CIVE 401. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Civil Engineering Design Project 2', '401', 'CIVE', 'A continuation of CIVE 400. The final design of the major civil engineering project proposed in CIVE 400 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design of the solution proposed in CIVE 400. Requirements of this part of the two-term project include a symposium presentation and a final report. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structural Dynamics', '505', 'CIVE', 'Dynamics of discrete and continuous structures. Free and forced vibrations of single and multi-degree of freedom systems. Shock loads, earthquake loads, response spectra. Analysis and design of frames for shock and earthquake loads. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Building Science and Technology', '507', 'CIVE', 'The building process. Loadings; gravity, wind, thermal, moisture, and fire. Enclosure design; walls, windows, and roof. Subgrade construction. Energy related considerations. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structural Steel Design', '413', 'CIVE', 'Advanced coverage of design of structural steel members and connections, building on CIVE 310. Design of laterally-unsupported beams, compression members and beam-columns. Plate girders. Connections. Special topics in design of structural steel or other metals. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structural Concrete Design', '414', 'CIVE', 'Advanced analysis and design of concrete members for flexure. Beam shear analysis and design. D-region shear design. Column design. Special topics. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structural System Design', '415', 'CIVE', 'Building loads. Lateral load systems. Floor systems for buildings. Composite construction. Introduction to bridge design. Special topics.[Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finite Element Analysis', '422', 'CIVE', 'This course focuses on the development of the basic fundamentals of finite element method with applications in fluid flow, mass transport, solid mechanics and structures. Topics include discrete problems, matrix methods, variational principle, method of weighted residuals, element shapes, and interpolation functions. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transit Planning and Operations', '440', 'CIVE', 'The historical evolution of transit in cities; the technological innovations which made transit possible; and transit mode definitions. Models of transit vehicle motion are presented; transit travel times under different travel regimes are derived. Transit scheduling methods are shown. System operational characteristics are defined and quantitative measures of effectiveness are introduced. Transit network planning objectives are identified; actual geometries are qualitatively and quantitatively analyzed. Transit ownership structures and economics are discussed; contemporary ITS applications are presented. Methods for selecting appropriate transit modes are covered. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transit Planning and Operations', '478', 'PLAN', 'The historical evolution of transit in cities; the technological innovations which made transit possible; and transit mode definitions. Models of transit vehicle motion are presented; transit travel times under different travel regimes are derived. Transit scheduling methods are shown. System operational characteristics are defined and quantitative measures of effectiveness are introduced. Transit network planning objectives are identified; actual geometries are qualitatively and quantitatively analyzed. Transit ownership structures and economics are discussed; contemporary ITS applications are presented. Methods for selecting appropriate transit modes are covered. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Pavement Structural Design', '542', 'CIVE', 'Pavement design, soil identification, subgrade design, base courses, flexible pavement design, design and testing of asphaltic concrete mixes, surface treatments. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Traffic Simulation Modelling and Applications', '343', 'CIVE', 'Fundamental knowledge on the principles and applications of traffic simulations. System theory, traffic flow dynamics, stochastic simulation methods. Calibration and validation of simulation models, and interpretation and analysis of simulation output. Applications of state-of-the-art computer simulation software packages for solving real traffic engineering problems, involving scenario analysis, prediction, and optimization. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geotechnical Engineering 3', '554', 'CIVE', 'Simulation of geotechnical consulting practice. Students are required to complete several projects, based on actual case studies, which require problem identification, evaluation of geotechnical data, analysis, design, and report preparations. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geotechnical Engineering 3', '554', 'GEOE', 'Simulation of geotechnical consulting practice. Students are required to complete several projects, based on actual case studies, which require problem identification, evaluation of geotechnical data, analysis, design, and report preparations. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Biomechanics', '574', 'ME', 'Introduction to engineering technologies applicable to the field of biomechanics. Specific topics covered may include biological growth, form and function; biomaterials; kinematics and neurology of gait; biotribology; joint anatomy, function and repair; occupational biomechanics; trauma prevention. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Biomechanics', '460', 'CIVE', 'Introduction to engineering technologies applicable to the field of biomechanics. Specific topics covered may include biological growth, form and function; biomaterials; kinematics and neurology of gait; biotribology; joint anatomy, function and repair; occupational biomechanics; trauma prevention. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design of Urban Water Systems', '583', 'CIVE', 'Design of water supply and distribution systems. Design of waste and storm water collection systems. Storm water management. The course consists of 24 hours of lectures and a subdivision design project. The emphasis is on computer aided design and sustainability, using commonly used software packages. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design of Urban Water Systems', '583', 'ENVE', 'Design of water supply and distribution systems. Design of waste and storm water collection systems. Storm water management. The course consists of 24 hours of lectures and a subdivision design project. The emphasis is on computer aided design and sustainability, using commonly used software packages. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Law and Ethics', '491', 'CIVE', 'Background (Charter of Rights and Freedoms), contracts, torts (negligent malpractice), forms of carrying on business, professional practice (professional engineers act, joint practice rules, professional misconduct and sexual harassment), alternate dispute resolution, construction liens, intellectual property (patents, trade marks, copyrights and industrial designs), labour relations and employment law, environmental law. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Construction Engineering', '596', 'CIVE', 'Topics in construction engineering and management including methods of delivering construction, contractual relationships, prevailing construction practices, construction equipment, concrete form design, concrete, steel, and masonry construction, introduction to trenchless technology, construction safety, planning and scheduling of repetitive construction, cash flow analysis, and construction project control. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('An Introduction to Classical Studies', '100', 'CLAS', 'An introduction to Greek and Roman civilization, focusing on six key aspects of the discipline of classical studies: history, literature, philosophy, myth and religion, art and architecture, and classical archaeology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Love, Life, and Death in Greece', '201', 'CLAS', 'This course surveys how different people gave meaning to their lives in ancient Greece. It will cover topics ranging from personal identities, culture wars, sports, art, myth, and history, to sex, slavery, and social justice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Love, Life, and Death in Rome', '202', 'CLAS', 'This course surveys how different people gave meaning to their lives in ancient Roman world. It will cover topics ranging from personal identities, family roles, myth, and history, to sex scandals, slavery, gladiatorial games, and mystery cults.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Greek History', '251', 'CLAS', 'A survey of ancient Greek history, from the Bronze Age to Alexander the Great, emphasizing particularly its political and military aspects.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Greek History', '242', 'HIST', 'A survey of ancient Greek history, from the Bronze Age to Alexander the Great, emphasizing particularly its political and military aspects.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Roman History', '252', 'CLAS', 'A survey of ancient Roman history, from the Republic to the Empire, emphasizing particularly its political and military aspects.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Roman History', '252', 'HIST', 'A survey of ancient Roman history, from the Republic to the Empire, emphasizing particularly its political and military aspects.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medieval Society', '205', 'CLAS', 'A survey of Medieval civilization featuring such topics as the individual, political institutions, art, architecture, religion, philosophy, literature, social life, and leisure activities.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medieval Society', '205', 'MEDVL', 'A survey of Medieval civilization featuring such topics as the individual, political institutions, art, architecture, religion, philosophy, literature, social life, and leisure activities.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sex and Gender in the Ancient World', '311', 'CLAS', 'This course examines a variety of issues around sexuality and gender relations in antiquity, including sexual mores and behaviours; literary, artistic, and philosophical constructs of gender; the roots of misogyny; and the legal and social restrictions placed on women in most ancient societies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Greek and Roman Religion', '325', 'CLAS', 'An examination of the religious beliefs and cult practices of the classical world. Topics include prayer and sacrifice; divination and oracles; temples, priests, and festivals; mystery cults and their relation to Christianity.  [Note: This course fulfils an Area 1 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Greek and Roman Religion', '315', 'RS', 'An examination of the religious beliefs and cult practices of the classical world. Topics include prayer and sacrifice; divination and oracles; temples, priests, and festivals; mystery cults and their relation to Christianity.  [Note: This course fulfils an Area 1 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Science and Technology of Ancient Greece and Rome', '384', 'CLAS', 'A study of scientific thought and achievements in such areas as astronomy, biology, anatomy, and medicine, and of the technological skills which produced and distributed raw materials, manufactured goods, and agricultural products.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Classical Studies Abroad', '390', 'CLAS', 'This course features a combination of academic study and first-hand investigation of museums and ancient sites, normally in Greece and/or Italy. [Note: This is a concentrated study course (block format) normally offered in a spring term. This is a repeatable course, subject to different content; it may be completed a total of four times. This course incurs extra costs for the student above and beyond the registration fee in the form of travel, room and board, and incidental costs.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Thesis', '490A', 'CLAS', 'Students wishing to undertake a senior honours thesis in their fourth year should consult the departments undergraduate officer.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Thesis', '490B', 'CLAS', 'Continuation of the Senior Honours Thesis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Study', '492', 'CLAS', 'Under exceptional circumstances, and only with the prior approval of the department, a student may substitute an individualized course of study at the senior level. Such circumstances might include, for example, the students participation in an approved archaeological dig. For further details, consult the department.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary Croatian 1', '101', 'CROAT', 'For students with little or no knowledge of Croatian. The basic elements of Croatian grammar with emphasis on oral practice and pronunciation.
[Note: CROAT 101 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary Croatian 2', '102', 'CROAT', 'A continuation of CROAT 101. [Note: CROAT 102 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computing Through Applications', '100', 'CS', 'Using personal computers as effective problem solving tools for the present and the future. Effective use of spreadsheets to process, manipulate, and visualize numeric and textual information. Introduction to the Internet, World Wide Web, HTML, and XML. Algorithms underlying the functional components of web search engines and their influence on data access. Using wikis to publish, reshape, and organize data collaboratively. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Concepts for Advanced Computer Usage', '200', 'CS', 'Important concepts underlying major personal computer application categories; methodologies for learning and evaluating software; operating system and hardware design from the users point of view, with implications for maintaining a personal computer. Students are encouraged to use their own personal computer for assignments.  Social media and the effect of technology on society are explored throughout the course. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computers and Computer Systems', '230', 'CS', 'Basic computer architecture, organization, system services, and software. Typology of  processors, memory, I/O devices, and their performance. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work. Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Data Types and Structures', '234', 'CS', 'Top-down design of data structures. Using representation-independent data types. Introduction to commonly used data types, including lists, sets, mappings, and trees. Selection of data representation. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Data Structures and Data Management', '240', 'CS', 'Introduction to widely used and effective methods of data organization, focusing on data structures, their algorithms, and the performance of these algorithms. Specific topics include priority queues, sorting, dictionaries, data structures for text processing. [Note: Enrolment is restricted; see Note 1 above. Lab is not scheduled and students are expected to find time in open hours to complete their work. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations of Sequential Programs', '241', 'CS', 'The relationship between high-level languages and the computer architecture that underlies their implementation, including basic machine architecture, assemblers, specification and translation of programming languages, linkers and loaders, block-structured languages, parameter passing mechanisms, and comparison of programming languages. [Note: Enrolment is restricted. Lab is not scheduled and students are expected to find time in open hours to complete their work. CS 251 is a recommended corequisite. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Object-Oriented Software Development', '246', 'CS', 'Introduction to object-oriented programming and to tools and techniques for software development. Designing, coding, debugging, testing, and documenting medium-sized programs: reading specifications and designing software to implement them; selecting appropriate data structures and control structures; writing reusable code; reusing existing code; basic performance issues; debuggers; test suites. [Note: Enrolment is restricted; see Note 1 above. Lab is not scheduled and students are expected to find time in open hours to complete their work. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computer Organization and Design', '251', 'CS', 'Overview of computer organization and performance. Basics of digital logic design. Combinational and sequential elements. Data representation and manipulation. Basics of processor design. Pipelining. Memory hierarchies. Multiprocessors. [Note: Students enrolled in Digital Hardware Specialization should enrol in ECE 222. Enrolment is restricted; see Note 1 above. Lab is not scheduled and students are expected to find time in open hours to complete their work. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Statistical Problem Solving', '316', 'STAT', 'This is an applications oriented course which prepares the nonmathematical student to use statistical software as a research tool. Topics include aids for statistical analysis and the preparation of documents such as reports and theses. The course provides sufficient background for application to other problems specific to the individuals field.  [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Management Information Systems', '330', 'CS', 'An introduction to information systems and their strategic role in business. Topics include types of information systems, organizational requirements, systems development strategies, decision support systems, data and information management, and information systems management, control, and implementation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computer Applications in Business: Databases', '338', 'CS', 'A user-oriented approach to the management of large collections of data. Methods used for the storage, selection, and presentation of data. Common database management systems. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algorithms', '341', 'CS', 'The study of efficient algorithms and effective algorithm design techniques. Program design with emphasis on pragmatic and mathematical aspects of program efficiency. Topics include divide and conquer algorithms, recurrences, greedy algorithms, dynamic programming, graph search and backtrack, problems without algorithms, NP-completeness and its implications. [Note: Enrolment is restricted; see Note 1 above. Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to the Theory of Computing', '360', 'CS', 'Models of computers including finite automata and Turing machines. Basics of formal languages with applications to the syntax of programming languages. Alternate characterizations of language classes. Proving unrecognizability.  Unsolvable problems and their relevance to the semantics of programming. [Note: Enrolment is restricted; see Note 1 above.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Numerical Computation', '370', 'CS', 'Principles and practices of basic numerical computation as a key aspect of scientific computation. Visualization of results. Approximation by splines, fast Fourier transforms, solution of linear and nonlinear equations, differential equations, floating point number systems, error, stability. Presented in the context of specific applications to image processing, analysis of data, scientific modeling. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applications Software Engineering', '430', 'CS', 'An investigation into the role and function of software engineering practice in the construction of computer based systems. Topics include: requirements and specification; documentation techniques; analysis and design; implementation; testing and maintenance; management issues.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Networks and Distributed Computer Systems', '436', 'CS', 'An introduction to networks, protocols, and distributed systems. Layered models, resource management, naming, addressing and routing, reliable communication, security, and higher-level services. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Stochastic Simulation Methods', '340', 'STAT', 'Random variate generation in the univariate and multivariate case, Monte Carlo integration, advanced computer implementation, variance reduction, statistical analysis of simulated data, extensions to challenging simulation problems. Mathematical treatment of the underlying stochastic concepts and proofs. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Programming Languages', '442', 'CS', 'An exposure to important concepts and issues in contemporary programming languages. Data types, abstraction, and polymorphism. Program structure. Lambda calculus and functional programming, logic programming, object-oriented programming. Semantics of programming languages. Critical comparison of language features and programming methodologies using examples drawn from a variety of programming languages including Lisp, Prolog, ML, Ada, Smalltalk, Icon, APL, and Lucid. Programming assignments involve the use of some of these languages. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Compiler Construction', '444', 'CS', 'Phases of compilation. Lexical analysis and a review of parsing. Compiler-compilers and translator writing systems. LEX and YACC. Scope rules, block structure, and symbol tables. Runtime stack management. Parameter passage mechanisms. Stack storage organization and templates. Heap storage management. Intermediate code. Code generation. Macros. [Note: This course involves project work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Software Requirements Specification and Analysis', '445', 'CS', 'Introduces students to the requirements definition phase of software development. Models, notations, and processes for software requirements identification, representation, analysis, and validation. Cost estimation from early documents and specifications. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Software Requirements Specification and Analysis', '451', 'ECE', 'Introduces students to the requirements definition phase of software development. Models, notations, and processes for software requirements identification, representation, analysis, and validation. Cost estimation from early documents and specifications. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Software Design and Architectures', '446', 'CS', 'Introduces students to the design, implementation, and evolution phases of software development. Software design processes, methods, and notation. Implementation of designs. Evolution of designs and implementations. Management of design activities. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Software Design and Architectures', '452', 'ECE', 'Introduces students to the design, implementation, and evolution phases of software development. Software design processes, methods, and notation. Implementation of designs. Evolution of designs and implementations. Management of design activities. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Software Testing, Quality Assurance, and Maintenance', '447', 'CS', 'Introduces students to systematic testing of software systems. Software verification, reviews, metrics, quality assurance, and prediction of software reliability and availability. Related management issues. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Software Testing, Quality Assurance, and Maintenance', '453', 'ECE', 'Introduces students to systematic testing of software systems. Software verification, reviews, metrics, quality assurance, and prediction of software reliability and availability. Related management issues. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Database Management', '348', 'CS', 'The main objective of this course is to introduce students to fundamentals of database technology by studying databases from three viewpoints: those of the database user, the database designer, and the database administrator. It teaches the use of a database management system (DBMS) by treating it as a black box, focusing only on its functionality and its interfaces. Topics include introduction to database systems, relational database systems, database design methodology, SQL and interfaces, database application development, concept of transactions, ODBC, JDBC, database tuning, database administration, and current topics (distributed databases, data warehouses, data mining). [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computer Architecture', '450', 'CS', 'The course is intended to provide the student with an appreciation of modern computer design and its relation to system architecture, compiler technology, and operating system functionality. The course places an emphasis on design based on the measurement of performance and its dependency on parallelism, efficiency, latency, and resource utilization. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Real-time Programming', '452', 'CS', 'Intended to give students experience with tools and techniques of real-time programming, this course includes not only issues of microcomputer architecture and a real-time programming language and operating system, but also hands-on experience programming a microcomputer for applications such as process control, data acquisition and communication. [Note: This course involves project work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Distributed Systems', '454', 'CS', 'An introduction to distributed systems, emphasizing the multiple levels of software in such systems. Specific topics include fundamentals of data communications, network architecture and protocols, local-area networks, concurrency control in distributed systems, recovery in distributed systems, and clock synchronization. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('System Performance Evaluation', '457', 'CS', 'Basic techniques of system performance evaluation. Specific topics include: performance modeling, discrete event simulation, verification and validation of simulation models, analysis of simulation output, analysis of single server queue and queueing networks, modeling of computer systems, networks, and other queueing or non-queueing systems. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Formal Languages and Parsing', '462', 'CS', 'Languages and their representations. Grammars --Chomsky hierarchy. Regular sets and sequential machines. Context-free grammars -- normal forms, basic properties. Pushdown automata and transducers. Operations on languages. Undecidable problems in language theory. Applications to the design of programming languages and compiler construction.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algorithm Design and Analysis', '466', 'CS', 'Algorithmic approaches and methods of assessment that reflect a broad spectrum of criteria, including randomized algorithms, amortized analysis, lower bounds, approximation algorithms, and on-line algorithms. Particular examples will be chosen from different areas of active research and application.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Information Systems Management', '490', 'CS', 'The integration of business and technical considerations in the design, implementation and management of information systems. Topics include: IS planning and development; business, management, executive, and strategic information systems, including case studies of selected large- scale systems; decision support systems; end-user training and development; systems security, disaster planning and recovery. Practical examples of information systems in industry.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Techniques in Biological Sequence Analysis', '482', 'CS', 'Computer science principles and algorithms in biological sequence analysis.  Topics include algorithms for sequence comparison, for large-scale database search in biological databases, for sequence assembly, for evolutionary tree reconstruction, for identifying important features in DNA and RNA sequences, and underlying computational techniques for understanding strings and trees and for making probabilistic inferences.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Artificial Intelligence', '486', 'CS', 'Goals and methods of artificial intelligence. Methods of general problem solving. Knowledge representation and reasoning. Planning. Reasoning about uncertainty. Machine learning. Multi-agent systems. Natural language processing. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Symbolic Computation', '487', 'CS', 'An introduction to the use of computers for symbolic mathematical computation, involving traditional mathematical computations such as solving linear equations (exactly), analytic differentiation and integration of functions, and analytic solution of differential equations. [Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computer Graphics', '488', 'CS', 'Software and hardware for interactive computer graphics. Implementation of device drivers, 3-D transformations, clipping, perspective, and input routines. Data structures, hidden surface removal, colour shading techniques, and some additional topics will be covered. [Note: This course involves project work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Social Implications of Computing', '492', 'CS', 'This course is designed to consider the problems encountered by individuals, organizations and society as computer technology is adopted, with a view towards assessing possible courses of action.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Readings in Computer Science', '499R', 'CS', 'Reading course as announced by the department.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Theatre and Performance in Context', '200', 'THPERF', 'Students experience, analyze, and interpret a range of works of theatre and performance. Close attention is paid to the original circumstances in which these works were produced, the ways in which they have reflected and influenced the communities that produced them, and their relevance to current performance practice and research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Theatre', '100', 'THPERF', 'This course introduces students to processes of theatre creation including textual analysis, conceptual development, design, rehearsal, production, performance, audience engagement, and self-evaluation. Students develop original productions of influential works while paying close attention to the works relevance and issues such as race, gender, and sustainability. [Note: Prior experience in theatre-making is not required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Performance', '102', 'THPERF', 'This workshop course introduces students to the creative processes of performance in a range of formal and informal settings. Emphasis is placed on the students development as a performer. [Note: Prior performance experience is not required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Performance', '102', 'COMMST', 'This workshop course introduces students to the creative processes of performance in a range of formal and informal settings. Emphasis is placed on the students development as a performer. [Note: Prior performance experience is not required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Performing Text', '221', 'THPERF', 'Students explore techniques to analyze, interpret, and perform texts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Performing the Body', '222', 'THPERF', 'Students explore techniques to access and develop the body as a resource for performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Public Speaking', '223', 'COMMST', 'Theory and practice of public speaking. A workshop course involving design and delivery of various kinds of speeches, and the development of organizational, vocal, listening, and critical skills. Students will be videotaped. [Note: Must attend first class. May be subject to priority enrolment.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Interpersonal Communication', '100', 'COMMST', 'Focuses on the one-to-one, face-to-face communication in both the personal and professional realms. Such topics as verbal and non-verbal interactions, listening, and the better management of interpersonal communication will be studied.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Interviewing', '225', 'COMMST', 'Theory and practice of interviewing. A workshop course which teaches theory, design, and presentation of interviews. Videotaping student exercises will enhance interview design and delivery, as well as listening and critical skills.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Technical Production 1', '243', 'THPERF', 'Students develop basic proficiency in the technical elements of intermedial performance and design, including carpentry, lighting, video, projection, sound, wardrobe, and properties. Students apply these concepts in a performance creation project.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Technical Production 2', '244', 'THPERF', 'Students develop intermediate proficiency in the technical elements of intermedial performance and design, including carpentry, lighting, video, projection, sound, wardrobe, and properties. Students apply these concepts in a performance creation project, normally a departmental production.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Performance Creation', '301', 'THPERF', 'Students study, apply, and critique a range of processes for making performance in a range of media, paying close attention to the relationship between the approach to creation and the significance of works in the communities where they are performed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 3', '306', 'THPERF', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 4', '307', 'THPERF', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Early Modern Worlds on Stage', '361', 'ENGL', 'This course explores plays from the English Renaissance in their historical and theatrical contexts. Topics may include playhouses and staging, censorship, and collaboration.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Approaches to Acting with Text', '321', 'THPERF', 'Students apply conceptual and theoretical approaches to acting in traditions emphasizing the text as a resource for performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Approaches to Acting with the Body', '322', 'THPERF', 'Students apply conceptual and theoretical approaches to performance in traditions emphasizing the body as a resource for performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Small Group Communication', '324', 'COMMST', 'A workshop course which works from theory to develop the skills to work in groups effectively. The principles of group dynamics, leadership, and conflict resolution will be studied and implemented in small group meetings and presentations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Performing the Voice', '326', 'THPERF', 'Students explore techniques to access and develop the voice as a resource for performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Performing the Voice', '326', 'COMMST', 'Students explore techniques to access and develop the voice as a resource for performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Stage Management', '343', 'THPERF', 'Students explore approaches to stage management for theatre and performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Approaches to Directing', '361', 'THPERF', 'Exploration of the directors task in its practical, theoretical, and historical aspects.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Performance History', '371', 'THPERF', 'This course explores significant case studies in performance history drawn from the earliest historical records of performance to recent performance events in a variety of social contexts. Close attention is paid to historiographical research methods and the ways they determine our understanding of the past.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 7', '406', 'THPERF', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. Students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 8', '407', 'THPERF', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Selected Seminars in Drama & Theatre Arts', '490', 'THPERF', 'Seminars in special areas of drama and theatre. [Note: Formerly DRAMA 490. This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organizational Communication', '325', 'COMMST', 'This course examines organizational theory, the communication process, and the interplay between the two. Students will develop the skills to identify, analyze, and solve a variety of organizational communication problems through case study group work and other workshop-style methods.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Seminar', '499A', 'THPERF', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in their major area of concentration. [Note: A grade for THPERF/COMMST 499A will be submitted only after the completion of THPERF/COMMST 499B.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Seminar', '499A', 'COMMST', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in their major area of concentration. [Note: A grade for THPERF/COMMST 499A will be submitted only after the completion of THPERF/COMMST 499B.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Seminar', '499B', 'THPERF', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in their major area of concentration. Second part of THPERF/COMMST 499.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Seminar', '499B', 'COMMST', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in their major area of concentration. Second part of THPERF/COMMST 499.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary Dutch 1', '101', 'DUTCH', 'The basic elements of Dutch grammar with emphasis on oral practice and pronunciation. Introduction to aspects of Dutch culture. [Note: DUTCH 101 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary Dutch 2', '102', 'DUTCH', 'A continuation of DUTCH 101.  [Note: DUTCH 102 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Programming', '150', 'ECE', 'Software design process in a high-level programming environment. Programming fundamentals, language syntax, simple data types, control constructs, functions, parameter passing, recursion, classes, arrays and lists, list traversals, introduction to searching and sorting algorithms, basic object-oriented design, polymorphism and inheritance, simple testing and debugging strategies, pointers and references, basic memory management. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Numerical Methods', '204', 'ECE', 'Application of computational methods to engineering problems. Number systems, errors and error propagation. Roots of nonlinear equations. Introduction to numerical linear algebra. Interpolation and numerical integration. Introduction to numerical solutions of ordinary differential equations, optimization. Emphasis will be placed on algorithm development. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Computers', '222', 'ECE', 'Computer organization. Memory units, control units, I/O operations. Assembly language programming, translation and loading. Arithmetic logic units. Computer case studies.  [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algorithms and Data Structures', '250', 'ECE', 'Data structures, abstract data types, recursive algorithms, algorithm analysis, sorting and searching, and problem-solving strategies.  [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Thermodynamics and Heat Transfer', '309', 'MTE', 'Macroscopic approach to energy analysis. Energy transfer as work and heat, and the First Law of thermodynamics. Properties and states of simple substances. Control-mass and control-volume analysis. The essence of entropy, and the Second Law of thermodynamics. The Carnot cycle and its implications for practical cyclic devices. Introduction to heat transfer by conduction, convection, and radiation. Basic formulation and solution of steady and transient problems. Issues relevant to the cooling of electrical devices. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Communication Systems', '318', 'ECE', 'Introduction to random processes, power spectral density. Thermal noise and the white noise model. Amplitude and angle modulation, generation and detection schemes. Sampling and reconstruction, quantization. Digital baseband transmission. Overview of digital passband communications. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Analog Control Systems', '380', 'ECE', 'Introduction to control systems. Advantages of closed-loop feedback systems. The role of the system mathematical model. Block diagrams and signal flow graphs. The basic control system design problem, stability in control systems. Frequency response analysis techniques. Root-locus analysis. Elementary lead-lag compensation. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Signal Processing', '313', 'ECE', 'Fourier representations in discrete and continuous time. Discrete Fourier transform and fast Fourier transform algorithms. Sampling theory. Sampling and quantization errors. Transform analysis of linear time-invariant systems. Filter design. Discrete Hilbert transform. Introduction to filter banks and discrete wavelet transform. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Wireless Communications', '414', 'ECE', 'Overview of wireless communications including standards. Characterization of mobile radio propagation channels. Signal representations. Transmission and reception techniques for wireless channels. Fundamentals of cellular communications and multiple-access schemes. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Hardware Systems', '327', 'ECE', 'Design and modelling of digital hardware systems using a hardware description language. Development process. Impact of implementation technologies. Performance analysis and optimization. Functional verification. Timing analysis. Power analysis and optimization. Faults and testability. Reliability and fault tolerance.[Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Distributed Computing', '454', 'ECE', 'Principles of distributed computing; architectures and middleware; servers, processes, and virtualization; upper-layer network protocols, interprocess communication and remote procedure calling; concurrency, synchronization and distributed algorithms, dependable distributed systems and fault tolerance. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Embedded Software', '455', 'ECE', 'Concepts, theory, tools, and practice to understand, design, and write embedded software. This course covers computing elements, structures in embedded software, resource access protocols, uniprocessor scheduling, programming-language support, languages for MDD (model-driven development), worst-case execution time analysis, and overview of embedded distributed systems. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design and Applications of Power Electronic Converters', '463', 'ECE', 'Principles of power conditioning. Switching characteristics of power semiconductor devices. Computer simulation of power electronic circuits. Analysis, design, and applications of power converters. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('High Voltage Engineering and Power System Protection', '464', 'ECE', 'The course provides the fundamentals concepts of generation and measurements of high voltage ac, dc, and impulses. Briefly introduces the students to basic conduction and breakdown mechanisms of insulating materials. The scope of this course also includes understanding the basic protection system, studying the principles for protecting different elements and studying different technologies used in designing protective relays. Exposure to several state-of-art high voltage testing techniques of power system components will ensure that students have knowledge of the industrial solutions to the management of the problems associated with overvoltage and the protection mechanisms used. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Radio and Wireless Systems', '474', 'ECE', 'Modern transmitter and receiver architectures, Noise and linearity in radio and wireless systems, Design considerations of RF/microwave subsystems, radio and wireless system designs, CAD tools for radio and wireless systems, Antennas, Radio wave propagation models, Indoor radio, Satellite communication, Personal communication systems (PCSs). [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Control Systems', '481', 'ECE', 'Performance specifications for design. Dynamic system modelling and basic system identification. Dealing with basic nonlinear effects. Sampled data systems. Discrete-time system stability and dynamic performance. Digital control system design: emulation methods, z-domain, frequency domain, pole placement. Implementation of digital controllers. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Robot Dynamics and Control', '486', 'ECE', 'Introduction to the study of robotics focusing on the mechanics and control of serial manipulators. Topics include rigid body motion, forward and inverse kinematics, differential kinematics, forward and inverse dynamics, trajectory generation, motion planning, and feedback control. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Earth Sciences', '121', 'EARTH', 'This course will provide students with the foundational knowledge to become earth science literate, understanding Earths influence on humans and human influence on the Earth. Students will learn to think like a geoscientist by adopting four ways of thinking that take into account a systems approach, a variety of spatial and temporal scales, and field thinking. Systematically learning about rocks, geological time, natural resources, natural hazards, and water in this course will prepare students to address many challenges currently facing society and to identify future potential opportunities. [Note: EARTH 121L is recommended. Offered: F; also offered online S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Earth Sciences Laboratory', '121L', 'EARTH', 'For students taking EARTH 121. Laboratory exercises cover selected topics from EARTH 121 lectures. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Environmental Sciences', '122', 'EARTH', 'This course presents a broad overview of earth system processes and their influence on humans. Course emphasis is placed on anthropogenic impacts on natural systems, the impacts of geologic, biologic, and atmospheric processes on humans, and the effects of human activities on the environment. Course topics include sustainable development and the availability and use of natural resources, principles of ecology and environmental science, biogeochemical cycles, climate and climate change, soils and food supply, energy systems, surface water and groundwater, waste generation and management, pollution, and catastrophic natural processes. [Note: EARTH 122L is recommended. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Environmental Sciences Laboratory', '122L', 'EARTH', 'For students taking EARTH 122. Laboratory exercises cover selected topics from EARTH 122 lectures. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Hydrology', '123', 'EARTH', 'This course is intended to provide students with a practical overview of the hydrologic cycle and all of the interesting ways water flows and interacts with its environment, both surface and subsurface. The material is both qualitative and quantitative in nature and focuses on the circulation of water through the atmosphere, soil, groundwater aquifers, as well as looking at both the global fresh and marine water systems. Emphasis is placed on the physical aspects of hydrology, and the geologic and environmental factors that control the occurrence and cycling of water on Earth, as well as the impacts caused by population growth, contamination, urbanization or land use management, and climate change. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Geochemistry', '221', 'EARTH', 'Origin, abundance, and geochemistry of elements. Introduction to environmental isotope geochemistry and radiometric dating. Basic aqueous geochemistry: pH, carbonate equilibrium, and common ions in natural waters. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Methods in Hydrology', '223', 'EARTH', 'This course consists of field exercises and lectures designed to provide students with practical hands-on experience conducting a variety of hydrological monitoring techniques. Emphasis will be placed on the collection, analysis, and interpretation of field measurements. Field exercises will cover groundwater, surface water, meteorological, and water quality. [Note: Additional field trip fees will apply; Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mineralogy', '231', 'EARTH', 'This course provides a systematic study of the physical and chemical properties of the major rock-forming silicate minerals. Study of mineral chemistry, the internal geometry of crystalline solids and the interrelationship of crystal structure to the  physical and chemical properties of minerals. Laboratory work focuses on observing and evaluating the physical properties of minerals and rocks in hand sample in order to identity them. This course includes a weekend field trip to Bancroft, Ontario. [Note: Additional field trip fees will apply; Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Petrography', '232', 'EARTH', 'This course provides an introduction to the petrogenesis of igneous, metamorphic, and sedimentary rocks. Overview of textural, mineralogical, and chemical classification schemes for rocks. Introduction to silicate phase equilibria and the use of mineral stability diagrams to understand petrogenetic processes. Major relationships between tectonic setting and rock types. Laboratory work focuses on using a transmitted light microscope to study the optic properties of minerals in order to identify them as well as observe rock textures and interpret geological histories. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Stratigraphic Approaches to Understanding Earths History', '235', 'EARTH', 'An introduction to stratigraphic principles and methods used in deciphering geological history. Techniques of collecting surface and subsurface geologic data and representation of stratigraphic data. Procedures applicable to the classification and nomenclature of stratigraphic units using the North American Stratigraphic Code. The development of stratigraphic sequences as controlled by global and regional tectonics and sea level fluctuations. Interrelating aspects of Earths physical, chemical, and biological history. Examples are drawn primarily from Canadian geology. Laboratory work will include construction and interpretation of various types of learning media such as traditional maps, cross sections, new physical and digital models, as well as extended reality. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Structural Geology', '238', 'EARTH', 'Concepts of stress and strain; elementary rock mechanics; description and classification of folds, faults, foliations, lineations and joints; use of primary structures; introduction to geometrical analysis. Labs will emphasize geometrical problems, including geological maps and cross sections, and stereographic projection. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Applied Geophysics', '260', 'EARTH', 'Applied geophysics provides geoscientists with a wide-range of non-invasive methods for characterizing the Earths interior. This course is an introduction to the physical principles underlying field acquisition, processing, and data interpretation for commonly used applied geophysical techniques (seismic, electrical, electromagnetic induction, gravity, and magnetic methods). This course is not only pertinent for geoscientists but also for engineers and others interested in learning how physics can be used to explore the Earths interior or perform non-destructive testing. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Volcanology and Igneous Petrology', '331', 'EARTH', 'Principles and theories of the origins of volcanic and plutonic igneous rocks. Topics include the physics and chemistry of magma; igneous hazards and implications; magmatic differentiation; and igneous tectonic associations. Laboratories focus on the identification, description, and classification of igneous rocks [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Metamorphic Petrology', '332', 'EARTH', 'Principles and theories of metamorphic rock genesis. Topics include static and dynamic metamorphism, recrystallization, metamorphic facies, thermodynamic equilibrium, metasomatism, and metamorphic signatures of global tectonics. Laboratories focus on the description, classification, and interpretation of metamorphic rocks. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Sedimentology', '333', 'EARTH', 'An introduction to sediment properties, sedimentation processes, and sedimentary deposits in a variety of depositional environments and climatic conditions. Processes affecting sediments after initial deposition, such as those involved in the transformation of sediments into sedimentary rocks are also examined. Laboratories will focus on the different methods to describe and interpret sediments and sedimentary rocks. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Evolution 2: Fossil Record', '336', 'EARTH', 'An overview of the origin of life, how it evolved and diversified, modern principles of paleontology with particular emphasis on analytical tools to interpret ancient life forms. Patterns, processes of evolution and paleoecology of key groups of microfossils, invertebrates, plants, vertebrates and their trace fossils.  [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Evolution 2: Fossil Record', '360', 'BIOL', 'An overview of the origin of life, how it evolved and diversified, modern principles of paleontology with particular emphasis on analytical tools to interpret ancient life forms. Patterns, processes of evolution and paleoecology of key groups of microfossils, invertebrates, plants, vertebrates and their trace fossils.  [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geomorphology and GIS Applications', '342', 'EARTH', 'An introduction of geomorphology with emphasis on landscape-forming processes. Characteristics and development of various landforms (e.g. mountain belts, volcanoes, impact craters, and those created by erosion/deposition of rivers/glaciers) are introduced. Skills for interpreting landforms in air photos or satellite images (in Google Earth or ArcGIS) are practised in the lab work. Application of Geographic Information Systems (GIS) to Earth Sciences. Basic concepts of GIS and GIS applications in mineral exploration industry and in fieldwork are introduced. Laboratory work of GIS involves map construction and spatial analysis (e.g., landslide hazard assessment). [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Water: Data to Decisions', '355', 'EARTH', 'This course explores approaches for interpreting environmental data to support water decisions using theoretical and computational techniques. These approaches focus on statistical methods, including descriptive statistics, probability, hypothesis testing, frequency and time series analyses, point patterns, and correlation and regression, and also include discussion of analytical and numerical approaches. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Earth System Science', '358', 'EARTH', 'Study of the Earth as a system, with a focus on global climate history and dynamics, biogeochemical cycling, and the impacts of human activity.  Critical analysis and synthesis are emphasized in the context of group presentations and discussions.  [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Methods in Geological Mapping', '390', 'EARTH', 'Field study in Sudbury and Whitefish Falls areas. Held for at least nine days at end of the winter term. Geological and geotechnical field techniques, map construction, and report writing. [Note: Additional field trip fees will apply to all students. Depending on availability of space, EARTH 390 is also open to students who do not require this course in their plan. There will be an additional fee for such students. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Geochemistry', '421', 'EARTH', 'The application of chemical thermodynamics to geochemical problems. Development of the three laws of thermodynamics; Gibbs free energy and equilibria constants. Introduction to various topics in aqueous geochemistry such as mineral equilibria, ion exchange, and redox equilibria. Laboratory session will involve various experiments related to mineral solubility, chemical kinetics, acid-base equilibria, and chemical modelling. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Structural Geology', '435', 'EARTH', 'Stress and strain; deformational behaviour of rocks; origin of folds, foliations, lineations, joints and faults; geometrical and kinematic analysis; relationships of structures from the microscopic to the megascopic scale. Labs will include simple experiments, advanced geometrical problems and observation and measurement of microstructure and fabric. [Note: EARTH 332 is recommended as a prerequisite. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Thesis Proposal', '436A', 'EARTH', 'The first term of a potential two-term supervised research project in which students prepare a research proposal and conduct research. Students will present their research proposal in an oral presentation and written report. [Note: In order to progress to a two-term project via EARTH 436B, a grade of 75% must be achieved in this course; Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Thesis', '436B', 'EARTH', 'An optional second term of a two-term supervised research project in which students learn to gather and interpret data. Students will present their findings in a formal presentation and written thesis. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Rock Mechanics', '437', 'EARTH', 'Stress, strain and strength in geomaterials. Origins of stress and stress measurement methods, including hydraulic fracture and strain relief. Rock Mechanics principles and design procedures in areas of mining, civil engineering and petroleum engineering. Monitoring methods, including introduction to microseismic surveillance. Course includes laboratory and project work. [Note: Prerequisites are not listed for this course which is highly suitable for interested Engineering and Science students. However, some background in engineering, materials science, mechanics or structural geology is recommended; Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Geology', '438', 'EARTH', 'Review of basic concepts in engineering geology as applied to rock and soil, including material properties, variability in properties, external factors such as stress, and evaluation of design adequacy. Site investigation and characterization techniques used to define and characterize the properties of geological materials and their use in selected engineering geologic design and construction problems. Laboratory assignments will focus on the determination of physical properties and site assessment problems. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Flow and Transport Through Fractured Rocks', '439', 'EARTH', 'Fractures are ubiquitous in geologic media and important in disciplines such as physical and contaminant hydrogeology, geotechnical engineering, civil and environmental engineering, and petroleum engineering among other areas. Despite the importance of fractures, its characterization and predictions of groundwater flow and contaminant transport are fraught with significant difficulties. Students are taught to deal with fractures in hydrogeology, to conceptualize them, and to build reliable models for predicting groundwater flow and contaminant transport. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Quaternary Geology', '440', 'EARTH', 'Glacial-interglacial cycles and sub-Milankovitch oscillations from ocean sediments and ice cores.  Quaternary geochronology. Glacial sediment-land systems. Mineral exploration techniques pertaining to glaciated terrains and hydrostratigraphic analyses of Quaternary basins.  Local field trips.  Laboratory studies on glacial sediments. [Note: Additional field trip fees will apply. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Numerical Methods in Hydrogeology', '456', 'EARTH', 'An introduction to the theory and practice of groundwater flow and contaminant transport modeling. Topics related to variably-saturated flow will focus on derivation of Richards equation, a control volume finite difference discretization, assembly and solution of the flow equation, material balance error, truncation error analysis, stability and monotonicity. Topics related to contaminant transport will focus on derivation of the contaminant transport equation, a control volume finite difference discretization along with various spatial and temporal weighting schemes and their associated accuracy, monotonicity, and material balance error. Hands-on experience is provided using software applied to industry-standard variably-saturated flow and transport problems, with an emphasis on visualization and interpretation of results. [Note: EARTH 458, a first-year linear algebra, and a second-year calculus course are recommended. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Hydrogeology', '458', 'EARTH', 'An introduction to physical hydrogeology, including Darcys law, the groundwater flow equations for steady-state and transient conditions, applications to flow nets, aquifer testing, groundwater resources, and groundwater protection. The role of groundwater in the hydrologic cycle is explored with emphasis on natural groundwater flow systems and their influence on stream flow. Physical processes controlling groundwater contamination are introduced. [Note:  EARTH 123 is recommended as a prerequisite. Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemical Hydrogeology', '459', 'EARTH', 'An introduction to the chemical side of hydrogeology with emphasis on groundwater quality and contaminants in the groundwater zone, the geochemical origin of major ions in natural groundwater, causes of hardness, groundwater age determination using isotopes, common causes of groundwater contamination; processes governing contaminant behaviour including dispersion, diffusion and adsorption, hydrogeologic aspects of site selection for waste disposal. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geophysical Data Analysis', '460', 'EARTH', 'Geophysical data sets require the application of specialized analysis methods to extract information about the Earths interior. This course covers commonly used concepts and techniques used in geophysical data analysis including spectral analysis, convolution, filtering, and sampling. Emphasis is given to the analysis and processing of radar and seismic reflection profiling data sets. [Note: A full credit in first-year Calculus is recommended as a prerequisite. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Near-Surface Geophysics', '461', 'EARTH', 'Near-surface geophysics is used to characterize the shallow subsurface for a wide-spectrum of applications. This application-oriented course covers current methodology in near-surface geophysics, including electrical, electromagnetic, ground penetrating radar, seismic refraction, and well-logging methods. Techniques for geophysical data acquisition and interpretation are studied and applied to field data. Examples of the application of geophysical techniques to archeological, forensics, geotechnical, glaciology, and hydrogeological problems are examined. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mineral Deposits', '471', 'EARTH', 'An introduction to the principles and theories of mineral deposit genesis. Basic technical, economic, legal, and ethical aspects of mineral exploration and responsible development of natural resources are also covered. Laboratories involve hand sample and ore petrology of suites from diverse deposits. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Course', '490', 'EARTH', 'One or more trips that emphasize field observations.  Specific trips may be organized to examine field aspects of any of the disciplines within Earth Sciences or Geological Engineering.  Field exercises and reports may be part of the requirements.  Additional field trip fees will apply.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to East Asia', '100R', 'EASIA', 'An introductory survey of East Asia. This course examines common grounds and differences in the historical, cultural, economic, and political foundations of East Asian societies and states. [Note: Formerly EASIA 201R]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religions of East Asia', '205R', 'EASIA', 'An examination of the leading religious and philosophical ideas that have shaped the cultures and histories of East Asia: China, Korea, and Japan. Folk, Shamanic, Confucian, Daoist, Shinto, and Buddhist traditions are examined. [Note: This course fulfils an Area 1 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religions of East Asia', '201', 'RS', 'An examination of the leading religious and philosophical ideas that have shaped the cultures and histories of East Asia: China, Korea, and Japan. Folk, Shamanic, Confucian, Daoist, Shinto, and Buddhist traditions are examined. [Note: This course fulfils an Area 1 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Microeconomics', '101', 'ECON', 'This course provides an introduction to microeconomic analysis relevant for understanding the Canadian economy. The behaviour of individual consumers and producers, the determination of market prices for commodities and resources, and the role of government policy in the functioning of the market system are the main topics covered.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Macroeconomics', '102', 'ECON', 'This course introduces students to the measurement and behaviour of key macroeconomic variables both in Canada and around the world. Topics include national accounts, inflation, interest rates, wages, international balance of payments, business cycles, growth, employment, unemployment, poverty, and inequality.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Microeconomic Theory for Business and Policy', '201', 'ECON', 'This course offers an introduction to the theory of market based economies. Topics include consumer choice, production, price and output under perfect and imperfect competition, price discrimination and two part pricing, vertical and horizontal firm boundaries and integration, and market structure.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Mathematical Economics', '211', 'ECON', 'An introduction to mathematical techniques of particular use in economics. Topics include matrix algebra, differentation, partial derivatives, optimization techniques including constrained optimization - all developed within the context of economic problems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Entrepreneurship and the Creative Workplace', '220', 'MGMT', 'This course has two distinct but related components. The first explores entrepreneurship as a key input to economic activity. It also reviews numerous perspectives of entrepreneurship and introduces basic tools available for use by the entrepreneur, including the business canvas. The second part of this course explores the relationship between entrepreneurship and strategy. The design of an appropriate strategy and the implementation of this strategy through appropriate controls and structure are considered in the context of the innovative firm. [Note: Formerly ECON 220.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Statistics for Economists', '221', 'ECON', 'This course introduces students to describing economic data and drawing inferences from features of economic data. Starting from fundamental axioms of probability, students will learn about the calculation of probabilities of basic events and the features of random variables, the most important tool for representing the outcomes of complex economic phenomena. Students will describe discrete and continuous random variables via their probability distributions and summary statistics such as means and standard deviations, as well as the relationships between two random variables in terms of covariance, correlation, and simple regression models. The concepts of hypothesis testing and confidence intervals, and the fundamentals of statistical inference are discussed for basic features of random variables and for comparing the features of more than one random variable.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to International Economics', '231', 'ECON', 'This course explores international trade in goods and services, as well as the international exchange of financial assets. Economic theories will be examined, which help explain how international transactions affect the worlds economies. Topics include the theory of comparative advantage and the gains from trade, tariff theory, concepts and measurement of balance of payments, exchange rate systems, and the international monetary system.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mathematical Economics', '311', 'ECON', 'This course builds on and expands the material in ECON 211. Multi-variate calculus and optimization are covered in detail, with a focus on economic applications of the envelope theorem. The implicit function theorem and its uses in comparative statics are explored. Difference and differential equations and their application to dynamic models are introduced, culminating in an introduction to optimal control.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Finance', '332', 'ECON', 'An analysis of the main issues in international finance. Topics include international borrowing and lending, intertemporal gains from trade, current account and balance of trade movements, the determination of exchange rates, and foreign exchange markets.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Labour Economics', '351', 'ECON', 'A study of the supply of labour by individuals (and unions) and the demand for labour by firms. Topics include the labour market effects of social assistance, unemployment insurance and minimum wages, discrimination in the labour market, efficient wage contracts, the determinants of wage inflation and unemployment.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Economics', '457', 'ECON', 'Application of economic theory to problems of the environment, in particular, air, water, and land pollution. Emphasis is on the theory of the management of common property resources. [Note: Formerly ECON 357.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cost-Benefit Analysis and Project Evaluation', '361', 'ECON', 'Methods for evaluating private and public projects; decision rules, efficiency conditions, and methods of conducting cost-benefit analysis. Application of the technique. This course assumes prior familiarity with probability, expected values, and variance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Economics of Social Problems', '363', 'ECON', 'A topic-oriented course. Problems are selected from a list that includes regulatory economics, poverty, unemployment, industrial policy, safety, social policy, government deficits/debt, stabilization policy, and others. The format assists the student in gaining analytical skills through work on the selected topics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Business Finance 1', '371', 'ECON', 'The course explores decisions faced by managers of firms. In particular, decision-makers must determine which long-term real investment opportunities to exploit. Once undertaken, managers must decide how to finance the projects, for example, by debt or equity. The course develops both the conceptual framework and the tools required for these decisions. The course assumes prior familiarity with probability, expected values, and variance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Business Finance 2', '372', 'ECON', 'This course examines a number of topics relevant to financial practitioners. The topics examined may include options, derivatives securities, futures markets, swaps and hedging. The course assumes prior familiarity with probability, expected values, and variance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics', '382', 'ECON', 'One or more special topics courses may be offered at different times as announced by the department. [Note: This is a repeatable course, subject to different content; it may be completed a total of five times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Econometric Theory', '421', 'ECON', 'This course offers an advanced treatment of topics covered in ECON 322/323 through the extensive use of matrix algebra, statistical theory and numerical methods. After a review of the required mathematics, these tools will be used to derive the least-squares estimator for the standard linear model and to conduct inference about features of the model, checking the results of theory using regression software and economic data. Then this framework will be used for theoretical generalizations of the model and their estimation using regression software. By the end of the course, students will be able to estimate a linear model and conduct accurate inference in cases where economic data violate the assumptions of the standard linear regression model. This includes methods for dealing with heteroskedastic data, observations that may be dependent over time, response distributions with heavy tails or limited range (such as binary outcomes and count variables), and observations that may suffer from selection bias.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Microeconometric Analysis', '422', 'ECON', 'This course investigates advanced estimation and inference techniques for microeconomic data. Students will learn about error components models that are used for economic data that exhibits significant unobserved heterogeneity. The estimation of treatment effects using fixed effects and difference-in-differences methods will be covered, and design-based methods of causal inference such as matching and regression discontinuity may be covered as well. Extensions such as multilevel or hierarchical models, limited dependent variable models, duration models or selection models may also be included. Students also learn how to apply these methods using computer software, and will use it to analyze complex data from household and firm-level surveys in assignments.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Essay', '472', 'ECON', 'Students are required to identify a research topic, conduct research independently, write a research paper, and attend class meetings. Each student is supervised by a member of the Economics faculty. Class meetings cover topics such as a general overview of research methods in economics, research ethics, finding reference material, citing practices, and effective writing and presentation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Studies', '488', 'ECON', 'Research and reading course under the direction of individual instructors. See Economics undergraduate officer for course registration.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Studies', '489', 'ECON', 'Research and reading course under the direction of individual instructors. See Economics undergraduate officer for course registration.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Varieties of English', '103B', 'ENGL', 'Introduction to the study of varieties of the English language - regional, social, temporal, functional, and stylistic. The relations of languages and literature and of speech and writing will be discussed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gender and Representation', '108E', 'ENGL', 'A study of the ways gender in all its diversity is constructed and gendered experience is expressed in literature, rhetoric, and a variety of media.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gender and Representation', '108', 'GSJ', 'A study of the ways gender in all its diversity is constructed and gendered experience is expressed in literature, rhetoric, and a variety of media.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Rebel', '108F', 'ENGL', 'A study of various works of literature in which the protagonist is a rebel against existing norms. The course will examine a number of rebel types and concepts, moral implications, and final outcomes either in successful realization or in tragic defeat.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Academic Writing', '109', 'ENGL', 'The course will explore a variety of issues in academic writing such as style, argument, and the presentation of information. Frequent written exercises will be required.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Written Academic English', '129R', 'ENGL', 'Designed specifically for students for whom English is not the first language, this writing skills course provides instruction in grammar, sentence and paragraph structure, elements of composition, and academic essay writing, including a focus on theme, development of central ideas, exposition, and argumentation. [Note: Not open to fluent writers of English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Written Academic English', '129R', 'EMLS', 'Designed specifically for students for whom English is not the first language, this writing skills course provides instruction in grammar, sentence and paragraph structure, elements of composition, and academic essay writing, including a focus on theme, development of central ideas, exposition, and argumentation. [Note: Not open to fluent writers of English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Use of English', '140R', 'ENGL', 'This course examines the use of English in a variety of contexts (colloquial, scientific, legal, political, commercial, journalistic, literary, etc.) to increase critical awareness of the language and help students write more clearly and effectively.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Shakespeare', '190', 'ENGL', 'Designed for students in all faculties, the course examines some of Shakespeares comedies, history plays, and tragedies. Shakespeares variety and flexibility in developing characters and dramatic structures are stressed, as are significant themes. [Note: No previous work in Shakespeare is required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('English Literatures 1', '200A', 'ENGL', 'An introduction to the diverse forms and voices of literature written in English from the Middle Ages to the late 18th century, focussing on key writers and works, including works by women and people of colour. Students will explore literary techniques, historical and cultural contexts, and the question of the canon.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('English Literatures 2', '200B', 'ENGL', 'An introduction to the diverse forms and voices of literature written in English from the late 18th century to the present, focussing on key writers and works from Britain and North America, and including works by women and people of colour. Students will explore literary techniques, historical and cultural contexts, and the question of the canon.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Short Story', '201', 'ENGL', 'This course deals with the history and techniques of the short story, with emphasis upon works by such British, American, and Canadian writers as Henry James, James Joyce, D.H. Lawrence, Ernest Hemingway, and Alice Munro.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Bible and Literature 1', '202A', 'ENGL', 'A study of the major stories, themes, and literary characteristics of the Old Testament of the King James Bible (also known as the Hebrew Scripture), and of its influence on other English literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Bible and Literature 2', '202B', 'ENGL', 'A study of the major stories, themes, and literary characteristics of the New Testament of the King James Bible and of its influence on English literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Canadian Short Story', '205R', 'ENGL', 'The short story is Canadas most vibrant literary form. Students will examine short stories from the 19th century to the present. Topics to be covered may include national, regional and Indigenous identity, mythology, multiculturalism and cosmopolitanism, as well as explorations of contemporary social justice issues.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Forms of Fantasy', '208A', 'ENGL', 'A study of fantasy literature, including some subgenres such as romances, fairy tales, fables, and gothic and horror fiction.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Science Fiction', '208B', 'ENGL', 'Various examples drawn, for instance, from Utopian and anti-Utopian science fiction, social science fiction, "gadget" science fiction, parapsychology, and alternate worlds and beings will be considered. Some attention will be given to the historical development of the genre.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Studies in Childrens Literature', '208C', 'ENGL', 'A critical examination of works of childrens literature. Specific readings may range broadly, encompassing works as diverse as ancient folk tales and novels and poetry from the 18th century to the present day.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Womens Writing', '208E', 'ENGL', 'This course explores a range of womens writing and the social and cultural contexts in which they made their voices heard.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Womens Writing', '208E', 'GSJ', 'This course explores a range of womens writing and the social and cultural contexts in which they made their voices heard.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Arthurian Legend', '208H', 'ENGL', 'The story of Arthur and his knights of the Round Table will be discussed as it is treated at various times in various works and genres. Such matters will be considered as the character of Arthur, the concept of Camelot, and the Fellowship of the Round Table.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Detective Fiction', '208K', 'ENGL', 'A study of the detective novel, the novel of crime, the thriller, the novel of intrigue, and of espionage with texts drawn from various time periods and national literatures. The course includes the examination of critical approaches to the form of detective fiction.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Genres of Technical Communication', '210E', 'ENGL', 'This course explores writing, presentation, and design across various genres of technical communication, with a primary focus on printed and/or online computer documentation. Other assignments might include white papers, product specifications, help-desk communication, etc.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Genres of Business Communication', '210F', 'ENGL', 'This course explores the genres of communication in business and other organizations. Students will study and produce instances from several of the following: reports (of several kinds), letters, email messages, marketing materials, public relations materials, and any other types of organizational communication.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadian Childrens Literature', '217', 'ENGL', 'A study of 19th- and 20th-century Canadian literature for children.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literary Theory and Criticism', '251', 'ENGL', 'What exactly are we doing when we study literature? By examining a selection of critical methods and theoretical approaches, this course will enhance understanding of the many different emphases, values, and priorities critics bring to literature, and the many available perspectives on what constitutes literatures significance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Rhetorical Theory and Criticism', '292', 'ENGL', 'This course provides a survey of the multidisciplinary field of rhetorical studies. In addition to introducing key concepts, theoretical frameworks, and critical debates, this course examines the role of rhetoric in a range of academic disciplines and social contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Old English Language and Literature', '305A', 'ENGL', 'An introduction to the English language in its earliest form, and study of selected prose and poetry from pre-Conquest England in the original language, with attention to historical, cultural, and religious contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Age of Beowulf', '305B', 'ENGL', 'A study of the earliest English literature in translation. The heroic epic Beowulf will be studied in depth, along with a selection of Old English poetry and prose, such as lyrics, riddles, and historical and religious writing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Linguistics', '306A', 'ENGL', 'Introduction to linguistics and the principles of linguistic analysis through an examination of English phonology, forms, syntax, and discourse.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('How English Grammar Works', '306B', 'ENGL', 'This course analyzes English grammar structures, "grammar rules," and the reasoning behind them. The course then examines English-language change, and considers grammar in pedagogical and multicultural contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The History of the English Language', '306D', 'ENGL', 'This course explores the history of the English language, from Anglo-Saxon dialects ("Old English"), through the combining of Anglo-Saxon and Norman French, its evolution in medieval and early modern Britain, up to its transformation within multilingual and multicultural contexts. The course examines not only the evolving vocabulary and grammar of the language, but also its social history.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Semiotics', '306F', 'ENGL', 'A study of systems of signs, codes, and signification in language, culture, and literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Rhetoric, Classical to Enlightenment', '309A', 'ENGL', 'A study of rhetorical theories from antiquity through the Renaissance to the 18th century, with an emphasis on how these theories reflect changing attitudes towards language, society, and the self.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Rhetoric', '309C', 'ENGL', 'An examination of contemporary rhetorical theory and its relationships to criticism, interdisciplinary studies, and digital applications.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Critical Discourse Analysis', '306G', 'ENGL', 'This course provides an introduction to the theory and practice of critical discourse analysis (CDA), the close study of language and its effects in social context. Students will learn to apply discourse-analytical tools to a wide range of texts, conversations, images, and other artifacts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Speech Writing', '309E', 'ENGL', 'The analysis, writing, and editing of speeches. This course considers how genre and style impact the creation, reception, and implications of meaning. Students practice writing, as form and style, in the construction of arguments and other genres of speech making.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Speech Writing', '323', 'COMMST', 'The analysis, writing, and editing of speeches. This course considers how genre and style impact the creation, reception, and implications of meaning. Students practice writing, as form and style, in the construction of arguments and other genres of speech making.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Middle English Literature', '310A', 'ENGL', 'A study of English writings during the later Middle Ages. Possible representative works include romances such as Sir Gawain and the Green Knight; alliterative literature, such as The Vision of Piers Plowman; selections from Chaucer; spiritual prose writings; Middle English lyrics and verse.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chaucer', '310B', 'ENGL', 'A study of Geoffrey Chaucers writings. Depending on the instructor, this course may focus on a single work such as The Canterbury Tales, or a selection.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Early Canadian Literatures', '313', 'ENGL', 'This course examines a selection of pre-1920 Canadian texts concerning first contact, imperialism, colonization, incipient nationhood, and early multi-racial immigration that participate in the ongoing invention of Canada.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modern Canadian Literature', '315', 'ENGL', 'This course focuses on the varied ways in which 20th-century writers of poetry and prose participate in the shaping of Canadian literary culture, with emphasis on the literature of the middle decades.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadian Drama', '316', 'ENGL', 'This course explores traditions and experiments in Canadian drama through an analysis of Canadian plays, especially those from 1960 to the present, in their historical and theatrical contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Canadian Literature', '318', 'ENGL', 'This course examines Canadian Literature written in the latter decades of the 20th century and into the 21st century. Literary works are studied in relation to relevant contemporary social, cultural and political topics, such as nationalism, indigeneity, multiculturalism, and diaspora.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sixteenth-Century Literature 1', '330A', 'ENGL', 'A study of short poems by such writers as Wyatt, Gascoigne, Whitney, Ralegh, Spenser, the Sidneys, Shakespeare, and Donne.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sixteenth-Century Literature 2', '330B', 'ENGL', 'A study of selected genres, topics, and works from Tudor literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Creative Writing 1', '335', 'ENGL', 'Designed to assist students with an interest in developing their creative writing skills in various genres, this course consists of supervised practice, discussions of craft, and peer critiques.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Creative Writing 2', '336', 'ENGL', 'This course is designed to assist advanced creative writers in developing a body of work in one or more genres by means of supervised practice, discussions of craft, and peer critiques. [Note: Admission by portfolio review]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('American Literature 1860-1910', '343', 'ENGL', 'A survey of literary developments in America from the Civil War through the turn of the 20th century, including significant movements of the period such as realism, regionalism, and naturalism; the New Womans writing and other developments in womens literatures; popular forms such as the Western; and minority literatures.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modern American Literature', '344', 'ENGL', 'A study of American Literature from the early twentieth century through the second world war, emphasizing aesthetic innovation in the modernist movement, and its aftermath in the social writings of the 1930s.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('American Literature in a Global Context', '345', 'ENGL', 'A study of the ways in which movements of peoples and cultures have shaped American literature. Topics may include colonialism, immigration and migration, literary influence across borders and languages, nativism and internationalism, racial and ethnic styles and exchanges.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('American Literature Since 1945', '347', 'ENGL', 'A study of the movements of American Literature following the second world war.  The course will consider the formal and cultural diversity of writing in this period, with attention to topics such as avant-garde experiment, the persistence of realism, counter-cultural politics, feminism and literature, postmodernism, and the emergence of minority writers in the mainstream.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Seventeenth-Century Literature 1', '350A', 'ENGL', 'A study of literature by such writers as Jonson, Donne, Wroth, Herbert, Bacon, Milton, Behn, and Dryden.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Seventeenth-Century Literature 2', '350B', 'ENGL', 'An intensive study of Miltons epic, Paradise Lost, in its historical and literary contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Shakespeare 1', '362', 'ENGL', 'A study of the plays written before 1599-1600, excluding Julius Caesar.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Shakespeare 1', '386', 'THPERF', 'A study of the plays written before 1599-1600, excluding Julius Caesar.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Shakespeare 2', '363', 'ENGL', 'A study of the plays written after 1599-1600, including Julius Caesar.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Shakespeare 2', '387', 'THPERF', 'A study of the plays written after 1599-1600, including Julius Caesar.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Selected Studies', '365', 'ENGL', 'Designed to provide an in-depth study of problems and/or authors selected by the instructor. Students interested in initiating such courses are encouraged to do so by bringing their ideas to the attention of individual instructors.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Selected Studies', '366', 'ENGL', 'Designed to provide a study in-depth of problems and/or authors selected by the instructor. Students interested in initiating such courses are encouraged to do so by bringing their ideas to the attention of individual instructors.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Information Design', '392A', 'ENGL', 'The theory and practice of design for print and digital media, including the study of design concepts such as space, colour, typography, interactivity, immersion, motion, and presence. Students produce designs using professional software tools.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visual Rhetoric', '392B', 'ENGL', 'This course introduces students to the study of images from a rhetorical perspective, including the interaction of texts and images in such professional writing fields as advertising, book illustration, technical documentation, journalism, and public relations. Issues may include visual and textual literacy, the semiotics and rhetoric of design, and the ideological basis of social communication.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Writing for the Media', '408A', 'ENGL', 'This course examines the genres and strategies of both journalism and public relations. With a strong orientation towards rhetorical and linguistic theories, this course will cover audience concerns from both within and outside organizations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Discourse of Advertising', '408B', 'ENGL', 'This course introduces students to writing and editing advertising copy. Students will also be introduced to models of discourse and rhetorical analysis of advertising texts. Assignments include creating a portfolio of advertising copy and an extensive analysis of sample advertising discourse.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Rhetoric of Digital Design: Theory and Practice', '408C', 'ENGL', 'Students apply a variety of analytic perspectives - design discourse, multimodal discourse, rhetorical theory, social semiotics - to the design and production of a major digital project (or compilation of projects) using professional software and hardware tools.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literature of the Romantic Period 1', '430A', 'ENGL', 'An examination of the first generation of Romantic writers, including such authors as Barbauld, Blake, Wollstonecraft, Wordsworth, and Coleridge.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literature of the Romantic Period 2', '430B', 'ENGL', 'An examination of the second generation of Romantic writers, including such authors as Byron, P. B. Shelley, Mary Shelley, Keats, and Hemans.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literature of the Victorian Age 1', '451A', 'ENGL', 'A critical study of early to mid-Victorian literature, including authors such as Carlyle, Arnold, Tennyson, the Brontës, Elizabeth Barrett Browning, Robert Browning, Gaskell, Ruskin, and Dickens. Topics may include liberty, work, gender, class, imperialism, and poetry.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literature of the Victorian Age 2', '451B', 'ENGL', 'A critical study of mid to late Victorian literature, including authors such as Christina Rossetti, Dante Gabriel Rossetti, George Eliot, Newman, Hopkins, Michael Field, Wilde, and Hardy. Topics may include the "Woman Question," the crisis in religious faith, and aestheticism.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Early Literature of the Modernist Period in the United Kingdom and Ireland', '460A', 'ENGL', 'A study of the literatures of the United Kingdom before and after World War I, including such writers as Conrad, Forster, Hopkins, Mansfield, Shaw, Synge, Wilde, and Yeats.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literature of the Modernist Period in the United Kingdom and Ireland', '460B', 'ENGL', 'A study of the literatures of the United Kingdom and Ireland from World War I to World War II, including such writers as Auden, Eliot, Isherwood, Joyce, Lawrence, Orwell, West, and Woolf.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literature of the Postwar Period in the United Kingdom and Ireland', '460C', 'ENGL', 'A study of the literatures of the United Kingdom and Ireland after World War II, including such writers as Beckett, Greene, Larkin, Murdoch, Osborne, Pinter, and Spark.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Critical Theory', '470A', 'ENGL', 'Contemporary critical theory offers an array of competing constructions of text and culture. This course examines several topics in recent critical theory, such as gender, race, subjectivity, textuality, and popular culture.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Literary Criticism', '470B', 'ENGL', 'An historical survey of major critical texts and movements from the Greek and Roman classics to the New Criticism of the mid-20th century, examining different critical theories and practices in a context of cultural changes.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literary Studies in Digital Forms', '470C', 'ENGL', 'A critical examination of literary publication, research, and criticism in digital forms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Supervision of Honours Essay', '495A', 'ENGL', 'Senior honours essay will be completed under supervision. [Note: A grade for ENGL 495A will be submitted only after the completion of ENGL 495B.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Supervision of Honours Essay', '495B', 'ENGL', 'Senior honours essay will be completed under supervision.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental and Geological Engineering Concepts', '100', 'ENVE', 'An introduction to the fundamental methods, principles and skills of environmental and geological engineering. Fundamentals of technical communication, the engineering design process and problem solving. Completion of a pre-design study and report for an environmental engineering project. Independent and team work. Fundamentals of engineering computation units, data collection, measurement, and error analysis. Field surveying (automatic level, engineers transit, differential global positioning system (GPS), total station). Laboratory on engineering graphics auto-computer assisted diagnosis (AutoCAD) and computational software (Excel, Matlab). Aspects of the engineering profession (code of ethics, negligence, misconduct, role of the Professional Engineers Ontario (PEO), etc.), diversity in the workplace, and professional development. Preparation for the University of Waterloo co-operative education program (Co-operative Education and Career Action (CECA), résumé writing, job search and interview skills). [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental and Geological Engineering Concepts', '100', 'GEOE', 'An introduction to the fundamental methods, principles and skills of environmental and geological engineering. Fundamentals of technical communication, the engineering design process and problem solving. Completion of a pre-design study and report for an environmental engineering project. Independent and team work. Fundamentals of engineering computation units, data collection, measurement, and error analysis. Field surveying (automatic level, engineers transit, differential global positioning system (GPS), total station). Laboratory on engineering graphics auto-computer assisted diagnosis (AutoCAD) and computational software (Excel, Matlab). Aspects of the engineering profession (code of ethics, negligence, misconduct, role of the Professional Engineers Ontario (PEO), etc.), diversity in the workplace, and professional development. Preparation for the University of Waterloo co-operative education program (Co-operative Education and Career Action (CECA), résumé writing, job search and interview skills). [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Differential Equations and Balance Laws', '223', 'ENVE', 'An introduction to ordinary differential equations with applications to mass and energy balance problems in engineering. Standard methods of solution of first and second order linear equations with constant coefficients. Numerical methods for solving ordinary differential equations. Partial differential equations. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Differential Equations and Balance Laws', '223', 'GEOE', 'An introduction to ordinary differential equations with applications to mass and energy balance problems in engineering. Standard methods of solution of first and second order linear equations with constant coefficients. Numerical methods for solving ordinary differential equations. Partial differential equations. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Probability and Statistics', '224', 'ENVE', 'Role of probability in engineering and decision-making under uncertainty. Basic probability concepts. Probability distributions. Functions of random variables. Data analysis. Confidence intervals and hypothesis testing. Introduction to regression analysis. Introduction to design of experiments and statistical quality control. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Probability and Statistics', '224', 'GEOE', 'Role of probability in engineering and decision-making under uncertainty. Basic probability concepts. Probability distributions. Functions of random variables. Data analysis. Confidence intervals and hypothesis testing. Introduction to regression analysis. Introduction to design of experiments and statistical quality control. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Chemistry', '275', 'ENVE', 'Overview of risk, biosphere compartments and contaminant fate. Composition of water. Electroneutrality and activity. Reactions and speciation including reaction kinetics, mass transfer, vapor pressure, equilibrium, and chemical thermodynamics. Equilibrium chemistry including Log-concentration diagrams, titration and buffering intensity, dissolution/precipitation, carbonate system, hardness, and complex formation. Classification, nomenclature, physical/chemical parameters and partitioning of organic compounds. Basic redox chemistry including: half cell reactions, Faraday and Nernst equations, and pE-pH diagrams. Four labs. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Lab Analysis and Field Sampling Techniques', '330', 'ENVE', 'An introduction to the fundamental concepts of physical and chemical measurement of the environment. Review of basic statistics, quality assurance and control, sources of error, seasonal effects, sample preservation. Practical and essential elements of water, soil and air sampling. Introduction to measurement techniques including: colorimetry, chromatography, spectroscopy, electrochemical probes, remote sensing. Design of monitoring strategies, and use of methods to assess validity of laboratory data. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physico-Chemical Processes', '375', 'ENVE', 'Fundamentals of coagulation, flocculation, clarification, sedimentation, filtration, adsorption, air stripping, membrane technologies, chemical reduction/oxidation, and disinfection processes with applications to natural and various engineered systems. Quantitative analysis and design of processes and applications to the treatment of drinking water, wastewater, stormwater, groundwater and soils. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Law and Ethics for Environmental and Geological Engineers', '391', 'ENVE', 'Philosophy of environmental controls; introduction to national and international regulatory structures relevant to industrial planning, emissions control, environmental impact assessment, occupational health; stance of government, industry and community pressure groups. Contract law. Professional ethics, including the social responsibility of engineers, conflicts of interest. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Law and Ethics for Environmental and Geological Engineers', '391', 'GEOE', 'Philosophy of environmental controls; introduction to national and international regulatory structures relevant to industrial planning, emissions control, environmental impact assessment, occupational health; stance of government, industry and community pressure groups. Contract law. Professional ethics, including the social responsibility of engineers, conflicts of interest. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Engineering Design Project 1', '400', 'ENVE', 'Students undertake an independent environmental engineering design project during the last two terms of their plan. The purpose of the project is to demonstrate students abilities to practise in an environmental engineering capacity in their chosen area of expertise, using knowledge gained from their academic and employment experiences. The first part of the project (ENVE 400) will include problem identification, generation and selection of solutions, and time management. Incorporation of technical, ecological, social, political, and economic issues in the solution for the project will be required. A basic requirement of the proposed solution is that it must be compatible with the principles of sustainability. Requirements include proposal, progress report, and a final report containing recommendations for part two of the project, ENVE 401. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Engineering Design Project 2', '401', 'ENVE', 'A continuation of ENVE 400. The final design of the major environmental engineering project proposed in ENVE 400 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design of the solution proposed in ENVE 400. Requirements of this part of the two-term project include a symposium presentation and a final report. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contaminant Transport', '573', 'ENVE', 'Importance and complications associated with environmental modelling, the model building process, limitations, and measures of success. Types of contaminants; transport phenomena with a focus on advection-dispersive transport; development of governing equations; types and utility of boundary and initial conditions; and mass balance considerations. Review of completely mixed systems including lakes, streams, source functions, feedback systems, and toxic substance models. Model calibration, sensitivity, and uncertainty; methods and approaches. Solute transport models and solution techniques including random walk, method of characteristics, finite difference method and finite volume method. Aspects of multiphase flow (gas/water and NAPL/water systems) with an emphasis on groundwater problems. Introduction to mass removal technologies for remediation of soil and groundwater systems. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering for Solid Waste Management', '577', 'ENVE', 'The engineering aspects of solid waste management are examined. Attention is given to the engineering design and operational aspects of the control of generation, storage, collection, transfer and transport, processing and disposal of solid wastes in landfill site. Design of natural attenuation sites and system reliability features for landfill designs. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Applications of Data Management and Statistics', '178', 'ENVS', 'This course introduces techniques for collecting, evaluating, and using data-based evidence in environmental research, including descriptive statistics (measures of centre, variation and shape, and measures of association between two variables), statistical research designs, sampling theory, and fundamental probability theory for inferential statistics. The course also develops skills in using statistical software for data display and analysis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Environmental Studies', '195', 'ENVS', 'This course introduces the field of environmental studies through an exploration of environmental issues that contribute to crises and challenges at the global, national and local levels. The course provides an overview of human ecological aspects of environmental studies from an inter-cultural and global perspective, and offers an integrative framework for understanding them.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Ecology', '200', 'ENVS', 'Introduces the main concepts and principles of ecology; the cycling of elements; energetics and structural organization of major ecological systems; population dynamics; impact of natural resource management practices and urban and industrial development on the environment; incorporating environmental quality considerations into development activities. The lab sessions include field trips to study natural and disturbed ecosystems, urban and applied ecology. [Note: Field trip fee normally $30+HST; will not exceed $60+HST. WHMIS required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Canadian Environmental Law', '201', 'ENVS', 'Introduction to the basic legal framework that regulates the natural environment in Canada. The course provides students with an understanding of the foundational legal institutions in Canada and their respective roles in environmental regulation. The course includes both public and private law approaches to environmental regulation, and covers the principal regulatory tools used to protect the environment.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Management Systems', '202', 'ENBUS', 'The examination and evaluation of environmental management systems such as ISO 14001. Alternate EMS systems will be compared and reviewed to identify their respective strengths and weaknesses. Case studies will be used to illustrate the ideas presented.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ecological Economics', '220', 'ENVS', 'Evaluation of various economic approaches to the environment. The links between economics, systems and the natural environment will be explored and future directions examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Statistics for Environmental Research', '278', 'ENVS', 'This course examines further techniques for collecting, evaluating, and using data-based evidence in environmental research. It builds upon ENVS 178, with a focus on inferential statistics, including sampling distributions, confidence intervals, parametric and nonparametric hypothesis tests, and linear regression models. It further develops skills in using statistical software for data analysis and modeling of environmental data.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Study Abroad', '395', 'ENVS', 'Study abroad for academic transfer credit under a Faculty of Environment Exchange Program.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadian Law, Indigenous Peoples, and Natural Resource Development', '401', 'ENVS', 'The course examines the historical development of the constitutional relationship between the Crown (the Canadian and provincial governments) and Indigenous Peoples, and the current impact of this constitutional relationship on the development of resources in Canada. Particular focus will be paid to the judicial development of Aboriginal rights and Aboriginal title, the duty to consult and accommodate Indigenous Peoples in the context of resource development, and the future trajectory of the relationship between the Crown, Indigenous Peoples, and resource developers.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ecotourism and Communities', '433', 'REC', 'This blended course includes a field and online learning experience and focuses on the philosophical, socio-cultural, health, and ecological dimensions of ecotourism, and the skills and practices necessary for planning and managing ecotourism for community development and well-being. The course will involve one or more overnight excursions to ecotourism destinations in Ontario (e.g., Muskoka or Haliburton region). [Note: Field trip fee: $500.00+HST to $1,500.00+HST]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ecotourism and Communities', '433', 'ENVS', 'This blended course includes a field and online learning experience and focuses on the philosophical, socio-cultural, health, and ecological dimensions of ecotourism, and the skills and practices necessary for planning and managing ecotourism for community development and well-being. The course will involve one or more overnight excursions to ecotourism destinations in Ontario (e.g., Muskoka or Haliburton region). [Note: Field trip fee: $500.00+HST to $1,500.00+HST]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Landscape Ecology, Restoration and Rehabilitation', '469', 'ENVS', 'Survey of the major concepts and theories of landscape ecology. Application of these concepts to case studies in restoration and/or rehabilitation. Interaction with professionals from government, NGOs or private industry on ecological issues will also be part of the course. The course includes a practical component on the planning of ecological restoration or rehabilitation projects. [Note: Field trip fee based on destination; will not exceed $100+HST. WHMIS may be required pending project lab analysis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations: Environment, Resources and Sustainability', '100', 'ERS', 'Introduces analytical approaches for problem definition and problem solving that are appropriate for a wide range of environment and resource issues. Considers the limitations of approaches that perceive and attempt to manage issues as isolated phenomena. Also examines alternative approaches that recognize the broader context and underlying roots in ethical positions and ecological, economic, and institutional systems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental and Sustainability Assessment 1', '215', 'ERS', 'An introduction to processes and techniques for incorporating environmental considerations in planning and evaluating proposals for future undertakings that may have significant social and biophysical effects. The course provides an overview of methodologies for, and controversies surrounding, the design and conduct of biophysical and socioeconomic impact studies, and the testing of reported findings. The main focus is on the purposes and design of environmental assessment processes, with particular reference to the Canadian federal and Ontario provincial legal mandates, and the evolution of assessment into a sustainability framework.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Sustainable Agroecosystems', '270', 'ERS', 'This course introduces and discusses the fundamental concepts of agroecology, the integration of agriculture and society, and the transition to sustainability. Topics include the impact of biotic and abiotic factors - including the use of genetically modified organisms - on crops, soil, and the agroecosystems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Readings/Seminar on Select Topics', '275', 'ERS', 'Background reading and study in consultation with faculty. Typically utilized when a student must study a topic in connection with other work, but no course offering in that topic is available.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ecological Monitoring', '382', 'ERS', 'Through online readings and a 10-day field trip, this course provides students with theoretical and practical knowledge of ecological monitoring through active participation in programs applying protocols developed by the Smithsonian Institute/Man and the Biosphere Program. The course is a collaborative effort with professional staff from selected governmental agencies, and independent organizations. [Note: Field trip fee normally $750+HST; will not exceed $1000+HST. Offered: After spring examinations, prior to the fall term.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Waste Management', '317', 'ERS', 'This course will deal with the solid waste system, landfilling, incineration, energy from waste, recycling, composting, reduction and reuse. The context will be primarily Ontario and municipal waste management.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental and Sustainability Assessment 2', '315', 'ERS', 'Continuing from concepts developed in ERS 215, this course places more emphasis on case studies and projects by students. The course provides a synthesis of ecological, physical, economic, socio-cultural, and institutional concerns, as well as experience in the use of impact assessment methodologies and approaches, as a key element in achieving more informed and responsible decision making.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Communities and Sustainability', '253', 'ERS', 'Exploration of the interconnected roles that communities at various scales
can play in a transition towards sustainability. Examination of structures, options, initiatives, and decision-making processes involving a wide range of concerns, opportunities, and applications. Illustrative cases include largely urban communities, the urban periphery, and the broader influences that affect them.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Corporate Sustainability: Issues and Prospects', '370', 'ERS', 'A course that examines the ways in which sustainability issues and business operations have interacted, considering progressively greener corporate responses and broader sustainability challenges.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Readings/Seminar on Select Topics', '375', 'ERS', 'Background reading and study in consultation with faculty. Typically utilized when a student must study a topic in connection with other work, but no course offering that topic is available.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Ecological Systems Analysis', '300', 'ERS', 'This course provides an opportunity to learn and begin to apply systems-based tools in the context of an interdisciplinary research problem defined by the student in cooperation with the teaching-team. Students are provided with an introduction to the conceptual tools of systems thinking and resilience that help understand the dynamics of social change and social innovation. These conceptual tools will then be applied by students to provide a framework for interdisciplinary research and to develop a richer understanding of a case study of fostering social change and building adaptive capacity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Environmental Governance', '404', 'ERS', 'Examination of the ways in which world society is striving to address environmental challenges by means of "global governance" - that is, international organizations and institutions intended to deal with these challenges. The history of international environmental politics will be reviewed, specific organizations and other actors involved in global environmental governance will be examined and the management of particular global environmental challenges investigated.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Environmental Governance', '432', 'PSCI', 'Examination of the ways in which world society is striving to address environmental challenges by means of "global governance" - that is, international organizations and institutions intended to deal with these challenges. The history of international environmental politics will be reviewed, specific organizations and other actors involved in global environmental governance will be examined and the management of particular global environmental challenges investigated.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental and Sustainability Assessment 3', '415', 'ERS', 'Continuing from concepts developed in ERS 215 and ERS 315, this course will focus on the latest concepts and applications of assessment principles and practices. The course focus will vary from year to year following development in the field. Topics may include assessment into land use planning and community design, policy and program assessment, and assessment of new technologies and alternative futures.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Readings/Seminar on Select Topics', '475', 'ERS', 'Background reading and study in consultation with faculty. Typically utilized when a student must study a topic in connection with other work, but no course offering that topic is available.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Thesis', '403A', 'ERS', 'This course is for students who have defined a problem, which fits within the scope of research in the School of Environment, Resources and Sustainability, and will undertake original research that leads to production of a thesis. ERS 403A normally leads to the production of a research proposal and the beginning of the research tasks; this process may vary slightly, depending on the nature of the research. The research process and thesis write-up will continue and be concluded in ERS 403B. [Note: Students who wish to take this course should begin intensive planning and discussion with potential advisors in a field relevant to their intended research 6-12 months prior to the course. Students must secure agreement with a professor in SERS to act as the thesis advisor no later than 4 months prior to the start of the course. WHMIS may be required pending project lab analysis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Thesis', '403B', 'ERS', 'This course is a continuation of ERS 403A. It normally consists of a continuation of the research process that began in ERS 403A and leads to the completion of the research and the thesis write-up. It is for students who have defined a problem related to the mission and scope of the School of Environment, Resources and Sustainability and will undertake original research that leads to production of a thesis. The procedures outlined in ERS 403A must be followed. [Note: WHMIS may be required pending project lab analysis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modern Art: 1874-1945', '112', 'FINE', 'A study of the culture of Europe and North America from 1874 (Impressionism) to 1945 (Nazi propaganda) with particular focus on the visual arts. Topics such as primitivism, Cubism, abstraction, artists reactions to calamitous world events such as World War 1, and various methodologies, including semiotic and Marxist analyses, will be explored through reading and writing assignments. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Art: 1780-1875', '210', 'FINE', 'A study of art and architecture in European and American culture between 1780 and 1875. Some of the pertinent themes discussed include art and revolutions, art and the Industrial Revolution, the rise of landscape painting, the urban environment, the exotic, and the erotic. Cross-cultural influences and the complex relationship between the traditional and the modern are particularly emphasized. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Renaissance Art: 1300-1500', '212', 'FINE', 'An examination of the art of the 14th and 15th centuries in Europe. Amongst the many themes that will be explored in this course are mediums (including exploitation of oil paint), scientific perspective, humanism, court structures, art and mercantile sectors, and prominent collectors. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Art of the 16th Century in Europe', '213', 'FINE', 'Some of the finest works of art in the western world were produced and some of the most successful artists flourished in this tumultuous century that saw the split in the western Christian Church and the continued rise of extraordinarily powerful court families and monarchs. This course will examine these works of art and the artists against this backdrop. Arranged according to patrons, the course also examines particular stylistic trends (Mannerism, the persistence of the Gothic, etc.) as well as other pertinent issues such as collecting, the writing of art history, the teaching of art, and the role of the artist in society. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visual Culture of Medieval Europe', '214', 'FINE', 'This course explores the visual culture of medieval Europe from the fourth century to the 13th century. The primary focus of this course is the dynamic intersection of cultures and faiths with particular emphasis on emergent and evolving Christianity, Islam, and Judaism, as expressed in architecture, sculpture, manuscripts, metalwork, textiles, and more. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Art of the 17th Century in Europe', '215', 'FINE', 'Art of the 17th century. This course focuses on art produced in Italy, Spain, France, England, and the Netherlands in the 17th century. Various types of art (history painting, portraiture, still life, etc.) and architecture (churches, palaces, city homes, etc.) are discussed in relation to the primary political, religious, and societal concerns that characterize the different geographical regions. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Oil Painting', '220', 'FINE', 'Various technical methodologies of oil painting and pictorial composition are developed in this course through a series of explorations in observation and abstraction. The plurality of approaches available to painters today is set within a contemporary critical context. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Acrylic and Mixed Media', '221', 'FINE', 'Through this course students will take a materially specific approach to painting through research and experimentation with acrylic paints, paint mediums, and supports. Students will engage with the concepts and techniques of contemporary painting through a series of exercises, lectures, and demonstrations. Projects and assignments will emphasize creative problem solving through an interdisciplinary approach to painting. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Sculpture', '222', 'FINE', 'This studio course is designed to introduce students to a variety of traditional and contemporary approaches, materials, processes, and conceptual concerns related to working in sculpture. Through demonstrations, presentations, critiques, and discussions, students gain expertise and develop a sculptural vocabulary. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Methods and Materials of Sculpture', '223', 'FINE', 'This studio course emphasizes practical applications of traditional and contemporary materials, processes, and conceptual concerns. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Clay Studies', '272', 'FINE', 'Students will explore the creative development of clay and examine cultural, historical, and personal modes of expression using a variety of construction techniques. Students will explore figurative and abstract sculptural concepts to develop a working knowledge of clay as a sculptural medium. The emphasis is on design, form, and visual thinking. [Note: Studio course.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Expressive Drawing', '224', 'FINE', 'Expressive drawing techniques are emphasized through study with the life model. Conceptual drawing projects provide the student with the opportunity to produce bodies of work. [Note: Studio course. This course includes a significant component of mandatory life drawing from the nude model.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Observational Drawing', '225', 'FINE', 'Observational techniques are used to develop an understanding of the principles and concepts of drawing practice. [Note: Studio course. This course includes a significant component of mandatory life drawing from the nude model.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Printmaking', '230', 'FINE', 'Students are introduced to a variety of printmaking processes. Techniques explored include etching, engraving, monoprinting, collograph, and lithography. Training in traditional skills is combined with opportunities for individual expression. [Note: Studio course.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design and Imaging', '228', 'FINE', 'In this course students will explore the fundamentals of design through a series of exercises and assignments that cultivate artistic expression and technical skill. Through an exploration of both analogue and digital processes, students will learn to apply the principles of aesthetic design to a variety of contexts, including two-dimensional space, three-dimensional form, and digital frameworks. [Note: Studio course. Previous experience with digital imaging software is useful.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Photography', '227', 'FINE', 'In this introductory course students will engage with the concepts and techniques of contemporary photographic practices covering a range of studio, documentary, experimental, and street photography. The focus is on still imaging, allowing students to explore digital and analogue processes through a series of exercises, lectures, and demonstrations. [Note: Studio course. Students may need to have access to a fully manual 35mm film camera and/or digital camera, depending on instructor.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religion in Popular Film', '270', 'RS', 'This course examines how religion has been a central concern and inspiration for filmmakers and how popular films have informed and shaped our understanding of religion. [Note: Film Studies course. This course fulfils an Area 3 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religion in Popular Film', '252', 'FINE', 'This course examines how religion has been a central concern and inspiration for filmmakers and how popular films have informed and shaped our understanding of religion. [Note: Film Studies course. This course fulfils an Area 3 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Religion and Film', '271', 'RS', 'This course focuses on a specific genre or theme to explore the complex
relationship between cinema and religion. [Note: Film Studies course. This
course fulfils an Area 3 requirement for Religious Studies majors. This
course is repeatable up to five times, subject to different content. RS 271
was formerly RS 271R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Religion and Film', '253', 'FINE', 'This course focuses on a specific genre or theme to explore the complex
relationship between cinema and religion. [Note: Film Studies course. This
course fulfils an Area 3 requirement for Religious Studies majors. This
course is repeatable up to five times, subject to different content. RS 271
was formerly RS 271R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Art and Gender', '281', 'FINE', 'A study of selected themes dealing with gender in the history of art: e.g., the representation of the human body; portrayal of mythological, spiritual, and allegorical figures; professional and amateur artists and their status within societies; etc. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Survey of Greek Art and Architecture', '241', 'FINE', 'A survey of Greek art and architecture from the earliest times to the coming of the Romans. Material studied may include the art of the Bronze Age, the development of Greek sculpture, the evolution of the Acropolis at Athens, and the change in art and architecture after Alexander the Great.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Survey of Greek Art and Architecture', '241', 'CLAS', 'A survey of Greek art and architecture from the earliest times to the coming of the Romans. Material studied may include the art of the Bronze Age, the development of Greek sculpture, the evolution of the Acropolis at Athens, and the change in art and architecture after Alexander the Great.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Survey of Roman Art and Architecture', '242', 'FINE', 'A survey of Roman art and architecture from the earliest times to the age of Constantine the Great. Material studied may include the art of the Etruscans, the evolution of Roman portraiture, innovations in architectural materials and forms, the use of art and architecture by the Emperors, and the change to Late Antique art.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Survey of Roman Art and Architecture', '242', 'CLAS', 'A survey of Roman art and architecture from the earliest times to the age of Constantine the Great. Material studied may include the art of the Etruscans, the evolution of Roman portraiture, innovations in architectural materials and forms, the use of art and architecture by the Emperors, and the change to Late Antique art.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Indigenous Visual Culture in Canada', '216', 'FINE', 'This course will introduce students to issues in contemporary Indigenous visual culture within the Canadian context. Special attention will be paid to the complexities of contemporary art and cultural practices. The course will examine both practice and relevant theoretical paradigms. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Indigenous Visual Culture in Canada', '216', 'INDG', 'This course will introduce students to issues in contemporary Indigenous visual culture within the Canadian context. Special attention will be paid to the complexities of contemporary art and cultural practices. The course will examine both practice and relevant theoretical paradigms. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Art', '319', 'FINE', 'A survey of contemporary international art movements with emphasis on work since 1970. Readings in contemporary criticism and gallery visits are an integral part of the course. [Note: Art History course.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics Course in Museums, Galleries, Curatorship', '330', 'FINE', 'Often taught by the University of Waterloo Curator, this course explores a range of topics dealing with museums, galleries, and curatorial practices. Field visits may be a required component of this course. [Note: A field trip fee of up to $100 may be required. This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Art of the 18th Century in Europe', '217', 'FINE', 'This course is designed to introduce students to the culture of 18th-century Europe. Particular attention is paid to France and Britain but these countries are discussed within the wider context of Europe and, at times, much of the world. By looking at the art, artists, patrons, and collectors, some of the issues that will be discussed include cosmopolitanism, nationalism, empire-building, "taste", consumerism, and women in society. [Note: Art History course. Formerly FINE 331]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Art Academies', '332', 'FINE', 'This course examines the history of the institutionalization of the teaching of art.  The course is organized as a series of case studies dealing with various formal academies of art and less formal gatherings of artists from the Renaissance to the 20th century. The aims of the course are to analyze why these institutions were formed, how they were organized, what were their mandates, and what was their status in their contemporary worlds. Some of the themes that will be investigated throughout the course include: art in the service of power and politics, the status of the artist, the ideal human form, the classical and medieval traditions, the impact of the avant-garde, the conflict with authority, and the academy as community. [Note: Art History course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Technology Art Studio', '392', 'FINE', 'This unique upper-level course is focused on interdisciplinary collaboration. Students work in teams to create aesthetically and technically engaging works of art that bridge the worlds of fine art and engineering. Students learn about conceptual development and critical analysis, prototyping, iterative design, and interactivity.  The course provides the opportunity for students to share knowledge and develop skills outside of their discipline. Through lectures and presentations students are exposed to a history of art that uses different forms of technology and interactivity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fine Arts Honours Thesis 1', '472', 'FINE', 'This thesis course involves an intensive exploration and development of individual studio research through practice and experimentation, resulting in the production of a significant body of work. Students will be guided by the work of relevant artists and theorists. In addition to embedded professional practice, an important part of the culminating year is critiques by other faculty members, graduate students, visiting artists, and fellow students. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fine Arts Honours Thesis 2', '473', 'FINE', 'This thesis course will continue to guide students in the development of their individual studio research as they work towards completion of a significant body of work supported and complemented by appropriate documentation. In addition to embedded professional practice, an important part of the culminating year is critiques by other faculty members, graduate students, visiting artists, and fellow students. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Studio Practicum 1', '474', 'FINE', 'This course provides Fine Arts students with an opportunity to study a range of topics that will explore, expand, and refine personal areas of interest in studio practice. Students will be required to undertake a range of classroom assignments in addition to working independently on an area of focus. Submitted studio work will be evaluated by a committee of Fine Arts faculty.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Studio Practicum 2', '475', 'FINE', 'Students will research and execute a series of self-directed projects, addressing historical precedents in the context of the concerns of contemporary art practice, and supported and complemented by appropriate documentation. Submitted studio work will be evaluated by a committee of Fine Arts faculty. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Basic French 1', '151', 'FR', 'For students with some elementary or secondary school French not exceeding Year Two (Grade 10 in Ontario) or equivalent. Emphasizes comprehension, grammar, and basic speaking skills.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Basic French 2', '152', 'FR', 'For students with some elementary or secondary school French, not exceeding Year Three (Grade 11 in Ontario) or equivalent. Emphasizes comprehension, grammar, and basic speaking skills.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Language 1: Module 1', '192A', 'FR', 'An intensive French Language course. Vocabulary enrichment and development of reading, writing, and oral expression.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Language 1: Module 2', '192B', 'FR', 'An intensive French Language course. Vocabulary enrichment and development of reading, writing, and oral expression.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Phonetics of French', '203', 'FR', 'An introduction to the structure of the French sound system with a view to improving pronunciation. Careful attention will be paid to the individual students difficulties.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate Spoken French', '250', 'FR', 'A course intended to develop the oral and aural skills. [Note: Formerly FR 250A]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Language 2: Module 1', '251', 'FR', 'Intensive work on grammar and written French.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Language 2: Module 2', '252', 'FR', 'Intensive work on grammar and written French.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Business French 1', '255', 'FR', 'A French language course designed to enable the student to carry on standard business practice in spoken and written French.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Civilization 1', '291', 'FR', 'This course traces the cultural development of France from its origin to the French Revolution. Emphasis is given to the study of music, art, architecture, literature, ideas, and daily life in their historical context. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Civilization 2', '292', 'FR', 'This course completes the study of the cultural development of France from the French Revolution to the present. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Linguistics', '303', 'FR', 'An introduction to the basic principles of linguistic analysis (as opposed to traditional grammar) applied to the sounds, vocabulary and sentence structure of contemporary standard French and certain features of current Canadian French. Students language learning needs will be an important feature of the course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('17th-Century French Literature', '332', 'FR', 'A detailed study of selected aspects of 17th-century French literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('18th-Century French Literature', '343', 'FR', 'A detailed study of selected aspects of 18th-century French literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Language 3', '351', 'FR', 'Intensive development of writing skills through a study of stylistics and advanced composition.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('19th-Century French Literature', '354', 'FR', 'A detailed study of selected aspects of 19th-century French literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('20th-Century French Literature', '363', 'FR', 'A detailed study of selected aspects of 20th-century French literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Quebec Literature', '375', 'FR', 'A study of selected texts by modern French-Canadian authors.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Independent Cultural Study', '399A', 'FR', 'An independent study course, in which the student chooses an area of French life on which to make a detailed study (topic must be approved by the department). A written cultural studies report is submitted, on which the student is examined orally.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Translation', '400', 'FR', 'Intensive development of advanced comparative stylistics, translation, and composition skills.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Linguistics', '403', 'FR', 'A study of selected topics in linguistics. Topics will be chosen by the professor according to their area of specialization. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medieval French Literature', '410', 'FR', 'An introduction to French literature of the Middle Ages through the study of representative texts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('16th-Century French Literature', '424', 'FR', 'A focused study of a particular theme of Renaissance (1500-1600) writing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
