INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Francophone Literatures in Canada', '471', 'FR', 'A detailed study of a selected genre or aspect of French-Canadian literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Aspects of French Canada', '473', 'FR', 'A presentation of traditional and contemporary French Canada in the fields of the arts, literature, music, politics, and society.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Childrens Literature in French', '484', 'FR', 'A detailed study of selected aspects of French and French-Canadian childrens literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('French Women Writers', '485', 'FR', 'A study of selected works by women writers in France from the Middle Ages to the 21st-century. The course will focus on the literary features of these works and on their value as reflections of the position of women in French society throughout the period.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('African and Caribbean French Literature', '487', 'FR', 'A detailed survey of selected Francophone writers from outside Europe and Canada.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Tutorials', '490', 'FR', 'A small group of students follows a course of study under the supervision of a faculty member. For details, inquire with the department. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Tutorials', '491', 'FR', 'A small group of students follows a course of study under the supervision of a faculty member. For details, inquire with the department. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Computation', '121', 'MTE', 'Introduction to electronic digital computers, hardware and software organization, examples of efficient numerical algorithms for basic scientific computations. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electrical Circuits and Instrumentation', '123', 'GENE', 'Charge, current and voltage. Voltage and current sources, resistors, capacitors and inductors. Ohms Law, Kirchhoffs Laws, nodal analysis, Thevenin and Norton models, instrumentation amplifier circuits, time response, impedance. Function and characteristics of basic electrical transducers. Resolution, precision and accuracy. Basics of data acquisition. [Note: Normally labs are held alternate weeks. Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Directed Studies', '301', 'GENE', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 3A or 3B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the students plan. [Note: Permission of the Associate Chair of the Department in which the student is registered is required. Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Directed Studies', '302', 'GENE', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 3A or 3B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the students plan. [Note: Permission of the Associate Chair of the Department in which the student is registered is required. Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Studies in Engineering', '303', 'GENE', 'Engineering students register for this course for credit towards the International Studies in Engineering Option upon return from study or work terms abroad. Credit will be assessed on the basis of a written report and individual interviews. The report may include technical, non-technical, and professional aspects of the foreign residence experience. It may discuss socio-economic aspects of life in the foreign country, and it may compare and contrast conditions in the country or countries involved in the students international experiences, or trace the social or political histories of those countries. In all cases the report must include materials addressed to the needs of other students considering a similar experience. The instructor for this course is the Option co-ordinator. Restricted to students who intend to complete the International Studies in Engineering Option. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Directed Non-Technical Studies', '315', 'GENE', 'This course is provided for students who, through academic studies during international exchange programs or other university related scholarly activities, wish to request a course credit for this work. Students wishing to claim this credit must show evidence of university level academic activity not otherwise claimed for credit. [Note: Permission of the Associate Chair of the Department in which the student is registered is required. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Directed Studies', '401', 'GENE', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 4A or 4B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the students plan. [Note: Permission of the Associate Chair of the Department in which the student is registered is required. Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Directed Studies', '402', 'GENE', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 4A or 4B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the students plan. [Note:  Permission of the Associate Chair of the Department in which the student is registered is required. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ethics and the Engineering Profession', '315', 'PHIL', 'An analysis from the standpoint of philosophical ethics of moral issues arising in professional engineering practice. Issues include the social responsibility of engineers, conflict of interest and obligation, morally acceptable levels of risk, and moral implications of technology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Directed Non-Technical Studies', '415', 'GENE', 'This course is provided for students who, through academic studies during international exchange programs or other university related scholarly activities, wish to request a course credit for this work. Students wishing to claim this credit must show evidence of university level academic activity not otherwise claimed for credit. [Note: Permission of the Associate Chair of the Department in which the student is registered is required. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Technical Entrepreneurship', '454', 'MSCI', 'Technical entrepreneurship is examined considering the role of independent business, entrepreneurial behaviour, types of business and enterprises, business structure, sources of venture concepts and capital, company operation and control, and business start up. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Studies for Visiting Exchange Students', '501', 'GENE', 'An assignment of study or project work under the direction of a faculty member. The specific project is established on the basis of a written proposal that is agreed to by the supervisors at both the host and home institutions. The academic level and the time commitment shall be specified in the proposal. Registration is restricted to international exchange students at Waterloo under an Exchange Agreement with the Faculty of Engineering. Candidates for degrees at the University of Waterloo are not eligible. [Note: A student must arrange with a Waterloo faculty member to serve as advisor prior to registering for this course. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Studies for Visiting Exchange Students', '502', 'GENE', 'An assignment of study or project work under the direction of a faculty member. The specific project is established on the basis of a written proposal that is agreed to by the supervisors at both the host and home institutions. The academic level and the time commitment shall be specified in the proposal. Registration is restricted to international exchange students at Waterloo under an Exchange Agreement with the Faculty of Engineering. Candidates for degrees at the University of Waterloo are not eligible. [Note: A student must arrange with a Waterloo faculty member to serve as advisor prior to registering for this course. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Studies for Visiting Exchange Students', '503', 'GENE', 'An assignment of study or project work under the direction of a faculty member. The specific project is established on the basis of a written proposal that is agreed to by the supervisors at both the host and home institutions. The academic level and the time commitment shall be specified in the proposal. Registration is restricted to international exchange students at Waterloo under an Exchange Agreement with the Faculty of Engineering. Candidates for degrees at the University of Waterloo are not eligible. [Note: A student must arrange with a Waterloo faculty member to serve as advisor prior to registering for this course. Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geological Engineering Design Project 1', '400', 'GEOE', 'Students undertake an independent geological engineering design project during the last two terms of their plan. The purpose of the project is to demonstrate students abilities to practise in a geological engineering capacity in their chosen area of expertise, using knowledge gained from their academic and employment experiences. The first part of the project (GEOE 400) will include problem identification, generation and selection of solutions, and time management. Incorporation of technical, ecological, social, political, and economic issues in the solution for the project will be required. A basic requirement of the proposed solution is that it must be compatible with the principles of sustainability. Requirements include proposal, progress report, and a final report containing recommendations for part two of the project, GENE 401. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geological Engineering Design Project 2', '401', 'GEOE', 'A continuation of GEOE 400. The final design of the major geological engineering project proposed in GEOE 400 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design of the solution proposed in GEOE 400. Requirements of this part of the two-term project include a symposium presentation and a final report. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Geographies: People, Space and Change', '101', 'GEOG', 'An introduction to human geography through key subfields of the discipline, examining population change, the rise of cities, our relationship to nature, social inequalities, economic forms, cultural difference and environmental change at the local and global scale.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Environmental Systems: Processes and Change', '102', 'GEOG', 'An introductory course in the study of the energy and mass flows that impact the climate and landscape of the Earth system. There is focus on understanding processes of change and variability as well as the impacts of humans on these systems that increasingly define the Anthropocene.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluvial Geomorphology', '305', 'GEOG', 'Emphasis on concepts related to fluvial processes, river mechanics, the relationship between environmental change and river regime. Selected topics include fluvial processes and landscape formation, flow and sediment regimes, channel processes, form and behaviour, river response to natural and anthropogenic change, and river management. [Note: Formerly GEOG 201]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geography of the Global Economy', '202', 'GEOG', 'An introduction to globalization of the world economy through an examination of its causes, patterns, and consequences in a variety of geographic contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geomorphology and the Southern Ontario Environment', '300', 'GEOG', 'Study of the origin and evolution of landforms with emphasis on southern Ontario. Analysis of geomorphic processes. Study of human impact on geomorphological landscapes. The lectures will be supplemented by field trips and field work required for term projects. [Note: Field trip fee normally $55+HST; will not exceed $75+HST.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Hydrology', '303', 'GEOG', 'Fundamental processes in physical hydrology are addressed. Components of the water balance are examined to determine the nature of their variation in time and space. Precipitation, interception, infiltration, groundwater and soil water processes, evapotranspiration, runoff and storage will be examined from a theoretical and practical viewpoint, and their linkages demonstrated by lab and fieldwork. [Note: Field trip fee normally $20+HST; will not exceed $55+HST.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Climatology', '309', 'GEOG', 'Principles of physical climatology with emphasis on regional and global change and variability. Topics include radiation and energy balances, general circulation patterns, synoptic development and micro-climatology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Multivariate Statistics', '316', 'GEOG', 'The theory and application of multivariate statistics, with particular emphasis upon the use of the computer.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Multivariate Statistics', '351', 'PLAN', 'The theory and application of multivariate statistics, with particular emphasis upon the use of the computer.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Analysis', '318', 'GEOG', 'Advanced quantitative analysis in a spatial context. A selection of techniques from sampling, geostatistics, point pattern analysis and cluster detection, spatial classification, and spatial data mining.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Analysis', '353', 'PLAN', 'Advanced quantitative analysis in a spatial context. A selection of techniques from sampling, geostatistics, point pattern analysis and cluster detection, spatial classification, and spatial data mining.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economic Analyses for Regional Planning', '319', 'GEOG', 'Practical application and critical appraisal of regional analysis techniques used by planners, economic developers and consultants. Problem based approaches to understanding the strength and leverage of business and industrial sectors, projection and forecasting, employment and demographic trends, investment decision-making and cost benefit analysis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economic Analyses for Regional Planning', '320', 'PLAN', 'Practical application and critical appraisal of regional analysis techniques used by planners, economic developers and consultants. Problem based approaches to understanding the strength and leverage of business and industrial sectors, projection and forecasting, employment and demographic trends, investment decision-making and cost benefit analysis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geographical Study of Canada', '222', 'GEOG', 'An exploration of the geographical bases of Canadas regional identity.  How physical, demographic, and economic geographies have combined to create unique regions within Canada.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Tourism Impacts - International Perspectives', '323', 'GEOG', 'The course examines the nature, significance, and costs and benefits of tourism in the context of tourism destinations in developed and developing countries. It employs an international perspective to assess the economic, environmental, social, and cultural impacts of tourism in diverse settings.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Tourism Impacts - International Perspectives', '383', 'REC', 'The course examines the nature, significance, and costs and benefits of tourism in the context of tourism destinations in developed and developing countries. It employs an international perspective to assess the economic, environmental, social, and cultural impacts of tourism in diverse settings.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geography of Transportation', '351', 'GEOG', 'Focuses on Canadian transportation systems and issues and is organized into three modules: links between transportation and regional economic development, urban land use - transportation interactions, and sustainable transportation.  Approximately one-quarter of the course focuses on analytic techniques including network analysis, category analysis, and the gravity model. Particular attention is paid to trends in air travel and related issues.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Databases', '387', 'GEOG', 'This course focuses on design and development of a GIS database. It addresses theoretical issues regarding data models used in GIS and data modeling techniques used in designing spatial databases. It considers the processing required to input data from a variety of sources and clean and edit a multi-theme database and introduces students to creation and use of internet map services.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Databases', '387', 'PLAN', 'This course focuses on design and development of a GIS database. It addresses theoretical issues regarding data models used in GIS and data modeling techniques used in designing spatial databases. It considers the processing required to input data from a variety of sources and clean and edit a multi-theme database and introduces students to creation and use of internet map services.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Resources Management', '356', 'GEOG', 'Reviews selected theories, methods, and terminology related to economic, behavioural, institutional and decision-making aspects of resources and environmental problems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Research', '391', 'GEOG', 'Field research course in which a specific area will be analyzed from a geographic point of view. Individual or group analysis of specific field problems. [Note: Field trip fee normally $50+HST; will not exceed $150+HST. WHMIS may be required pending project lab analysis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Approaches to Research in Human Geography', '293', 'GEOG', 'This course develops student skills in the area of research in human geography emphasizing all aspects of design, measurement, and evaluation with a particular emphasis on personal transferable skills (e.g., oral and written communication, working as part of a team). [Note: Formerly GEOG 393]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Wetlands', '405', 'GEOG', 'Basic concepts on the distribution, classification, development, hydrology, biogeochemistry, and ecology of wetlands with an emphasis on temperate and boreal/subarctic systems. Human impacts, restoration and reclamation of wetlands are considered with the view of wetlands as functional ecosystems. [Note: Field trip fee normally $20+HST; will not exceed $55+HST. WHMIS may be required pending project lab analysis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Hydrology of Terrestrial Ecosystems', '407', 'GEOG', 'This course focuses on ecological and biogeochemical processes that are linked to the terrestrial hydrological cycle and how these relate to the management of natural resources. The objectives of this interdisciplinary course are to explore topics that integrate ecosystem processes with physical hydrology and examine the impacts of human activities on ecohydrological and hydrochemical processes within terrestrial systems. This course focusses on the storage and movement of water, solutes and nutrients within selected ecosystems (forests, agricultural, wetlands), considering the biogeochemical consequences of human activity such as climate change, wetland drainage, agriculture, and forest harvesting. [Note: WHMIS required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Energy Balance Climatology', '409', 'GEOG', 'A field and lecture course including the radiation and energy balances of various surfaces, the principles of turbulent energy exchange, and the biotic response to the energy environment. These concepts will be illustrated through the collection and examination of field data. The student will be responsible for presentation of a seminar on an assigned topic as well as presentation of the results of research incorporating data collected at the University of Waterloo weather station.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Digital Economy', '411', 'GEOG', 'This course explores the spatial, social, cultural, and economic impacts of digital and social media on society from the perspective of economic geography. Content will cover contemporary issues about digital media and economic spaces that create spatial patterns (including international differences) of digital media access and use, social media economies, the sharing economy, influencer and streamer economies, and global online work platforms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geographies of Development', '426', 'GEOG', 'Examines international development theories and practice, emphasizing the interactions between social, economic, political, and environmental dimensions at the micro- and macro-scales. Selected case studies illustrate divergent outcomes of development and the contested process that development represents.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Research in Regional Geography', '430A', 'GEOG', 'Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary, and travel costs of course offerings, contact the Geography Undergraduate Advisor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Research in Regional Geography', '430B', 'GEOG', 'Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary, and travel costs of course offerings, contact the Geography Undergraduate Advisor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Research in Regional Geography', '430C', 'GEOG', 'Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary, and travel costs of course offerings, contact the Geography Undergraduate Advisor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Changing Form and Structure of Metropolitan Canada', '450', 'GEOG', 'Selected analysis of processes, problems and planning issues associated with the internal growth and spatial reorganization of Canadian metropolitan areas. Three or four topics are chosen for detailed investigation; these will vary from year to year.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Changing Form and Structure of Metropolitan Canada', '450', 'PLAN', 'Selected analysis of processes, problems and planning issues associated with the internal growth and spatial reorganization of Canadian metropolitan areas. Three or four topics are chosen for detailed investigation; these will vary from year to year.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Geographic Information Systems', '381', 'GEOG', 'Students learn theoretical and operational approaches to advanced spatial analysis using geographical information systems. Emphasis is placed on the use of automation procedures using models and programming to address a variety of topics that may include but are not limited to digital terrain modeling, suitability analysis, network analysis, and cell-based models. The domain of spatial problems explored may vary by instructor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Geographic Information Systems', '381', 'PLAN', 'Students learn theoretical and operational approaches to advanced spatial analysis using geographical information systems. Emphasis is placed on the use of automation procedures using models and programming to address a variety of topics that may include but are not limited to digital terrain modeling, suitability analysis, network analysis, and cell-based models. The domain of spatial problems explored may vary by instructor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Energy and Sustainability', '459', 'GEOG', 'Renewable and non-renewable energy supply systems are compared using economic and environmental measures. Consumption trends and conservation options are considered at the local and global level. Projects are used to demonstrate the economic and environmental challenges in the design of a sustainable energy system.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Remote Sensing Project', '471', 'GEOG', 'Digital image analysis for resource mapping and evaluation using remote sensing data. Topics range from initial data selection to final map production and assessment. Using commercial image analysis software, students will analyse data for a selected area and produce a portfolio of results. In addition, they will undertake a literature review on a selected topic and present highlights of the review at an end-of-term mini-conference.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Thesis Preparation', '490A', 'GEOG', 'Preparatory work and first draft of thesis. [Note: Students must have a thesis advisor for department consent to be granted. WHMIS may be required pending project lab analysis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Thesis Completion', '490B', 'GEOG', 'Completion of thesis. [Note: WHMIS may be required pending project lab analysis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary German 1', '101', 'GER', 'This course aims to bring German language, culture, and everyday life into the classroom. Students with little or no prior knowledge are introduced to the structure of German and develop their listening/comprehension, speaking, reading, and writing skills. Classes are communicative and emphasize interaction in German. Online exercises and audio/video materials accompany each chapter of the textbook. [Note: GER 101 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary German 2', '102', 'GER', 'A continuation of GER 101. [Note: GER 102 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate German 1', '201', 'GER', 'This course continues the work of GER 101/102. It offers practice in speaking, reading, and writing, with vocabulary building, grammar, and exercises in comprehension. [Note: GER 201 and GER 211 can be taken concurrently. GER 201 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate German 2', '202', 'GER', 'Strengthening of communicative skills, grammar review, vocabulary building, written practice, and conversation on issues of contemporary life in German-speaking countries. [Note: GER 202 and GER 211 can be taken concurrently. GER 202 is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('German Thought and Culture: Objects', '271', 'GER', 'An exploration of human-made objects that open up perspectives on German-speaking society and culture. Objects to be studied might include the Berlin Wall, the swastika, Luthers Bible, donairs, and Volkswagens. Students acquire skills in cultural analysis and learn about major developments in literature, philosophy, religion, art, architecture, and music. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('German Thought and Culture: People', '272', 'GER', 'An exploration of key figures whose contributions to the arts and intellectual life have shaped German-speaking society and culture. Persons to be studied might include Friedrich Nietzsche, Sigmund Freud, Leni Riefenstahl, and Hannah Arendt. Students acquire skills in cultural analysis and learn about major developments in literature, philosophy, religion, art, architecture, and music. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Waterloo in Germany Program', '395', 'GER', 'Study abroad at a German university in the fall term.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Waterloo in Germany Program', '396', 'GER', 'Study abroad at a German university in the winter term.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Project', '490', 'GER', 'German Honours students in their third or fourth year may complete, with departmental permission, a major project, the results of which must be presented in a substantial essay. The topic should reflect the students field of interest.  [Note: Requests for permission should be submitted to the undergraduate officer, in consultation with the intended faculty advisor, and should include an outline of the project and a bibliography. The project will be assessed and graded by two faculty members.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reading Course in Approved Topics', '495', 'GER', 'Reading course in topics chosen in consultation with an advisor. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Independent Study in Aging', '401A', 'GERON', 'For the student who desires to pursue a particular topic in depth through independent research and/or extensive reading. A faculty member must approve a students project prior to registration for this course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Independent Study in Aging', '401B', 'GERON', 'For the student who desires to pursue a particular topic in depth through independent research and/or extensive reading. A faculty member must approve a students project prior to registration for this course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Ancient Greek 1', '101', 'GRK', 'This course is designed for students beginning the study of ancient Greek. It covers the foundational grammar of Attic and Hellenistic Greek and is the first step in preparing students to read original ancient Greek texts for the study of classical studies and religious studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Ancient Greek 1', '101', 'RS', 'This course is designed for students beginning the study of ancient Greek. It covers the foundational grammar of Attic and Hellenistic Greek and is the first step in preparing students to read original ancient Greek texts for the study of classical studies and religious studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Ancient Greek 2', '102', 'GRK', 'This course is a continuation of GRK 101/RS 101. The majority of the rules of ancient Greek grammar will be covered by the end of the course. Students will begin to read more complex ancient Greek texts relevant to the study of classical studies and religious studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
