INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate Greek', '201', 'GRK', 'This course is a continuation of GRK 102/RS 102. It will complete the initial study of ancient Greek grammar and devote substantial time to the reading of ancient Greek literature. Texts read will include a selection of poetry and prose in a variety of fields, including authors such as Homer, Herodotus, Euripides, Plato, the New Testament, and Josephus.');
