INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Selections from Greek Authors', '202', 'GRK', 'This course is designed to follow GRK 201/RS 201 and to expand students experience of reading ancient Greek texts for the study of classical studies and religious studies. Texts read will include a selection of poetry and prose in a variety of fields, including authors such as Homer, Herodotus, Euripides, Plato, the New Testament, and Josephus.');
