INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Greek Composition, Grammar and Reading', '351', 'GRK', 'Composition, translation, and grammar with intensive analysis of selected passages.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Law and Society in the Middle Ages', '236', 'HIST', 'A study of the laws and legal procedures of the  Middle Ages. This course examines the relationship between legal procedures and institutions and the medieval societies that produced them.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Law and Society in the Middle Ages', '236', 'LS', 'A study of the laws and legal procedures of the  Middle Ages. This course examines the relationship between legal procedures and institutions and the medieval societies that produced them.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('War and Society in Europe, 1914-1945', '102', 'HIST', 'This course explores the impact of World Wars 1 and 2 on European society, with a special emphasis on the experiences of the ordinary person.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadian History Through Biography', '103', 'HIST', 'An examination through lectures and films of the lives of Canadian men and women who have played formative roles in developing the Canadian nation.  Examples will be drawn from such areas as politics, religion, business and labour, social reform, arts and entertainment, and sports.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('An Introduction to Western Intellectual History Since the Renaissance', '104', 'HIST', 'An exploration of some of the questions and answers posed by thinkers on the human predicament from Renaissance and Reformation times to the modern period. Readings range from Luther to J.P. Sartre, Shakespeare to Marx and Freud.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canada at War', '106', 'HIST', 'This course introduces students to the ways in which historians have examined Canadas military experience. Beginning with the Boer War, and continuing through the two World Wars and the post-war era, students examine the political, social, as well as military effects of war on Canada.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Modern World in Historical Perspective', '275', 'HIST', 'This survey of the 20th century explores the non-Western worlds response to a series of selected global themes: the rise of the West; post-colonialism; war and peace; human migration; culture; international organizations; climate change; human rights; disease; and globalization.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History and Film', '200', 'HIST', 'An introduction to issues in modern cultural history through the study of selected narratives and documentary films with supplementary reading, lectures, and discussions. [Note: This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Western Sport', '205', 'HIST', 'This course considers the historical impact of Western sport. It traces the history from individual play through amateurism to professionalism, big business, and media. It examines sports social role within local, national, and international communities, and its relationship to class, gender, leisure, race, and politics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Western Sport', '202', 'REC', 'This course considers the historical impact of Western sport. It traces the history from individual play through amateurism to professionalism, big business, and media. It examines sports social role within local, national, and international communities, and its relationship to class, gender, leisure, race, and politics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Smallpox to Medicare: Canadian Medical History', '209', 'HIST', 'Starting with Aboriginal medicine, the course examines topics such as the rise of the medical and nursing professions, changing public attitudes to health and disease, and the evolution of the Canadian health insurance system.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Ancient Law', '210', 'HIST', 'A historical introduction to law in the ancient world. Babylonian, Assyrian, Hittite, and Roman law, legal practices, and concepts will be examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Ancient Law', '210', 'CLAS', 'A historical introduction to law in the ancient world. Babylonian, Assyrian, Hittite, and Roman law, legal practices, and concepts will be examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Ancient Law', '235', 'LS', 'A historical introduction to law in the ancient world. Babylonian, Assyrian, Hittite, and Roman law, legal practices, and concepts will be examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('British History to 1485', '211', 'HIST', 'A survey of the main stages in the transition of Britain from a remote province of the Roman Empire to a prominent state in post-Reformation Europe. Within the chronological framework, political and constitutional as well as ecclesiastical and social developments will be examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('A History of Popular Culture', '213', 'HIST', 'This course introduces students to the history of Western popular culture and may include the study of popular literature, spectacle and performance, witchcraft, crime, sexual attitudes, consumption, sports, advertising, and the media.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Racism and Response in Canadian History', '221', 'HIST', 'The "race problem" has appeared on the Canadian public agenda, but the issue is not of recent origin. This course examines Euro-Canadian attitudes and practices toward non-European minorities from pioneer times to the present and sets racial policies in the context of the evolution of a Canadian national identity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Holocaust in History', '223', 'HIST', 'An examination of the Holocaust in the context of the history of modern racism. Study topics will include historic anti-Judaism, scientific racism and the development of modern antisemitism, Nazi  "race" ideology, wartime policies from ghetto to genocide, resistance movements, Nuremberg trials, Holocaust denial, and universal lessons from the Holocaust.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Ancient Near East and Egypt', '237', 'HIST', 'A study of the civilizations of the ancient Near East focusing on Mesopotamia (Sumer and Akkad, the Babylonian Dynasty, and the Third Dynasty of Ur), Hatti, Assyria, Egypt, and Persia.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Ancient Near East and Egypt', '237', 'CLAS', 'A study of the civilizations of the ancient Near East focusing on Mesopotamia (Sumer and Akkad, the Babylonian Dynasty, and the Third Dynasty of Ur), Hatti, Assyria, Egypt, and Persia.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Modern China, 1911 to the Present', '239', 'HIST', 'Some of the topics studied in this course include: the three stages of warlordism, the May Fourth Movement, and the structure of society in the Peoples Republic of China.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('European Business History: From Workshop to Factory and Beyond', '243', 'HIST', 'This course examines the changing nature of work and the workplace in Europe and the impact of those changes on European society. The objective of this course is to develop a better understanding of todays workplace and its challenges by exploring its historical roots and the forces that gave it shape.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mennonite History: A Survey', '247', 'HIST', 'This course covers Mennonite origins, teachings, migrations, settlement patterns, divisions, leaders, institutions, and religious and social practices, indeed all facets of Mennonite history in various national settings.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canada and the Americas', '350', 'HIST', 'This course will examine the economic, cultural, and diplomatic aspects of Canadas relationship with the United States, Latin America, and the Commonwealth Caribbean from the time of the American Revolution to the present.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('What is History? An Introduction to Historical Thinking', '250', 'HIST', 'This course provides a collegial learning setting within which students are  introduced to techniques of historical writing and research, and some examples of the best of recent historical scholarship.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canada: Cultures and Conflicts in the Colonial Era', '253', 'HIST', 'This course examines the major themes in pre-Confederation Canadian history including the rise and fall of New France, the creation of British North American societies in the Maritimes and Upper Canada, and economic and political development.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canada Since 1867: A New Nation', '254', 'HIST', 'This course examines Confederation, the rise of political parties, Canadian external relations, western discontent, the impact of both World Wars, and political and economic changes in Canada since 1867.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The United States Through the Civil War Era', '257', 'HIST', 'This historical survey focuses on the emergence of the United States as a nation. The topics explored may include Indigenous peoples, slavery, race, gender, labour, immigration, urbanization, culture, sectionalism, politics, and ideologies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The United States Since the Civil War Era', '258', 'HIST', 'This course begins in the aftermath of the Civil War and ends at the present day. Topics may include major social movements, the place of the United States in world politics, immigration and imperialism, and the economy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medieval Europe c.300-c.1500', '260', 'HIST', 'The political, cultural, economic, and ecclesiastical development of Europe from the fall of the Roman Empire to the end of the high middle ages.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medieval Europe c.300-c.1500', '260', 'MEDVL', 'The political, cultural, economic, and ecclesiastical development of Europe from the fall of the Roman Empire to the end of the high middle ages.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Early Modern Europe 1450-1700', '262', 'HIST', 'This course examines European life in the Early Modern Period (1450-1700) and investigates the social, political, religious, and intellectual changes during the Renaissance, the Reformations, and the era of explorations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Age of Revolution: Europe in the 19th Century', '263', 'HIST', 'This period marks the emergence of modern-day Europe. The course focuses on the way in which European society, politics, and culture changed, and why. It also examines the continents descent into war in 1914.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Vietnam War', '220', 'HIST', 'The Vietnam War, the longest war in U.S. history, was fought on two fronts, by American GIs abroad and anti-war protesters at home. Those two subjects, as well as a history of the war from Vietnamese perspectives, are the focus of this course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Rights in Historical Perspective', '321', 'HIST', 'A detailed analysis of selected topics in the history of human rights. Special attention will be paid to revolutionary developments since World War II, and to the emergence of modern human rights policies. Topics will be examined through assigned readings, lectures, and films.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Rights in Historical Perspective', '331', 'LS', 'A detailed analysis of selected topics in the history of human rights. Special attention will be paid to revolutionary developments since World War II, and to the emergence of modern human rights policies. Topics will be examined through assigned readings, lectures, and films.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Origins of the Common Law', '329', 'HIST', 'A study of the common law of England from its introduction in the 11th century to the 15th century. Original documents and court cases will be examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('A Social History of Europe: 1789-1914', '340', 'HIST', 'European society amidst the dramatic changes of the 19th century. Emphasis is given to the impact of the French and industrial revolutions on class, the family, religion, and living conditions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Nazi Occupation of Europe', '341', 'HIST', 'This course examines the nature and impact of Nazi occupation on Western and Eastern Europe preceding and during World War II and the responses of the people occupied.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Russia: From Tsars to Putin', '356', 'HIST', 'This course examines the history of Russia and the Soviet Union from the beginning of the 20th century to the present. Topics include: origins and nature of the Russian Revolution, communist society, Stalinism, the Cold War, and impact of the communist experience on contemporary Russia.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Nazi Germany', '358', 'HIST', 'An examination of the social, economic, and political history of Nazi Germany. Topics may include the rise of the Nazis, the secret police, war, population policies and mass murder, culture, and women.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadas Social History', '374', 'HIST', 'A topical consideration of key themes, approaches, and chronologies in the history of society in Canada.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reformation History', '379', 'HIST', 'A study of the major 16th century reformers, and their intellectual background in humanism and late medieval scholasticism. Special attention will be given to the Lutheran and Reformed traditions, and their ideological, social, and political expressions. [Note: This course fulfils an Area 2 requirement for RS majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reformation History', '343', 'RS', 'A study of the major 16th century reformers, and their intellectual background in humanism and late medieval scholasticism. Special attention will be given to the Lutheran and Reformed traditions, and their ideological, social, and political expressions. [Note: This course fulfils an Area 2 requirement for RS majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('From Macdonald to Laurier: Canada, 1841-1921', '385', 'HIST', 'A topical examination of major political and social developments over this eighty-year period. These include Irish immigration, Confederation, the Riel rebellions, social reform, the development of labour and business, and the Boer and First World Wars.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modern Canada', '388', 'HIST', 'Lectures, tutorials, and independent research will provide a decade-by-decade examination of the central social, political, and economic themes that have helped characterize "modern Canada".');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canada in World Affairs', '389', 'HIST', 'An analytical and historical examination of Canadian foreign policy in the international system. Domestic sources of Canadian foreign policy and international sources of Canadian foreign policy are examined in detail.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Studies in Special Topics', '397', 'HIST', 'Study in a limited field under tutorial guidance. A high standard of written work will be expected.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Studies in Special Topics', '398', 'HIST', 'Study in a limited field under tutorial guidance. A high standard of written work will be expected.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Independent Study in Special Topics', '491', 'HIST', 'Under exceptional academic circumstances a student may seek permission to pursue a course of independent study under the direction of a faculty member. The special topic is determined in consultation between them.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Aging and Health', '201', 'HLTH', 'This course focuses on the challenges facing older adults and on strategies to promote successful aging. A basic understanding of the physical aging process is explored, distinguishing between pathological states/illness and normal change. Many physical changes and associated functional decline are affected by modifiable factors such as sedentary lifestyles, substance abuse, and diet. Education, environment, and personal supports also play key roles in preventing illness and accidents (e.g. falls, driving accidents, and medication errors). The course addresses the interests of those working with older adults, and anyone who has older grandparents, parents, or friends.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Aging and Health', '201', 'GERON', 'This course focuses on the challenges facing older adults and on strategies to promote successful aging. A basic understanding of the physical aging process is explored, distinguishing between pathological states/illness and normal change. Many physical changes and associated functional decline are affected by modifiable factors such as sedentary lifestyles, substance abuse, and diet. Education, environment, and personal supports also play key roles in preventing illness and accidents (e.g. falls, driving accidents, and medication errors). The course addresses the interests of those working with older adults, and anyone who has older grandparents, parents, or friends.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Health 1', '101', 'HLTH', 'This course will be of interest to students pursuing careers in health and those with a general interest in health and health care. Various perspectives of the concepts of health and illness will be introduced. Emphasis is on understanding the origins, factors and conditions that determine health throughout the lifespan, how these factors influence one another and the role/impact of health care. Additional topics include how health is measured, the leading causes of death, illness and disability, as well as how health status has changed throughout history. Students will also learn how to research and critically analyze the health literature and discuss important health issues with their peers.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Health 2', '102', 'HLTH', 'This course expands upon the general concepts introduced in HLTH 101 while extending the discussion to include issues such as: additional barriers to health; disease prevention; and methods used to plan, monitor, and improve individual and population health. Discussion will include when various interventions are justified and why, and where and when combinations of policies, treatments, education, and other approaches are necessary. The health of Canadians relative to that of people in other countries, as well as the inequalities that exist in health status within Canada and around the globe will be examined. Case examples will be used to illustrate points.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Development, Aging, and Health', '310', 'HLTH', 'The physiology of human growth, development, and aging is examined, with special reference to the influence of diet, environment, exercise, and disease on the normal processes.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Development, Aging, and Health', '310', 'GERON', 'The physiology of human growth, development, and aging is examined, with special reference to the influence of diet, environment, exercise, and disease on the normal processes.');
