INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Psychology of Death and Dying', '218', 'HLTH', 'Variations in the meaning and significance of death and dying will be considered from a psychological perspective, with particular attention to the contexts (e.g., cultural, familial, life-span developmental) in which these variations occur.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
