INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Psychosocial Perspectives on Lifespan Development and Health', '320', 'HLTH', 'This course will focus on the normative psychosocial aspects of development of the individual and their influence on the individuals physical and mental health and well-being. Through the use of a developmental systems, lifespan approach, the course will emphasize the life-long process of development. [Note: Formerly HLTH 220/GERON 220]');
