INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadian Health Systems', '245', 'HLTH', 'This course examines Canadian health systems by considering organizational principles, health resources, service utilization, health care planning, and health promotion strategies. There is a focus on societal and political issues which affect the health of the society through the delivery system.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadian Health Systems', '245', 'GERON', 'This course examines Canadian health systems by considering organizational principles, health resources, service utilization, health care planning, and health promotion strategies. There is a focus on societal and political issues which affect the health of the society through the delivery system.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Toxicology and Public Health', '340', 'HLTH', 'An introduction to the basic biological and toxicological processes that determine the effects of environmental pollutants on human health. Emphasis is placed on the mechanisms that give rise to chronic or delayed health effects, such as cancer, genetic mutations, and birth defects.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Pathobiology', '341', 'HLTH', 'An introduction to the study of biological factors governing disease in humans that will use selected diseases to identify risk factors and illustrate pathogenic mechanisms. The role that behaviour has in modifying biological response to disease may also be considered.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Nutrition and Metabolism', '346', 'KIN', 'This course will examine advanced nutrient metabolism and the interplay between diet, biochemistry, and health and disease outcomes. Challenges in assessing dietary intake and the impact of aging and other behaviours such as exercise on nutritional status will be examined. Approaches used in nutritional research will also be introduced.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Occupational Health and Safety', '350', 'HLTH', 'This course explores methodological approaches to the recognition, evaluation, and control of health and safety hazards in the workplace. The health effects of chemicals, physical energies and agents, biological agents, as well as psychosocial stressors are examined. Policies, programs, and practices that encourage a holistic approach to worker well-being are also considered.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sociology of Aging', '352', 'HLTH', 'An introduction to individual and population aging. Topics discussed include: aging from a historical and comparative perspective; aging in subcultures; aging and the social structure; aging and social processes; aging and the environment; work and retirement; and aging and leisure patterns.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
