INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Thesis (A)', '432A', 'HLTH', 'An independent research project on an approved topic, supervised by a faculty member. Includes an approved proposal and completion of -- introduction, review of literature, methods, data collection, data analysis and presentation of results in thesis form. Recommended for students planning graduate studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Thesis (B)', '432B', 'HLTH', 'An independent research project on an approved topic, supervised by a faculty member. Includes an approved proposal and completion of -- introduction, review of literature, methods, data collection, data analysis and presentation of results in thesis form. Recommended for students planning graduate studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Experimental Methods', '433', 'HLTH', 'This course focuses on the key issues related to the design, conduct, analyses, and interpretation of experimental studies. Examples will be drawn from animal research investigating disease mechanisms and from clinical and population studies investigating efficacy of preventive or therapeutic strategies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Epidemiology of Non-Communicable Diseases', '442', 'HLTH', 'This course builds upon the concepts learned in HLTH 333. The primary objective is to provide an understanding of the fundamental concepts, principles and applications of non-communicable disease epidemiology. The course emphasizes understanding of epidemiologic methods and identification of risk and protective factors.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Basic Human Resources Management', '200', 'HRM', 'Examines the major areas of human resources administration including recruiting, salary administration, labour relations, benefits administration, employee relations, labour law, and organizational behaviour. Reviews the role of human resources administration in organizations and the manner in which human resources executives contribute to the well-being of a total enterprise. [Offered F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Political Ideologies and Social Development', '131R', 'SDS', 'This course introduces major political ideologies, their influence in political institutions and public discourse, and their impact on contemporary Canadian social policy and social development. [Note: Formerly ISS 131R]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Lifespan Processes', '150R', 'SDS', 'This course is an introduction to human development that spans conception to death. It provides an overview of events which occur throughout the lifespan and considers how biological, psychological, and social factors may impact healthy development. [Note: Formerly ISS 150R]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Changing Concepts of Childhood', '220R', 'SDS', 'Childhood has changed as a social and cultural concept. This course will trace these changes, examining sociological, psychological, cross-cultural, historical and political factors. Art and literature will also be used to reflect attitudes about childhood. [Note: Formerly ISS 220R]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Art and Society', '240R', 'SDS', 'Social issues and themes explored through the arts. Topics include art and social change, war and peace, propaganda, art of conscience, and the response of artists to poverty, hunger and catastrophic events. Specific applications include art as cross-cultural awareness and art as therapy. [Note: Formerly ISS 240R]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Statistics', '250R', 'SDS', 'This introductory level statistics course will emphasize the collection, manipulation, descriptive presentation, and statistical analysis of social research data. [Note: SWREN 250R is available only to students who have been given conditional admission to the BSW program; such students must have already completed their first undergraduate degree.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Statistics', '250R', 'SWREN', 'This introductory level statistics course will emphasize the collection, manipulation, descriptive presentation, and statistical analysis of social research data. [Note: SWREN 250R is available only to students who have been given conditional admission to the BSW program; such students must have already completed their first undergraduate degree.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Research', '251R', 'SDS', 'Introduction to the philosophy and methodology of applied social science research, including treatment of the problems and strategies of research design and execution. [Note: SWREN 251R is available only to students who have been given conditional admission to the BSW program; such students must have already completed their first undergraduate degree.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Research', '251R', 'SWREN', 'Introduction to the philosophy and methodology of applied social science research, including treatment of the problems and strategies of research design and execution. [Note: SWREN 251R is available only to students who have been given conditional admission to the BSW program; such students must have already completed their first undergraduate degree.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Critical Encounter with Human Nature', '420R', 'SDS', 'This course explores human nature, issues fundamental to human life, and theories which have developed around these issues. The approach is interdisciplinary and intercultural/interreligious with emphasis on such themes as self knowledge, community, loneliness and anxiety, free will and purpose in human life, and the nature of human happiness.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Critical Encounter with Human Nature', '420R', 'RS', 'This course explores human nature, issues fundamental to human life, and theories which have developed around these issues. The approach is interdisciplinary and intercultural/interreligious with emphasis on such themes as self knowledge, community, loneliness and anxiety, free will and purpose in human life, and the nature of human happiness.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Adult Life Crises and Events', '350R', 'SDS', 'A study of normal events occurring during the adult years, why they happen, and how we cope with them. Relying on research, popular literature, and life experiences, students examine social change, the future, adult development and adjustment. [Note: Formerly ISS 350D]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Values and Contemporary Families', '354R', 'SDS', 'An exploration of how religious, economic, political, and other social institutions shape values in our society, and what impact societys changing values are having upon families. [Note: Formerly ISS 350H]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Independent Study', '398R', 'SDS', 'Interdisciplinary focus in greater depth than is available in other courses, on a selected area of concern to the student. Available to individuals or small groups of third- or fourth-year Social Development Studies students and arranged with one of the programs faculty members. [Note: Normally, a student may take only two of the independent studies courses: PSYCH 398R, 399R; SDS 398R [Formerly ISS 398R], 399R; SOC 398R, 399R; SOCWK 398R, 399R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Independent Study', '399R', 'SDS', 'Interdisciplinary focus in greater depth than is available in other courses, on a selected area of concern to the student. Available to individuals or small groups of third- or fourth-year Social Development Studies students and arranged with one of the programs faculty members. [Note: Normally, a student may take only two of the independent studies courses: PSYCH 398R, 399R; SDS 398R, 399R [Formerly ISS 399R]; SOC 398R, 399R; SOCWK 398R, 399R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Essay/Thesis', '499A', 'SDS', 'Under the supervision of a faculty member, students focus on an area of Social Development Studies and either complete an essay involving in-depth literature research and analysis or complete a scholarly report on their own study involving literature review, research design, data collection, and analysis. [Note: Formerly ISS 499A. A numeric grade for SDS 499A will be submitted only after completion of SDS 499B.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Honours Essay/Thesis', '499B', 'SDS', 'Under the supervision of a faculty member, students focus on an area of Social Development Studies and either complete an essay involving in-depth literature research and analysis or complete a scholarly report on their own study involving literature review, research design, data collection, and analysis.  [Note: Formerly ISS 499B]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Italian Language 1', '101', 'ITAL', 'An intensive study of the fundamentals of Italian grammar and conversation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Italian Language 2', '102', 'ITAL', 'A continuation of ITAL 101, with more emphasis on conversation and everyday uses of language.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate Italian 1', '201', 'ITAL', 'Advanced study of grammar. Conversation sessions based on intermediate-level readings reflecting contemporary Italian life. Intensive practice in the spoken and written language.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate Italian 2', '202', 'ITAL', 'A continuation of ITAL 201.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Issues in Contemporary Italian Society', '251', 'ITAL', 'Through lectures, discussions, and readings in Italian, this course studies diverse aspects of contemporary Italian society, such as politics, the arts, food, fashion, and popular culture. In addition to improving their language comprehension and cultural awareness, students will expand their vocabulary and improve their oral and written competencies in Italian.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Italian Culture and Civilization 1', '291', 'ITALST', 'A survey of developments in Italian culture - history, literature, and the arts - up to and including the Renaissance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Italian Culture and Civilization 2', '292', 'ITALST', 'A survey of developments in Italian culture - history, literature, painting, and music - in the post-Renaissance period, with emphasis on modern Italy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medieval Italian Literature', '311', 'ITALST', 'An introduction to the Italian literature of the Middle Ages, with special reference to selections from the major works by Dante, Petrarch, and Boccaccio.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Renaissance Italian Literature', '312', 'ITALST', 'An introduction to the Italian literary production of the 15th and 16th centuries, focusing on selections from the major works by writers of the period.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Italian Cinema and the Novel', '281', 'ITALST', 'A survey of some of the principal novels of 20th century Italy as they have been adapted in film by Italian directors. Students will study literary and cinematic representations of themes such as the rise of the Italian bourgeoisie, the economic boom of the 1960s, domestic violence, and organized crime.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modern Italian Poetry and Theatre', '392', 'ITALST', 'This course studies the works of major Italian poets and playwrights, modern and contemporary, paying special attention to works by women authors.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics/Directed Readings', '396', 'ITAL', 'This course gives the student an opportunity to study authors and works of special interest which are not covered in other courses. [Note: This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First-Year Japanese 1', '101R', 'JAPAN', 'An introductory course for students who have little or no knowledge of Japanese to develop basic listening, speaking, reading, and writing skills. Practical oral and written exercises incorporating the Hiragana Writing System provide a firm grammatical foundation for further study. [Note: JAPAN 101R is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First-Year Japanese 2', '102R', 'JAPAN', 'Listening, speaking, reading, and writing skills acquired in JAPAN 101R are further developed. Practical oral and written exercises incorporating the Katakana Writing System are used to develop a more solid grammatical base.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Japanese for Business 1', '111R', 'JAPAN', 'An introductory course to develop basic comprehension, speaking, reading, and writing skills specifically related to the Japanese business culture. This course is designed for students with little or no previous knowledge of Japanese. [Note: JAPAN 111R is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Japanese for Business 2', '112R', 'JAPAN', 'A continuation of JAPAN 111R designed to further develop listening, speaking, reading, and writing skills. Language skills required for the business environment are stressed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Second-Year Japanese 1', '201R', 'JAPAN', 'A continuation of the study of grammar and vocabulary through development of listening, reading, writing, and speaking skills. Some study of Japanese culture is also included. By the end of the course, 120 Kanji (Chinese characters in their Japanese readings) will have been introduced.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Second-Year Japanese 2', '202R', 'JAPAN', 'In this course students continue to develop their language skills with an increased emphasis on spoken Japanese. In addition, students work on improving grammatical accuracy and vocabulary development as well as continue to acquire basic information about Japanese culture. The writing of an additional 200 Kanji is taught.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Third-Year Japanese 1', '301R', 'JAPAN', 'This course concentrates on advanced conversation, polite forms, and idioms. It provides an opportunity to revise and practise the Hiragana and Katakana writing forms. Upon completion, students should be able to write 800 characters and use a Japanese dictionary with ease.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Regional Human Anatomy', '100', 'KIN', 'Regional anatomy of the limbs and trunk, including a brief introduction to the anatomy of the central nervous and cardiovascular systems. The course focuses on the bones, muscles, and neurovasculature of the limbs and trunk.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Regional Human Anatomy Laboratory', '100L', 'KIN', 'Regional gross anatomy of the limbs and trunk, including a brief introduction to the central nervous and cardiovascular systems. The anatomy is explored using pre-dissected human cadavers. For the limbs and trunk, the emphasis is placed on structures (muscles, nerves, and bones) involved in gross movement.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Kinesiology Laboratory', '104L', 'KIN', 'This lab provides exposure to techniques utilized in the discipline of kinesiology. Students will gain practical skills in tests that measure cardiovascular function, neuromuscular function, and body composition.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomechanics of Human Movement', '121', 'KIN', 'This course will provide students an understanding of human movement from a mechanical perspective, which enables identification of potential risks for injury, optimizes exercise prescription, and promotes understanding of clinical evaluations. Specifically, concepts related to functional anatomy, muscle and passive tissue mechanics, anthropometry, electromyography, and linked segment mechanics are introduced and applied to clinical, occupational, and athletic situations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Anatomy of the Central Nervous System', '301', 'KIN', 'Functionally-oriented anatomy of the brain, spinal cord, cranial nerves, and the tissues they innervate using pre-dissected cadavers. Major functional systems, including somatosensory and motor pathways, special sense pathways, and integrative systems of the brain, will be examined through an understanding of anatomical connectivity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Biochemistry', '217', 'KIN', 'An elementary course in human biochemistry including the metabolism and function of proteins, carbohydrates, lipids, enzymatic function, energy metabolism, and introductory genetics. Emphasis is placed on clinical and medical applications.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Neurological Disorders', '312', 'KIN', 'An introduction to selected neurological disorders and their implications for physical activity. The neurological disorders examined include those which accompany neuromuscular and perceptual-motor impairment, intellectual disability, cardiovascular and respiratory disease.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Neuroscience', '255', 'KIN', 'An introduction to the principles of the nervous system control of movement, cognition, and learning. The course will introduce the basic structure and function of the nervous system as it relates to understanding the control of movement and behaviour. The course will provide a foundation for understanding the neural mechanisms of learning, recovery from injury to the nervous system, and the factors that determine skilled performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Musculoskeletal Injuries in Sport and Activity', '340', 'KIN', 'This course will introduce students to a variety of common orthopedic injuries sustained during sport and activity. Topics covered include the mechanisms of injury, tissue injury biomechanics, pathophysiology, initial assessment, management, and prevention of acute and chronic trauma.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Psychology of Physical Activity', '354', 'KIN', 'This course will provide an overview of the field of sport and exercise psychology and examine how various factors impact our interest, passion, and commitment to sport, exercise, and physical activity. Topics include personality, motivation, team dynamics, coaching psychology, interventions, aggression, body image, and youth development through sport.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sensory Systems Neuroscience', '356', 'KIN', 'This course will examine how different regions of the nervous system interact to shape our perception, decisions, memories, movements, and consciousness. The focus will be placed on the role of the sensory systems in transducing external stimuli into neural activity, how sensory information is processed within and across sensory modalities (i.e., multisensory integration), and the implications for nervous system function. Topics will be considered from developmental, injury, disease, and aging perspectives.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Motor Learning and Neuroplasticity', '357', 'KIN', 'This course will examine the neural control of movement and how experience shapes brain reorganization to support skilled motor ability. Evidence from behavioural and neuroimaging studies will be considered with an emphasis on leveraging principles of motor learning to skill acquisition and skilled performance in sport/occupational environments and the rehabilitation of movement disorders/injuries.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Physiology', '402', 'KIN', 'This course will examine the human cardiorespiratory and metabolic responses at rest and during exercise in altered gravitational, thermal, humidity, and barometric environments.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Neuromuscular Integration', '416', 'KIN', 'An examination of the neural processes involved in the maintenance of posture and the control of movement.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Occupational Biomechanics', '420', 'KIN', 'A course designed to provide the student with knowledge to reduce the risk of injury and increase worker productivity. Issues include identification of injury risk factors, understanding injury mechanism, quantitative assessment of injury risk and intervention strategies to reduce the risk of injury. Specific examples include the use of computerized models and EMG methods to analyze low back loading, optimizing tool design and workspace layout and the examination of related issues such as office seating and vibration.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Posture, Balance and Gait', '422', 'KIN', 'This course will provide a detailed understanding of the kinematics, kinetics, and neural control of standing posture, stepping, walking, and running. Measurement techniques and the interpretation of movement data will be emphasized from biomechanical and neural control perspectives. Applications to aging and pathology will be emphasized.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomechanical Modelling', '425', 'KIN', 'This course will examine the quantitative measurement and analysis of the human musculoskeletal system. Multi-segment dynamic movements will be studied using computer programs, with emphasis on kinematics, kinetics and energetics, as well as the use of EMG in the assessment of the control of the movement. Examples are presented from pathological, normal, and athletic movement.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Proposal', '431', 'KIN', 'An independent paper in the form of a research proposal on an approved topic. The topic may include survey, field, laboratory, theoretical, or applied research, program evaluation, mathematical modelling, fitness appraisal, etc. The format is to be determined with the supervisor and may be in chapters or in journal style.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Project', '432', 'KIN', 'An independent research project on an approved topic, supervised by a faculty member. This is the completion of the research proposed in KIN 431. The format is to be determined with the supervisor and may be in chapters or in journal style.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Essay', '433', 'KIN', 'An extensive critical review of the literature on an approved topic, supervised by a faculty member. The topics will be broader in scope than those associated with specific research proposals.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Sport Psychology', '453', 'KIN', 'An examination of the methods and techniques for developing psychological skills to enhance performance and personal growth. Focus will be on learning strategies to increase the ability to self-regulate and influence others behaviour and improve performance. Topics may include confidence, mental toughness, imagery, concentration, and attention.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cognition, Cognitive Dysfunction, and Movement', '359', 'KIN', 'This course provides a comprehensive introduction to neuropsychology, with emphasis on the implication for movement and discussion of how neurological disorders reflect disturbances at different stages in the sequence of information processing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cognitive, Emotional and Motor Assessment', '459', 'KIN', 'This course is designed to provide the student with an introduction to the principles underlying the assessment of cognitive, emotional, and motor functions. Measurement issues associated with test development and use, factors involved in the administration and interpretation of test results, and methods of report writing will be examined. Students will learn to administer a number of test instruments used in the assessment of cognitive, emotional, and motor functions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Seminar in Kinesiology', '470', 'KIN', 'An examination of current major issues and trends in Kinesiology. Students select areas of major interest from a series of faculty introduced topics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Study in Special Topics', '472', 'KIN', 'For the student who desires to pursue a particular topic in depth through guided independent research and/or reading. A faculty member must approve a students project prior to registration. [Note: May be repeated in subsequent terms.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Clinical Kinesiology -- Sports Injuries Assessment', '491', 'KIN', 'Practical experience in the examination, diagnosis, and treatment of sports injuries under the supervision of a Certified Athletic Therapist. Case presentations and musculoskeletal assessments are discussed in a group setting.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Clinical Kinesiology: Movement Assessment Practicum', '493', 'KIN', 'Practical experience in movement assessment of persons from various special populations such as the normal elderly and those with neurological, degenerative, or developmental disorders. Motor functions involving gait, posture, and balance or upper-limb movements will typically be examined in these assessments.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First-Year Korean 1', '101R', 'KOREA', 'An introductory course for students who have no or little knowledge of Korean to develop listening, speaking, reading, and writing skills along with a sound basis of grammar. The distinctive features of the Korean language and writing system are introduced. Practical oral, reading, and writing exercises develop the students grammatical skills. Particular emphasis is placed on the acquisition of a basic working vocabulary. [Note: KOREA 101R is not open to students with native, near-native, or similar advanced ability.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First-Year Korean 2', '102R', 'KOREA', 'Students deepen their understanding of basic grammatical (particularly verb, noun, and adverb) forms and sentence construction and enlarge their general vocabulary. Reading ability is expanded, and more attention is given to idiomatic expressions and the use of the language in actual contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Second-Year Korean 1', '201R', 'KOREA', 'Designed for students who have completed KOREA 102R or the equivalent. To achieve a balanced Korean language proficiency in listening, speaking, reading, and writing, a variety of teaching materials and methods is used. The text includes adapted versions of short stories, essays, and poems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Latin 1', '101', 'LAT', 'A course designed for students beginning the study of Latin or who have not yet reached the level expected in LAT 201/202. Although the teaching approach emphasizes exposure to simple texts as soon as possible, students desiring minimal competence in reading should go on to do LAT 102.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Latin 2', '102', 'LAT', 'Continuation of LAT 101. Most of the rules of Latin grammar will be covered by the end of the year, and students should have a minimal competence in reading prose texts; but for the remaining grammar and further practice students should go on to do LAT 201.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate Latin', '201', 'LAT', 'The course will complete the study of Latin grammar and move on to unadapted readings in Latin authors, particularly Caesar.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Selections from Latin Authors', '202', 'LAT', 'A course designed to follow LAT 201, including both literature and grammar review. Authors normally read are Vergil and Ovid.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Latin Composition, Grammar, and Reading', '351', 'LAT', 'Composition, translation, and grammar with intensive analysis of selected passages.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medieval Latin', '381', 'LAT', 'Survey of Medieval Latin poetry and prose.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Latin Epigraphy', '421', 'LAT', 'The course introduces and investigates Latin inscriptions as evidence for the Latin language and Roman political, religious, legal, social, and economic history.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Mechanical Engineering Practice 1', '100', 'ME', 'This course is focused on fundamental knowledge and skills essential for academic and professional development in mechanical engineering. It covers basic methods and principles used by mechanical engineers, e.g., fundamentals of technical communication, the design process and problem solving, measurements and data analysis, engineering professionalism, safety, and intellectual property. The fundamentals of engineering graphical communication using computer-aided design (CAD) and freehand sketching will be a significant component of this course. Written, graphical and oral communications are emphasized. Examples are drawn from Mechanical Engineering. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electrical Engineering for Mechanical Engineers', '123', 'ME', 'Definitions of electric and magnetic fields. Introduction to circuit theory (direct current) DC circuits, amplifiers, operational amplifiers, single- and three-phase (alternating current) AC circuits. Introduction to basic electronic devices. [Note: Labs are alternate weeks. Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Calculus', '201', 'ME', 'A continuation of First Year calculus, focusing on calculus of scalar and vector functions of several variables. Both classical calculus techniques and the computer implementation of numerical methods are discussed. Partial differentiation, total derivatives, chain rule, transformation of variables, Taylor series. Applications include geometrical problems, error estimation, maxima and minima, least squares curve fits. Multiple integration in standard coordinate systems, Jacobians. Vector calculus, divergence, curl, Laplacian, and Stokes, Greens and Divergence theorems. Scalar flux transport, work and energy, conservative force fields. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Statistics for Engineers', '202', 'ME', 'Frequency distributions; measures of central tendency; standard deviation and other measures of dispersion. Probability. Binomial, Poisson, normal distributions. Techniques of sampling and statistical estimation. Tests of hypotheses; significance. The t-test and chi-squared test. Curve fitting by least squares. Statistical process control. Correlation and regression. Experimental design. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ordinary Differential Equations', '203', 'ME', 'Solution of ordinary differential equations. First and higher order differential equations. Nonlinear equations. Linear equations with constant and variable coefficients. Systems of linear equations. Applications involving simple dynamical systems and principles of mass, momentum and heat conservation will emphasize the role of ordinary differential equations in understanding the behaviour of physical systems. Introduction to the Laplace transform method for solving ordinary differential equations. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Dynamics', '212', 'ME', 'An introduction to the kinematics of particle and rigid body motion. Impulse-momentum equations. Work-energy methods and Eulers equations. Simple gyroscopes. Vibrations. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structure and Properties of Materials', '115', 'ME', 'The relevance of materials to engineering practice. The microstructure of materials, crystallinity and crystal imperfections, glasses, and amorphous solids. Elastic and plastic deformation in metals, viscoelasticity of plastics. Strengthening mechanisms in metals, polymers, and ceramics. Fracture of brittle and ductile solids. Electrical and magnetic properties of materials. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanics of Deformable Solids 1', '219', 'ME', 'Concept of equilibrium, force analysis of structures and structural components, equilibrium of deformable bodies, stress and strain concepts, stress-strain relationships, stress analysis of prismatic members in axial, shearing, torsional and flexural deformations, shear force and bending moment diagrams. [Offered: F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanics of Deformable Solids 2', '220', 'ME', 'A general treatment of the behaviour of structural components from the study of stress and strain in solids. Topics include superposition, energy theorems, theories of failure, elastic and inelastic analysis of symmetrical bending, torsion of circular members, columns and stability, and virtual work. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Control of Properties of Materials', '230', 'ME', 'Phase equilibria, non-equilibrium behaviour, heat treatment of metals, diffusion, strengthening processes. Alloying, composite materials, cold and hot working. Failure of engineering materials; creep, fatigue, corrosion and other environmental degradation processes. Prevention of service failures. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Thermodynamics 1', '250', 'ME', 'The engineering science of energy. The scope and limitations of thermodynamics. Macroscopic approach to heat, work, energy and the First Law. Properties and state of simple substances. Control-mass and control-volume energy analysis. The Second Law of Thermodynamics, principle of increase of entropy, limiting cycle efficiencies, criteria for equilibrium. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Microprocessors and Digital Logic', '262', 'ME', 'Number systems, logic gates, Boolean algebra. Karnaugh maps and combinational logic design. Sequential logic and state machines. Programmable Logic Controllers (PLCs) and PLC programming using ladder logic and statement list.  Microcomputer structure and operation, I/O and interfacing. Assembly language programming. Laboratory work includes microcomputer and PLC programming. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electromechanical Devices and Power Processing', '269', 'ME', 'Review of circuit analysis. Basic electromagnetic theory. DC machines, synchronous generators, transformers, and induction motors. Introduction to typical speed and torque control techniques of machines using power electronic based devices.  [Note: Labs: Alternate Weeks. Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Engineering Mathematics', '303', 'ME', 'A continuation of ME 201 and ME 203 in which both classical calculus techniques and the computer implementation of numerical methods are discussed. Partial differential equations of mathematical physics: wave, diffusion, Laplace, Poisson equations. Boundary and initial conditions. Separation of variables. Numerical methods for ordinary and partial differential equations. Applications will emphasize the role of ordinary and partial differential equations in understanding the behaviour of physical systems. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Kinematics and Dynamics of Machines', '321', 'ME', 'Principles of the geometry of motion, uniform and non-uniform motion, linkage, gears, cams. Synthesis and analysis of mechanisms. Consideration of the static and dynamic forces in machines. Vibration analysis, response to shock, motion and force transmissibility, vibration isolation. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanical Design 1', '322', 'ME', 'Adequacy assessment and synthesis of machine elements with a focus on the design process.  Static failure of ductile and brittle materials, fatigue analysis of structures.  Topics include the design of welds, bolted connections, springs and shafts. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Manufacturing Processes', '340', 'ME', 'The principles of manufacturing unit processes including casting, forming, machining and joining. Interactions between design, materials (metals, polymers, ceramics) and processes. Advantages and limitations, relative cost, and production rates of competitive processes. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluid Mechanics 1', '351', 'ME', 'Physical properties of fluids and fundamental concepts in fluid mechanics. Hydrostatics. Conservation laws for mass, momentum and energy. Flow similarity and dimensional analysis as applied to engineering problems in fluid mechanics. Laminar and turbulent flow. Engineering applications such as flow measurement, flow in pipes and fluid forces on moving bodies. [Offered: F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Heat Transfer 1', '353', 'ME', 'Introduction to heat transfer mechanisms. The formulation and solution of steady and transient heat conduction. Radiant heat transfer including exchange laws and view factors. Introductory convective heat transfer. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Thermodynamics 2', '354', 'ME', 'Emphasis on applications of thermodynamics to flow processes. Real fluids, evaluation of state functions of real fluids. Non-reacting mixtures, reacting mixtures, equilibrium considerations. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Control Systems', '360', 'ME', 'Open loop and feedback control. Laws governing mechanical, electrical, fluid and thermal control components. Analogies. Analysis of some engineering control systems using block diagram algebra, transient and steady-state operation. Different modes of control. Review of Laplace Transform methods. Concepts of stability. Principles of analog computer simulation. Brief treatment of linear flow graphs and bondgraphs. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluid Mechanics 2', '362', 'ME', 'Basic equations of two-dimensional flow, potential flow, exact viscous solutions. Introduction to lubrication, boundary layers, turbulence, and compressible flow. Turbomachinery fundamentals and applications. Selected advanced topics. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanical Engineering Design Workshop', '380', 'ME', 'In this course, students study the design process, including needs analysis, problem definition; design criteria and critical parameter identification, generation of alternative solutions; conceptual design, detailed design, optimization; and implementation. Most of the term is devoted to a significant design project in which student groups work independently and competitively, applying the design process to a project goal set by the faculty co-ordinator. The design project typically includes construction of a prototype, and part of the course grade may depend on the performance of the prototype in a competitive test. In exceptional circumstances, the requirement for a prototype may be replaced by a computer simulation, or may be waived. Other mechanical engineering faculty members, particularly those teaching 3B courses, are available to provide advice and supervision to ME 380 students. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanical Design 2', '423', 'ME', 'A continuation of the ME 322 course in analysis and synthesis of machinery, including advanced analysis of machine elements such as clutches, brakes, couplings, journal bearings and gears. Advanced machine design concepts such as reliability, optimization and techniques for stimulating innovative design. A synthesis project involving the machine elements studied is usually included. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Industrial Metallurgy', '435', 'ME', 'This course is intended for those students interested in acquiring a working knowledge of metallurgy. It covers: metals and alloy systems, iron-carbon alloys, heat treatment and the function of alloying elements in steel, corrosion and scale resistant alloys, copper and nickel base alloys, light metals and their alloys; casting, hot and cold working of metals; soldering, brazing and welding; corrosion and oxidation; metal failure analysis. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('HVAC Load Analysis and Design Fundamentals', '452', 'ME', 'Advanced psychrometric analysis applied to HVAC system design. Duct design and fan selection. Indoor air quality and thermal comfort. Heating and cooling load calculations. Heat flow through building elements. Solar radiation and solar gains through windows. Infiltration and exfiltration. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Heat Transfer 2', '456', 'ME', 'Selected topics in heat transfer fundamentals and applications. Topics to be covered include the fundamentals of convection with analytical solutions to simple laminar flow problems and approximate solutions to turbulent flow problems based on analogies between momentum and heat transfer. Also covered is radiant exchange in grey enclosures and in black enclosures containing emitting-absorbing gases. The remaining topics will be chosen from design of heat exchangers; condensation heat transfer; boiling heat transfer; and the treatment of problems in heat conduction. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Energy Conversion', '459', 'ME', 'Review of reserves and consumption trends of Canadas and the worlds energy resources. Design of fossil-fuel central power plants, including boiler efficiency calculations and advanced steam and binary cycles. Review of atomic physics including fission and fusion energy. Design of nuclear fission power plants including design of reactor core for critical conditions, fuel cycles and radiation hazards. Design considerations for solar energy conversion devices including: availability of solar energy, solar-thermal converters, thermal storage and photovoltaics. Principles of fuel cells and some aspects of their design. Other topics as appropriate. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanical Engineering Design Project 1', '481', 'ME', 'The first of two required courses for the mechanical engineering capstone design project. This course is intended to enable students to engage in in-depth engineering design and decision-making using engineering science while encouraging creativity and resourcefulness, and addressing the criteria listed in the faculty of engineering design rubrics. Students will work in small groups on a design project of their own choosing, or as part of a major student team project. The goal is to develop a design proposal, consisting of the needs analysis, design specifications and project plan, followed by the initial and detailed design work. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanical Engineering Design Project 2', '482', 'ME', 'A continuation of ME 481. The final design of the major mechanical engineering project proposed in ME 481 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design and proof of feasibility of the solution proposed in ME 481. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Dynamics and Vibrations', '524', 'ME', 'This course is a continuation of ME 212 and ME 321. It includes study of planar and three-dimensional motion of rigid bodies and systems, including gyroscopic effects. Vector (Newtons Method) and analytical (Lagranges Equation) methods are used to derive equations of motion. Linear vibrational analyses are performed to examine natural frequencies, stability, and mode shapes. Computer simulation of non-linear systems is discussed. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Metallurgy Applied to Manufacturing', '531', 'ME', 'This course will allow the student to develop a more in depth knowledge of physical metallurgy and its application in understanding and solving relevant manufacturing problems. It will begin with a treatment of solid-state diffusion, mass transport and the principles of solidification including constitutional supercooling. This knowledge will then be applied to understand the microstructural development (and resultant properties) which occur in materials during manufacturing processes including casting, solid-state heat treatments, laser processing, and various joining operations. The course will include case studies aimed at providing the students with an opportunity to apply their knowledge in a practical way. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Non-metallic and Composite Materials', '533', 'ME', 'This course is intended to provide an advanced treatment of the structure, properties, and processing of non-metallic and composite materials based on polymers, metals, and ceramics. The structure and properties of polymers and ceramics in bulk form and as matrices and reinforcements in composites will be covered. Processing methods for non-metallics and composites (example extrusion, injection molding, etc.) will be considered. The geometrical arrangement of fibres within laminae and their influences on elastic and strength properties of composites will be described based on suitable micromechanical models. The role of the matrix and fibre/matrix interface in determining composite properties will be described. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Welding Metallurgy', '535', 'ME', 'Metallurgy of welding of steels (carbon, microalloy, low alloy and stainless steels), cast irons, aluminum-based, copper-based, nickel-based, cobalt-based, titanium-based and other alloys, (including dissimilar combinations) to explain the effects of welding processes and conditions (including post-weld heat treating) on microstructure and properties; causes and prevention of defects and deficiencies which can occur in different alloys, including porosity, cracking, embrittlement (hydrogen, temper, strain aging, ductile-brittle transition temperatures), overaging; metallurgy of soldered and brazed joints. Laboratory experiments will demonstrate microstructural effects and defects in a range of alloys for different welding processes and conditions. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Welding and Joining Processes', '436', 'ME', 'Introduction to modern welding and joining processes for metals, polymers and ceramics. Fundamentals of the joining process and the influence of the process parameters on weld dimensions, strength and quality. Fusion welding processes such as shielded metal arc, gas tungsten arc, gas metal arc, submerged arc welding and others including electron beam and laser beam welding.  Resistance welding processes, solid-state welding processes, soldering and brazing. Laboratory exercises will provide hands-on experience with a number of industrially significant welding processes. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Robot Manipulators: Kinematics, Dynamics, Control', '547', 'ME', 'Homogeneous transformations, D-H convention, forward and inverse kinematics. Differential transformations and Jacobians. Robot dynamics. Programming, trajectory generation and joint control. End-of-arm sensing and outer loop control. Industrial applications. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Numerical Control of Machine Tools 1', '548', 'ME', 'Operation fundamentals of NC machine tools. NC part programming: manual and CAD/CAM methods. Mechanics of metal cutting: examples of turning, milling, and drilling. Tool wear and breakage. Optimum cutting conditions. Dimensional and form errors due to static deformations. Dynamics of machining. Laboratory work provides hands-on experience in tool path generation, machining, and measurements of cutting forces and vibration. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computer-Aided Design', '555', 'ME', 'Need for geometric modelling, historic developments; wire frame models; hidden line removed models; polyhedral models; surface models and solid models. Constructive solid geometry; boundary representation and decomposition modelling. Hybrid models. Data structures and their role in modelling. Curves and surfaces in modelling (Bezier, B-splines and NURBS). Geometric models and the role of engineers. Parametric and feature-based design. The course has a heavy lab component which provides exposure to solid modelling on SDRC IDEAS and PC-based CAD packages. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Combustion 1', '557', 'ME', 'Combustion thermodynamics, introduction to chemical kinetics of combustion, combustion properties of fuels, flammability of combustible mixtures. Flame propagation mechanisms, pre-mixed and diffusional; stability of flames; introduction to combustion aerodynamics, jet flames; atomization; droplet and spray combustion. Elementary ignition concepts and theory. Basic detonation theory. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Finite Element Methods', '559', 'ME', 'A course presenting the fundamental ideas involved in conventional finite element analysis in mechanical engineering. Domain discretization, interpolation and shape functions, element derivation and types, element stiffness or property equations, assembly procedure, boundary conditions, solution methods for the algebraic equation system, applications in heat transfer, fluid flow, and stress analysis. Students will, throughout the course, write and test their own finite element code through individual subroutine construction as the course progresses. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluid Power Control Systems', '561', 'ME', 'Properties of hydraulic fluids. Design and function of conventional hydraulic and pneumatic circuits. Characteristics of flow and pressure control valves. Speed control in fluid power circuits. Performance of pumps and fluid motors. Hydrostatic and hydrokinetic transmission systems. Principles of sealing, filtration and heat control in hydraulic circuits. Industrial applications of fluid power systems. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Turbomachines', '563', 'ME', 'Classification of turbomachines, performance parameters and laws of modelling. Basic equation of flow in turbomachines, compressible flow. Energy transfer in radial and axial turbomachines, performance characteristics, losses and efficiencies. Blade and cascade design, 3 dimensional effects. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Aerodynamics', '564', 'ME', 'An introductory course in aerodynamics for engineers. Kinematics and dynamics of inviscid flow; airfoil dynamics including thin airfoil theory, finite wings, panel methods and airfoil parameters. Boundary layer theory and boundary layer control as applied in aerodynamics. Introduction to high speed aerodynamics. Introduction to dynamics of flight including stability and control. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Fluid Dynamics for Engineering Design', '566', 'ME', 'A course to develop the understanding required to simulate complex fluid flows, such as those found in turbo-machines, duct systems, and other engineering hardware. Course topics include the physics of complex viscous fluid flows, first- and second-order finite control volume discretization methods, iterative algorithms for the solution of sparse matrix equation sets, including multi-grid acceleration, boundary condition modelling, two-equation and Reynolds stress turbulence models, and grid generation techniques. Computational fluid dynamics software is used throughout the course to simulate and analyze complex fluid flows relevant to engineering applications. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Air Pollution', '571', 'ME', 'Nature and sources of air pollution, chemical and biological aspects, effects on health and environment. Physical aspects of the atmosphere, thermodynamics, vertical variation of wind and temperature, stability, convection, atmospheric turbulence, diffusion equations, plumes, thermals, jets in stratified flow, radioactive plumes, micrometeorological instrumentation, air pollution control techniques and equipment monitoring instrumentation. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Mechanical Engineering', '595', 'ME', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Mechanical Engineering', '596', 'ME', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Mechanical Engineering', '598', 'ME', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Mechanical Engineering', '599', 'ME', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organizational Behaviour', '211', 'MSCI', 'Introduction to the concepts of learning, person perception, attitudes, and motivation in an organization. Consideration of communication, roles, norms, and decision making within a group. Discussion of power, control, leadership, and management in light of the above concepts. [Offered: F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Probability and Statistics 1', '251', 'MSCI', 'A first of a two-course sequence that introduces fundamental concepts in probability and statistics. It covers probability concepts, random variables, graphical display of distributions and data, discrete and continuous probability distributions, sampling, estimation, confidence intervals, experimental design, hypothesis testing, and simple linear regression and correlation. Students learn how to graphically explore data, conduct, and analyze a two-treatment experiment, and model data with linear regression, and interpret its fit. Students learn to use statistical computing software (e.g., R) to perform data analyses. Emphasis is placed on gaining experience with data collected from student-conducted experiments. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Economics:  Financial Management for Engineers', '261', 'MSCI', 'Introductory finance: time value of money, cash flow analysis. Investment evaluation methods: present worth, annual worth, and internal rate of return. Depreciation models and asset replacement analysis. The impact of inflation, taxation, uncertainty, and risk on investment decisions. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organizational Design and Technology', '311', 'MSCI', 'The focus of this course is on the procedures and variables involved in the design and redesign of organizations. Issues such as departmentation, differentiation, integration, internal politics, innovation, authority, and control are discussed in the context of the underlying technology of the organization. Emphasis will be placed on how one designs both the technical and the organizational systems to ensure their compatibility, noting the effects that one has on the other. [Offered: F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Optimization', '331', 'MSCI', 'This first course in optimization uses a quantitative approach to problem solving involving, mathematical modelling and formulations, solution methods, and output analysis. Students are introduced to a variety of practical problem formulations in management and engineering, a number of solution methods, including, but not limited to, linear optimization, network models, project management, and decision analysis. Students are also involved in a group project, where they go through conceptual and operational model design, analytical solution, output analysis, and recommendation. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Stochastic Models and Methods', '431', 'MSCI', 'Introduction to operations research models and methods for problems with random, stochastic, and probabilistic components. Topics include birth and death processes, branching processes, waiting line models, and Markov decision processes. Applications include the design, modelling, and analysis of service and manufacturing systems, with emphasis on important functions such as queueing, inventory, reliability, equipment replacement, and maintenance. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production and Service Operations Management', '432', 'MSCI', 'Introduction to management, planning, and control decisions in manufacturing and service settings using quantitative approaches. Topic areas include production, inventory, distribution, quality control, facilities layout, and process design. Students are exposed to a number of examples and case studies, and work on a project that involves analysis and discussion of improved designs. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Impact of Information Systems on Organizations and Society', '442', 'MSCI', 'This course is designed to familiarize the student with issues related to the impact of computer-based technologies on individual jobs, organizations, and broader societal level. Particular emphasis will be placed on critical examination of various issues including privacy, security, ethical concern and professional responsibilities. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Decision Making Under Uncertainty', '452', 'MSCI', 'This course deals with normative, descriptive, and prescriptive theories and models of decision making under uncertainty. The course focuses on concepts such as risk measures, Bayes theorem, and basic and multi-attribute utility theories. The course uses these concepts to build analytical decision-making models considering the randomness and/or risk-attitude of the decision-makers. Various forms of decision analysis techniques will be covered and typically include, expected utility maximization, the value of information analysis, one/two-way sensitivity analysis, and sequential Bayesian inference. Practical uses and limitations of the models and techniques will be illustrated. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Algebra for Arts and Social Science', '103', 'MATH', 'An introduction to applications of algebra to business, the behavioural sciences, and the social sciences. Topics will be chosen from linear equations, systems of linear equations, linear inequalities, functions, set theory, permutations and combinations, binomial theorem, probability theory. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Calculus for Arts and Social Science', '104', 'MATH', 'An introduction to applications of calculus in business, the behavioural sciences, and the social sciences. The models studied will involve polynomial, rational, exponential, and logarithmic functions. The major concepts introduced to solve problems are rate of change, optimization, growth and decay, and integration. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra for Engineering', '115', 'MATH', 'Linear equations, matrices and determinants. Introduction to vector spaces.  Eigenvalues and diagonalization. Applications. Complex numbers. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 1 for Engineering', '116', 'MATH', 'Functions: review of polynomials, exponential, logarithmic, trigonometric. Operations on functions, curve sketching. Trigonometric identities, inverse functions. Derivatives, rules of differentiation. Mean Value Theorem, Newtons Method. Indeterminate forms and LHopitals rule, applications. Integrals, approximations, Riemann definite integral, Fundamental Theorems. Applications of the integral. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 1 for Engineering', '117', 'MATH', 'Functions of engineering importance; review of polynomial, exponential, and logarithmic functions; trigonometric functions and identities. Inverse functions (logarithmic and trigonometric). Limits and continuity. Derivatives, rules of differentiation; derivatives of elementary functions. Applications of the derivative, max-min problems, Mean Value Theorem. Antiderivatives, the Riemann definite integral, Fundamental Theorems. Methods of integration, approximation, applications, improper integrals. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 2 for Engineering', '118', 'MATH', 'Methods of integration: by parts, trigonometric substitutions, partial fractions; engineering applications, approximation of integrals, improper integrals. Linear and separable first order differential equations, applications. Parametric curves and polar coordinates, arc length and area. Infinite sequences and series, convergence tests, power series and applications. Taylor polynomials and series, Taylors Remainder Theorem, applications. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 2 for Engineering', '119', 'MATH', 'Elementary approximation methods: interpolation; Taylor polynomials and remainder; Newtons method, Landau order symbol, applications. Infinite series: Taylor series and Taylors Remainder Theorem, geometric series, convergence test, power series, applications. Functions of several variables: partial derivatives, linear approximation and differential, gradient and directional derivative, optimization and Lagrange multipliers. Vector-valued functions:  parametric representation of curves, tangent and normal vectors, line integrals and applications. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Linear Algebra 1', '106', 'MATH', 'Systems of linear equations.  Matrix algebra.  Determinants.  Introduction to vector spaces.  Applications. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Linear Algebra 2', '225', 'MATH', 'Vector spaces. Linear transformations and matrices. Inner products. Eigenvalues and eigenvectors. Diagonalization. Applications. [Offered: F,S; online: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 1 for the Sciences', '127', 'MATH', 'Functions of a real variable: powers, rational functions, trigonometric, exponential and logarithmic functions, their properties and inverses. Intuitive discussion of limits and continuity. Definition and interpretation of the derivative, derivatives of elementary functions, derivative rules and applications. Riemann sums and other approximations to the definite integral. Fundamental theorems and antiderivatives; change of variable. Applications to area, rates, average value. [Offered: F,W,S; online: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 2 for the Sciences', '128', 'MATH', 'Transforming and evaluating integrals; application to volumes and arc length; improper integrals. Separable and linear first order differential equations and applications. Introduction to sequences. Convergence of series; Taylor polynomials, Taylors Remainder theorem, Taylor series and applications. Parametric/vector representation of curves; particle motion and arc length. Polar coordinates in the plane. [Offered: F,W,S; online: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algebra for Honours Mathematics', '135', 'MATH', 'An introduction to the language of mathematics and proof techniques  through a study of the basic algebraic systems of mathematics: the integers, the integers modulo n, the rational numbers, the real numbers, the complex numbers and polynomials. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra 1 for Honours Mathematics', '136', 'MATH', 'Systems of linear equations, matrix algebra, elementary matrices, computational issues. Real n-space, vector spaces and subspaces, basis and dimension, rank of a matrix, linear transformations, and matrix representations. Determinants, eigenvalues and diagonalization, applications. [Offered: F,W,S; online: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 1 for Honours Mathematics', '137', 'MATH', 'Absolute values and inequalities. Sequences and their limits. Introduction to series. Limits of functions and continuity. The Intermediate Value theorem and approximate solutions to equations. Derivatives, linear approximation, and Newtons method. The Mean Value theorem and error bounds. Applications of the Mean Value theorem, Taylor polynomials and Taylors theorem, Big-O notation. Suitable topics are illustrated using computer software. [Offered: F,W,S; online: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 2 for Honours Mathematics', '138', 'MATH', 'Introduction to the Riemann integral and approximations. Antiderivatives and the fundamental theorem of calculus. Change of variables, methods of integration. Applications of the integral. Improper integrals. Linear and separable differential equations and applications. Tests for convergence for series. Binomial series, functions defined as power series and Taylor series. Vector (parametric) curves in R2. Suitable topics are illustrated using computer software. [Offered: F,W,S; online: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algebra (Advanced Level)', '145', 'MATH', 'MATH 145 is an advanced-level version of MATH 135. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra 1 (Advanced Level)', '146', 'MATH', 'MATH 146 is an advanced-level version of MATH 136. [Note: Students who receive a minimum grade of 90% in MATH 135 may contact the instructor of MATH 146 to seek admission without the formal prerequisites. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 1 (Advanced Level)', '147', 'MATH', 'MATH 147 is an advanced-level version of MATH 137. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 2 (Advanced Level)', '148', 'MATH', 'MATH 148 is an advanced-level version of MATH 138. [Note: Students who receive a minimum grade of 90% in MATH 137 may contact the instructor of MATH 148 to seek admission without the formal prerequisites. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Calculus 1 for Electrical and Computer Engineers', '211', 'MATH', 'Fourier series. Ordinary differential equations. Laplace transform. Applications to linear electrical systems. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Calculus 1 for Electrical and Computer Engineers', '205', 'ECE', 'Fourier series. Ordinary differential equations. Laplace transform. Applications to linear electrical systems. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Adv Calculus 2 for Electrical Engineers', '212', 'MATH', 'Triple integrals, cylindrical and spherical polar coordinates. Divergence and curl, applications. Surface integrals, Greens, Gauss and Stokes theorems, applications. Complex functions, analytic functions, contour integrals, Cauchys integral formula, Laurent series, residues. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Adv Calculus 2 for Electrical Engineers', '206', 'ECE', 'Triple integrals, cylindrical and spherical polar coordinates. Divergence and curl, applications. Surface integrals, Greens, Gauss and Stokes theorems, applications. Complex functions, analytic functions, contour integrals, Cauchys integral formula, Laurent series, residues. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 3 for Chemical Engineering', '217', 'MATH', 'Curves and surfaces in R3. Multivariable functions, partial derivatives, the chain rule, gradients. Optimization, Lagrange Multipliers. Double and triple integrals, change of variable. Vector fields, divergence and curl. Vector integral calculus: Greens theorem, the Divergence theorem and Stokes  theorem. Applications in engineering are emphasized. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Differential Equations for Engineers', '218', 'MATH', 'First order equations, second order linear equations with constant coefficients, series solutions, the Laplace transform method, systems of linear differential equations. Applications in engineering are emphasized. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 3 for Honours Physics', '227', 'MATH', 'Directional derivative and the chain rule for multivariable functions. Optimization, Lagrange multipliers. Double and triple integrals on simple domains; transformations and Jacobians; change of variable in multiple integrals. Vector fields, divergence and curl. Vector integral calculus: Line and surface integrals, Greens Theorem, Stokes Theorem, Gauss Theorem, conservative vector fields. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Differential Equations for Physics and Chemistry', '228', 'MATH', 'First-order equations, second-order linear equations with constant coefficients, series solutions and special functions, the Laplace transform method. Applications in physics and chemistry are emphasized. [Offered: F,W; online: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra 2 for Honours Mathematics', '235', 'MATH', 'Orthogonal and unitary matrices and transformations. Orthogonal projections, Gram-Schmidt procedure, best approximations, least-squares. Inner products, angles and orthogonality, orthogonal diagonalization, singular value decomposition, applications. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 3 for Honours Mathematics', '237', 'MATH', 'Calculus of functions of several variables. Limits, continuity, differentiability, the chain rule. The gradient vector and the directional derivative. Taylors formula. Optimization problems. Mappings and the Jacobian. Multiple integrals in various co-ordinate systems. [Note: MATH 247 may be substituted for MATH 237 whenever the latter is a plan requirement. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Combinatorics', '239', 'MATH', 'Introduction to graph theory: colourings, matchings, connectivity, planarity. Introduction to combinatorial analysis: generating series, recurrence relations, binary strings, plane trees. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra 2 (Advanced Level)', '245', 'MATH', 'MATH 245 is an advanced-level version of MATH 235. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calculus 3 (Advanced Level)', '247', 'MATH', 'Topology of real n-dimensional space: completeness, closed and open sets, connectivity, compact sets, continuity, uniform continuity. Differential calculus on multivariable functions: partial differentiability, differentiability, chain rule, Taylor polynomials, extreme value problems. Riemann integration: Jordan content, integrability criteria, Fubinis theorem, change of variables. Local properties of continuously differentiable functions: open mapping theorem, inverse function theorem, implicit function theorem. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Combinatorics (Advanced Level)', '249', 'MATH', 'MATH 249 is an advanced-level version of MATH 239. [Offered: F,W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Commercial and Business Law for Mathematics Students', '231', 'COMM', 'The judicial process, contract law, agency, bankruptcy, negotiable instruments, law of banking, insurance law, partnership law, company law, torts, real estate law.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Mathematics Education', '206', 'MTHEL', 'Current trends in education, professional practices and administration, the role of the department head, lesson planning, techniques of teaching, evaluation of students, special students, extracurricular activities, the relationship between elementary and secondary school mathematics, audio-visual materials. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Entrepreneurship, Technology and the Emerging Information Economy', '400', 'COMM', 'A study of the spirit of entrepreneurship in the technology industry, opportunities emerging in the new information economy, and the implementation issues associated with starting an entrepreneurial venture in todays rapidly changing environment. Many of the concepts covered will also be applicable to careers in the technology industry or in information-based companies, where "intrapreneurship" is an emerging theme. Approximately one-half of the class time will involve guest lectures by entrepreneurs actively involved in the business community. [Note: COMM 400 would serve as an excellent sequel to MGMT 220 which focuses on identifying markets for viable entrepreneurial ideas, but MGMT 220 is not a prerequisite.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Understanding Music', '100', 'MUSIC', 'The styles, forms, techniques, and terminology of Western music through lectures and listening, as exemplified by great works from all eras of music history. [Note: The ability to read music notation is not required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Music Theory', '111', 'MUSIC', 'An introduction to the primary skills of music practice emphasizing the reading and writing of musical notation. Students will learn elementary keyboard, listening, and sight-singing skills. [Note: For students with minimal musical background. Does not fulfil Music major or minor requirements.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Ensemble', '116', 'MUSIC', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required. [Note: For musical reasons, admission to any particular ensemble is at the discretion of the director. Audition required for some ensembles. Contact music department prior to first day of class. Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Ensemble', '117', 'MUSIC', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required. [Note: For musical reasons, admission to any particular ensemble is at the discretion of the director. Audition required for some ensembles. Contact music department prior to first day of class. Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Popular Music and Culture', '140', 'MUSIC', 'An examination of the styles, forms, and development of 20th century popular music. The social, commercial, and technological aspects of popular music are considered.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Ensemble', '216', 'MUSIC', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required. [Note: For musical reasons, admission to any particular ensemble is at the discretion of the director. Audition required for some ensembles. Contact music department prior to first day of class. Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Ensemble', '217', 'MUSIC', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required. [Note: For musical reasons, admission to any particular ensemble is at the discretion of the director. Audition required for some ensembles. Contact music department prior to first day of class. Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Conducting 1', '222', 'MUSIC', 'A study of conducting techniques appropriate for song leading, choral rehearsal, and public performance. The course will include score analysis and rehearsal procedures for music from a wide variety of historical styles.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Studio', '226', 'MUSIC', 'Individual instruction in voice, piano, organ, classical guitar, and orchestral instruments. [Note: Royal Conservatory Grade 8 Level. Audition required. Contact music department prior to first day of class. Studio Fee.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Studio', '227', 'MUSIC', 'Individual instruction in voice, piano, organ, classical guitar, and orchestral instruments. [Note: Studio Fee]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Cognition', '231', 'MUSIC', 'The study of music from a behavioural science perspective. Topics include auditory perception, creativity and aesthetic experience, emotive human responses, and the social psychology of music activities. Recent research in the field of music cognition will be explored in detail.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Jazz', '240', 'MUSIC', 'A survey of the development of jazz schools and individual styles as well as a study of melodic, harmonic, and rhythmic improvisation. Styles will be demonstrated through recordings and live performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cathedral and Court: Music to 1600', '253', 'MUSIC', 'A study of major developments in western music during the Middle Ages and Renaissance, including Gregorian chant, mass, secular songs, instrumental music, and other musical genres that flourished under courtly and church patronage from the early Christian church to 1600.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Monteverdi to Mozart: Music from 1600-1800', '254', 'MUSIC', 'A study of Baroque and Classical music of the 17th and 18th centuries in cultural, political, and artistic contexts. Topics include the development of opera, choral music, symphony, sonata, and the music of Monteverdi, Bach, Handel, Mozart, and others.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Romantic Century: Beethoven and Beyond', '255', 'MUSIC', 'A study of music of the 19th century in cultural, political, and artistic contexts. Topics include the development of principal forms in the music of Beethoven, Schubert, Schumann, Liszt, Chopin, Wagner, Verdi, Brahms, Bruckner, and others.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Since 1900', '256', 'MUSIC', 'A study of music of the 20th and early 21st centuries in cultural, political, and artistic contexts. Topics include modern and contemporary composers challenges to traditional musical ideals in the music of Debussy, Stravinsky, Schoenberg, Cage, and others.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Symphony', '260', 'MUSIC', 'A survey of the great symphonies from Haydn to Stravinsky, through lectures and listening. A portion of the course will be devoted to works being performed by the Kitchener-Waterloo Symphony Orchestra during the term. [Note: The ability to read music notation is not required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Theory 1', '270', 'MUSIC', 'The study of basic melodic, harmonic, and voice leading concepts including an introduction to figured bass and functional harmony. Ear-training, sight-singing, and keyboard lab sessions will be integrated with written and analytical work. [Note: A basic knowledge of scales, triads, and music notation is required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Theory 2', '271', 'MUSIC', 'The study of harmony, counterpoint, and form of 18th and early 19th century music. Sight-singing, ear-training, and keyboard lab sessions will be integrated with written and analytical work.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music and Technology', '275', 'MUSIC', 'A hands-on introduction to music and technology, including recording, audio editing, digital signal processing, and Musical Instrumental Digital Interface (MIDI). Course work will foster a practical understanding of digital music production software and techniques, and involve electroacoustic or acoustic composition. [Note: Studio Fee]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Ensemble', '316', 'MUSIC', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required. [Note: For musical reasons, admission to any particular ensemble is at the discretion of the director. Audition required for some ensembles. Contact music department prior to first day of class. Course will be graded on a CR/NCR basis.]');
