INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Conducting 2', '322', 'MUSIC', 'A continuation of the study of conducting techniques begun in MUSIC 222. The course focuses on the development of analytical and musical skills including score study, aural skills, and gestural clarity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Studio', '326', 'MUSIC', 'Individual instruction in voice, piano, organ, classical guitar, and orchestral instruments. [Note: Studio Fee]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Studio', '327', 'MUSIC', 'Individual instruction in voice, piano, organ, classical guitar, and orchestral instruments. [Note: Studio Fee]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Aesthetics of Music', '332', 'MUSIC', 'This course employs various methods to explore questions of musical meaning.  Through study and discussion of selected sources from antiquity to the present, such themes as imitation, abstraction, beauty, and other debated issues will be examined in historical context and in relation to the other arts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Women and Music', '334', 'MUSIC', 'An examination of womens roles in music from a cross-cultural perspective. Topics may include womens music as a genre, historical accounts of womens music-making, and the effects of gender ideology on womens musical activities, behaviour, and performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Women and Music', '334', 'GSJ', 'An examination of womens roles in music from a cross-cultural perspective. Topics may include womens music as a genre, historical accounts of womens music-making, and the effects of gender ideology on womens musical activities, behaviour, and performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music and Culture Travel Course', '355', 'MUSIC', 'A spring term seminar approximately three weeks long, taught on location including daily lectures, attendance at music performances, and tours to places of cultural importance. Past and possible future locations include Vienna, London, Leipzig, South Africa, Morocco, Bali, and Estonia. [Note: Additional costs will apply. This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Art Song', '361', 'MUSIC', 'A study of the music written for solo voice from the 17th century to the present.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Piano Literature', '362', 'MUSIC', 'A study of music for solo piano from its 17th and 18th century antecedents to the present. Using historical and analytical approaches, individual works will be considered within a tradition of particular forms, genres, and styles.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Churchs Song', '363', 'MUSIC', 'This course examines the origins of the music sung by the Christian church and its development up to the present. The course considers the Churchs Song as theological, poetic, musical, cultural, and spiritual expression, and the use of songs in a variety of worship settings. Students will practice methods of song analysis, assess musical meaning, value and effects in various cultural contexts, and reflect on written and oral transmission of music.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Churchs Song', '357', 'RS', 'This course examines the origins of the music sung by the Christian church and its development up to the present. The course considers the Churchs Song as theological, poetic, musical, cultural, and spiritual expression, and the use of songs in a variety of worship settings. Students will practice methods of song analysis, assess musical meaning, value and effects in various cultural contexts, and reflect on written and oral transmission of music.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
