INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Worship and its Music', '364', 'MUSIC', 'The nature of worship and the role of music within worship in historical, theological, and cultural perspectives. Field trips to services of various traditions. [Note: This course fulfils an Area 2 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
