INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religion and Spirituality in the Social and Legal World', '462', 'RS', 'This course examines key substantive, theoretical, and methodological issues in the field of sociology of religion. Students will critically analyze high-profile media and legal cases surrounding the role of religion, spirituality, and secularity in Canadian society. Students will also further their knowledge by completing their own projects in this thematic area, developing both their research and written skills. [Note: This course fulfils an Area 3 requirement for Religious Studies majors.]');
