INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Metropolitan Form and Structure in Canada', '621', 'PLAN', 'A seminar on selected topics of particular relevance to understanding and planning for contemporary processes of metropolitan change in Canada.  Major topics represent an interrelated set of exogenous forces, (globalization, economic restructuring, technological innovation and immigration and demographic shift), and endogenous structural responses, (central city decline, revitalization, dispersed styles of suburban development and new urbanist and ecologically sustainable styles of planned intervention).');
