VALUES ('Seminar on Tourism Research', '602', 'TOUR', 'Participants will prepare research proposals and present their proposals for discussion in a seminar situation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sustainable Tourism', '603', 'TOUR', 'This course examines sustainability issues relevant to tourism. Conceptual and practical examples related to economic, environmental, and social/cultural sustainability of tourism are critically appraised, combined with reviews of policy and institutional effects on sustainability practices. The course also focuses on best practices in sustainable tourism within government, non-government and private sector.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sustainable Tourism', '638', 'GEOG', 'This course examines sustainability issues relevant to tourism. Conceptual and practical examples related to economic, environmental, and social/cultural sustainability of tourism are critically appraised, combined with reviews of policy and institutional effects on sustainability practices. The course also focuses on best practices in sustainable tourism within government, non-government and private sector.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Planning for Tourism', '604', 'TOUR', 'The course will examine the social categorizations of tourism through an exploration of the interaction between the tourist and the host communities by investigating the socio-cultural impacts of tourism; host and guest attitudes, behaviours and responses to tourism; gender in tourism; indigenous tourism; tourism and morality; tourist promotional images; and authenticity and cultural commodification.  The course will have an international focus through the presentation of case studies from both developed and developing countries.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Practicum in Tourism', '609', 'TOUR', 'This practicum course provides students with the opportunity to apply concepts and theories from other courses and their own reading to a better understanding of the functioning of some aspect of the tourism sector.  The practicum will be administered under the supervision of a faculty member and will also involve the production of an appropriate academic report.');
