INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Control Applications', '484', 'ECE', 'Dynamic system modelling: linear, nonlinear, state-space, sample data systems, computer simulation, system identification. Discrete system stability and dynamic performance. Nonlinear system analysis, limit cycles. Digital control system design: emulation methods, z-domain, frequency domain, pole placement.  Implementation of digital controllers. Laboratory projects in computer control of mechatronic and other systems. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Multivariable Control Systems', '488', 'ECE', 'Review of feedback control design fundamentals; SISO controller parameterizations; the fundamental effect of MIMO interaction; introduction to state-space models in continuous and discrete time; SISO techniques for MIMO design; optimal control; model-predictive control design; state estimation; decoupling, MIMO PID control design; applications in areas such as aerospace systems. [Offered: W]');
