INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Comparative Politics of State and Nation', '250', 'PSCI', 'How do states and regimes interact with cultural and national identity? In this core comparative politics course, students will assess various approaches to the study of politics within states, emphasizing political developments, institutions, and processes in countries both across the Global South and advanced industrial worlds.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Models of Computation', '365', 'CS', 'Finite automata and regular expressions. Pushdown automata and context-free grammars. Turing machines and undecidability. Time and space complexity. Diagonalization and hierarchies. CS 365 covers the material in CS 360 at an accelerated pace plus additional topics in computational complexity. [Note: CS 365 may be substituted for CS 360 in any degree plan or for prerequisite purposes; enrolment is restricted; see Note 1 above.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Research Practicum', '497', 'SOC', 'Denoting a preprofessionalization experience, this course provides selected students with an opportunity to work [to a maximum of 8 hours per week over one term] as an apprentice with a specific instructor on a research project in which the instructor is currently engaged. [Note: This is a nonpaid position and may not be combined with sponsored research positions. Earlier or outside research involvements will not meet the criteria for this course. This course cannot be substituted for other 400-level course requirements (as in seminars, theory courses). This course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Memory, Human Rights, and Reconciliation', '400', 'SPAN', 'We remember the past to understand the present and imagine the future. With a focus on Latin America and Spain this course examines how the diverse forms of cultural production help develop our shared understanding of past violations of human rights to demand justice, build resilience, and develop tolerance. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Applied Mathematics', '900', 'AMATH', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computational Mathematics', '242', 'AMATH', 'A rigorous introduction to the field of computational mathematics. The focus is on the interplay between continuous  models and  their solution via discrete processes. Topics include pitfalls in computation, solution of linear systems, interpolation, discrete Fourier transforms, and numerical integration. Applications are used as motivation. [Note: This course may be substituted for CS 370 in any degree plan or for prerequisite purposes; lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computational Mathematics', '371', 'CS', 'A rigorous introduction to the field of computational mathematics. The focus is on the interplay between continuous  models and  their solution via discrete processes. Topics include pitfalls in computation, solution of linear systems, interpolation, discrete Fourier transforms, and numerical integration. Applications are used as motivation. [Note: This course may be substituted for CS 370 in any degree plan or for prerequisite purposes; lab is not scheduled and students are expected to find time in open hours to complete their work.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Semidefinite Optimization', '471', 'CO', 'Optimization over convex sets described as the intersection of the set of symmetric, positive semidefinite matrices with affine spaces. Formulations of problems from combinatorial optimization, graph theory, number theory, probability and statistics, engineering design, and control theory. Theoretical and practical consequences of these formulations. Duality theory and algorithms. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Appreciation and Expression', '150', 'FINE', 'The course delivers an introduction to a variety of basic techniques and concepts relevant to contemporary 2D, 3D, and digital art practices, through a series of structured studio exercises and assignments. [Note: Studio Course. This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Irish Literature', '260', 'ENGL', 'A study of modern and contemporary Irish literature in English. This course will introduce students to a range of Irish writing in its often turbulent historical and cultural context. The international dimensions of Irish writers and their work will be explored.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Semidefinite Optimization', '671', 'CO', 'Optimization over convex sets described as the intersections of the set of symmetric, positive semidefinite matrices with affine spaces.  Formulations of problems from combinatorial optimization, graph theory, number theory, probability and statistics, engineering design and control theory.  Theoretical and practical consequences of these formulations.  Duality theory and algorithms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Indigenous Peoples and Canadian Public Policy', '311R', 'SDS', 'Framed within the Truth and Reconciliation process underway in Canada, this course examines the historical and ongoing colonial processes and impacts of government policies targeting First Nations, Inuit, and Métis peoples. Particular analysis will be given to government policy as a cause of (and response to) social, political, and economic problems experienced by Indigenous peoples in Canada. [Note: SWREN 311R is available only to students who have been given conditional admission to the BSW program; such students must have already completed their first undergraduate degree.]');
