INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economic Impact of Technological Change and Entrepreneurship', '422', 'MSCI', 'This course is designed to analyze the impact of technological change and entrepreneurship at a firm and societal level, primarily in terms of the economic antecedents and consequences of new technology. The scope of the course ranges from the study of the determination of productivity and its effect on economic growth to the determination of innovative activity and performance. [Offered: F] Prereq: (One of AE 392, BME 364, CIVE 392, ECON 101, ENVE 392, GEOE 392, MSCI 261, SYDE 262) and (One of AE 224, BME 213, CHE 220, CIVE 224, ECE 203, 306, ECON 221, ENVE 224, ENVS 278, MSCI 251 or 252, ME 202, MTE 201, NE 215, PSCI 314, PSYCH 292, REC 371, SDS 250R, SOC 280, STAT 202, 206, 211, 221, 231, 241, SYDE 212).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Symbolic Computation', '780', 'CS', 'A deep investigation into fundamental problems of symbolic computation. These may include algorithms for linear, non-linear, and differential systems of symbolic equations, symbolic integration, factoring polynomials, and symbolic-numeric algorithms. Covers the basic data types and structures for algebraic objects and operations. Issues in the design of computer algebra systems.');
