INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sports Injury Management Laboratory', '140L', 'KIN', 'This lab provides exposure and practical training in the management of injuries in an athletic population. Techniques include musculoskeletal assessment, emergency care and management, taping and splinting supports, heating, icing, stretching, and manual therapy skills. [Note: Current first aid and CPR certification required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Leisure and Well-being', '405', 'REC', 'This course examines relationships between various aspects of leisure and well-being from a holistic perspective. Both positive and negative implications of leisure and recreational pursuits for well-being are considered for individuals, communities, and environments.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Recreation and Leisure Studies 4', '472', 'REC', 'One or more term courses will be offered from time to time as announced by the Recreation and Leisure Studies Department. Subjects will be dependent upon special research and/or instructional interests of faculty.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cell Biology Laboratory', '130L', 'BIOL', 'Experiments to study the principles of cell biology that are elaborated in BIOL 130.  [Offered:  F,S]');
