VALUES ('Microbiology Laboratory', '240L', 'BIOL', 'Experiments to study the principles of microbiology that are elaborated in BIOL 240.  [Formerly BIOL 140L. Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Laboratory Methods in Microbiology', '348L', 'BIOL', 'Selected experiments to provide students with a range of laboratory skills in microbiology. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Current Topics in Microbiology', '475', 'BIOL', 'Students will explore and analyze a variety of often controversial topics in microbiology as reflected in popular culture and the current scientific literature. This course involves significant in-class group work. An essay and presentations are required. [Offered W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
