INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Canadian Legal History', '277', 'HIST', 'This course examines the Canadian legal system from colonial times to the present with particular emphasis on such themes as law and the economy, courts and judiciary, the legal profession, family and criminal law, women and the law, and civil liberties.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
