INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Literary Studies', '101A', 'ENGL', 'An introduction to the study of literature, covering such areas of enquiry as literary history, genre, criticism, analysis, and theory.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Rhetorical Studies', '101B', 'ENGL', 'This course introduces students to rhetoric: the art of persuasion. The history, theory, practice, and impacts of rhetoric will be considered. Students will analyze persuasive artifacts including propaganda, advertisements, political texts, and scientific communications. Students will also act as rhetors (users of rhetoric) to craft persuasive arguments.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Postcolonial Literature of the Americas', '322', 'ENGL', 'This course examines postcolonial literature in English from Canada, the U.S., and the Caribbean. Through study of both written and oral genres, we will discuss how language practices adapt to and are created in colonial and postcolonial contexts.  Topics may include diaspora and migration, nationalism, gender, neo-colonialism, and multiculturalism.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Postcolonial Literatures', '463', 'ENGL', 'This course examines postcolonial literature (fiction, poetry, and drama) from Africa, Australia, Britain, India, New Zealand, and Pakistan. Topics may include the range of creative forms and language use in texts; indigeneity and migration; intersections of gender, sexuality, and race; and resistance, nationalism, and history.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Postcolonial Literatures', '463', 'GSJ', 'This course examines postcolonial literature (fiction, poetry, and drama) from Africa, Australia, Britain, India, New Zealand, and Pakistan. Topics may include the range of creative forms and language use in texts; indigeneity and migration; intersections of gender, sexuality, and race; and resistance, nationalism, and history.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Personality', '356', 'PSYCH', 'This course addresses the science of measuring and explaining patterned individual differences in behaviour, including temperaments and traits; values, goals, and personal strivings; and meaning systems and self-narratives. Topics include behavioural, physiological, genetics, and cultural studies of personality; continuity and change in personality over the lifespan; and personality disorders. [Note: PSYCH 356 offered on campus; PSYCH 356R offered at Renison University College and Online.]');
