INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Soundtracks: Music in Film', '246', 'MUSIC', 'An examination of the techniques, styles, and functions of music in film, and how music interacts with image and narrative to create meanings. Weekly screenings of films selected from the 1930s to the present.');
