INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cryptography/Network Security', '758', 'CS', 'Cryptographic protocols and their application to secure communication, especially in a network setting. Identification and entity authentication; protocols for key establishment, transport, agreement and maintenance; secret sharing, broadcast encryption, tracing schemes; certificates, public-key infrastructure, PGP.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
