INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Performance German 1', '250', 'GER', 'This course improves students oral and written skills via creative performances in our everyday life. Students will be actively involved in marketing products, conducting job interviews, writing résumés, improvising theatre skits, hosting poetry slams, or even instructing yoga - all in German.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Interactive German Language and Culture', '303', 'GER', 'This course develops German language skills and cultural awareness. Discussions include such topics as Heimat, nation, migration, and study abroad. Students will engage with and reflect on different social and cultural perspectives through interviews, videos, and texts. Online interactions, including with people in and from Germany, form an integral part of the course. [Note: Offered through University of Guelph in alternating years, but always available to both University of Waterloo and University of Guelph students.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reading and Translating', '304', 'GER', 'Students learn strategies for understanding a variety of texts such as newspaper reports, manuals, and fiction. Translation exercises are used to improve language skills.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Exploring German Language and Literature', '331', 'GER', 'An introduction to German studies through close engagement with one specific topic. This course prepares students for advanced undergraduate courses in German studies by developing historical, theoretical, and methodological knowledge as well as the language skills necessary for reading literature and analyzing language. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
