INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Seminar', '431', 'GER', 'Topics in German literary and cultural studies and linguistics chosen by the instructor in consultation with the department. These courses reflect research interests of the faculty and form part of a well-rounded undergraduate education in "Germanistik". [Note: This is a repeatable course, subject to different content; it may be completed a total of 10 times.]');
