INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religion in Canada', '244', 'RS', 'Canada is a multicultural and a multi-faith society. While Christians remain the majority, Canada is home to increasing numbers of Buddhists, Hindus, Sikhs, Jews, Muslims, and people with no religion. This course examines religious diversity in Canada, including topics such as religion and multiculturalism, human rights, and selected controversies. [Note: Formerly RS 319.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
