INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Politics in Russia', '353', 'PSCI', 'What explains the successes and failures of democratization in Russia? Students will undertake the comparative study of political processes, institutions, and reforms in Russia, focusing on the dilemmas posed by democratization and authoritarianism. [Note: One of PSCI 244, 250, 252, 255, 259, 260, 264 strongly recommended]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Public Policy', '334', 'PSCI', 'An examination of the policy-making process in liberal democratic societies, and an evaluation of the tools employed by policy analysts to understand that process.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Public Policy', '334', 'SOC', 'An examination of the policy-making process in liberal democratic societies, and an evaluation of the tools employed by policy analysts to understand that process.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gender and Politics', '370', 'PSCI', 'How does gender inform - and challenge - political ideas? Students will study the interrelationship of important concepts such as power, equality, oppression, representation, intersectionality, and autonomy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra for Science', '114', 'MATH', 'Vectors in 2- and 3-space and their geometry. Linear equations, matrices, and determinants. Introduction to vector spaces. Eigenvalues and diagonalization.  Applications. Complex numbers. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Approaches to Translation', '290', 'SPAN', 'This course introduces students to translation studies through the examination of different approaches to the discipline, along with practical experience. Students review the grammar of the Spanish and English languages within particular contexts as the basis for translation practices. The course also enhances students lexical and semantic knowledge of the Spanish language through an experiential component in the community. [Note: Formerly SPAN 450.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Apprenticeship', '264', 'PSYCH', 'Students are introduced to the research process by participating in an unpaid apprenticeship of 96 hours in a faculty members research lab in the Psychology Department. Apprenticeship hours will be completed before the end of the lecture period for the term of enrolment. Application forms are available on the Psychology undergraduate website. Grading is on a credit/no credit basis. Paid or volunteer positions obtained outside the context of this course are not eligible for credit in this course.  [Offered: F,W,S.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Urban Water and Wastewater Systems: Integrated Planning and Management', '316', 'ERS', 'Focus on urban water management in North America, Australia, Western Asia, and the Global South as illustrative systems or cases. Given changing climate conditions, there is increased likelihood of extreme events (e.g., urban flooding and drought) and new water management challenges. Development and maintenance of urban water and wastewater systems, along with demand and supply management planning, efficiency mechanisms, development and equity concerns, will be examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First Nations and the  Environment', '372', 'ERS', 'First Nation environmental issues are often complex and require a holistic approach where the lines between different disciplines (e.g. natural, physical, health, and social sciences) are often obscured.  The environment, as described in this course, includes the natural (biological) and built (social, political) settings.  Case studies will be used to illustrate significant issues.');
