VALUES ('Statutory Interpretation', '604', 'ACC', 'This course will introduce the student to the Canadian standards related to the interpretation of tax legislation and decisions of the courts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Tax', '605', 'ACC', 'This course will introduce the student to Canadian taxation of international transactions, including the interpretation of tax treaties.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Business Valuations', '606', 'ACC', 'This course will provide the fundamentals of business valuation theory and practice. Successful completion of this course can provide students pursuing a Chartered Business Valuator (CBV) designation with advanced standing in the CBV Institute Program of Studies through course exemptions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Tax Issues Integration', '607', 'ACC', 'This course will develop the students skills in identifying and addressing tax issues, through the use of case studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Financial Statement Analysis', '609', 'ACC', 'This course introduces fundamental tools of analysis and evaluation that are widely used in a variety of financial careers.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research in Industrial/Organizational Psychology', '399', 'PSYCH', 'Current research methods and procedures employed in industrial/organizational psychology are covered. Activities may include research proposals, group and/or individual projects (e.g., "hands on" lab experience and data collection), research reports, critiques of published and proposed research, individual and/or group presentations. [Offered F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Designing Digital Media', '201', 'DAC', 'This course draws on multiple theoretical perspectives to introduce students to the fundamental principles of multi-modal communication design in its social context. Students will analyze, design, and produce digital media for use in a variety of platforms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Designing Digital Media', '203', 'ENGL', 'This course draws on multiple theoretical perspectives to introduce students to the fundamental principles of multi-modal communication design in its social context. Students will analyze, design, and produce digital media for use in a variety of platforms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Designing Digital Video', '202', 'DAC', 'This course introduces students to the principles of designing time-based multi-modal communication in a social context. Students will analyze, design, and produce video for use in a variety of digital platforms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Designing Digital Video', '204', 'ENGL', 'This course introduces students to the principles of designing time-based multi-modal communication in a social context. Students will analyze, design, and produce video for use in a variety of digital platforms.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Digital Design', '300', 'DAC', 'In this course students will learn advanced digital design theory. They will participate in workshops with professional designers, develop specialized digital materials, and contribute signature work to their digital portfolio. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
