INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Information Technology, Globalization and International Development', '605', 'GEOG', 'This course considers the role of GIS and RS technologies for problem solving and planning decision support in developing countries. Topics covered include the globalisation of information technology and its transfer from developed to developing countries and general aspects of the spatial information technology industry and its relationship to society. *eligible for MES.');
