INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Chemical Engineering', '500', 'CHE', 'Special courses on advanced topics may be offered from time to time, when resources are available. For current offerings, inquire at the CHE undergraduate office.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Public Issues Anthropology', '600', 'ANTH', 'An examination of the application of anthropological knowledge to public issues. The issues studied will vary from year to year; they may include such topics as race and ethnicity, citizenship and the state, marriage and sexuality, conservation and the environment, and the ownership, interpretation and display of artifacts and human remains. Students will present their research in forms appropriate to both academic and general audiences. [Note: Department Consent required]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Methods', '614', 'ANTH', 'An examination of the methods of qualitative research, including participant observation and unstructured interviews, as well as the ethical considerations of fieldwork. Other topics, such as comparative and historical methods, may be included. [Note: Department Consent required]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Anthropological Theory', '608', 'ANTH', 'An examination of classical and contemporary anthropological theory, including an emphasis on the most recent directions in the discipline. [Note: Department Consent required]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Skeletal Biology and Forensics', '655', 'ANTH', 'This laboratory course will focus on the evaluation of human skeletal remains in archaeological and forensic contexts. Topics will include determination of basic biological categories, e.g., age, sex, race, evaluation of paleopathological conditions, and aspects of forensic anthropology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Adaptation and Evolution', '662', 'ANTH', 'An examination of the Principles of Variation in human evolution past and present. This will include the development of the genus Homo, adaptation of modern populations to heat, cold, altitude and stress, and the continuing micro-evolutionary development of humans.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Selected Topics in Theory and Research', '605', 'ANTH', 'This course will be offered with varying content focusing on theory or research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Readings in Special Subjects', '498', 'RS', 'Readings in selected topics chosen in discussion with instructor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Senior Seminar', '499', 'RS', 'This seminar examines contemporary methods and theories in the study of religion and how they may be used to address specific themes in religious studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reading Course', '660', 'ANTH', 'A program of directed reading, complemented with the writing of papers or participation in research.  Reading courses are arranged by students through their advisors or advisory committees and must be approved by the graduate chair of the department.  This course may be repeated provided different content is involved.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Governing Global Food and Agriculture Systems', '606', 'PSCI', 'This course examines the international rules and organizations that have emerged to govern the increasingly global system of food and agriculture.  Specific themes to be covered include governance issues related to the rise of global food corporations, agricultural trade liberalization and the WTO, food aid distribution, international agricultural assistance, the global agro-chemical industry, and agricultural biotechnology.');
