INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary Engineering Mathematics', '113', 'SYDE', 'Functions: trigonometric, exponential, log, inverse functions. Mathematical notation and proof techniques. Metric spaces. Geometry and algebra: root-finding, vectors, coordinate systems, lines and planes, conic sections, complex numbers. Introduction to numerical computation: binary and floating point numbers and arithmetic, accuracy and sources of error. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Probability and Statistics', '212', 'SYDE', 'Elementary probability theory. Random variables and distributions. Binomial, Poisson, and normal distributions. Elementary sampling. Statistical estimation. Tests of hypotheses and significance. Regression. Goodness-of-fit tests. Analysis of experimental measurements. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Optimization and Numerical Methods', '411', 'SYDE', 'Root-finding methods. Linear programming, simplex and interior-point methods. Local and global optimization methods. Constrained optimization. Multiobjective and multidisciplinary design optimization.[Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Quantitative Data Analysis for Management Sciences', '609', 'MSCI', 'Descriptive statistics and probability concepts; discrete and continuous probability distributions; sampling methods and the central limit theorem; estimation and confidence intervals; inference and hypothesis testing; linear regression and correlation; multiple regression and model building.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Seminar', '199', 'CIVE', 'General seminar. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Calculus and Numerical Methods 1', '216', 'NE', 'Ordinary differential equations with constant coefficients. Boundary value problems and applications to quantum mechanics. Laplace and Fourier transforms, Fourier series and applications. Numerical solution of ordinary differential equations. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Calculus and Numerical Methods 2', '217', 'NE', 'Gradient, Divergence and Curl: Applications. Line and Surface Integrals. Greens, Gauss, and Stokes Theorems: Applications to electromagnetism and fluid mechanics. Numerical solution of partial differential equations. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Classical Mechanics', '105', 'ECE', 'Forces in nature and Newtons laws, dynamics and circular motion, work, energy, and conservation of energy. Linear momentum and linear impulse, rotational dynamics. Oscillations; simple harmonic motion. Wave motion; traveling waves and standing waves. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electricity and Magnetism', '106', 'ECE', 'Electrostatics: electric field, flux, Gausss Law, potential and potential energy. Capacitors: dielectric, capacitance, electric energy storage. Resistors: charge flow, current, resistance. Magnetostatic: magnetic force, magnetic fields, Amperes Law. Inductors: magnetic flux, inductance, magnetic materials, magnetic energy storage. Time-varying fields: Faradays Law, mutual inductance, simple motors and generators. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Circuits and Systems', '124', 'ECE', 'Number systems and Boolean arithmetic. Boolean algebra and simplification of Boolean functions. Combinational circuits. Sequential circuits; design and implementation. Hardware description languages. Timing analysis. Implementation technologies. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Circuits', '140', 'ECE', 'Analysis of linear circuits. Voltage, current, resistance, capacitance, inductance, voltage source, current source, dependent sources, Ohms Law, Kirchhoffs Laws, nodal analysis, mesh analysis, circuit transformations, operational amplifier circuits, time response, sinusoidal steady-state response. Preparing for, conducting, and reporting of laboratory experiments. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Design with Embedded Systems', '155', 'ECE', 'Introduction to embedded systems, review of engineering design and analysis principles, software development life cycle, integrated development environments, use of software requirements and specifications, unified modelling language and documentation, event handling, simulation, project management, project scheduling, testing, verification, and maintenance considerations. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Signals and Systems', '207', 'ECE', 'Discrete, continuous and periodic signals, time- and frequency-domain analysis of continuous- and discrete-time linear systems, periodic signals and Fourier series, non-periodic signals, and Fourier transforms. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Embedded Microprocessor Systems', '224', 'ECE', 'Microprocessor system architecture, bus systems, memory systems, peripherals, parallel interfaces, serial interfaces, analog interfaces, data transfer, synchronization, error detection/correction, testing and debugging. [Offered F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electronic Circuits 1', '240', 'ECE', 'Introduction to electronic signal processing; second-order circuits; operational amplifier circuits; diode device and circuits; metal-oxide-semiconductor (MOS) biasing networks; load-line analysis; diode and MOS small-signal equivalent circuits; single-stage small-signal MOS amplifiers; complementary metal-oxide-semiconductor (CMOS) logic circuits. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electronic Circuits 2', '242', 'ECE', 'Electronic circuits and their limitations, including; differential pairs, biasing, the cascode configuration and active loads.  Differential and multistage amplifiers.  Feedback, stability and compensation.  CMOS logic circuits.  [Offered:  F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Operating Systems and Systems Programming', '254', 'ECE', 'Concepts of operating systems and systems programming; utility programs, subsystems, multiple-program systems; processes, interprocess communication, synchronization, and concurrency; memory management, segmentation, and paging; loading and linking, libraries; resource allocation, scheduling, performance evaluation; I/O systems, storage devices, file systems; protection, security, and privacy. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electronic Devices', '331', 'ECE', 'Review of band theory and doped semiconductors in thermal equilibrium, charge neutrality, mass action law, recombination and transport mechanisms, Boltzmann relations. Device theory and modelling of p-n junction diode and derivation of dc and ac characteristics, charge storage effects. Principles, device theory, and modelling of metal-oxide semiconductor field-effect transistors (MOSFETs), and the derivation of threshold voltage, dc current characteristics, small signal ac models. Principles of bipolar transistor and derivation of dc and ac terminal characteristics, equivalent circuits. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Compilers', '351', 'ECE', 'Programming paradigms, compilation, interpretation, virtual machines. Lexical analysis, regular expressions, and finite automata. Parsing, context-free grammars, and push-down automata. Semantic analysis, scope and name analysis, type checking. Intermediate representations. Control flow. Data types and storage management. Code generation. [Note: Electrical Engineering students with at least 80% in ECE 108 will be granted an override for ECE 208 prerequisite on the understanding that they will need to make up missed material from ECE 208 on their own. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Database Systems', '356', 'ECE', 'Data models, file systems, database system architectures, query languages, integrity and security, database design. [Note: Electrical Engineering students with at least 80% in ECE 108 will be granted an override for ECE 208 prerequisite on the understanding that they will need to make up missed material from ECE 208 on their own. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computer Networks', '358', 'ECE', 'This course is a comprehensive introduction to computer networks. The focus is on the concepts, the protocols, and the fundamental design principles that have contributed to the success of the internet. Topics include history of the Internet, transmission media and technologies, switching and multiplexing, protocols and layering, wired and wireless LAN (local-area networks), congestion/flow/error control, routing, addressing, internetworking (Internet) including TCP (transmission control protocol). [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Power Systems and Components', '361', 'ECE', 'This course is an introduction to basic modeling and analysis techniques in electricity generation, transmission and distribution, including basic concepts in nonlinear system analysis. Functional descriptions and modeling of generators, transformers, transmission lines, motors and other loads are discussed. Power flow analysis techniques are studied in detail, from the basic equations to their use in power networks. Fault analysis and basic protection concepts are also discussed. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Design Project', '498A', 'ECE', 'Team-oriented design-project which comprises a significant design experience based on the knowledge and skills acquired by students in previous courses and on co-operative work terms.  Development of the design specification and plan documents, followed by the initial design work. [Offered:  F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Design Project', '498B', 'ECE', 'Completion of the design cycle started in ECE 498A and communication of the engineering design work.  Submission of a written final report.  Lecture-style technical presentation by group members.  Poster-style technical presentation with group members available to discuss the project.  [Offered:  F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electromagnetic Fields and Waves', '375', 'ECE', 'Maxwells equations; plane waves; time-harmonic fields; waves at planar boundaries; boundary conditions; reflection and transmission; transmission lines; electric fields in matter; magnetic fields in matter. [Offered W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Radio-Wave Systems', '475', 'ECE', 'Review of Maxwells equations and uniform plane waves. Electromagnetic wave propagation, reflection and transmission through different uniform media, metallic waveguides, multilayer structures, radiation theory, transmitting and receiving antenna, antenna arrays and applications, simple radio-wave propagation models, radio transmission systems and wireless networks. [Offered: S]');
