INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Analysis and Research', '302', 'FINE', 'This course hones critical analysis skills and develops research practices (by exploring artists, materials, techniques, readings, etc.) that will enhance the students knowledge of contemporary art practice, as well as equiping students to articulate their area of interest within contemporary art. Gallery visits and artists talks are components of this course. [Note: A field trip fee of $100 may be required.]');
