INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fine Arts Internship', '344', 'FINE', 'This experiential learning course is intended to enable Fine Arts students to gain valuable work experience within an art related environment. The course is designed to complement the students academic program by providing an opportunity for the practical application of skills and concepts. [Note: Course will be graded on a CR/NCR basis. A minor field trip fee may apply.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Study', '402', 'FINE', 'This course is intended to help Fine Arts students make the transition from art school to professional practice by providing an opportunity to examine their own practice within the larger context of the professional art world. [Note: A field trip fee of $100 may be required.]');
