INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intensive Introductory Italian Language', '155', 'ITAL', 'This course aims to present the basic principles of the Italian language, geography, and culture in a practical and efficient way. Throughout the course, the goal of practical, communicative competence will be emphasized and cultivated via in-class activities and assignments focusing on five areas of performance: listening, speaking, reading, writing, and culture.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Communication and Counselling Skills', '301', 'SMF', 'This course is an examination and analysis of the theories and methods of communication as applied within the processes of individual, relational, and family consultation and counselling.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
