INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('German for Professional Purposes', '307', 'GER', 'This language course teaches students the skills needed to function in German business and professional environments. Praxis-oriented language and intercultural training are combined with a discussion of the German business world.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('German Through Comics', '308', 'GER', 'German-language comic strips and graphic novels enable students to explore modern language use. Colloquial German language skills are sharpened in this course, and the place of the comic strip and graphic novel in society is discussed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
