INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modern Physics', '124', 'PHYS', 'An introductory course in modern physics; includes relativity, quantum physics, atomic physics, nuclear physics, particle physics and gravitation. [Offered:  W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electricity and Magnetism 1', '242', 'PHYS', 'Electrostatics in vacuum, electric potential, conductors and currents, magnetostatics in vacuum, electromagnetic induction. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electricity and Magnetism 2', '342', 'PHYS', 'Electric and magnetic fields in media, auxiliary fields, Maxwells equations, electromagnetic waves, electric and magnetic properties of matter. [Note: PHYS 236 or knowledge of computational methods recommended. Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physical Optics', '393', 'PHYS', 'Fourier Optics: diffraction, dispersion, coherence, imaging. Polarization: birefringence, Jones vectors, Mueller Matrices. Fresnel Equations: optics at an interface, thin films. [Offered: W even years]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Light-Matter Interactions', '394', 'PHYS', 'Lasers: semi-classical interaction, properties, cavity, Gaussian beams.  Radiation and Detectors. Introduction to nonlinear optics. [Offered: W odd years]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electricity and Magnetism 3', '442', 'PHYS', 'Review of the origin of Maxwells equations, conservation laws, potential formulation of Maxwells equations and gauge choices, vacuum solutions of Maxwells equations (free space, waveguides, dispersion), solution to Maxwells equations for arbitrary sources (static and time-dependent problems), relativistic formulation of electrodynamics, macroscopic Maxwells equations and plane waves in macroscopic media. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Nanophysics', '461', 'PHYS', 'Fundamentals of nanotechnology.  Applications of nanotechnology in biology and medicine.  Nanotechnology and society.  Physical foundations of nanodevices.  Conduction at the nanoscale.  Modern nanodevices. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to the Implementation of Quantum Information Processing', '468', 'PHYS', 'Photonic quantum computing, Superconducting qubits, NMR, Ion Trap quantum computing, Atomic quantum computing. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Culture Behind the Iron Curtain', '385', 'GER', 'An examination of the culture of the Soviet Union, the German Democratic Republic, and other states that experimented with socialism in the 20th century. Representations of Marxist-Leninist and Stalinist ideologies in literature, cinema, and the arts; the role of the state in directing cultural expression; and resistance to totalitarian control of culture are examined. Western representations of the Cold War are also studied. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Culture Behind the Iron Curtain', '385', 'REES', 'An examination of the culture of the Soviet Union, the German Democratic Republic, and other states that experimented with socialism in the 20th century. Representations of Marxist-Leninist and Stalinist ideologies in literature, cinema, and the arts; the role of the state in directing cultural expression; and resistance to totalitarian control of culture are examined. Western representations of the Cold War are also studied. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Waves, Electricity and Magnetism', '122', 'PHYS', 'Simple harmonic motion, resonance, damped harmonic motion, complex numbers, wave motion and sound, electrostatic force and potential, electric current and power, capacitors, DC circuits, LRC circuits, introduction to magnetic fields Lorentz Force. [Offered: W, S; also offered online: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Once Upon a Fairy Tale: Fairy Tales, Then and Now', '120', 'GER', 'This course studies fairy tales in the broader context of the history of childhood and practices of education and socialization. The course will focus on fairy tales of German and Russian origins and consider their interpretations and reinterpretations in different historical periods. This may include figures from the Brothers Grimm and Alexander Afanasyev, such as Snow White, Sleeping Beauty, Red Riding Hood, and the Firebird. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Once Upon a Fairy Tale: Fairy Tales, Then and Now', '120', 'REES', 'This course studies fairy tales in the broader context of the history of childhood and practices of education and socialization. The course will focus on fairy tales of German and Russian origins and consider their interpretations and reinterpretations in different historical periods. This may include figures from the Brothers Grimm and Alexander Afanasyev, such as Snow White, Sleeping Beauty, Red Riding Hood, and the Firebird. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Elementary Algorithm Design and Data Abstraction (Advanced Level)', '146', 'CS', 'CS 146 is an advanced-level version of CS 136. [Note: This course may be substituted for CS 136 in any degree plan or for prerequisite purposes. Students who receive a good grade in CS 135 may contact the instructor of CS 146 to seek admission without the formal prerequisites. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Methods in Business and Finance', '335', 'CS', 'An introduction to numerical methods for business and finance. Floating-point arithmetic, interpolation. Methods for portfolio optimization and contingent-claims valuation. Solution of nonlinear equations. Monte Carlo methods, lattice methods, simulation of hedging strategies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biophysics of Therapeutic Methods', '395', 'PHYS', 'The effect of radiation of various kinds on cells and tissues; elements of radiobiology and photobiology; molecular mechanisms of radiation-induced DNA damage and cell death, repair of radiation damage, dose-response relationships; tumor radiobiology and therapies, radiotherapy, photodynamic therapy, combination therapies; radiosensitizers and photosensitizers for cancer therapies; transdisciplinary advances in physical methods (ultrafast laser and ultrasound techniques) for biomedical applications. [Note: PHYS 380 is recommended. Offered: W even years]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Microeconometrics I', '622', 'ECON', 'This course provides an introduction to the analysis of microdata - data in which the unit of analysis in an individual decision-maker, such as a consumer, worker, or firm.  The course familiarizes students with the various types of microdata structures and draws links between theoretical economic models and statistical models.  It devotes considerable attention to analytical techniques for describing univariate and bivariate distributions, including the use of kernel density estimators and scatterplots.  The course addresses statistical inference using multivariate regressions, covering a series of techniques and issues of particular significance in the analysis of microdata.  Examples include Monte Carlo methods; bootstrapping; the use of sampling weight; omitted variable bias; measurement error; and instrumental variables.  Completion of an empirical project is a course requirement.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Macroeconometrics I', '623', 'ECON', 'This course focuses on the econometric techniques of empirical problems.  Topics may include some basic concepts in time series analysis (such as deterministic and stochastic processes, stationary, ACF, Ergodicity conditions), established estimation techniques (such as OLS/GLS, MLE, GMM, Monte-Carlo simulation), popular univariate and multivariate time series models (such as ARMA  process, autoregressive conditional heteroskedasticity model, stochastic volatility model, Vector Autoregression, Vector Moving Average), and non stationary models (such as random walk, unit roots, Dickey-Fuller Tests, Co-integration System and error corrections).  Some related (empirical/theoretical) published papers for each topic may be discussed in class.  An empirical project is required in the course, requiring the use of software (such as Matlab, SAS).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Finance', '650', 'ENBUS', 'Financial and accounting "tools" have potential to create effective and far-reaching market-based solutions to address a range of environmental problems, including climate change, deforestation, water issues and biodiversity loss, while at the same time identifying and securing new business opportunities for companies and their customers.  An increasing number of financial and accounting products and services have emerged to direct resources and lending power to mitigate ecological degradation and/or encourage sustainable practices and decision-making.  the course considers environmental finance products, tools and services; carbon finance an economics, and other approaches to environmental assets; international capital projects, structured financing, risk and sustainability in financial modeling; full-cost accounting; relation of sustainability performance to capital market valuation and financial performance; socially responsible investing; sector governance, disclosures, regulations, principles and codes.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Lebesgue Integration and Fourier Analysis', '650', 'PMATH', 'Lebesgue measure on the line, the Lebesgue integral, monotone and dominated convergence theorems, LP spaces, completeness and dense subspaces; separable Hilbert space, orthonormal bases; Fourier analysis on the circle, Dirichlet kernel, Riemann-Lebesgue lemma, Fejers theorem and convergence of Fourier series.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Model Theory and Set Theory', '733', 'PMATH', 'Model theory: the semantics of first order logic including the compactness theorem and its consequences, elementary embedding and equivalence, the theory of definable sets and types, quantifier elimination, and w-stability.  Set theory: well-orderings, ordinals, cardinals, Zermelo-Fraenkel axioms, axiom of choice, informal discussion of classes and independence results.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Analytic Number Theory', '740', 'PMATH', 'Summation methods; analytic theory of the Riemann zeta function; Prime Number Theorem; primitive roots; quadratic reciprocity; Dirichlet characters and infinitude of primes in arithmetic progressions; assorted topics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Functional Analysis', '753', 'PMATH', 'Banach and Hilbert spaces, bounded linear maps, Hahn-Banach theorem, open mapping theorem, closed graph theorem, topologies, nets, Hausdorff spaces, Tietze extension theorem, dual spaces, weak topologies, Tychonoffs theorem, Banach-Alaoglu theorem, reflexive spaces.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Water: Environmental History and Change', '265', 'ERS', 'This course explores issues of water management from ancient to recent history.  Tensions related to water supply and demand, agriculture and urbanization, health and sanitation, gender and household access to water resources, urban water and wastewater infrastructure, trans-boundary politics, and water privatization debates are considered. Readings and detailed assessments of national and international cases from Europe, Western Asia, and North America are used as a basis for in-class discussion and research projects.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Water Governance', '365', 'ERS', 'Water governance refers to the processes and institutions through which societies make decisions and take actions that affect water. A profound, world-wide shift in the nature of water governance is occurring because governments can no longer be the primary source of decision-making authority regarding water. Instead, through mechanisms that range from markets to co-management arrangements, citizens, non-government organizations, and corporations are now playing key roles in water governance. The course explores major water governance challenges in Canada, and assesses different ways of addressing or resolving them.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Governance', '600', 'GGOV', 'This course provides an overview of current scholarly debates relating to the interdisciplinary study of global governance in the context of globalization. It examines competing perspectives on globalization and global governance and
explores the sources and consequences of global power and authority,  as well as the key actors, institutions,  regimes, and norms of global governance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Governance', '700', 'GGOV', 'This course provides an overview of current scholarly debates relating to the interdisciplinary study of global govemance in the context of globalization. It examines competing perspectives on globalization and global govemance, and
explores the sources and consequences of global power and authority, as well as the key actors, institutions, regimes, and norms of global governance. This course is open only to students in the PhD program in Global Govemance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Methods', '701', 'GGOV', 'The course exposes students to various methodological approaches and debates among them in order to help students develop the ability to professionally assess
academic work as well as to prepare their own dissertation research. The course examines such topics as statistical methods for the social sciences, issues in methods and methodology, case selection, critical assessment and
proposal writing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Human Rights', '641', 'GGOV', 'This interdisciplinary course will address issues in the study of the international legal regime of human rights, its history and evolution.  The course will cover a number of debates in the literature, including but not limited to philosophical and regional/cultural/religiously - based objections to this international regime; human rights in international relations; and human rights and globalization.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Security Governance: Actors, Institutions, and Issues', '679', 'PSCI', 'In this course we examine a range of "security" issues on the global agenda - both traditional and non-traditional - and examine recent and possible future institutional and policy responses.  Issues examined include nuclear proliferation, terrorism, intrastate conflict, resource and territorial disputes, climate change, drugs, disease, and migration.  Students will have an opportunity to research in depth a specific security issue of their choice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Security Governance: Actors, Institutions, and Issues', '631', 'GGOV', 'In this course we examine a range of "security" issues on the global agenda - both traditional and non-traditional - and examine recent and possible future institutional and policy responses.  Issues examined include nuclear proliferation, terrorism, intrastate conflict, resource and territorial disputes, climate change, drugs, disease, and migration.  Students will have an opportunity to research in depth a specific security issue of their choice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Security Governance: Actors, Institutions, and Issues', '635', 'PACS', 'In this course we examine a range of "security" issues on the global agenda - both traditional and non-traditional - and examine recent and possible future institutional and policy responses.  Issues examined include nuclear proliferation, terrorism, intrastate conflict, resource and territorial disputes, climate change, drugs, disease, and migration.  Students will have an opportunity to research in depth a specific security issue of their choice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Business and Development', '614', 'PSCI', 'This course examines the impact of international business on development in the context of economic globalization.  It explores contending perspectives on how international business, particularly multinational corporations, has affected the economic, social and political development in the host countries, the home countries, as well as the world in general.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Business and Development', '614', 'GGOV', 'This course examines the impact of international business on development in the context of economic globalization.  It explores contending perspectives on how international business, particularly multinational corporations, has affected the economic, social and political development in the host countries, the home countries, as well as the world in general.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Organizations Law', '661', 'GGOV', 'This course introduces graduate students to the law of international organizations.  It covers the rise of international organizations; the legal position of international organizations; the foundations of powers of organizations; international organizations and the law of treaties; issues of membership; financing; privileges and immunities; institutional structures; legal instruments; decision-making and judicial review; dispute settlement; treaty-making by international organizations; issues of responsibility and accountability; and dissolution and succession.');
