VALUES ('Quantitative Ecology', '458', 'BIOL', 'The course addresses the integration of mathematical and statistical tools to solve problems in ecology, using examples from individual, population, community, and ecosystem scales. Coursework will explore the relationships between models, hypotheses, experiments and data.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Practicum in Recreation, Sport, and Tourism', '312', 'REC', 'REC 312 involves an unpaid placement in an organization related to recreation, sport, business or tourism. Placement opportunities will be facilitated by the course instructor, in partnership with the supervising organization.The placement will require 105 hours interspersed over one academic term (15 weeks). The completion of these hours will be determined by the placement supervisor, in consultation with the student. [Note: The course will be offered on a credit/no credit basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Food and Agricultural Politics', '462', 'ERS', 'This course examines the global food and agriculture system. Specific themes to be covered include political and governance issues related to the Green Revolution, global food corporations, agricultural trade liberalization, food aid, international agricultural assistance, the global agro-chemical industry, and agricultural bio-technology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
