INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Food and Agricultural Politics', '488', 'PSCI', 'This course examines the global food and agriculture system. Specific themes to be covered include political and governance issues related to the Green Revolution, global food corporations, agricultural trade liberalization, food aid, international agricultural assistance, the global agro-chemical industry, and agricultural bio-technology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Food Systems and Sustainability', '361', 'GEOG', 'This course examines dimensions of food systems sustainability and food security, from production to consumption, from local to global scales, in the Global North and South. Specific themes covered in the course include technological and genetic change in food production, resource depletion and wider environmental impacts of the industrial food system, and policy, market-oriented, and civil society initiatives to bring about change in the food system, including organic production and localizing food systems. [Formerly: GEOG/ERS 461]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Food Systems and Sustainability', '361', 'ERS', 'This course examines dimensions of food systems sustainability and food security, from production to consumption, from local to global scales, in the Global North and South. Specific themes covered in the course include technological and genetic change in food production, resource depletion and wider environmental impacts of the industrial food system, and policy, market-oriented, and civil society initiatives to bring about change in the food system, including organic production and localizing food systems. [Formerly: GEOG/ERS 461]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Digital Imaging', '130', 'FINE', 'This online course is an introduction to current imaging software that students use to explore contemporary topics through digital imaging. This course is taught from a fine arts perspective. [Note: Studio course. Not open to students who have completed FINE/GBDA 228 or FINE/GBDA 229.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Directed Studies', '345', 'SPAN', 'Study in a limited field under tutorial guidance. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to the Universe', '175', 'PHYS', 'A survey course in astrophysics intended for Physics and Astronomy students. Astrophysical processes, the sky, the Sun, stars, black holes, the Milky Way and other galaxies, Big Bang cosmology. [Note: students in Honours Physics and Astronomy programs must also take PHYS 175L; Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to the Universe Laboratory', '175L', 'PHYS', 'For students who are taking PHYS 175. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Astronomical Observations, Instrumentation, and Data Analysis Laboratory', '270L', 'PHYS', 'Selected experiments in Astronomy. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Galaxies', '474', 'PHYS', 'Galaxy structure, formation and evolution.  Dynamics and stellar populations; gas and dust; supermassive black holes; large-scale structure. [Offered:  F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modeling Life Physics', '225', 'PHYS', 'Introduction to modeling living systems and their components. Statistical methods in data analysis, curve fitting, including p values. Fourier series and transforms, structural analysis, including nearest neighbor distributions in biomedical applications.  Introduction to methods for analysis of transport properties in biological systems. Use of computers in these areas. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Quantum Mechanics', '233', 'PHYS', 'Introduction to quantization, wave-particle duality and the uncertainty principle The Schroedinger equation and solvable examples. Topics will include stationary states of particle-in-a-box, harmonic oscillator, and the hydrogen atom. Quantization of angular momentum and spin. Introduction to approximation methods including time-independent perturbation theory. Modern applications of quantum mechanics. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Medical Physics', '383', 'PHYS', 'Applications of physics in medicine. The course will address basic concepts of medical imaging, nuclear medicine and radiation isotopes, radiation therapy and biomedical laser applications. Nuclear structure and binding energy. Nuclear decays, radioactivity and nuclear reactions. Interaction of radiation with matter. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Therapeutic Concepts in Oncology and Medical Physics', '483', 'PHYS', 'This course examines advanced oncology therapeutic concepts required as a foundation for patient management in the inpatient, ambulatory, and community settings. Topics include a comprehensive review of cancer epidemiology and pathophysiology, therapeutic agents used in the oncology setting, management of cancer therapy toxicities, and treatment and palliation of cancer symptoms. Physics methods in radiation therapy and the medical physicists role in cancer treatment will be addressed. [Offered: W; Held with PHARM 464]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Life, Medical and Biophysics', '491', 'PHYS', 'A lecture and project course offered in areas of life, medical and biophysics, which will add to the learning experience of the students in these plans at a fourth-year level. Lecture topics may include advanced topics in molecular and cellular biophysics, imaging, biophotonics, interaction of radiation with biomaterials, radiation dosimetry and other physics based diagnostics and therapeutics. Projects may include studies of recent advances in any areas of medical and biophysics. 
[Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Astronomy Laboratory 1', '370L', 'PHYS', 'Selected experiments in astronomy. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Materials and Nanosciences in the Modern World', '101', 'MNS', 'Overview of materials, including physical and chemical classification of materials, and structure-property relationships; survey of emerging fields in materials and nanoscience research such as nanotechnology, quantum materials and devices, bionic research; societal impacts of materialsn and nanoscience research. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Techniques for Materials and Nanosciences', '102', 'MNS', 'Overview of materials synthesis, including both wet chemical and dry physical based methodologies; basic metrology and materials characterization of surface and bulk properties; introduction to the design, fabrication, and evaluation of simple devices; survey of emerging new techniques in materials and nanosciences.
[Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemistry and the Solid State', '211', 'MNS', 'Crystal structures and bonding in solids; introduction to diffraction, solid state synthesis, and thermal analysis techniques employed in the study of materials; defects, non-stoichiometry, and solid solutions; phase diagrams for solid systems. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physics and the Solid State', '221', 'MNS', 'Physical properties of crystalline and amorphous solids; introduction to electric, magnetic and optical properties of solids.  [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomaterials', '331', 'MNS', 'Mechanical, chemical and biological properties of biomaterials; relationships between biomaterials and their properties, functions, manufacture, and applications; applications of biomaterials in technologies, such as tissue engineering, prosthesis manufacture, drug delivery systems, bioreactors. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electrical and Optical Properties of Materials', '321', 'MNS', 'Optical properties of and electrical transport in materials; topics may include insulators, semiconductors, metals, superconductors and their applications in photovoltaics, thermoelectrics, and nanocomposites.[Offered: S, F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Nano-Biomaterials', '431', 'MNS', 'After a review of the essential concepts in nano-biomaterials, in-depth discussion includes two or three of the following topics: biomaterials on atomic and nanometre scales in which DNA, RNA, and/or polypeptides are incorporated as components; physical, chemical, and biological processes; nanoscale forces, adhesion, indentation; applications in nanomotors, nano-biomimetic materials, nanocomposite materials, and nano-biosensors. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Solid-State Materials', '410', 'MNS', 'After a review of the essential concepts in solid-state materials, in-depth discussion includes two or three of the following topics: transistors, semiconducting materials and devices, superconducting materials and superconductivity-based devices (such as Josephson junctions, SQUIDS), effects of low dimensionality and interfaces, quantum dots, magnetoresistance, spintronics, multiferroics, topological phases, and, nanofabrication of new materials. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modelling the City', '416', 'PLAN', 'This course examines the use of computer modeling and simulation in the realm of urban analysis and forecasting, with the goal of understanding urban land-use change trajectories. Topics include an overview of the drivers and consequences in urban land-use change, the role of models, an overview of current methodological approaches, and an examination of urban simulation models as used in the development of urban policies and official plans. This course provides an applied learning environment in which students will gain experience in the use of spatial (GIS) modeling approaches.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music Studio', '428', 'MUSIC', 'Individual instruction in voice, piano, organ, classical guitar, and orchestral instruments. Students perform a senior recital. [Note: Studio Fee]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Korean Culture and Society', '204R', 'EASIA', 'The course explores the interface of tradition and modernity in Korean life. Topics include traditional and modern Korean culture, language, education, art, technology, and business. [Note: Formerly KOREA 272R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chinese Culture and Society', '202R', 'EASIA', 'The course explores the interface of tradition and modernity in Chinese life. Topics include traditional and modern Chinese culture, language, education, art, technology, and business. [Note: Formerly CHINA 272R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Japanese Culture and Society', '203R', 'EASIA', 'The course explores the interface of tradition and modernity in Japanese life. Topics include traditional and modern Japanese culture, language, education, art, technology, and business. [Note: Formerly JAPAN 272R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate Arabic 1', '201R', 'ARABIC', 'This is an intermediate course focusing on reading comprehension of authentic Arabic materials. Students are introduced to verb patterns and subject-verb agreement rules and write longer paragraphs and short essays. [Note: Not open to students with native, near native, or similar advanced ability. Formerly SI 201R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intermediate Arabic 2', '202R', 'ARABIC', 'This is an intermediate course where students construct complex sentences and write essays. They read, analyze, and translate authentic texts. Students participate in conversations on topics of interest and learn complex grammar patterns, lexical derivations, and dictionary use. [Note: Formerly SI 202R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Arabic 1', '301R', 'ARABIC', 'This is an advanced course focusing on honing reading comprehension skills. Students write longer essays using advanced grammatical structures, and they participate in longer conversations. [Note: Not open to students with native, near native, or similar advanced ability. Formerly SI 301R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Arabic 2', '302R', 'ARABIC', 'This is an advanced course that provides additional practice of the four integrated language skills. Students examine and analyze texts more deeply and confidently, responding to the texts in oral presentations and class discussions. [Note: Not open to students with native, near native, or similar advanced ability. Formerly SI 302R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Entrepreneurship and Scaling Social Innovation', '612', 'ENBUS', 'This course explores different approaches that can be used to bring social innovations to scale so that they can have a broad, durable impact on a system.  The role of social entrepreneurs and institutional entrepreneurs will be introduced and explored as being key to catalyzing changes in resource flows, the understanding of problems and in shifting laws and policies that govern the problem domain.  New approaches to generating financing and revenue for social good, the governance and partnership structures these imply, and how they can enable or constrain systems change.  Examples will be drawn from Canadian and international contexts form the perspectives of entrepreneurs, investors/donors, policymakers and beneficiaries');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics for Sustainable Development', '605', 'INDEV', 'This course introduces students to the history, theories and practices of development economics.  Select issues such as trade, (Public and private) capital flows, transnational corporations, technological change and innovation, agricultural and industrial policy and production, poverty and reduction, structural adjustment, etc. are treated, as are recent developments in globalization and global economic governance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics for Sustainable Development', '651', 'PACS', 'This course introduces students to the history, theories and practices of development economics.  Select issues such as trade, (Public and private) capital flows, transnational corporations, technological change and innovation, agricultural and industrial policy and production, poverty and reduction, structural adjustment, etc. are treated, as are recent developments in globalization and global economic governance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Methods for Sustainable Development Practice:  A Systems Approach', '607', 'INDEV', 'The course is aimed at enhancing skills useful for development practice. From a systems perspective, students will learn to conceptualize problems using real cases and to analyze them both at the level of structure and dynamics. Problem solving will rely on analytical, heuristic and normative categories and will include learning select methods focusing on sustainability coupled with socio-ecological systems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Water and Security', '608', 'INDEV', 'The course will provide students with comprehensive background knowledge relevant to the increasingly important policy challenge of water security.  The course will explore how the multiple levels of water security - human, community, state, international, global - require broad but considered policy inputs.  Emphasis will be placed on the interdependencies of difference sectors (climate security, food security, energy security) that interact within a web of water security.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Water and Security', '652', 'PACS', 'The course will provide students with comprehensive background knowledge relevant to the increasingly important policy challenge of water security.  The course will explore how the multiple levels of water security - human, community, state, international, global - require broad but considered policy inputs.  Emphasis will be placed on the interdependencies of difference sectors (climate security, food security, energy security) that interact within a web of water security.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Water Resources', '612', 'INDEV', 'This course presents a broad survey of water resources processes and issues.  How much water does the world need to support growing human populations?  What factors influence water quality, droughts, floods, and waterborne diseases?  What are the potential effects of climate change on the worlds water resources?  This course presents a thorough introduction to the complex world of water resources.  The fundamentals of the science of water, aquatic ecology, geomorphology and hydrology, chemistry, and biology of lakes, rivers, and wetlands are covered.  Major disease issues, worldwide water quality and quantity problems, and potential solutions are examined.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Water, Human Security and Development', '613', 'INDEV', 'Whereas "water is life" is a frequently invoked truism, the fact that 1.1 billion people lack access to a safe supply of drinking water and 2.4 billion lack access to adequate sanitation suggests that the wrong kind of water may also bring both misery and death.  This course surveys the role of water in human security and development.  It locates water use in history and articulates the links between water and human security through a mix of cases and issues from around the world.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Integrated Water Resources Management', '614', 'INDEV', 'The purpose of this course is to investigate the major issues involved in water and related (e.g. land) resource management in both local (case study) and global (global governance) contexts.  Students will develop an integrated approach to the analysis and management of water resources, appreciate the relevance of an integrated approach through a consideration of selected resource management issues and problems, and apply an integrated approach to the analysis of specific water resource problems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transboundary Water Governance', '615', 'INDEV', 'There are approximately 265 river basins shared between and among states.  More than 40 percent of the worlds population resides in a transboundary river basin.  While many speculate that wars of the future will be about water, many other suggest that water forms the basis for long-term inter-state cooperation.  This course describes and interrogates the adequacy of existing bilateral, regional and global laws, institutions and policies designed to organize the governance and management of transboundary waters in the world today.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Urban Food Security', '616', 'INDEV', 'This course focuses on the pressing issue of providing sustainable food sources for the worlds fast growing urban population.  Drawing on research from around the world, and with a particular focus on the worlds largest cities, and their poorest residents, this seminar explores the ways and means of achieving sustainable urban food security.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Power Electronics and Motor Drives', '420', 'MTE', 'Analysis, design and control of power electronic converters, principles of operation and control of DC and special-purpose motors, power electronics-based control of DC and special-purpose motors.  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Field Placement', '611', 'INDEV', 'The student spends 4 months in the field, working with a local community partner on a particular development challenge.  Ideally, placement will be tailored to the students specialization, while also providing a setting in which integrative planning and management is required.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Materials and Nanosciences Laboratory', '201L', 'MNS', 'The laboratory course is aimed at students enrolled in the MNS program and is composed of experiments involving the syntheses of materials and nano-particles; in addition to their characterization and application using modern instrumentation techniques.  [Offered:  W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Polymer Materials', '322', 'MNS', 'Basic definitions and polymer nomenclature, molecular weight distributions and averages, molecular weight measurements, step-growth and radical chain polymerization reactions, chain conformations, glass transition, crystallization, mechanical properties of polymers, phase behaviour and morphologies, and self-assembly. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations of Venture Creation', '300', 'BET', 'This course provides students with an introduction to the processes involved in moving an idea for a new venture from concept through to launch. The theoretical knowledge and practical skills needed to create a successful small entrepreneurial enterprise are developed. Topics include definition and evaluation of entrepreneurial opportunities, business planning, funding strategies and early-stage revenue models, legal issues and intellectual property protection. [Offered: F, W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Marketing Foundations for Early-stage Ventures', '400', 'BET', 'This course develops the theoretical knowledge and practical skills needed to develop a marketing strategy for early-stage ventures. Topics include managing product development, marketing and brand development, segmentation and targeting, competitor analysis, and positioning. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mathematical Modeling with Differential Equations', '650', 'MATH', 'Solving and interpreting differential equations motivated by a variety of systems from the physical and social sciences.  Analytical solutions of standard linear and non-linear equations of first and second order; phase portrait analysis; linearization of non-linear systems in the plane.   Numerical and graphical solutions using mathematical software.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Ecology', '652', 'BIOL', 'Discussion of the major concepts in ecology, and current advances in selected topics.  A broad overview of important ideas in population, community and ecosystem ecology will be provided, and following this, recently published research results on a subset of topics will become the central focus of the course.  Topics might include but are not limited to: spatial-temporal dynamics of populations, meta- and hierarchical community structure, and species-specific influences on ecosystem function.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Economics', '103', 'COMM', 'This course provides an introduction to the basic concepts and tools of micro and macro economic analysis of individuals, businesses, and government in the global world. Topics may include consumers, producers, market structures, national income accounting, unemployment, and inflation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Economics', '100', 'ECON', 'This course provides an introduction to the basic concepts and tools of micro and macro economic analysis of individuals, businesses, and government in the global world. Topics may include consumers, producers, market structures, national income accounting, unemployment, and inflation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Financial Econometrics', '974', 'ACTSC', 'The focus of this course is on the statistical modelling, estimation and inference and forecasting of nonlinear financial time series, with a special emphasis on volatility and correlation of asset prices and returns.  Topics to be covered normally include: review on distribution and dynamic behaviour of financial time series, univariate and multivariate GARCH processes, long-memory time-series processes, stochastic volatility models, modelling of extreme values, copulas, realized volatility and correlation modelling for ultra high frequency data and continuous time models.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Financial Econometrics', '974', 'STAT', 'The focus of this course is on the statistical modelling, estimation and inference and forecasting of nonlinear financial time series, with a special emphasis on volatility and correlation of asset prices and returns.  Topics to be covered normally include: review on distribution and dynamic behaviour of financial time series, univariate and multivariate GARCH processes, long-memory time-series processes, stochastic volatility models, modelling of extreme values, copulas, realized volatility and correlation modelling for ultra high frequency data and continuous time models.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human-Computer Interaction', '649', 'CS', 'An introduction to the fundamental theories, methods, and research in the design and evaluation of description:  novel computational artifacts designed to meet real-world human needs.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Linear Algebra', '675', 'CS', 'Basic concepts and implementation of numerical linear algebra techniques and their use in solving application problems.  Special methods for solving linear systems having special features.  Direct methods: symmetric, positive definite, band, general sparse structures, ordering methods.  Iterative methods: Jacobi, Gauss-Seidel, SOR, conjugate gradient.  Computing and using orthogonal factorizations of matrices.  QR and SVD methods for solving least squares problems.  Eigenvalue and singular value decompositions.  Computation and uses of these decompositions in practice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Laboratory Characterization Methods', '226L', 'NE', 'This laboratory course introduces students to six materials characterization techniques employed routinely by nanotechnology engineers in their practices. Specifically, the six techniques are: Fourier-Transform Infrared (FTIR), Raman light-scattering, and ultra-violet and visible (UV-Vis) spectroscopies, ellipsometry, X-ray diffraction (XRD), and scanning electron microscopy (SEM). This course is intended to familiarize students with the instrumentation involved, prior to its application to nanomaterials in a follow-up laboratory course, NE 320L. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Simulation Methods', '451', 'NE', 'This course provides an introduction to and an overview of computational methods that are currently employed for the simulation of structural and bulk properties of matter, particularly as applied to physical and biological systems at the nanometer scale.  Topics to be covered in this course include energy functions and force fields, geometry optimization, normal mode analysis, and reaction--path techniques at the molecular level, and an introduction to the simulation of static and dynamic properties of substances via both Monte Carlo and molecular dynamics (MD) methodologies. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Nanoscale Simulations', '452', 'NE', 'Topics in this theme area may include: an overview of modern computational methods and algorithms in nanoscale materials, such as steered molecular dynamics, ab initio molecular dynamics, multiscale modelling, dissipative particle dynamics, transition path sampling, phase-field modelling, quantum simulations using Feynman path integral techniques, condensed-phase spectroscopy, linking of simulations to experiment, simulations and their practical applications. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to the Analysis of Spatial Data in Health Research', '836', 'STAT', 'The objective of this course is to develop understanding and working knowledge of spatial models and analysis of spatial data.  The course provides an introduction to the rudiments of statistaical inference based on spatially correlated data.  Methods of estimation and testing will be developed for geostatistical models based on variograms and spatial autoregressive models.  Concepts and application of methods will be emphasized through case studies and projects with health applications.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Pharmacokinetics', '609', 'PHARM', 'This course provides statistical hands-on knowledge for the implementation of nonlinear mixed effects (NLME) models in the analysis of population pharmacokinetic data. In order to provide a foundation for NLME, the content builds from simple to multiple linear regression models and to linear mixed effects models. Exploratory and descriptive analyses as well as model implementation will be taught in R and Phoenix.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Drug Development', '610', 'PHARM', 'This course will cover all aspects of drug development, from basic research through to post-marketing surveillance of drug safety and efficacy.  Faculty members from the UW School of Pharmacy and beyond will provide their expertise on aspects of the drug development process.  Students will gain valuable knowledge and appreciation of the drug development process from "bench to bedside".  Using real-life drug research problems, students will learn how identification of disease stat pathophysiology directs drug design, how medicinal chemistry optimizes lead molecule creation, how drug dosage forms are designed, pharmacokinetic studies, how animal models are used to test the efficacy and toxicity of lead compounds, the design and evaluation of clinical trials, drug manufacturing, perspectives from the pharmaceutical industry, as well as business and regulatory issues.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Language Education', '615', 'GER', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Language Education', '715', 'GER', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Working in Teams', '612', 'DEI', 'This course will cover the three key areas for working in teams:  communicating, differing and facilitating.  To master the art of managing and participating in high performing, problem-solving teams, team members must be aware of both their own and others strengths and weaknesses when working in teams.  They must also be aware of the dynamics of teams throughout the phases of a problem-solving cycle.  Theory on teams and on team development will be introduced, as well as the particulars of working in teams on early-stage innovation projects.  The course will then focus on deepening and practicing relevant skills.  It will begin with a self-assessment of personal capacities and of the distribution of skills in the team as a whole.  Following this, it will focus on deepening skills in a sequence which shadows the phase of the project on which the teams are working.  Students will be trained in the skills of reflective listening, problem identification and analysis, supportive listening and facilitation, creative brainstorming and alternatives generation, construction differing and choice, idea development and presentation, and team process evaluation.  The format of the course will combine short lecture with experiential skill-building exercises.  Coaches assigned to each team will support individual and team learning.  Students will be required to enter a "contract" relative to their team role and performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Media Solutions 1: Design Principles and Practice', '613', 'DEI', 'This course combines the creation of an analysis or digital design projects.  Students will be provided with a theoretical and critical framework in order to learn how to contextualize digital design within the broader social, cultural, political, economic, and global context.  Students will also receive hands on training in design processes, digital tools, as well as the principles of design.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Marketing', '614', 'DEI', 'The objectives of this course are to provide the students with a firm understanding of market strategy and the techniques used for marketing in a globalized context.  This course aims to instruct students in two primary, fundamental areas of marketing: (1) basic principles of marketing, which includes market segmentation strategies, the use of marketing channels, how to develop promotion and pricing strategies, and how to develop competitive advantage, and (2) how to leverage technology-driven, digital media for achieving marketing goals.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Media Solutions 2: Project Management', '623', 'DEI', 'This course will focus on managing production processes and resources for digital media projects, from initial planning and budgeting through the management of milestones, tasks, personnel, technical resources and delivery.  Students will receive training in initiating and tracking individual tasks within the overall structure of a project, learning how to adjust for change in project needs and available resources.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Business Innovation and Impact', '625', 'DEI', 'Changing media have a profound impact on our economy, governance, and community life.  Our institutions, our approaches to economic and cultural development, and traditional business models are challenged to adapt to the realities of an information-rich, technology-enabled society.  This course will provide a survey of the broad implications of evolving technology through research and case studies focusing o personal, institutional, and political choices.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ecosystem Assessment', '340', 'ERS', 'An applied ecology course for those interested in becoming professional ecologists. In keeping with the Ecological Society of Americas Professional Ecologist Certification and the Society for Ecological Restorations Certification Programme, intensive, multiple-weeks of field skill exercises are undertaken including advanced ecological sampling and experimental design, ecological sample analysis, use of provincially recommended protocols such as VSP (Vegetative Sampling Protocol), and intermediate to advanced taxonomic identification skills. May include certification and accreditation opportunities such as the Ontario Benthos Biomonitoring Network (OBBN) and the Ontario Stream Assessment Protocol and training in Electrofishing and Boating (for research). Location of the course will be within southern Ontario. [Note: Field trip fee normally is $700 +HST; it will not exceed $1000+HST. WHMIS required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Professional Conservation and Restoration Practice 1', '341', 'ERS', 'An applied ecology course for those interested in becoming professional ecologists. In keeping with the Ecological Society of Americas Professional Ecologist Certification and the Society for Ecological Restorations Certification Programme, an intensive, multiple-weeks field ecology project that students help design and then implement; students learn how to manage and perform ecosystem restoration and conservation projects. Students also will improve practical site inventory and assessment skills for restoration and conservation goals. Projects may involve site constraints, and potential for bioengineering, bioremediation, vegetation installation, and erosion-control measures. Location of the course will be within southern Ontario. [Note: Field trip fee normally $700+HST; will not exceed $1000+HST. WHMIS required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Aviation', '474', 'AVIA', 'An advanced special topics course offered in a particular branch of aviation, subject to availability of instructor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Independent Studies of Selected Topics', '475', 'AVIA', 'Individual study of special topics not covered in other aviation courses. Students will not be given permission to register for this course until a faculty member has agreed to supervise the study and the student has developed a brief outline of study to be approved by the Director of Aviation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Creative Thinking', '251', 'INTEG', 'An introductory, integrative course in the history, psychology, value and practice of creativity across a variety of domains, from scientific research to the fine and performing arts. Students will learn to identify and explain key ideas and historical figures in the study and practice of creative thinking. Theyll also learn to reliably generate novel ideas of value, both solo and collaboratively.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to User Experience Design', '210', 'GBDA', 'In this course students will learn the basic principles and practices of user experience design and testing. Students will learn how to plan, implement, and interpret tests conducted on digital design applications, focusing both on functionality and on the quality of the users interaction with these technologies. [Note: Offered at Stratford campus. Formerly GBDA 103.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Differential Equations (Advanced Level)', '251', 'AMATH', 'AMATH 251 is an advanced-level version of AMATH 250. This course offers a more theoretical treatment of differential equations and solution methods. In addition, emphasis will be placed on computational analysis of differential equations and on applications in science and engineering. [Note: AMATH 251 may be substituted for AMATH 250 whenever the latter is a requirement in an Honours plan. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sustainable Cities', '604', 'INDEV', 'This course surveys the dominant trends in human settlement since the industrial revolution.  Emphasis is placed on selected problems (e.g., provision of basic services such as water supply and sanitation, waste disposal, expanding ecological footprints) faced by cities of various sizes (from mid-sized to mega), the resources available to deal with them, and the new approached to sustainability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sustainable Cities', '650', 'PACS', 'This course surveys the dominant trends in human settlement since the industrial revolution.  Emphasis is placed on selected problems (e.g., provision of basic services such as water supply and sanitation, waste disposal, expanding ecological footprints) faced by cities of various sizes (from mid-sized to mega), the resources available to deal with them, and the new approached to sustainability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Warming and Climate Change', '201', 'SCI', 'This course will survey a broad spectrum of topics relating to global climate change, with an emphasis on the scientific approaches to address this issue. Topics covered will include natural and anthropogenic causes of climate change, monitoring methods, current and low emission alternatives of energy production.  The scientific, economic, public and political aspects of implementation of carbon capture and storage technology and other geo-engineering options to mitigate climate change will also be discussed.  [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Storytelling Design', '302', 'DAC', 'This course teaches students the principles and practices of telling stories using digital technologies to produce short narratives in audio and/or visual formats. Students will learn to develop and present story ideas, create storyboards, write short scripts, and then render their narratives using a variety of digital techniques and tools. [Note: No programming skills are required for this course.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Designing Screen-Based Media', '307', 'DAC', 'This course focuses on the creative, technical, and artistic potential of screen-based media and projection art for both public and online digital spaces. In the course students will explore the design affordances of different types of screen and projection media. Students will create projects that produce dynamic, interactive content for public audiences.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cinematic Art and Practice', '308', 'DAC', 'This course explores cinematic representation from an audio-visual production perspective. Students will learn different approaches to analyzing cinematic technique in a variety of contexts and genres. As a production-oriented course students will create videographic essays or other formal film project in order to analyze, interrogate, and extend forms of cinematic representation. [Note: No prior experience of audio, film, or video production is required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Paleopathology of Health and Disease', '305', 'ANTH', 'This course introduces students to the identification and interpretation of skeletal evidence for disease, ancient and modern concepts of health and disease, and current clinical interpretations of skeletal pathologies. Topics include pathology in archaeological remains and the process of developing a differential diagnosis of skeletal lesions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics of Sport', '254', 'ECON', 'The course will develop fundamental economic concepts in the context of the sports industry. The course begins with an investigation of some of the primary aspects of the way that the sports industry is organized. The course then considers labour economics in the context of sport. Finally, the course examines the issues that arise with the introduction of various levels of government and/or regulatory bodies in the sports industry, looking at competition between cities for sports teams.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Punishment and Society', '326', 'SOC', 'A critical criminological and sociological examination of theories and practices of punishment. This course will examine transformations in penal theory, penal management, and penal institutions and their social and policy implications.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Punishment and Society', '326', 'LS', 'A critical criminological and sociological examination of theories and practices of punishment. This course will examine transformations in penal theory, penal management, and penal institutions and their social and policy implications.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Migration and Legality', '342', 'SOC', 'This course introduces students to key concepts and case study research on the regulation of human migration and its exclusionary practices. It examines how state policies, laws, and international organizations control the mobility of migrants and refugees across regions and borders.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Migration and Legality', '342', 'LS', 'This course introduces students to key concepts and case study research on the regulation of human migration and its exclusionary practices. It examines how state policies, laws, and international organizations control the mobility of migrants and refugees across regions and borders.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Migration and Development', '349', 'SOC', 'This course explores theoretical perspectives and empirical studies on development and the related field of migration studies. Specific themes may include history and colonialism, seasonal and labour migration, forced migration, immigration policies and their social and economic implications, and transnationalism and diasporas.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Development', '451', 'SOC', 'This course explores various theoretical perspectives and case studies on development around the world. Through an in-depth examination of the socioeconomic and cultural consequences of development efforts across nations, students will have the opportunity to learn and think critically about global issues such as conflict and inequalities, alternative development, aid, post-colonialism, and climate change.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Theory and Popular Culture', '418', 'SOC', 'This course will critically assess contemporary approaches to the study of culture and media in sociology and related fields. The course will examine the production, mediation, and consumption of cultural artifacts, images, and spaces, and the problems of culture as ideology, reproduction, and resistance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Humanitarianism', '452', 'SOC', 'This course examines how humanitarianism and its actors shape human displacements and mobilities, marginalized groups, and civil conflicts. Students will learn how to use and apply key concepts related to humanitarianism, and will study their implications for problem solving, developing social policy, and conveying the limits of the field of humanitarianism.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Childrens Rights in Canada', '210R', 'SDS', 'This course examines childrens rights from a moral and comparative legal perspective. Students explore the welfare and developmental interests of children; the corresponding duties of parents, custodians, educators, and social workers; and the nature and scope of public educational authority in the common law jurisdictions of Canada.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Childrens Rights in Canada', '273', 'LS', 'This course examines childrens rights from a moral and comparative legal perspective. Students explore the welfare and developmental interests of children; the corresponding duties of parents, custodians, educators, and social workers; and the nature and scope of public educational authority in the common law jurisdictions of Canada.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Globalization and Social Development', '388R', 'SDS', 'This course explores the rise of globalization; its impact on economics, politics, society, and culture; and its implications for national development and social policies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Islamic and Arab Cultures', '375R', 'SI', 'This course allows for in-depth study of selected topics in Islam, Islamic cultures, or Arab cultures. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Effective English Pronunciation', '103R', 'EMLS', 'Designed specifically for students for whom English is not the first language, this course has an intensive focus on the structure of the English sound system with a view to improving pronunciation. Vowel and consonant sounds are practised as well as the phonetic elements above those sounds, such as stress, intonation, and rhythm. The goal is for students to identify their problem areas and to develop strategies to improve their comprehensibility.  [Note: Not open to fluent speakers of English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Earth and Environmental Sciences', '491', 'EARTH', 'A lecture course offered in a particular area of Earth and Environmental Sciences, subject to availablity of instructor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Demography', '418', 'PLAN', 'This course develops the capacity of students to apply methods of spatial demography. Spatial demography refers to the statistical study of human population using spatial methods for analyzing demographic data. It can provide insights into the understanding of geographic variations of populations characteristics, which in turn can help to make better plans in building the environment. Through this course, students will learn the basic concepts, data sources, data issues, methodologies, and applications of spatial demography.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Spatial Demography', '428', 'GEOG', 'This course develops the capacity of students to apply methods of spatial demography. Spatial demography refers to the statistical study of human population using spatial methods for analyzing demographic data. It can provide insights into the understanding of geographic variations of populations characteristics, which in turn can help to make better plans in building the environment. Through this course, students will learn the basic concepts, data sources, data issues, methodologies, and applications of spatial demography.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Tools for Sustainable Communities', '451', 'PLAN', 'This seminar course will focus on conceptual frameworks and tools intended to guide communities to a more sustainable condition. Examples of possible concepts covered in the course are material and energy flows, resilience, adaptability, passive survivability, sufficiency, biophilia, and localization. Course focus will vary from year to year. Recent topics have included The Natural Step, transition towns, LEED-ND, microclimate design, biophilia, and community energy systems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ten Days That Shook the World', '109', 'HIST', 'Focusing on ten different days that forever transformed the worlds history, this course introduces students to moments of dramatic change in diverse time periods and places. Topics may include the Fall of Ancient and Modern Regimes; the Age of Discovery; Religious Cataclysm; Revolutionary Wars; Technological, Intellectual, and Scientific Inventions. Students are introduced to the differing methods that historians use to understand the past.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Systems of Peace, Order, and Good Governance', '601', 'PACS', 'This course analyzes the roles and responsibilities of civil society, the market, and the state as agents capable of creating just and humane structures.  Case studies reveal how individual scan leverage collaboration among all sectors of society to advance positive systemic positive change.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Practice of Peace', '602', 'PACS', 'This course examines the characteristics and skills of effective peace practitioners, with particular attention to ways in which disciplines of peacemaking can be cultivated by individuals and nurtured by communities.  While investigating various roles of the third party, students will explore the practical responsibilities (or tasks) and functions of peace practitioners as well as the core values and qualities that may make them more effective interveners locally, nationally or globally.  Students will be introduced to peace research methodologies, research ethics, risk management, as well as various forms of communication used in documentation, analysis, and advocacy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Building Civil Society', '603', 'PACS', 'This course explores operational aspects of civil society organizations such as visionary leadership, goal setting, evaluation, report writing, financial management, applied research skills, and human resource management.  Students will also examine codes of conduct and practice, including rules, laws and customary understandings that guide the work of civil society organizations.  Students will research contending views of civil society organizations and their complex relationships with government and business, thereby developing a philosophical and ethical framework for evaluating civil society action.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Conflict Analysis', '604', 'PACS', 'This course examines theoretical and practical frameworks for understanding conflict, with particular attention to structures and dynamics inhibiting peace.  The course provides students with some of the analytical skills needed to understand how conflicts develop and escalate, to identify factors that can lead to or sustain violence, and to map root causes of conflict (e.g., human rights violations, needs deprivation, cultural and religious differences, inequality, resource misuse and environmental degradation) at interpersonal, intergroup, and international levels.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Conflict Transformation and Peacebuilding', '605', 'PACS', 'This course explores the theoretical and practical foundations of various approaches to working with conflict to advance positive goals such as social equity and reconciliation.  Attention will be given to a range of conflict resolution methods and practices (facilitation, negotiation, mediation, arbitration, adjudication) as well as to principles of restorative justice and dynamics of collective peacebuilding practice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Nonviolent Movements', '610', 'PACS', 'Through comparative case studies, this course examines contemporary nonviolent movements that illustrate pacifist and other nonviolent strategies for advancing social justice and other high-value political goals.  Local, national and transnational campaigns that seek to shape the agenda for global change are examined alongside movements of more limited scope and ambition (e.g., national liberation movements, civil rights campaigns, struggles for democracy).  Throughout, attention will be given to trends in practice and to debates concerning the effectiveness, ethical significance, and current relevance of nonviolent change methods.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reconciliation', '611', 'PACS', 'This course uses case studies to analyze the complex issues of trauma, abuse, historical injustice and violence - and investigates approaches to healing, forgiveness and reconciliation (including memory, testimony, tribunals and reparation/atonement).  The course explores theoretical and practical models for transforming relationships, including indigenous and non-formal mechanisms employed internationally, and teaches skills that are employed by effective agents of reconciliation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Healthcare Delivery in Rural and Underserved Populations', '373', 'PHARM', 'This course will develop students understanding of the unique challenges and opportunities in providing healthcare to rural and underserved populations. Through lectures and field work, students will examine topics such as healthcare disparities, interprofessional collaboration, the role of the rural pharmacist, and underserved patient case studies. [Offered: W,S,F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Complementary and Alternative Medicine', '374', 'PHARM', 'This complementary and alternative medicine (CAM) course is designed to give the student an overview of complementary and alternative healing practices and will heighten student awareness that CAM is multifactorial and often includes belief systems and practices other than drug therapy. [Offered: W,S,F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Substance Abuse and Chemical Dependency', '375', 'PHARM', 'This course serves as an overview of addiction, chemical abuse, and chemical dependency and how pharmacists can impact those affected. Topics include prevention, identification, treatment options, clinical aspects of treatment, and an understanding of support systems available for those in recovery. [Offered: W,S,F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Practicing Pharmacy with Diverse Populations', '376', 'PHARM', 'This course will provide students with tools to optimize patient care when working with diverse communities. Diversity will be defined in its broadest sense encompassing a discussion in how differences in ethnicity, gender, sexual orientation, physical or mental ability, etc. impact patient care. Students will learn about the unique needs of different populations and through a combination of lectures and case studies learn how to sensitively and effectively work with a variety of patients. [Offered: W,S,F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Drug-Induced Disease', '377', 'PHARM', 'Drug-Induced Disease will explore the most appropriate interventions for prevention, detection, and management of drug-induced diseases. Emphasis will be placed on the role of the pharmacist as a proactive practitioner in minimizing morbidity and mortality from drug-induced diseases. [Offered: W,S,F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Pharmacoepidemiology and Pharmacy Practice', '469', 'PHARM', 'The course aims to promote an enhanced understanding of conceptual and methodological issues essential for effective critical appraisal of the relevant clinical literature and ultimately, improved patient care. Key topics to be covered will include basic concepts of epidemiology; data sources and measures; study design and methodological issues in pharmacoepidemiology; and applications relevant to population-based research and pharmacy. [Offered: W,S,F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Community Practice in a Changing Environment', '472', 'PHARM', 'This course will prepare pharmacy students to provide services in an advanced community pharmacy practice environment. Students will explore, critique, and develop innovative clinical services for the community setting. Students will also develop change management and critical thinking skills. [Offered: W,S,F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Strategies and Skills for Academic Success', '101', 'UNIV', 'The goal for this course is to help students become successful independent learners. It takes a modeled approach to teaching learning strategies to students who struggle to meet the demands of a university curriculum. In the lectures, students will be taught various learning strategies that they will be able to apply to successfully meet the demands of their other courses. Students will then apply these modeled strategies in their other courses during a weekly application seminar. Students will be expected to demonstrate their understanding of the material through a variety of assignments. [Note: A grade of at least 60% is required to pass this course.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Culture, Religion, and Peacebuilding', '612', 'PACS', 'This course explores cultural, religious and identity-based dimensions of conflict and conflict resolution, examining major patterns of human difference and their implications for contemporary peacebuilding practice.  Case studies, simulations and role plays are used to expose students to the practical reality of building a common peace in the midst of diversity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Peace and Conflict Studies', '620', 'PACS', 'A seminar course investigating special issues related to peace and conflict.  Content may vary from year to year.  Course may be repeated if course topic differs.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Peace Research', '621', 'PACS', 'This course requires completion of a research project that develops a students capacity to do research for an applied objective.  The research may be to support a grant proposal, document and contextualize a need or a human rights abuse, analyze what various agencies are doing in the face of common challenges, or write an advocacy brief to a government.  Students are expected to demonstrate a high level of competence in research analysis and writing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Internship', '625', 'PACS', 'The internship allows students to engage in experiential learnig with a research institute, non-governmental organizaton or other agency/entity that deals with peace and conflict issues.  Students are expected to read relevant texts before, during and after the field study, to engage in stubstantial research on th eissues addressed by the host agency/entity and to submit a report reflecting on what the field study/internship revealed about the integration of peace studies theory and practice.  Field study placements may be either in Canada or international contexts.  Departmental consent is required.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Conflict Resolution Skills Training', '626', 'PACS', 'This course provides a framework for students to do academic work related to specific conflict resolution skills training they have received through workshops sponsored by the Center for Extended Learning or the Certified Program in Conflict Management or other credible training organizations. Students will complete this additional academic work as a Directed Study supervised by Peace and Conflict Studies faculty.  This course is offered on a credit/no credit basis.  The course may be repeated once.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Groups and Rings', '347', 'PMATH', 'Groups, subgroups, homomorphisms and quotient groups, isomorphism theorems, group actions, Cayley and Lagrange theorems, permutation groups, Sylow theorems, and the fundamental theorem of finite abelian groups. Elementary properties of rings, subrings, ideals, homomorphisms and quotients, isomorphism theorems, and polynomial rings.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fields and Galois Theory', '348', 'PMATH', 'Unique factorization domains, Gausss lemma, and Eisensteins criterion. Fields, algebraic and transcendental extensions, minimal polynomials, splitting fields, and the structure of finite fields. Galois theory. Solvable groups and the insolvability of the quintic.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Representations of Finite Groups', '445', 'PMATH', 'Basic definitions and examples: subrepresentations and irreducible representations, tensor products of representations. Character theory. Representations as modules over the group ring, Artin-Wedderburn structure theorem for semisimple rings. Induced representations, Frobenius reciprocity, Mackeys irreducibility criterion.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Commutative Algebra', '446', 'PMATH', 'Module theory: classification of finitely generated modules over PIDs, exact sequences and tensor products, algebras, localisation, chain conditions. Primary decomposition, integral extensions, Noethers normalisation lemma, and Hilberts Nullstellensatz.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics', '699', 'PS', 'This course, which may be offered periodically in place of one of the required program courses, addresses special topics related to government and public service. This course is restricted to students enrolled in the Master of Public Service program.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Clinical Practice in Mental Health and Addictions', '609R', 'SWK', 'This course aims to equip students to provide sensitive and effective help to clients by presenting material concerning a range of mental health problems, assessment and treatment. The course considers clinical social work approaches across the continuum of mental health and addiction. Attention is focused on how social workers assess, document, support and intervene with consideration of the client, their family and social environment. (Note: This is an online course).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Health Policy', '601R', 'SWK', 'This course offers critical analysis of health policy formulation, implementation, and evaluation related to population health initiatives and health care delivery.  The course includes discussion of the role of various regional, provincial, and national agencies in health care policy formation.  It examines various health care systems, and their funding as well as investigates how the Canadian healthcare system compares with systems in other countries.  (Note: This is an online course).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Health, Equity, and Social Justice', '600R', 'SWK', 'This course explores health inequity within a Canadian context. It engages critical debates on key concepts related to health inequity including diversity, equity, access, and inclusion. The ultimate goal of this examination is to support students understanding of the social and structural determinants of health and the ways in which uneven access to these determinants are implicated in health inequities for equity-seeking groups, and consequently, social injustice. The course centers the learner as a pivotal agent in changing social structures that sustain health inequities. (Note: This is an intensive on campus course with an
online component).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Work Practice in Health', '602R', 'SWK', 'This course examines practice models and multi-level methods of intervention for effective social work practice in health care, including health promotion, disease prevention, assessment treatment, rehabilitation, continuing care, and discharge planning within the context of social, economic, environmental, and cultural variations. The distribution and determinants, as well as psychological and behavioural aspects of health and disease across the life span are addressed. Practice models incorporate competence and empowerment as central themes. Different practice models of care are explored within the context of socio-cultural factors that inform health and well-being. Differences between the concepts of illness and disease are explored within the healthcare narrative. Resource allocation in the management of chronic and complex health conditions is examined, and interventions that support individual and family capacity to adapt to health crises or chronic health conditions are considered.
(Note: This is an online course).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Work Leadership in Health Care', '603R', 'SWK', 'This course examines the theories and practice skills of effective social work leadership within health care contexts, social service organizations, and community service delivery systems. The effective and responsible use of human and material resources are emphasized through a social justice lens, while knowledge and skills for ethical and value-based leadership in staff management and organizational change processes, coordination of inter-professional teams, creating a positive organizational culture, and policies to address the social determinants of health are addressed. Supervision will be examined as one form of leadership, where functions and skills of supervisory practice and the organizational contexts will be considered. Learners will be invited to critically examine and expand their own leadership style and skills via an inquiry-based approach, while developing foundational knowledge and skills for best practices in organizations. Development of styles of leadership, management and supervision that are respectful, reflective, and relational in nature are encouraged. (Note: This is an intensive on campus course with an online component).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Evaluation of Health and Human Service Programs', '604R', 'SWK', 'This course introduces students to theoretical and practical aspects of program evaluation.  Students learn about the application of data collection skills to all phases of developing a health or human services program or service innovation, from needs assessment to analysis of findings to implementation of changes based on results.  Students learn to appreciate how these skills can be used as practical tools for identifying health and human services problems, for developing and implementing programs including taking a reflective practice approach, ensuring equity and fairness in program delivery (i.e., combating disparities), and generally promoting health and human services through effective and efficient programming.  (Note:  This is an online course).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Knowledge Mobilization and Evidence-Based Practice', '605R', 'SWK', 'The goal of knowledge mobilization is to make research in health and social science more useful to policy, practice, and the public.  In this course students can learn how to gather, evaluate, synthesize and summarize scholarly knowledge in ways that meet the needs, timeframes, cultures, and realities of stakeholders. Activities include framing academic research in the context of public issues and creating knowledge products for practitioners and policy-makers in the students areas of interest.  (Note:  This is an online course).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Health Issues and Ethics', '608R', 'SWK', 'This course considers the nature of health itself, and explores the complexities of a health care system that corresponds with social work values and ethics while remaining sustainable. Three related themes serve as substrates for the semester: the nature of health, delivery of health care, and specific health experiences that are characterized by inequity, societal change, and/or a
particular ethical dilemma. Issues such as how health and illness are defined, cultural safety, the roles of preventive and curative approaches, and the impacts and ethics of new technologies and societal change will be addressed. The course critiques public, private, and individualized models of health, focuses on community health care, and examines the particular challenges of marginalized communities. (Note: This is an online course).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Symmetric Functions', '631', 'CO', 'The ring of symmetric functions, standard bases, the Hall inner product.  Young tableaux.  The Robinson-Schensted-Knuth correspondence, the hook-length formula, the Jacobi-Trudi formula, the Pieri rule, the Littlewood-Richardson rule.  Representation theory of the symmetric groups.  Enumeration of plane partitions.  Enumeration of maps on surfaces.  Other topics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Number Theory for Teachers', '640', 'MATH', 'This course explores the many fascinating properties of the natural numbers. Topics include: the Euclidean algorithm, congruences and modular arithmetic, primitive roots and quadratic residues, sums of squares, multiplicative functions, continued fractions and Diophantine equations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Mathematical Connections', '674', 'MATH', 'This course is intended to give the student insight to an important area of mathematics and how it connects with problems in the real world. Each topic consists of one six-week module. The emphasis will be on how the mathematics is used in a real world context.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Rhetorical Theory and Criticism', '788', 'ENGL', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Writing Studies', '789', 'ENGL', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Development Governance', '662', 'GGOV', 'The course explores theoretical perspectives on the global governance of development, with critical attention to how processes of global development shape local environments and their inhabitants, challenge notions of state sovereignty and territory, and engender diverse responses to regimes of control.  Cross-disciplinary perspectives will enable students to engage with a wide range of sociological, ethnographic, and political analyses of development through case studies and themes.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Development Governance', '781', 'SOC', 'The course explores theoretical perspectives on the global governance of development, with critical attention to how processes of global development shape local environments and their inhabitants, challenge notions of state sovereignty and territory, and engender diverse responses to regimes of control.  Cross-disciplinary perspectives will enable students to engage with a wide range of sociological, ethnographic, and political analyses of development through case studies and themes.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Security and Regulation', '783', 'SOC', 'An examination of criminological and sociological theory and research on the problem of security and related regulatory agencies.  Various forms of regulation will be examined (e.g. discipline, surveillance, control, security detention, security intelligence, risk management) as they apply to particular bodies, mobilities, borders, and private and public spaces.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Religion in North America', '705', 'RS', 'This course examines the history of varius religions and religious phenomena in North America, with attention to such factors as colonization, immigration, gender, race, class, theology, and praxis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Business French 2', '355', 'FR', 'A French language course designed to enhance written and oral expression through the study of economic and business topics in the social and economic contexts of Canada and France.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Developmental Neuropsychology', '335', 'PSYCH', 'Developmental neuropsychology is a field in which brain-behaviour relationships are examined in the context of typical and atypical development. This course focuses on the structural development of the brain, the emergence of functional brain systems, and the neuropsychological underpinnings of childhood brain disorders such as phenylketonuria, autism, epilepsy, and stroke. Emphasis is placed on the integration of theoretical perspectives and empirical research in neuropsychology with clinical practice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Theories of Political Economy', '300', 'PSCI', 'What is the relationship between politics and economics, power and wealth, states and markets? Students will study influential historical and contemporary works that shape political economy debates in the contemporary world.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Politics and Business', '403', 'PSCI', 'Course topics vary. Topics may include accumulation, crisis, and the state and the ups and downs of capitalism. [Note: This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('China and Global Governance', '480', 'PSCI', 'Has Chinas rising power changed it from a rule-taker to a rule-maker in global governance? This course examines Chinas involvement in global governance on a range of issues (e.g., security, economy, human rights, the environment, and social policy) in light of Chinas evolving preferences, capabilities, and relations with the rest of the world.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applications of Analytics to Business', '483', 'AFM', 'This course explores the metrics, quantitative analyses, and technologies used across a range of applications where an investigation of past business performance provides insights that drive business decisions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Management Control Systems', '484', 'AFM', 'This course provides an in-depth examination of operations management and related issues in performance evaluation and control for various business sectors (e.g., manufacturing, service, and IT intensive).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Witches, Wives, and Whores', '347', 'HIST', 'This course explores the ways in which Early Modern European women experienced, participated in, shaped, and responded to the world they inhabited. It investigates the ways in which women negotiated the Early Modern world as it unfolded in the Renaissance, the Reformation, European encounters around the world, and the challenges of everyday life.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Witches, Wives, and Whores', '347', 'GSJ', 'This course explores the ways in which Early Modern European women experienced, participated in, shaped, and responded to the world they inhabited. It investigates the ways in which women negotiated the Early Modern world as it unfolded in the Renaissance, the Reformation, European encounters around the world, and the challenges of everyday life.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Project Management', '651', 'MSCI', 'This course will provide students with an understanding of Project Management Fundamentals while allowing them to gain practical experience of International Project Management and Development in a live practical project.  Topics covered include: Project Management Methodologies, Project Managers Role, Managing Project Stakeholders, Scope, Quality and teams; Project Risk Management, Procurement, Leadership, and Ethics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('International Business Management', '652', 'MSCI', 'Companies today confront an increasing array of choices regarding markets, locations for key activities, organizational structures, and processes for managing across borders.  This course provides students with the conceptual tools necessary to understand and work effectively in todays interconnected world by developing strategic perspectives that link the global environment and the capabilities and position of the firm.  Topics include Globalization, International Strategy Development, Mode of Entry and Alliance Structures, Organizational Design, Structure and Control, Human Resources Management, Cross-cultural Issues, Ethics in International Business, International Marketing and Branding, International Operations, Exporting and Importing, and Global Corporate Sustainability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reading Course in Mathematics for Teachers', '698', 'MATH', 'Students will undertake a reading, research and writing project on a mathematical topic of interest to teachers.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('PSI Condensed Matter Physics', '611', 'PHYS', 'Theories of Condensed Matter Physics emphasizing the consequences of broken symmetries and the resulting collective modes which emerge, including broken translational symmetry and phonons, broken spin rotational symmetry and magnons, electronic properties of normal metals, and broken U(1) gauge symmetry in superconductors, excitations above the gap and collective modes in superconductors.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Marketing in the Digital World', '304', 'GBDA', 'This course focuses on best practices in marketing and communication within a variety of sectors, while examining how emerging technologies present new opportunities to engage audiences. [Note: Offered at Stratford Campus.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Entrepreneurship Principles and Practices', '200', 'ARBUS', 'The role of the entrepreneur, the entrepreneurial firm, and the creative society are examined. Students are exposed to the practices that enable workplace creativity and innovation with exposure to leadership theory and project management. [Note: Additional fee may be required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Professional Translation (Chinese to English)', '390R', 'CHINA', 'This course is for students with a strong command of Chinese and English who seek to learn the basics of professional translation between the two languages. Focus is on translation involving government and business documents, as well as news stories.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Radical Ideology and Social Policy', '431R', 'SDS', 'From a critical and comparative perspective, students explore the impact of radical political ideologies on U.S. and Canadian political institutions and processes. Students consider the potential implications of an ideologically polarized political landscape for social policy and social development.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religion and Japanese Film', '275R', 'EASIA', 'The course examines how religious beliefs and values in Japan are explored in the medium of film. [Note: This course fulfils an Area 3 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Religion and Japanese Film', '275', 'RS', 'The course examines how religious beliefs and values in Japan are explored in the medium of film. [Note: This course fulfils an Area 3 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visions of Israel in Judaism: From Biblical to Modern Times', '230', 'RS', 'This course surveys the significance of the land of Israel in Judaism from historical, textual, and religious perspectives. Topics covered include the politics of Ancient Israel; the concept of Israel in prayer and the rabbinic and medieval Jewish imagination; portrayals of Israel in Christian and Muslim texts; and the origins, visions, and challenges of Zionism and the modern State of Israel. [Note: This course fulfils an Area 1 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visions of Israel in Judaism: From Biblical to Modern Times', '215', 'JS', 'This course surveys the significance of the land of Israel in Judaism from historical, textual, and religious perspectives. Topics covered include the politics of Ancient Israel; the concept of Israel in prayer and the rabbinic and medieval Jewish imagination; portrayals of Israel in Christian and Muslim texts; and the origins, visions, and challenges of Zionism and the modern State of Israel. [Note: This course fulfils an Area 1 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Study Abroad', '365', 'GBDA', 'Study abroad for academic transfer credit during a fall term. Discussion and approval of options with the advisor is required. Credit for specific courses will be evaluated following receipt of a transcript of academic results and supporting course outline documentation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Study Abroad', '465', 'GBDA', 'Study abroad for academic transfer credit during a fall term. Discussion and approval of options with the advisor is required. Credit for specific courses will be evaluated following receipt of a transcript of academic results and supporting course outline documentation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intercultural Skills', '8', 'PD', 'This course provides students with the knowledge and skills needed to work effectively in todays culturally and linguistically diverse workplaces.  Emphasis is on understanding how cultural values shape our everyday behaviours, attitudes, thinking processes, and communication styles.  Students will examine their own cultural preferences, become familiar with key models used to described cultures, and apply intercultural concepts and tools to analyze cultural issues in the workplace. [Note: Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Timber: Design, Structure and Construction for Engineers', '277', 'ARCH', 'Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural timber systems. Topics such as flexural, compression and truss members, connections, and plywood construction are studied using calculations, design aids, rules of thumb and the latest CSA design standards. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Approaches to Architecture and Urbanism', '328', 'ARCH', 'The course develops a trans-disciplinary overview to design approaches in architecture and urbanism using image- and symbol-making as key activities in designing environments. The course evaluates the quality of results and the quality of process of design of environments. The students address topics within a wide range of subject matter groupings, from sustainable architecture to engineering, landscape and infrastructure, to branding and aesthetic practices, to design within the municipal political forum. Students present seminars on diverse categories of design as a means to develop a personal design ethos. The final submission includes a set of drawings, diagrams and images that represent a set of design principles allowing students to consciously take a position in the world as a designer. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Working with Wood', '331', 'ARCH', 'This course focuses on understanding the premise that hand skills can facilitate and help inform conceptual and academic skills. The content provides a broad-based introduction to woodworking equipment, its fabrication methods and methodology. A combination of lectures and hands-on experiences are used to equip students with the basic skills and knowledge required to conceptualize, fabricate, and finish a designed object from wood. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Competitions in Architecture', '346', 'ARCH', 'This course provides an opportunity for the student to independently engage in the respected tradition of the Architectural Competition. The competition entry and accompanying research paper must focus on the use of architectural precedents as the basis for the creation of typologically based propositions. Submission to the external competition is mandatory, the timing and detailed requirements of which will determine the personalized academic requirements for this course. [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Philosophy in Architecture', '347', 'ARCH', 'The course introduces the study of philosophical issues as they pertain to architecture, urbanism, image-making and symbol manipulation. The course focuses on the work of a selection of living and recent philosophers whose issues are relevant to questions of everyday 21st century life, with reference to their basis in the history of philosophy. Work by philosophers such as Giorgio Agamben, Kwame Anthony Appiah and Judith Butler on ethics, Gilles Deleuze on meaning, Friedrich Kittler on media and technology, Jean-Luc Nancy on community and Slavoj Zizek on psychoanalysis and political economy, as well as Canadian scholars such as Marshall McLuhan and Harold Innis is addressed. Students participate in a seminar format with a series of presentations linking their design projects with philosophical issues, and submit a final project making use of text and diagrams. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Irrational and Rational Choices in Politics', '244', 'PSCI', 'Why do some powerful dictators allow independent legislatures and parties to exist? Why are the frontlines of some civil wars spared from violence? Why do the same people sometimes choose language to define their ethnic identity but then choose religion at other times? Students will examine apparently random, puzzling, and even seemingly irrational choices made in political life. This course uses a rational choice framework to provide a unified explanation for some of these puzzling questions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transnational Migration', '375', 'PSCI', 'This survey of transnational migration in the contemporary moment examines the reasons people have migrated voluntarily (e.g., as migrant workers) and involuntarily (e.g., as refugees), and how international and domestic law and policies affect, enable, and/or criminalize the movements of people across state borders.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transnational Migration', '365', 'LS', 'This survey of transnational migration in the contemporary moment examines the reasons people have migrated voluntarily (e.g., as migrant workers) and involuntarily (e.g., as refugees), and how international and domestic law and policies affect, enable, and/or criminalize the movements of people across state borders.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Literature of the United Kingdom and Ireland', '460D', 'ENGL', 'A study of the contemporary literatures of the United Kingdom and Ireland, including such writers as Byatt, Boland, Drabble, Heaney, Hughes, Rushdie, and Stoppard.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '3', 'WKRPT', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student during the work term.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '4', 'WKRPT', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student during the work term.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Who are the Mennonites?', '125', 'MENN', 'This course is an interdisciplinary introduction to the range of Mennonite identities, beliefs, practices, and cultural expressions locally and globally. Themes such as Mennonite history, cultural diversity, theology, religious life, peacemaking, community development, music, literature, and artistic expression will be explored. [Note: Formerly ARTS 125.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Trauma, Healing, and Social Transformation', '331', 'PACS', 'This course examines how attention to trauma and healing can inform processes to transform conflict, violence, and injustice. With case examples from domestic abuse to colonialism and police violence, students explore theories and approaches to trauma and healing to better understand pathways to personal and social transformation and how they can be applied in various contexts. Topics include causes, types, and impacts of trauma; the role of trauma in cycles of violence; and frameworks for healing informed by contemporary social justice movements.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Welcome to Latin America', '150', 'SPAN', 'Discover the vibrant diversity of Latin American cultures and societies from their genesis in conquest and colonization through to independence, and the post-colonial quest for identity and self-determination. [Note: Taught in English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Interdisciplinary Design Project 1', '403', 'GENE', 'This is the first course in a two-course sequence where students from more than one engineering plan work together to solve an interdisciplinary engineering problem.  In the course they will formulate and communicate an interdisciplinary engineering problem or opportunity; develop a feasible design project proposal and plan; generate feasible solutions that address the formulated problem; evaluate alternatives and identify preferred solution; address safety, regulatory, sustainability and professional ethics requirements, as appropriate; effectively manage design project technical and non-technical risks using project management tools and techniques; deliver a report and/or a presentation that summarizes the work completed; work effectively as an interdisciplinary team member and/or team leader. [Note: Permission of the instructor is required]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Interdisciplinary Design Project 2', '404', 'GENE', 'This is the second course in a two-course sequence where students from more than one engineering plan work together to solve an interdisciplinary engineering problem.  At the end of this course students will be able to re-formulate and re-communicate an engineering problem or opportunity (as appropriate); revise and communicate a feasible design project plan; revise solutions that address the formulated problem (as appropriate); implement and verify design solutions by analysis, test or prototype; communicate design solutions using accepted data formats, working documents and oral presentations; address safety, regulatory, sustainability and professional ethics requirements, as appropriate; effectively manage and communicate design project plans, technical and non-technical risks, using project management tools and techniques; work effectively as an interdisciplinary team member and/or team leader. [Note: Permission of the instructor is required]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary German Language and Culture', '211', 'GER', 'This course develops reading, writing, and oral skills, and intercultural competence. [Note: Students may take GER 201 and 211 concurrently.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Modern Methods in Biomechanical Modeling, Kinematics, and Kinetics', '613', 'KIN', 'Biomechanics research typically requires the integration of experimental data collection and theoretical modeling approaches to answer complex questions.  The purpose of Kin 613 is to sensitize incoming graduate students to the range, biological basis, and practicality of using or developing mathematical models for biomechanical investigations.  This contrasts with KIN 612, which focuses on promoting rigorous experimental data collection and biological signal interpretation, or Kin 611, which seeks to develop the historical foundations of biomechanical research and promote critical reading of the scientific literature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Neuroscience', '552', 'SYDE', 'Introduction to modelling and analysis of neurophysiological systems. Models of synaptic plasticity and learning, with focus on the similarities and differences between neuroscience and deep learning. [Note: For systems design engineering students: It is recommended that one of BIOL 273, 376, 377 or BME 284 or SYDE 384/584 be taken before or concurrently with SYDE 552. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Neuroscience', '487', 'BIOL', 'Introduction to modelling and analysis of neurophysiological systems. Models of synaptic plasticity and learning, with focus on the similarities and differences between neuroscience and deep learning. [Note: For systems design engineering students: It is recommended that one of BIOL 273, 376, 377 or BME 284 or SYDE 384/584 be taken before or concurrently with SYDE 552. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Information Technology for the Health Professional', '613', 'HLTH', 'This course introduces health professionals to the conceptual and physical building blocks of large scale distributed health information system. Network architectures, web architectures, distributed processing, securing information, database structures, and other technical topics are covered with a specific focus on the concepts that need to be understood by health professionals to facilitate interaction with technical system specialists.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Requirements Specification and Analysis in Health Systems', '615', 'HLTH', 'This course introduces students to the requirements of definition phase of software development. Models, notations, and processes for software requirements identification, representation, validation, and analysis are discussed, as are mechanisms to evaluate the efficacy and efficiency of health information systems.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Decision Making and Systems Thinking in Health Informatics', '616', 'HLTH', 'This course focuses on the process by which decisions are made and how decision-making processes can be codified and machine supported. It will include topics such as reasoning and inference, techniques for managing uncertainty and information representation. The course is anchored in a systems thinking approach to problem solving.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Ultrasonics', '607', 'ECE', 'This course gives an in-depth coverage of technical concepts that are fundamental to the use of ultrasound in biomedicine. Concepts to be addressed include: acoustic wave propagation theory, field profile in ultrasound imaging, principles of ultrasound-based flow estimation, and the biophysical foundations of therapeutic ultrasound. The overall aim of this course is to equip students with essential foundational knowledge in biomedical ultrasonics, starting from first principles in wave physics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Software Reliability Engineering', '654', 'ECE', 'The course consists of two related parts.  The first part deals with the engineering of reliable software.  It introductes basic software reliability concepts, descritbes relevant models and discusses processes for engineering of reliable software, including schemes and patterns for the design of reliable and fault tolerant software.  The second part addresses development of secure software.  It presents key software security concept, techniques and models, overviews major software security vulnerabilities and their exploitation, and considers processes for development of secure software.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Protocols, Software and Issues in Mobile Systems', '655', 'ECE', 'This course is concerned with the architecture, protocol and software aspects of mobile systems.  The topics to be discussed include mobile communication and computing systems, supporting ad hoc networks, mobile network and transport layers, wireless application protocol, support for mobility, service advertisement and discovery in mobile systems, and emerging issues such as enviornment-aware software and low-power protocols and software.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Database Systems', '656', 'ECE', 'This course covers data models, file systems, database system architectures, query languages, integrity and security and database design.  Students attending this course should have at least a 4A level Electrical Engineering or Computer Engineering background.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Tools of Intelligent Systems Design', '657', 'ECE', 'Conventional approaches for tackling complex systems are usually implemented under the assumption of a good understanding of the process dynamics/functionalities and its operating environment.  These techniques fail, however, to provide satisfactory results when applied to ill-defined processes (for which analytical and experimental modeling may not be easily obtained) that may operate in unpredictable and possibly noisy environment.  Recent developments in the area of intelligent systems and soft computing have presented powerful alternatives for dealing with the behavior of this class of systems.  This course outlines fundamentals of soft computing based design approaches using such tools as approximate reasoning, fuzzy inferencing, neural networks, evolutionary algorithms, and neuro-fuzzy systems.  Fundamentals and advances on these procedures are outlined along with their potential applications to various real world applications in virtually most fields of engineering including pattern recognition, system planning, classification, power generation, intelligent transportation, systems and control, intelligent mechatronics, optimization, communication, robotics and manufacturing, to name a few.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physics and Models of Semiconductor Devices', '630', 'ECE', 'Basics of semiconductor physics.  Physical principles and operation of p-n junctions, metal/semidconductor contacts, MOS capacitors, MOS field-effect transistors, and related optoelectronic devices.  Short-channel MOSFETs, modern MOSFETs, and future transistor technologies.  Introduction to device simulators and SPICE models.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Component Based Software', '658', 'ECE', 'Building large-scale and complex software systems from available parts with the goal of increasing return on investment, decreasing time to market, and assuring quality and reliability.  The course covers the basic software component concepts, overview of advanced topics on software components and component-based software engineering from research and practice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('China and Global Governance', '619', 'PSCI', 'This course examines the evolution of Chinese involvement in global governance across different issue areas.  We discuss Chinese perspectives on the existing governance mechanisms, and Chinas role in preserving or changing these mechanisms.  We will also explore how Chinas involvement in global governance has shaped its domestic institutions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('China and Global Governance', '663', 'GGOV', 'This course examines the evolution of Chinese involvement in global governance across different issue areas.  We discuss Chinese perspectives on the existing governance mechanisms, and Chinas role in preserving or changing these mechanisms.  We will also explore how Chinas involvement in global governance has shaped its domestic institutions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('User Experience (UX) Fundamentals and User Research (UER)', '626', 'DEI', 'This course will cover the fundamental concepts of User Experience (UX) design and research (UR).  It provides students with insights into "real-life" user evaluation and/or design processes, challenges, considerations, tools, teams and models.  Students will learn how to design, evaluate, implement, and/or measure engaging user experiences.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Approaches to the Study of Religion in North America', '710', 'RS', 'A consideration of the theories, methods, and interpretations that have been influential in the study of religion in North America.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Academic Writing', '791', 'GER', 'Writing skills do provide an important tool in the academic world and are necessary for writing essays, presentations and research papers.  However well you know your subject, it is not necessarily an easy task to talk and write about it.  Therefore this course is designed for students who want to develop or imporove their academic writing skills and presentation techniques.  The focus of the course is on processes of generating ideas, collecting information, and establishing a thesis statement.  Furthermore it also gives an introduction to the development, structure, argumentation forms, and stylistic issues of academic papers with special attention to North American usage.  This course is taught either in Waterloo or Mannheim Germany.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Structure and Function in the Developing Brain', '795', 'PSYCH', 'This course will examine the development of brain-behaviour relationships between pre-infancy and adulthood from converging behavioural, neurophysiological, and neuroimaging perspectives.  Topics will include structural brain development, the emergence and refinement of functional brain systems, sequelae of early brain insult, and the role of plasticity in recovery and neuroenhancement.  This course is not recommended for students outside of the areas of Psychology, Health Studies and Gerontology, and Kinesiology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations of Sustainable Development Practice', '601', 'INDEV', 'This is an e-course delivered through the Earth Institute, Columbia University, New York, for all members of the Master of Development Practice international network.  It is a multidisciplinary, survey course in which students explore the connection between energy, health and poverty and the implications for sustainable development, the role of science, technology and policy, and the energy ladder.  Students will learn about the barriers to sustainable development and discuss cost-effective, culturally appropriate solutions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biological Determinants of Health', '103', 'HLTH', 'This course is an introduction to the biological determinants of health designed for students who have little formal knowledge of biology, but who wish to train for careers in health promotion, public health or other social and community services.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Public and Population Health', '202', 'HLTH', 'This course will introduce the student to the basic theories, principles and practice of public and population health.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Health Promotion: Theory and Community Engagement', '301', 'HLTH', 'This course introduces health promotion history, theories, and change strategies that address individual-level behaviour plus sociocultural, economic, political, and environmental conditions and resources for health that impact communities and foster engagement, empowerment, and functional collaborations. Topics include educational, persuasive, organizational, regulatory, and empowerment approaches for improving health at individual and community levels.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Program Planning and Evaluation', '303', 'HLTH', 'In this course the student is introduced to the theory and practice of public and population health programming, management, and evaluation applicable across the broad spectrum of core public health functions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Community Learning Project', '481', 'HLTH', 'Service learning is a method of teaching and reflection designed to use community service as a vehicle to enrich and integrate classroom learning, teach civic responsibility, encourage lifelong civic engagement, and strengthen communities. Students will be required to identify and design community learning placements, including the establishment of specific learning and outcome objectives, and a method of evaluation. Provisions must be approved by the community agency and the course coordinator. Placements are normally 8 to 10 hours per week for 8 weeks. In the final week of the term students will meet to reflect upon and share their experience with fellow students. Students will also complete a written report detailing whether and how they achieved their objectives, what they learned from the process relevant to the practice of health promotion, and how their experience utilized or exemplified course based material and assignments. Reports will be reviewed and graded as pass/fail by both the community partner and a faculty service leader.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations of Therapeutic Recreation Practice', '151', 'REC', 'This course is designed to examine models, theories, tools, and techniques required in the design, facilitation, and evaluation of therapeutic recreation services for individuals and groups.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sustainability Concepts, Applications and Key Debates', '609', 'INDEV', 'The course introduces the students to a spectrum of sustainability concepts, approaches, and key debates, setting the stage for practical engagement with development. The emphasis is on gaining clarity on the notion of sustainability and global environment change since its translation into practice will often imply confronting a number of decisions to be taken. A number of concepts, debates and proposals for the planet¿s sustainable future will be presented, evaluated and discussed on the basis of empirical findings and case studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Health', '603', 'INDEV', 'This course explores aspects of the distribution, diffusion, determinants and delivery of health and health care in a global context.  Building on foundational skills (i.e. basic understandings of epidemiology; the social determinants of health) the course will review a range of case studies (e.g. infectious disease; water; access to care; global environmental change) from a variety of regions around the world.  Students will gain an appreciation for the complexity of the issues, as well as science-policy bridging.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Co-op Fundamentals for Architects', '2', 'PDARCH', 'This course will explore aspects of career development related to preparing for and being successful in co-op work terms, such as understanding Co-operative Education and Career Action (CECA) policies and procedures, using Waterloo Works, preparing résumés, interviewing effectively, meeting employers expectations and preparing work-term reports. [Note: This course will be graded on a CR/NCR basis. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Portfolio Development', '1', 'PDARCH', 'This course will elaborate upon the graphic and communication conventions established in the fundamental architecture curriculum. Intermediate investigations in two dimensional representation and photographic manipulation are developed to enhance presentation skills within a professional and academic context. The topics covered in the course culminate in the generation of an architecture portfolio in readiness for job interviews. [Note: This course will be graded on a CR/NCR basis. Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Electronic Communications and Web Design', '3', 'PDARCH', 'This course will elaborate upon digital skills introduced in PDARCH 2. Students will learn the fundamentals of web design and will create a living, interactive, digital portfolio. Students will learn how to structure, design and manage web sites and web logs. [Note: This course will be graded on a CR/NCR basis. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Writing, Editing and Research', '4', 'PDARCH', 'This course is designed to assist students in building a foundation of skills for academic research, editing texts and designing a research program in preparation for higher studies, essay writing and thesis development. This course offers concise and pragmatic approaches to the challenges of academic and professional writing, including issues in formatting, referencing, creation of bibliographies, footnoting and endnoting. [Note: This course is graded on a CR/NCR basis. Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '103', 'WKRPT', 'Students will initiate an on-line record and assessment of their educational and work experience, tracked against two sets of criteria, the Student Performance Criteria established by the Canadian Architectural Certification Board and the Canadian Experience Record used by the provincial licensing bodies. The work-term reports are required courses for the degree but their grades are not included in the average. However, failed work-term report evaluations may delay a students progress as indicated in the Examinations and Promotions section of this calendar. Details regarding the evaluation of the work-term reports can be obtained from the students program advisor. 
[Note: This course is considered as DRNA]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '203', 'WKRPT', 'Students will complete a case study analysis of the firm in which they are employed. Work-term reports are required courses for the degree but their grades are not included in the average. However, failed work-term report evaluations may delay a students progress as indicated in the Examinations and Promotions section of this calendar. Details regarding the evaluation of the work-term reports can be obtained from the students program advisor.
[Note: This course is considered as DRNA]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '303', 'WKRPT', 'Students will complete an on-line record and assessment of their educational and work experience, tracked against two sets of criteria, the Student Performance Criteria established by the Canadian Architectural Certification Board and the Canadian Experience Record used by the provincial licensing bodies. Work-term reports are required courses for the degree but their grades are not included in the average. However, failed work-term report evaluations may delay a students progress as indicated in the Examinations and Promotions section of this calendar. Details regarding the evaluation of the work-term reports can be obtained from the students program advisor.
[Note: This course is considered as DRNA]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Professionalism and Ethics in Engineering Practice', '22', 'PD', 'This course provides a framework for understanding professionalism and ethics within society as reflected through practice in the field of professional engineering.  Course topics on professionalism include:  the history of engineering and professions, the licensing of professional engineers and the certification of the practice of offering engineering services to the public, and the regulation of professional engineering in Ontario through Professional Engineers Ontario.  Course topics on ethics include: the philosophy of ethics and the Code of Ethics, the philosophy of justice, and the definition and disciplining of professional misconduct.  Case studies will apply these principles to workplace situations including factors such as environmental ethics, whistle blowing, conflict of interest, and health and safety.  [Note: Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Algorithm Design and Analysis', '641', 'MATH', 'This course explores the intersection between mathematics and computer science by examining general methods for solving problems efficiently. We will study a variety of algorithm design techniques for problems in diverse application areas and use mathematical models to compare algorithms.  Students will be introduced to "big ideas" from computer science, such as the generalization and reuse of previous solutions, and the notion of limits to the power of computation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Causal Inference and Epidemiological Studies', '931', 'STAT', 'Causal inference in health research will be covered. Methods for the design and analysis of randomized controlled trials including randomization techniques, sample size and power calculations, and specialized additional topics including missing data, noncompliance, and ethics. The design and analysis of classical and modern epidemiological studies will then be discussed for settings in which randomization is not feasible. Causal inference methodologies for the analysis of observational data include propensity scores, marginal structural models and instrumental variables. Studies will be discussed from the epidemiological literature and other sources in the public domain. Simulations and data analyses will be carried out using software (e.g. R or SAS). Students will be trained and assessed in part based on the preparation of reports and delivery of presentations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Classification and Prediction in High Dimensional Analysis in Health Research', '932', 'STAT', 'In this course, we will cover classification and prediction in health research with a view to applications to screening and diagnosis of disease. This will lead to methods for evaluating the performance of various types of statistical models and learning techniques/algorithms. Cross-validation and bootstrap approaches will be introduced for model performance evaluation, and we will discuss discrimination and calibration as different components of prediction performance. We will cover variable selection techniques, including for high-dimensional data, with an emphasis on regularization techniques such as the LASSO and its variants. Model validation, both internal and external, and model updating will be covered, and we will also discuss post-model selection inference. An important focus will be on biomarker evaluation for a given disease, potentially connected to therapy, and leading to coverage of precision/personalized medicine. Finally, there will be coverage on the importance of reproducible and replicable research. Examples from different problems in health, including genetics, will be presented, and software (e.g. R or SAS) will be used throughout the course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Representations of Finite Groups', '745', 'PMATH', 'Basic definitions and examples: subrepresentations and irreducible representations, tensor products of representations.  Character theory.  Representations as modules over the group ring, Artin-Wedderburn structure theorem for semisimple rings.  Induced representations, Frobenius reciprocity, Mackeys irreducibility criterion.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Math for Teachers - OVGS Transfer Course', '700', 'MATH', '');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Thesis Development', '605', 'SUSM', 'The goal of the course is to ensure that all students will have a completed thesis proposal by the end of term.  Each student will make an oral presentation of their proposal to their committee members and other members of SEED.  The course will include discussions about how best to frame good research questions, find sources, organize a thesis, conduct ethical research, and communicate results.  Students will make regular class presentations throughout the term as their research proposal develops and thus gain skills in written, graphical oral presentation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gender and Global Politics', '620', 'PSCI', 'Does looking at the world through the lens of gender change how we see the state, sovereignty, diplomacy, security, trade, migration, globalization, governance, and other foundational concepts in global politics?  We review feminist theories of politics, with a particular focus on international relations and global governance; examine how gender shapes the roles and experiences of women and men in global politics; and discuss how to do feminist research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gender and Global Politics', '634', 'GGOV', 'Does looking at the world through the lens of gender change how we see the state, sovereignty, diplomacy, security, trade, migration, globalization, governance, and other foundational concepts in global politics?  We review feminist theories of politics, with a particular focus on international relations and global governance; examine how gender shapes the roles and experiences of women and men in global politics; and discuss how to do feminist research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sustainability Applications', '681', 'ERS', 'The course examines how societies can respond in practical ways to complexity, change and uncertainty, Social and ecological theory is blended with examples of applications and strategies (local/global) to support management and governance in social-ecological systems.  Emphasis is placed on how individuals and societies can transcend disciplinary thinking to build our capacity to foster sustainability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Biogeochemical Cycles', '626', 'EARTH', 'The global cycles of carbon, oxygen, nitrogen, phosphorus , sulfur, silicon and iron, are intimately linked to the chemical composition of the atmosphere and ocean, earths climate, global biological productivity and diversity, and the evolution of sedimentary rocks.  This course focuses on the quantitative representation of the processes and mechanisms driving the global biogeochemical cycles of carbon and nutrient elements on both short (100-104 years) and long (>104 years) time scales.  Attention will also be given to the perturbation of biogeochemical cycles by human activity.  To explore the interactions between the processes, as well as between the elemental cycles themselves, we will use mass balance models.  Each student will carry out an independent project based on an elemental cycle of his/her choice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Pharmacy Practice', '611', 'PHARM', 'This course will discuss specialized topics that are of mutual interest to the research of faculty members and graduate students in the field of Pharmacy Practice.  Topics could include, for example, pharmacy education; rural pharmacy practice; geriatrics and aging; pharmacoepidemiology; the role of technology in primary care; chronic disease management; expanded scope of practice; collaborative practice in different settings; health services design and care delivery; among others. This course may be presented in any of the following formats, reading based, lecture based, project based, or online learning.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Strategies and Skills for Academic Success', '101', 'GENE', 'The goal for this course is to help students become successful independent learners through learned study skills, a better understanding of their personality type, and an understanding of how personal wellness affects academic success. It takes a modelled approach to teaching learning strategies to students who struggle to meet the demands of a university curriculum. In the lectures, students will be taught various learning strategies that they will be able to apply to successfully meet the demands of their other courses. Students will then apply these modelled strategies in their other courses during a weekly application lab. Students will be expected to demonstrate their understanding of the material through a variety of assignments. The course will include an engineering component. [Note: Department Consent Required. Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics and Sustainability', '320', 'ERS', 'This course offers an examination of relationship between the economy, the environment and sustainability. The course aims to provide students with an understanding of the differing viewpoints on the economy-environment relationship and their associated methods, including their strengths, weaknesses and applicability in a policy context.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Issues in International Development', '101', 'INDEV', 'This course examines key issues in international development. International Development involves the search for sustainable and equitable solutions to poverty primarily but not only in the Global South. The causes and consequences of poverty are many and varied: environmental degradation; insufficient food production; inequitable access to land and related resources; poor governance; civil war and other types of conflict. The impacts of poverty manifest differently depending on culture, technology, geo-political factors and globalization.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Sustainability and Ethics', '105', 'ENVS', 'This course examines and evaluates the reasons why the compatibility of commercial activity, ethics and environmental stewardship has been challenged or defended by stakeholders in government, business and civil society organizations. Concepts and activities by which stakeholders describe and debate this compatibility will be discussed. Different cultural perspectives on the compatibility debate will be considered, compared and evaluated. Case studies will test whether and to what extent compatibility may be defended adequately. Private and non-profit enterprises will be considered.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Neuroscience 2: Lifestyle and Chronic Illness', '458', 'HLTH', 'This is an in-depth course intended for the intensive study of psychological, neuroscientific, and social processes that together set the stage for the development of chronic illnesses in the modern world. Topics include exercise neuroscience, health-related decision processes, and neural responses to health risk communications in the media.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Close Relationships', '448R', 'PSYCH', 'This course focuses on the life cycle of close adult relationships, ranging from the stages of initial attraction and development of an attachment, to growth and maintenance of the relationship, to conflict and dissolution. Seminars discuss contemporary theory and research with an emphasis on understanding the basic processes involved in intimate relationships.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Prejudice and Discrimination', '449R', 'SDS', 'This course takes an interdisciplinary approach in examining causes of, impacts of, and ways to overcome stereotyping, prejudice, and discrimination. The experience and behaviour of both the target and the purveyor of discrimination will be studied from individual, interpersonal, and structural perspectives. Topics may include overt and subtle forms of prejudice, intersectionality, and situational barriers to stigmatized groups success. [Note: Formerly PSYCH 449R]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Power, Politics, and Policy: An Introduction', '100', 'PSCI', 'What are the key political issues in Canada and abroad? Students will undertake comparative study of ideas, institutions, and interests central to issues of policy and politics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Global Politics', '150', 'PSCI', 'What are the politics underlying the contemporary distribution and governance of global wealth and power? This course provides students with an introduction to core issues in the study of global politics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Political Documentary', '254', 'PSCI', 'What role do political documentaries play in political thought, political practice, and the public sphere? Students will critically assess a range of documentary films made by government agencies, professional filmmakers, and activists.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cosmopolitanism and Social Development', '405R', 'SDS', 'What does it mean to be a "citizen of the world"? Cosmopolitanism is a social and political philosophy that views all human beings as members of a moral, political, relational, or cultural community. From a critical and comparative perspective, students in this course explore the philosophy of cosmopolitanism and its implications for social development in diverse social, political, and cultural contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Culture and Political Violence', '352', 'PSCI', 'What structural, direct, and symbolic violence occur in everyday experiences? Students will examine ethnographic and cultural accounts of political violence. [Note: PSCI 281 or 351 recommended.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Politics of Indigenous Peoples', '362', 'PSCI', 'Students will gain an understanding of and learn to articulate diverse perspectives on contemporary Indigenous issues, politics, and critical theory by examining these issues from the perspective of Indigenous North American peoples. Through a variety of research-based reading and writing assignments, students will critically reflect on colonialism, power, and state policy to understand the structures, interests, and processes that shape Indigenous struggles in Canada and North America.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Cultural Politics of Israel/Palestine', '460', 'PSCI', 'What are the key features of the cultural politics that underlie the Israel/Palestine conflict? Students will study the everyday lives of those affected by the violence to understand the complex narratives of violence, peace, and human rights that circulate across the region, as well as among those in diaspora and exile.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Video, New Media and the Digital Turn', '257', 'FINE', 'This course analyzes the shift in visual practices of the 20th century transformed by the arrival of various digital technologies. An emphasis is placed on the increasing interdisciplinarity of new media, as well as their multi-sensory nature.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Experimental Film', '256', 'FINE', 'The course introduces students to the history, methodologies, theories, and aesthetics of non-narrative experimental film with a focus on intersections between film and visual and performing arts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Film and Visual Media from 1900 to Today', '245', 'FINE', 'The course provides a chronological and thematic overview of the history of visual media in Modernity and Postmodernity. It starts at the beginning of the 20th century with the rise of cinema and ends with discussion of the digital age.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Childhood and Youth in Canada', '255', 'HIST', 'This course offers a Canadian history of childhood and youth. While childhood may seem like a timeless, eternal concept, it is actually a modern one. This course considers age as a category of analysis, complementing other key concepts such as gender, class, and ethnicity/race.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Indigenous Histories in Canada', '269', 'HIST', 'This course examines the histories of Indigenous peoples (First Nations, Inuit, Métis) in local and national contexts. It emphasizes the relationship between Indigenous peoples and settler society, including histories of treaty-making and colonial policies of dispossession and assimilation such as the Indian Act and the Indian residential school system. We will highlight forms of Indigenous resistance and resurgence, assertions of land rights, and the diversity of communities and cultures.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Indigenous Issues', '271', 'HIST', 'This course examines the histories of Indigenous peoples from around the world, covering areas such as educational assimilation, the internationalization of Indigenous rights, lands and resource development conflicts, Indigenous-state relations, language and cultural revitalization, and international political activism. We examine local Indigenous histories and place these experiences into the larger global context.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital and Public History Project', '302', 'HIST', 'This course provides the opportunity to design, research, and produce a history project in a multimedia format. Students incorporate archival, visual culture, and oral history research in their projects and examine the politics, ethics, and practice of historical research and its presentation to wide audiences.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History Gone Digital: An Introduction to History with the Web', '303', 'HIST', 'Digital history, the application of new and emerging technologies to the study of history, is an exciting new field. This course explores the literature on digital history and then puts theory into practice by exploring the digital collection, evaluation, and production of historical knowledge.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Finance or Accounting', '418', 'AFM', 'A course offered from time to time in relation to a significant school-related accounting or finance project or activity. [Note: Course will be graded on a CR/NCR basis. This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Islam, the West, and the Modern World', '221R', 'SI', 'This course examines conceptions of East-West divide and confrontation as well as topics of interest, both geo-political and religious, that influence the relationships of Muslims and the Western world. The focus is on the history of Muslims in the West and the challenges of integration of Muslim communities in pluralistic, secular societies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Islam, the West, and the Modern World', '221', 'RS', 'This course examines conceptions of East-West divide and confrontation as well as topics of interest, both geo-political and religious, that influence the relationships of Muslims and the Western world. The focus is on the history of Muslims in the West and the challenges of integration of Muslim communities in pluralistic, secular societies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Islam, Women, and the Modern World', '315R', 'SI', 'This course examines the lives and contributions of Muslim women in history - past and present - and political, social, and cultural influences that have affected the lives of Muslim women around the world. Challenging stereotypes of Muslim women and using religious, historical, and literary images, the course focuses on contributions of Muslim women as religious leaders, scholars, and politicians.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Islam, Women, and the Modern World', '315', 'GSJ', 'This course examines the lives and contributions of Muslim women in history - past and present - and political, social, and cultural influences that have affected the lives of Muslim women around the world. Challenging stereotypes of Muslim women and using religious, historical, and literary images, the course focuses on contributions of Muslim women as religious leaders, scholars, and politicians.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('History of Visual Media to 1910', '244', 'FINE', 'This course provides a chronological and thematic overview of the history of visual media examining technologies such as the printing press, photography, early cinema, and the representation of perspective.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Plants and Civilization', '225', 'BIOL', 'This course examines the importance of plants and the role they have in the local and global community. It introduces students to the impact of plants on the past, present and future of human civilization and describes the importance of plants as sources of food, medicine, fuel, shelter, clothing and psychoactive agents. Topics include medicinal plants and human health, the origin of agricultural crops, plants that changed the course of history, plant-based biofuels, the green revolution, plant biotechnology and genetic engineering of plants. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Methods of Planning Analysis', '704', 'PLAN', 'This course is a graduate level introduction into the analytical methods employed in planning research and practice.  The broad spectrum of tools, approaches and techniques drawn from quantitative and qualitative traditions will be reviewed.  Commonly employed examples will form the basis for developing applied problem solving skills.  Operational considerations, critical assessment, and analytical communication provide the important review context.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introductory Planning Project Studio', '720', 'PLAN', 'This studio course introduces students to the process of collaborative problem solving central in Planning practice.  Students work in teams, led by faculty facilitators, in applying theory and knowledge from Planning and related disciplines to problems drawn from the local community planning context.  The emphasis is on the development of student skills including design, plan making, teamwork, creativity, negotiation, management, and policy application and creation.  Problem based learning, reflection on process and communications are central in this course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Planning Project Studio', '721', 'PLAN', 'This student centered studio course involves partnering with a local community, providing student led groups on opportunity to emulate professional practice under the guidance of an assigned faculty member.  Skills and knowledge developed in the introductory studio course are enhanced and important community engagement skills are emphasized in the chosen project.  Reflective learning is central as students are expected to assess the process, the outcome, and the interrelationship between them.  Community presentation of the problem solution is required.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Engineering Mathematics', '620', 'CHE', 'Advanced engineering mathematics for the solution of linear differential equations arising from physiochemical models.  Topics include scaling analysis of differential equations, series solutions for ordinary differential equations (ODEs), numerical solutions for initial/boundary value problems, Laplace transforms, characterization of partial differential equations (PDEs), and solution methods for PDEs (Sturm-Liouville transform, numerical methods).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Register-transfer-level Digital Systems', '627', 'ECE', 'Syntax, semantics, and usage of the VHDL hardware description language. Modeling concurrency in VHDL, in other hardware description languages, and using other simulation techniques.  Modeling, design, and implementation at the register-transfer level.  Functional verification techniques.  Timing analysis.  Introduction to power analysis and optimization.  Introduction to faults and testing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ceramics: Studies in Material Practice', '271', 'FINE', 'Students will explore ceramic material practice as a method for creative expression. The course will focus on visual and conceptual problem solving, while acquiring technical skills and knowledge of ceramic material. The assignments will incorporate hand building, mold making, surface treatments, and glazing techniques. [Note: Studio course]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Studies in Greek Literature', '631', 'CLAS', 'An investigation of selected themes, topics, time periods or genres in ancient Greek literature.  This course is repeatable provided that the topic is different.  One example could be Attic Drama and Civic Identity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Studies in Roman Art in Architecture', '642', 'CLAS', 'An investigation of selected themes, topics, time periods, artistitc media or styles in Roman art and architecture.  This course is repeatable provided that the topic is different.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Greek History', '651', 'CLAS', 'An investigation of selected themes, topics, time periods, and methods in ancient Greek history.  This course is repeatable provided that the topic is different.  Examples could include: The Hellenistic Kingdoms: Conquest and Cultural Interaction; Greeks and Egyptians in Ptolemaic Egypt; or Athenian Democracy and Political Discourse.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Roman History', '652', 'CLAS', 'An investigation of selected themes, topics, time periods, and methods in Roman History. This course is repeatable provided that the topic is different.  Examples could include: Roman Frontiers and Provinces; The Decline of the Roman Empire and its Consequences; The History of Roman Citizenship (8th cent. BC-AD 6th cent.); or The World of Roman Law.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Classical Studies Abroad', '695', 'CLAS', 'This course features a combination of academic study and firsthand investigation of museums and ancient sites, normally in Greece and/or Italy.  Students are required to give a presentation during the trip on a topic relevant to one of the sites being visited.  A piece of written work involving original research relating to some of the material investigated during the visit is also required. [Note:  This course incurs extra costs for the student above and beyond the registration fee, in the form of travel, room and board and incidental costs.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Greek Language', '600', 'GRK', 'This course will involve the reading of original material in ancient Greek will focus on the advanced study of the language itself, although topics may vary from single authors to literary genres to chronological periods.  This course is repeatable provided the topic is different.  Examples could include: Archaic Greek Hexameter; Greek Palaeography; Ancient Greek Hymnography; Late Antique Greek Poetry; Greek Philosophy and Poetry; Hellenistic Composition, Grammar and Reading.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Greek Language', '601', 'LAT', '0.25 credit offering to be combined with LAT 601.  This 0.25 credit version of GRK 600 will allow faculty members to offer single-term courses that combine reading of original sources in both ancient Greek and Latin on selected topics. This course is repeatable provided the topic is different.  Examples could include:  Greek Palaeography or Greek Epigraphy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Greek Language', '601', 'GRK', '0.25 credit offering to be combined with LAT 601.  This 0.25 credit version of GRK 600 will allow faculty members to offer single-term courses that combine reading of original sources in both ancient Greek and Latin on selected topics. This course is repeatable provided the topic is different.  Examples could include:  Greek Palaeography or Greek Epigraphy.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Latin Language', '600', 'LAT', 'This course will involve the reading of original material in Latin with a focus on the advanced study of the language itself, although topics may vary from single authors to literary genres to chronological periods.  This course is repeatable provided the topic is different.  Examples could include: Latin Palaeography; Augustan Poetry; Epic Poetry; Advanced Grammar, Reading, and Composition; Greek and Roman Identities in the Lyric Poetry of Horace; Latin Epigraphy; or Medieval Latin.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cognitive Neuroscience of Face Perception', '794', 'PSYCH', 'The human face is one of the most important social stimuli we encounter every day.  Multiple aspects of the face such as its identity, age, race, gender, emotion or gaze direction need to be extracted and processed rapidly and accurately by the brain to allow efficient social interactions.  This seminar offers a multidisciplinary approach to the study of face perception.  We will cover major aspects of face processing including the perception and recognition of identity, emotions and gaze direction, their neural bases and their links to social cognition and theory of mind.  We will discuss chapters and fundamental papers integrating the most recent findings from neuropsychology, neuroimaging and cognitive psychology.  This course is most suited for students interested in brain-behaviour relationships.  Basic Cognitive Neuroscience knowledge is required.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computer Programming', '121', 'MSCI', 'An introduction to computer programming using a high level programming language. Concepts and topics covered include the basic components of algorithms (primitive operations, variables, sequencing operations, conditionals/branching, repetition/loops, subroutines/functions), problem decomposition, abstraction, testing and debugging, pseudo-code, file based input and output, use of a modern development environment including a symbolic debugger, good coding style, pointers/references, and basic data structures (arrays, records, objects). [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Government, Business and Civil Society', '623', 'PS', 'Public service work frequently involves ineractions wtih various levels of government, business, and civil society.  This course aims to help students develop critical analysis skils to assess the resources and interests of stakeholders and to understand tensions among them.  Drawing on political economy and social movement theory, the course examines several major current debates. Topics may include healthcare and social policy, inequality and poverty, environmental issues, and economic development.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Methods and Data Analysis', '624', 'PS', 'To effectively support evidence-based decision making, public servants must be skilled in collective, analyzing, interpreting and presenting data.  This course explores the principles and practices of effective research design, and equips students with essential skills in data collection and analysis.  Topics may include methods of data collection, measurement, data coding, descriptive and inferential statistics, sampling, survey research techniques, questionnaire design, interviews, and research ethics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chemistry Principles', '186', 'BME', 'The stoichiometry of compounds and chemical reactions. Periodicity and chemical bonding. Energy changes in chemical systems. Electronic structure of atoms and molecules, correlation with the chemical reactivity of common elements, inorganic and organic compounds. Discussion of the structure, nomenclature and reactions of important classes of organic compounds. Stereochemistry and its role in reaction mechanisms. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Biology', '285', 'BME', 'Introduction to basic concepts of biochemistry and cell biology. Overview of the chemistry of amino acids, carbohydrates, lipids and nucleic acid. Structure and properties of proteins and enzymes. Elements of cell structure and diversity, and relationship of biochemistry with cell metabolism. A focus on biomedical engineering with relevant examples such as biomimetic engineering design, system biology, and tissue engineering. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Biology Laboratory', '285L', 'BME', 'Laboratory experiments for students taking BME 285.  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physiological and Biological Systems', '284', 'BME', 'The structure, functions, and properties of the major biological systems (musculoskeletal, nervous, cardiovascular) will be presented in relation to the design of biomedical devices (imaging, assistive, and diagnostic). Concepts in modeling biological systems will be introduced. Various aspects of pathology and how they influence medical device design will also be discussed. [Offered:  S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physiology and Anatomy Laboratory', '284L', 'BME', 'Laboratory experiments for students taking BME 284. [Offered:  S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physiological Systems Modelling', '355', 'BME', 'Introduction to systems theory as a general modelling method, and applied to the skeletal, neuromuscular, central nervous, cardiovascular, and respiratory systems of the human body. Time-domain simulations, sensitivity analyses, and parameter identification, with the latter driven by experimental measurements of system performance. [Offered:  W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomedical Transport: Biofluids and Mass Transfer', '384', 'BME', 'Fundamental concepts in systems involving fluid flow. Basic treatment of statics, kinematics, and dynamics of fluids. Mass transfer, conservation of mass, momentum and energy for a control volume. Dimensional analysis and similarity. Discussion of flow in pipes and channels and brief introduction to boundary layers, lift and drag, ideal and compressible flow will be specifically covered in the context of the cardiovascular system (macrocirculation and microcirculation). [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Biomedical Design', '161', 'BME', 'Topics related to biomedical design will be covered: multidisciplinary system design, design process, problem definition, life-cycle design, design specification, function analysis, design evaluation and decision-making, introduction to mechanical design, prototyping, experimentation, safety and responsibility in engineering design, design for society and environment, and design documentation. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Factors in the Design of Biomedical and Health Systems', '162', 'BME', 'Design of human-machine environments, designing for patient safety and reduce human error in decision-making, analytical methods of determining user needs, information processing and human sensory processes and consideration of these elements in the design of systems with humans, and consideration of human physical capabilities in ergonomic design. Topics will be reinforced in the context of the design of prosthetics or rehabilitation devices. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Prototyping, Simulation and Design', '261', 'BME', 'Problem-solving approaches, agile design and development, rapid prototyping, revision control, design patterns, development cycles, and simulation. Topics will be reinforced in the context of biomedical engineering projects conducted in groups within a collaborative environment. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomedical Engineering Design', '361', 'BME', 'Design methods: problem definition, requirements analysis, criteria and generation of alternative solutions, feasibility analysis, and optimization. Product development. Design survey of biomedical equipment and assistive technologies. A term-long design project in small groups. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomedical Engineering Design Workshop 1', '362', 'BME', 'Engineering design project course where students work in small groups applying the principles of engineering problem solving, systems analysis, simulation, optimization, and design to a biomedical engineering problem of their own choosing. Lecture topics include project management, risk management, standards, regulatory clearance, and certification for biomedical devices. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomedical Engineering Ethics', '381', 'BME', 'This course explores ethical issues in biomedical engineering practice, including professional ethics, medical ethics, the ethics of human and animal subject use in biomedical research, and the impact of biomedical engineering solutions on society and the environment. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomedical Engineering Design Workshop 2', '461', 'BME', 'The first half of a two-term engineering design project continuing the biomedical design workshop sequence. A prototype and interim progress report are presented at the end of the first term. Lecture topics include safety and risk analysis of biomedical technologies. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biomedical Engineering Design Workshop 3', '462', 'BME', 'The concluding half of the fourth-year Biomedical Engineering Design Workshop. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Communications in Biomedical Engineering-Written and Oral', '101', 'BME', 'This course introduces first-year students to biomedical engineering with a focus on the engineering profession and technical communication skills. Through in-class and independent activities leading to formative and summative assessments, students will consider and reflect on communication intention, audience, content, medium, format, and tone to demonstrate and improve upon their listening, written and oral communication skills for academic, engineering, and professional context. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Communications in Biomedical Engineering-Visualization', '101L', 'BME', 'This laboratory course introduces students to visual communication methods relevant to engineering analysis and design. Through in-class and independent activities leading to formative and summative assessments, students will consider and reflect on communication intention, audience, content, medium, format, and level of detail to demonstrate and improve upon their skills in graphing, freehand sketching, technical drawing, and computer-aided design (CAD). [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Computation', '121', 'BME', 'The key skills necessary to develop software solutions to solve biomedical engineering problems. Topics include software development, software design, programming language syntax, object oriented programming, structured programming, arrays, matrices, pointers, and algorithm efficiency. The topics will be reinforced in the context of practical biomedical software systems such as physiological monitoring systems and clinical support systems.  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ethical Decision Making', '9', 'PD', 'Borrowing from philosophy, game theory, and economics, this course equips students with the theoretical knowledge required to objectively evaluate and discuss ethical issues in the workplace. It provides students with practical advice regarding how to make ethical decisions in an increasingly competitive marketplace. Topics include the ethics of profit-making, why good people make bad decisions, moral hazard, and personal responsibility in group contexts.
[Note: Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Data Structures and Algorithms', '122', 'BME', 'Topics of structured software design, data structures, abstract data types, recursive algorithms, algorithm and data structure analysis and design from both computational and memory perspectives, lists, stacks, queues, trees, graphs, sorting and searching, hashing, and problem-solving strategies. Embedded programming in health monitoring systems and healthcare management systems. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physics 1: Statics', '181', 'BME', 'Introduction to the basic theory and principles of mechanics of static systems. Topics covered include statics of particles, rigid bodies and force systems, equilibrium of rigid bodies, analysis of joints and frames, distributed forces, centroids and moments of inertia, and friction. Applications of mechanical principles to musculoskeletal systems will be presented. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Statistics and Experimental Design', '213', 'BME', 'Fundamentals of probability and statistics, and applications to biomedical engineering. Random variables and statistical distributions, statistical estimation, hypothesis testing, regression, and experiment design considerations. Applications to biomedical experiments, biomedical imaging data, and clinical trials. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanics of Deformable Solids', '281', 'BME', 'Introduction to mechanical response of materials and stress-strain relationship. Behaviour of prismatic members in tension, compression, shear, bending, and torsion. Shear-force and bending-moment diagrams. Introduction to instability. Mechanical properties of biological tissues, and viscoeleastic models. Applications to bone, cartilage, and biomedical implants. [Offered:  F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanics of Deformable Solids Laboratory', '281L', 'BME', 'Laboratory experiments for students taking BME 281. [Offered:  F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Materials Science for Biomedical Engineers', '282', 'BME', 'Crystalline structure, crystal defects, non-crystalline materials, structure and properties of metals, ceramics, glasses, semi-conductors, polymers, and composites. Factors in materials design, material selection and processing in the context of biomedical devices and instruments will also be discussed.[Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physics 2: Dynamics', '182', 'BME', 'The science of motion is taught with initial focus on particles, and then progressing to planar rigid body systems. Concepts such as inertia, momentum, work, energy, Newtons laws, and contact dynamics are covered, with particular application to human motions (e.g., walking, running, jumping, lifting, and throwing). [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Physics of Medical Imaging', '386', 'BME', 'The fundamental laws of electricity, magnetism, and optics will be taught through the introduction to basic concepts of medical imaging: radiation for imaging, x-ray, computed tomography (CT), magnetic resonance imaging (MRI), ultrasound or sonography imaging, electric impedance tomography, confocal microscopy, fluoroscopy. Radionuclide imaging: Single photon emission computed tomography (SPECT), and positron emission computed tomography (PET). Emerging technologies: elastography, tHz imaging, molecular imaging will also be discussed.  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Systems', '393', 'BME', 'Digital technology, combinatorial logic, binary arithmetic, sequential circuits, digital design, and microcontrollers. Topics will be reinforced in the context of biomedical microcontrollers and sensors used in physiological monitoring and clinical support systems. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Systems Laboratory', '393L', 'BME', 'Laboratory experiments for students taking BME 393, focusing on circuit construction, simulation, and design. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Systems and Signals', '252', 'BME', 'Models and analysis of linear systems in the context of measurement and processing of biosignals such as electroencephalography (EEG), electrocardiography (ECG), and electromyography (EMG). Discrete and continuous time systems, difference and differential equations, impulse and frequency response, transform domain techniques, transfer functions and frequency response, frequency domain analysis of linear systems, sampling theory, stability, and linear filters. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Control Systems', '356', 'BME', 'Classical and state space representations of control systems. Stability, controllability, observability, and sensitivity. Routh-Hurwitz and root-locus methods. Frequency domain behaviour, Bode plots, Nyquist stability criteria. Pole placement, PID, phase-lead and phase-lag controllers. Application to anatomical system models, including musculoskeletal and cardiovascular systems, and to physiological feedback systems. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Control Systems Laboratory', '356L', 'BME', 'Laboratory experiments for students taking BME 356. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Engineering Biomedical Economics', '364', 'BME', 'This course examines key economic issues in health care and biomedical industries. Topics include the market for medical care, health insurance, various models of healthcare delivery and competition and the role of government in policy, financing and delivery of health care. This course will train students to use economic analysis to model and understand the complex interactions between health care delivery, insurance markets, health innovators, governments, and firms. [Offered:  F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Optimization and Numerical Methods', '411', 'BME', 'Interpolation and curve fitting, root-finding methods, local and global optimization methods, constrained optimization, multiobjective and multidisciplinary design optimization for biomedical applications. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Historical Memory and National Identity', '305', 'HIST', 'What factors create a national identity: historical events or socially-constructed historical memory? By analyzing key themes such as elite and popular culture, historical sites and commemorations, sports, and iconic public events, this course demonstrates how nation-states and other communities have created a series of evolving national identities.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations for Sustainability Management', '601', 'SUSM', 'In this course the scientific foundations and fundamentals for sustainability management will be introduced. Basic theoretical, ethical, scientific, and technical foundations of sustainable development will be taught in addition to technical knowledge like statistical literacy and basic environmental knowledge.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Theories and Concepts of Sustainability Management', '602', 'SUSM', 'In this course theories and concepts such as international sources of sustainability concepts, basic environmental and ecological economics, social and environmental justice, sustainable management and finance, uncertainty, complexity, risk and decision making in sustainability management, etc. will
be introduced and discussed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Methods for Sustainable Management', '603', 'SUSM', 'This course will introduce quantitative and qualitative research methods such as statistical methods, case study analysis, life cycle assessment, survey methods, impact measurement, etc. Students will learn skills like risk assessment methods, sustainability measurement and reporting, qualitative and quantitative data analysis, literature review, ethical aspects of research and proposal writing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Investment Management - Junior Analyst', '328', 'AFM', 'This course provides students hands-on training in equity valuation and enables students to follow one industry sector. As a junior analyst, a student will understand why funds management is broken into different sectors, monitor existing equity holdings in a particular sector, make new equity selections in the sector, prepare equity research reports, and present trading recommendations to an investment team in a student-run investment portfolio.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Investment Management - Senior Analyst', '329', 'AFM', 'This course provides students hands-on training in equity valuation and enables students to follow more than one industry sector. As a senior analyst, a student will monitor existing equity holdings in different sectors, make new equity selections in the sectors, prepare equity research reports, and present trading recommendations to an investment team in a student-run investment portfolio.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Investment Management - Junior Portfolio Manager', '428', 'AFM', 'This course provides students hands-on training in equity valuation and portfolio management. As a junior portfolio manager, a student will make allocations in two sectors, monitor the performance of the existing equity holdings in these two sectors, mentor two analysts, make equity trading decisions for these two sectors, and present portfolio performance reports to the investment team and the advisory board. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Investment Management - Senior Portfolio Manager', '429', 'AFM', 'This course provides students hands-on training in equity valuation and portfolio management. As a senior portfolio manager, a student will make allocations in different industry sectors, monitor the performance of the existing equity holdings in these sectors, mentor analysts, make equity trading decisions for these sectors, and present portfolio performance reports to the investment team and the advisory board.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Cultural Studies', '298', 'GER', 'Selected topics in cultural studies. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('German Abroad', '299', 'GER', 'This immersion course provides students with an introduction to and a familiarity with selected topics in German-speaking environments. The topics range from history and culture, to language and linguistic varieties in the German-speaking world, as well as arts and the German-speaking media landscape. Course offerings include classes abroad as well as field trips. [Note: Information about current offerings can be obtained from the department. This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Remote Sensing Systems', '633', 'SYDE', 'A survey of modern quantitative remote sensing using optical, infrared, and microwave radiation.  The principles and technologies for acquiring and understanding remotely sensed image data are discussed.  Physical principles of EM propagation and interaction between the radiation and terrestrial and atmospheric materials.  Principles and operation of sensor systems.  Principles of pattern recognition and image processing techniques unique to remote sensing.  Applications of remote sensing to monitoring vegetation, soil, oceans, and inland waters, and snow and ice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Cultural Studies', '398', 'GER', 'Selected topics in cultural studies. [Note: This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('German Abroad', '399', 'GER', 'This immersion course provides students with an introduction to and a familiarity with selected topics in German-speaking environments. The topics range from history and culture, to language and linguistic varieties in the German-speaking world, as well as arts and the German-speaking media landscape. Course offerings include classes abroad as well as field trips. [Note: Information about current offerings can be obtained from the department. This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Literatures Romantic to Modern', '485', 'ENGL', 'A special study of a selected topic, author, genre, or period in Romantic to Modern literatures. Please see course instructor for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of 10 times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Literatures Modern to Contemporary', '486', 'ENGL', 'A special study of a selected topic, author, genre, or period in Modern to Contemporary literatures. Please see course instructor for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of 10 times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in the History and Theory of Rhetoric', '492', 'ENGL', 'A special study of a selected topic in the history and theory of rhetoric. Please see course instructor for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of 10 times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Professional Writing and Communication Design', '493', 'ENGL', 'A special study of a selected topic in professional writing and communication design. Please see course instructor for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of 10 times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Forms of Media and Critical Analysis', '494', 'ENGL', 'A special study of a selected topic in forms of media and critical analysis. Please see course instructor for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of 10 times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Model-Based Robust Design', '661', 'SYDE', 'Robust design encompasses the theories and methodologies that make performance measures (responses, critical times, energy, etc.) invariant to uncertainties in design variables (environmental conditions, manufacturing processes, material dimensions and properties, etc.).  In this course you will learn how robust design methods and mathematical models of engineering systems help find improved designs at a lower cost.  Topics include: the building of simple, efficient, meta-models through computer experiments to replace traditional mechanistic models.  Performance measures and their design specifications.  Sensitivity and importance analysis to select design variables.  Second moment methods using Taylor series to provide parameter (mean) design.  Probabilistic methods combined with manufacturing and scrap costs to perform simultaneous parameter design and tolerance allocation.  Desirability functions and loss functions to deal with multiple competing performance measures.  Integrated design by constrained optimization.  Examples come from industrial processes, as well as hydraulic, electrical and mechatronic systems.  Mathematics required: Total derivatives, Matrix calculus, Kronecker product, singular value decomposition.  Matlab serves as the computing tool.  Course notes are available.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Statistical Image Processing', '672', 'SYDE', 'Images are all around us!  The proliferation of low-cost, high-quality imaging devices has led to an explosion in acquired images.  Where these images are acquired from a microscope, telescope, satellite, or medical imaging device there is a statistical image processing task:  the interference of something - an artery, a road, a DNA marker, an oil spill - from imagery, possibly noisy, blurry, or incomplete.  The goal of this course is to address methods for solving multidimensional statistical problems, emphasizing theory, mathematical modeling, and algorithms.  Specific topics of interest include inverse problems, multidimensional modeling, random fields, and hierarchical methods.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Video Processing and Analysis', '673', 'SYDE', 'This course introduces methods to acquire state (both spatial and temporal) estimations from video streams.  Video streams are analyzed as dynamic systems, linear and non-linear.  If the system can be approximated as linear and Gaussian in terms of the dynamic noise in the process and measurement, then Kalman filter techniques are used.  This refers to sequential state estimation.  For nonlinear dynamical systems, the EFK (Extended Kalman filter) can be used by a liberalization of the stat space model.  Particle filters are used to address state estimation problems where the systems are non-linear and non-Gaussian.  Particle Filters are rooted in Bayesian estimation and Monte Carlo procedures.  The course builds upon these techniques in studying visual tracking and the components of Visual SLAM (Simultaneous Localization and Mapping) procedures.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('3D Computer Vision', '674', 'SYDE', 'This course focuses on 3D computer-vision and camera/optical-based 3D shape-measurement techniques.  Topics include stereo-camera 3D measurement, structures-light techniques, laser-camera range-sensing, fringe-projection phase shifting methods, curve and surface representation, surface fitting, and range-image registration.  The measurement methods will include setup of measurement systems and calibration of instrumentation.  Techniques will be demonstrated and practical exercises will be given.  Biomedical applications will be discussed.  Students will complete an individual course project with written report and oral presentation in class.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design of MEMS and NEMS', '683', 'SYDE', 'Rigorous grounding in the theory and practice of Micro-Electro-Mechanical Systems (MEMS) design, as well as ways of extending them to Nano-Electro-Mechanical Systems (NEMS) design. Analytical tools to explore the possibilities of NEMS. Includes methodology to derive and use MEMS and NEMS models as design tools, basic concepts of statics and dynamics needed to construct lumped-mass models, the use of reduced-order models in MEMS/NEMS design, advantages and disadvantages of these techniques versus commercial software (COMSOL, ANSYS, and Coventor), most effective uses and limitations for each modeling approach.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design of MEMS and NEMS', '605', 'NANO', 'Rigorous grounding in the theory and practice of Micro-Electro-Mechanical Systems (MEMS) design, as well as ways of extending them to Nano-Electro-Mechanical Systems (NEMS) design. Analytical tools to explore the possibilities of NEMS. Includes methodology to derive and use MEMS and NEMS models as design tools, basic concepts of statics and dynamics needed to construct lumped-mass models, the use of reduced-order models in MEMS/NEMS design, advantages and disadvantages of these techniques versus commercial software (COMSOL, ANSYS, and Coventor), most effective uses and limitations for each modeling approach.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Collaborative Systems Design', '643', 'SYDE', 'Interaction of humans with technological systems often takes place within the broader context of a collaborative setting.  Therefore, there is an increasing trend for the design of these systems to incorporate and support group interactions.  This course will emphasize the study of collaboration from an interdisciplinary perspective and the derivation of system design criteria.  Topics will include group theories, collaboration requirements (including communication, coordination, team awareness), quantitative and qualitative research methods (including laboratory studies, surveys, ethnographic research methods), data analysis, and collaboration technologies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Human Factors Testing', '644', 'SYDE', 'The focus of the course is on in-depth explorations of primary methods of data collection used in human factors engineering research and product design.  We will begin with various philosophical positions regarding human factors for testing and evaluation as they relate to both industrial and research applications.  We will discuss the applications and implications of data collection involving human participants; the limitations of statistical analyses; and the potential application of human factors research as guidelines for product or system design.  Students will be encouraged to explore human factors methods related to their own area of research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Optimal and Learning-Based Control', '655', 'SYDE', 'This course provides an understanding of the principles of optimal control while introducing the key ideas of learning-based control and discussing intersections between these two broad areas. Variational approach, Pontryagins Minimum Principle, linear quadratic optimal control, the principle of optimality, dynamic programming, and the Hamilton-Jacobi-Bellman equation are covered. Model predictive control is presented. A brief introduction to learning for control applications is provided, along with introducing iterative learning control. Model-based and model-free reinforcement learning (RL) techniques, connections between RL and optimal control ideas, and approximate RL solution methods for complex problems are discussed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Image Processing', '671', 'SYDE', 'This course is intended to provide insights into advanced topics in image processing.  The topics discussed include but are not limited to:  multi-scale and probabilistic image respresentation and analysis, image restoration, invariant image representation, image reconstruction, image fusion, otical flow, image segmentation, and image registration.  Recent research papers and review papers from the field will be sued as complimentary material to weekly lectures.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Materials Biocompatibility', '684', 'SYDE', 'The course covers fundamental topics of biocompatibility of materials in medicine (polymers, ceramics, metals, composites, bioengineered materials).  Fundamental principles of materials science (bonding, atomic/molecular structure) as well as interfacial engineering of materials will be reviewed.  Materials response to biological systems such as corrosion, degradation, leaching and fracture will be studied in the context of specific biomedical applications.  The host response to materials (immune, inflammatory response and coagulation) will also be treated.  Specific examples of material interactions with biological systems such as bone, blood, skin and the eye will be studied.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Psychology of Groups and Teams', '342', 'PSYCH', 'The psychology of groups and teams will address psychological processes particular to any social group situation, for example creative design teams, decision making groups, sports teams, group counselling, university clubs, and community groups. Topics include social identification and cohesion, group dynamics (e.g., social loafing, social influence, temporal dynamics, conflict management, etc.), communication in groups, creativity and brainstorming in groups, team leadership, team learning, and measuring team performance. In addition to lectures there will be discussion groups for team experiential exercises.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Intergroup Relations', '355', 'PSYCH', 'This course reviews social psychological theory and research on intergroup relations: how groups of people and people from different groups interact. It examines stereotyping, prejudice, and discrimination (e.g., racism, sexism, ageism, weight, or sexual prejudice) from both majority and minority perspectives. It also discusses implications for promoting intergroup trust, reducing inequality, and resolving real-world conflict.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Climate Prediction, Modeling and Scenarios', '610', 'GEMCC', 'Computer models of the physical climate system are essential tools for investigating future climate change.  This course will introduce students to the basic formulation of these models, and their strengths and limitations.  Inter-linkages between climate modeling and mitigation and adaptation policy agendas is reviewed and critiqued.  The types of climate modeling experiments peformed with such models and scenarios will be reviewed.  The latest projections of future climate on a variety of temporal and spatial scales will be presented and evaluated.  *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Climate Prediction, Modeling and Scenarios', '652', 'GEOG', 'Computer models of the physical climate system are essential tools for investigating future climate change.  This course will introduce students to the basic formulation of these models, and their strengths and limitations.  Inter-linkages between climate modeling and mitigation and adaptation policy agendas is reviewed and critiqued.  The types of climate modeling experiments peformed with such models and scenarios will be reviewed.  The latest projections of future climate on a variety of temporal and spatial scales will be presented and evaluated.  *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Climate Change Mitigation', '603', 'GEMCC', 'This course examines the anthropogenic sources of greenhouse gas emissions and the range of strategies to reduce these emissions and to enhance carbon sequestration.  The policy frameworks governing climate change mitigation are critically appraised from geopolitical, economic and ethics perspectives.  This course also considers the potential role of climate remediation strategies. *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Climate Change Mitigation', '679', 'GEOG', 'This course examines the anthropogenic sources of greenhouse gas emissions and the range of strategies to reduce these emissions and to enhance carbon sequestration.  The policy frameworks governing climate change mitigation are critically appraised from geopolitical, economic and ethics perspectives.  This course also considers the potential role of climate remediation strategies. *eligible for MES');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Climate Data and Analytics', '620', 'GEMCC', 'This course examines the scientific and practical challenges of finding and utilizing climate data to derive useful information in order to inform research and decision making processes. Through lectures, readings, videos and assignments, this course will introduce different types of climate data (historical, contemporary measurements and modelled) and discuss how they can be realistically analyzed to derive useful information (both within a research and policy context). From a general discussion on what climate data is and its value, this course will then focus on finding/collected climate data and critically evaluating how it can and should be used. Further, this course will introduce students to the development of data management plans, climate data curation, and effective ways of visualizing and contextualizing climate data to a variety of target audiences. Building upon this foundational knowledge, strategies and methodologies for analyzing climate data to derive useful information related climate risk in a variety of sectors with be explored. This includes areas such as community planning, human health, engineering/infrastructure among others. Students must have access to spreadsheet software for assignments. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Climate Data and Analytics', '674', 'GEOG', 'This course examines the scientific and practical challenges of finding and utilizing climate data to derive useful information in order to inform research and decision making processes. Through lectures, readings, videos and assignments, this course will introduce different types of climate data (historical, contemporary measurements and modelled) and discuss how they can be realistically analyzed to derive useful information (both within a research and policy context). From a general discussion on what climate data is and its value, this course will then focus on finding/collected climate data and critically evaluating how it can and should be used. Further, this course will introduce students to the development of data management plans, climate data curation, and effective ways of visualizing and contextualizing climate data to a variety of target audiences. Building upon this foundational knowledge, strategies and methodologies for analyzing climate data to derive useful information related climate risk in a variety of sectors with be explored. This includes areas such as community planning, human health, engineering/infrastructure among others. Students must have access to spreadsheet software for assignments. *eligible for MES.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Climate Change, Natural Hazards and Disaster Risk Reduction', '622', 'GEMCC', 'This course explores the complex links between climate change and natural hazards, and related disasters, including disaster risk reduction as an adaptation strategy.  Emphasis is placed on examining these concepts in a developing country context.  *eligible for');
