INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Nutrients in Watersheds', '643', 'EARTH', 'Nutrients such as nitrogen (N), phosphorus (P), silica (Si) and carbon (C) are vital to organisms, and play an essential role in our everyday life.  Used in excess, they can also become a nuisance with unwanted effects on the environment.  This course focuses on nutrients from a hydrological perspective, using a river basin based viewpoint.  The course covers general nutrient cycles, then nutrient budgets in watersheds, and how to model nutrient fluxes in catchments, using different approaches.  We will also discuss pollution history, using nutrients and metal pollution in river basins over the last centuries.  This will enable students to understand challengers that might arise from a variety of issues, from pollution history to current practices.  Individual student projects will be based on either a particular element or a particular model.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Drug Information Fundamentals', '155', 'PHARM', 'This course provides students with basic skills in retrieving and assessing drug information and medical literature.  The foundation acquired in this course will be used throughout the Integrated Patient Focused Care series in critically appraising literature to support an evidence-based patient care model. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Symposium', '425', 'PHARM', 'Symposium offers senior students the opportunity to deliver a seminar in a content area of their choice. Students will demonstrate an ability to critically appraise literature from a variety of sources and synthesize information in both written and presentation formats. [Note: Course will be graded CR/NCR; Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Clinical Rotation 1: Primary Care', '430', 'PHARM', 'Students will provide supervised direct patient care in a primary care setting.  Over the course of 24 weeks, students will be placed in three 8-week rotations where the students ability to apply therapeutic knowledge in a live practice setting will be refined and assessed.  [Offered: W, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
