INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations in Cognitive/Social Development: Basic', '701', 'PSYCH', 'This course will introduce students to classic and contemporary perspectives and research in basic cognitive development.  The instructor may cover a range of topics and perspectives, such as:  core knowledge/cognition; domain-general accounts of knowledge acquisition; methodology in infant/child cognition.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations in Cognitive/Social Development: Social Cognitive Development', '702', 'PSYCH', 'This course will introduce students to classic and contemporary perspectives in research in social development.  The instructor may cover a range of topics and perspectives in social development or social cognition.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations in Language Development: Basic Language Development', '705', 'PSYCH', 'This course will introduce students to the basics of language and to classic and contemporary perspectives and research in language development. The instructor may cover a range of topics and perspectives, including:  nativist vs. empiricist approaches to language development; methodology for the study of infant/child language development; early phonological, lexical, and grammatical development; language processing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations in Language Development: Pragmatics of Language', '706', 'PSYCH', 'This course will introduce students to classic and contemporary perspectives related to pragmatic language development in both early and later childhood.  This course will focus on aspects of language development that take into account multiple interactants (family, pets, teachers) and settings outside the home.  This course will also consider language development from a broader social and multicultural perspective.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ethics of Peacebuilding', '332', 'PACS', 'This course will explore ethical challenges in peacebuilding. It will answer the questions: What are ethical problems in peacebuilding? How can we better understand and assess these problems? And, how can we improve our ability to support, or engage in, ethical peacebuilding practice?');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Mediation Practice', '333', 'PACS', 'This course will give students both a thorough understanding of the mediation process and practical, hands-on mediation experience. Students will develop in-depth, first-hand knowledge and experience resolving conflicts through the role of the mediator as third party. The course will include a series of role-plays, readings, and case studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Jews and Jewishness', '209', 'JS', 'This course examines the central elements that make up contemporary Jewish identity/identities, traditional and otherwise, with particular focus on the North American Jewish experience and the nature of Jewish modernity. It explores historical, literary, religious, and social issues that have contributed to the formation of these identities.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Jews and Jewishness', '209', 'RS', 'This course examines the central elements that make up contemporary Jewish identity/identities, traditional and otherwise, with particular focus on the North American Jewish experience and the nature of Jewish modernity. It explores historical, literary, religious, and social issues that have contributed to the formation of these identities.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Islamic Visual Culture: Art, Architecture, and Aesthetics', '230R', 'SI', 'This course explores Islamic visual culture by focusing on three main design elements in Islamic art and architecture: calligraphy, arabesque, and geometry. The course emphasizes the aesthetic, visual, and cultural importance of ornamentation in Islamic art and examines its manifestation in the work of contemporary Muslim artists.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cold War in East Asia', '377R', 'EASIA', 'This course examines the origins, developments, and contemporary implications of the Cold War in East Asia.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cold War in East Asia', '377R', 'HIST', 'This course examines the origins, developments, and contemporary implications of the Cold War in East Asia.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calligraphy to Conceptual Art: Text as an Image in Islamic and East Asian Visual Arts', '231R', 'SI', 'Exploring the history of Islamic and East Asian calligraphic arts and the study of Eastern and Western artists, this course examines multiple ways in which writing and other forms of visible language are incorporated into visual arts. Through studio projects, students explore conceptual approaches to text-based image making.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
