INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Calligraphy to Conceptual Art: Text as an Image in Islamic and East Asian Visual Arts', '275', 'FINE', 'Exploring the history of Islamic and East Asian calligraphic arts and the study of Eastern and Western artists, this course examines multiple ways in which writing and other forms of visible language are incorporated into visual arts. Through studio projects, students explore conceptual approaches to text-based image making.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Pragmatic Language Development', '316', 'PSYCH', 'This course is an in-depth introduction to pragmatic language development with a focus on childrens conversational and narrative ability. Typical and atypical pragmatic language development is examined from a longitudinal, social-cognitive, and multi-cultural perspective and with respect to longer-term outcomes such as social competence, friendship, and success in school.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Genomics', '469', 'BIOL', 'An applied course on the computational exploration of genomes. Topics include comparative and evolutionary genomics, metagenomics, and genomics applications in health/medicine. The lab will involve analysis of genomic datasets using scripting and bioinformatic software. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chinese Foreign Policy Since 1949', '302R', 'EASIA', 'This course examines the major elements and evolution of Chinese foreign policy since 1949 and Chinas external relations in the context of rising Chinese power.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Chinese Foreign Policy Since 1949', '385', 'PSCI', 'This course examines the major elements and evolution of Chinese foreign policy since 1949 and Chinas external relations in the context of rising Chinese power.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gender and Performance', '282', 'THPERF', 'This course explores how gender is performed on the stage and in the media. Key performances and performers are studied from the perspectives of critical feminist theory and performance studies. Students consider how performances can create, affirm, and disrupt how society understands and performs gender.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Site-Specific Performance', '284', 'THPERF', 'This course explores performance sites as resources for creation. Students explore theoretical and practical approaches to understanding place-making through the body, language, space, and technology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Theories of Theatre and Performance', '300', 'THPERF', 'Students study and critique a range of existing works of theatre and performance in the context of related commentary, paying close attention to concepts of knowledge, experience, and positionality.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 5', '316', 'THPERF', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency through extensive contributions to specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 6', '317', 'THPERF', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency through extensive contributions to specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sustainability in Design', '374', 'THPERF', 'Students explore the interchange between performance design and sustainability. Emphasis will be placed on ecological and systems thinking as students engage with theoretical and practical projects throughout the term.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Race and Performance', '378', 'THPERF', 'This course explores the ways critical race and performance studies can help us understand English language-based performance in the 20th and 21st centuries. Close attention is paid to the primary historical, political, and cultural contexts that inform the creation, development, and presentation of performance works.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mixed Reality Design', '379', 'THPERF', 'This course explores design for material-world experiences that incorporate virtual, augmented, and/or extended reality technologies. Emphasis is placed on the relationships among different modes of perception and how they affect our understanding of the real and the virtual.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Collaborative Performance Project', '400', 'THPERF', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. As core members of the creative team, students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 9', '416', 'THPERF', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. Through extensive contributions to production, students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Production Participation 10', '417', 'THPERF', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. Through extensive contributions to production, students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation. [Note: This is a repeatable course, subject to different content; it may be completed a total of four times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design for Performance Media', '246', 'THPERF', 'Students explore theories and practices of design for performance media including set/space, light, projection, video, sound, and/or costume. In this studio course, emphasis is placed on the ways in which media establish and disrupt performance conventions, changing our relationship to the media themselves and to the world around us.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Literatures', '291', 'ENGL', 'How have cultural exchange, border crossings, and globalization shaped English language and literature? In this course students will discuss literary and cultural productions from around the world and explore themes such as colonialism, transnationalism, and globalization.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Writing for Performance', '366', 'THPERF', 'Students explore techniques for analyzing, interpreting, and creating texts for theatre and performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Literatures of Migration', '280', 'ENGL', 'This course explores the literatures and cultures of diasporic and immigrant communities in North America, such as African, Asian, Caribbean, Middle Eastern, and Latin American. Topics to be covered may include memory, race, hybridity, home, and belonging.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Collaborative Creation', '368', 'THPERF', 'Students explore techniques for collaborative creation in theatre and performance.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Aviation', '374', 'AVIA', 'A course offered in a particular branch of aviation, subject to availability of instructor.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Theatre and Performance in Canada', '280', 'THPERF', 'Students explore theatre and performance in Canada in its historical, political, and cultural contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Grant Writing', '210G', 'ENGL', 'The course covers researching, organizing, drafting, and editing proposals and applications for government grants for organizations. Topics may include interviews with domain experts, working with proposal guidelines and checklists, establishing milestones and expectations, using past proposals as models, treating individual sections as separate sub-genres, and creating coherence and flow in the final draft.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Technical Editing', '210J', 'ENGL', 'This course will introduce students to practices and tools of technical editing, such as language and format editing, verification and fact-checking, style guide consistency, discourse appropriateness, and the use of profession-specific software.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Methods in Technical Communication', '472', 'ENGL', 'This course teaches students the practice and theory of research methods in the field of technical and professional communication. Topics may include resource validity and renewal cycles, data-gathering techniques and analytics, interview techniques for subject-matter experts, rapid research skills, and user-experience design.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Project Management in the Arts', '248', 'THPERF', 'This course introduces students to principles of project management relevant in a broad range of contexts including theatre, dance, film, and digital media production. Students explore arts management concepts such as budgeting, communication, scheduling, leadership, problem-solving, and conflict resolution. Concepts are applied in case studies that accommodate individual student interests.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Computing Society', '210', 'STV', 'This course examines the historical and current relationships between computer technology and society. It explores the impact and consequences of computing from a societal perspective, but also considers various nontechnical factors and values that have shaped computing technology and practice. The scope of the course will range from early mechanical aids, through the mid 20th century invention of electronic digital computers, to the networks and mobile applications of the 21st century. Technological studies relating to gender, education, employment, and war will be used as focal points. Material artifacts will form a core element of the course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Computing Society', '212', 'HIST', 'This course examines the historical and current relationships between computer technology and society. It explores the impact and consequences of computing from a societal perspective, but also considers various nontechnical factors and values that have shaped computing technology and practice. The scope of the course will range from early mechanical aids, through the mid 20th century invention of electronic digital computers, to the networks and mobile applications of the 21st century. Technological studies relating to gender, education, employment, and war will be used as focal points. Material artifacts will form a core element of the course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Physics, the Universe, and Everything', '207', 'SCI', 'How does the universe work? How can it even exist? What is the ultimate nature of reality? This course explores big picture questions that connect humanity to the nature of the physical world. Beginning with introduction to Einsteins ideas and the quantum nature of the universe, this course considers how physicists tackle some of the mysteries (e.g., existence itself) and challenges (e.g., the worlds energy problem) in science today. [Note: No mathematics or other specialized knowledge is required; students from non-science faculties are encouraged to attend. Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Rights and Public Policy', '666', 'PSCI', 'An exploration of the role that rights and rights discourse plays in public policy development and policy change.  Examines Canadian public policy in relation to the Charter of Rights and Freedoms and human rights legislation, as well as cases in comparative context.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Game Theory and Recent Applications', '724', 'MSCI', 'Introduction to game theory including symmetric and asymmetric games, as well as static and dynamic games. Topics include game rules and information, mixed and continuous strategies, moral hazard, adverse selection, and introductory mechanism design. Applications such as: sustainability in supply chains, prediction markets, and contract design, are reviewed in this course.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Matrix-Analytic Methods in Stochastic Models', '731', 'MSCI', 'This course introduces matrix-analytic methods and shows their applications in queuing theory, inventory theory, supply chain management, telecommunications networks, reliability, finance mathematics, and risk and insurance analysis.  In the first half of the course, the basic theory on phase-type distributions, Markovian arrival processes, and matrix-geometric solutions is introduced.  In the second half of the course, applications of matrix-analytic methods in stochastic modeling (queuing, reliability, inventory, supply chain, etc.) are examined.  Previous study of stochastic models is required, such as MSCI 631.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Network Models and Applications', '734', 'MSCI', 'This course treats the broad subject of network models. Network flow problems form a special class of linear programming that arise in a wide variety of applications including transportation, telecommunications, logistics and supply chain management. The purpose is to study the properties of network flow models, to discuss extensions and various solutions approaches, and to survey some applications.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Science and Technology Policy', '744', 'MSCI', 'Science and Technology are increasingly viewed as important tools to promote the social, economic, and political goals of modern economies.  Accordingly, science and technology (S&T) policy issues have assumed prominence in contemporary policy discourse.  Firms are often operated under different policy environments and their strategies should be formulated under the various environments.  This course provides students with the background and tools needed to critically evaluate issues in S&T policy debates.  After reviewing the basics of science and technology policy, we will discuss a few instruments of S&T policy, e.g., intellectual property rights, tax and subsidy, public funding for research, and prizes.  Throughout the course, we will integrate the theoretical discussions with cases of contemporary S&T policy issues.  Previous study of economics is required, such as MSCI 607.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Complexity and Global Governance', '622', 'GGOV', 'The course is an advanced seminar consisting of two major components: 1. An introduction to key concepts, theories, and empirical findings in complexity science, and 2. A review of the implications of our emerging knowledge abut complex systems in three governance domains - energy, climate, and security.  The first component surveys the major branches of thought about complexity in physics, biology, ecology, geography, information theory, technology studies, and economics.  The second component highlights practical applications of complexity science in global governance, emphasizing research findings with clear policy implications.  The course includes some exposure to computational methods of modeling complex phenomena and visualizing complex data.  Assignments include three short integrative reports and a major paper.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Architectural Research and Analysis', '610', 'ARCH', 'This course exposes students to research methodologies and diagrammatic strategies used for the analysis of works of architecture, approaches to design and key texts. These will act as case studies to learn about the conceptual ideas, formal/spatial principles, material systems and operative design strategies employed in the making of architecture while introducing students to analytical methods and processes considered integral to advanced research and design.  The rigorous consideration of precedent and design research methods fosters a deeper connection of students work with the discipline, better enabling them to position their ideas within a larger conceptual discourse while giving them the methodological tools to prepare them for the undertaking of a research and design thesis.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Security and Privacy in Health Systems', '634', 'CS', 'An overview of basic security and privacy principles relevant in the design and use of applications in health settings.  Program security, operating system security, network security, data security, and issues related to the management of security and privacy policies are introduced.  Master of Health Informatics students only.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computer Networks and Distributed Computer Systems', '636', 'CS', 'An introduction to computer networks, Internet protocols, and distributed systems.  Layered models, naming, addressing and routing, reliable communication, security, and elements of distributed system design. Master of Health Informatics students only.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Principles of Data Management and Use', '638', 'CS', 'A user-oriented approach to the management of large collections of data. Relational database technology, relational algebra, SQL, database views, transactions, data modelling methodology, entity-relationship models. Introduction to several current topics in database research, such as warehousing, data mining, managing data streams, data cleaning, data integration, and distributed/parallel databases. Master of Health Informatics and Master of Data Science and Artificial Intelligence students only.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Geomicrobiology', '321', 'EARTH', 'This course will explore how microbial life influences geochemical cycles in natural environments and engineered systems. Emphasis will be on the interactions between microbial activity and environment geochemistry. Topics will include microbial life on early Earth, biomineralization, biosignature preservation, bioremediation, and geomicrobiological research methods. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Theory, Culture and Criticism', '640', 'ARCH', 'Against the backdrop of the larger interdisciplinary field known as cultural studies, this course will trace and examine critical bodies of theory that have influenced the development of contemporary architectural thought and practice.  This course will emphasize and study important writings and theoretical systems that have emerged in various fields including philosophy, art theory/criticism, literary theory, psychoanalysis, the natural and social sciences, and cultural studies, to investigate the ways in which these have generated conceptual tools within architectural theory, criticism and analysis while contributing to the evolution of architectural practice. 
Pedagogical Objectives:  To present a thematically and chronologically organized survey of contemporary architectural theory that focuses on the relationship between seminal theoretical texts drawn from outside of the architectural discipline and critical developments in contemporary architectural theory and practice.  The course is intended to provide students with an advanced knowledge base in contemporary architectural theory to ground more specialized graduate elective coursework while acting as a support for thesis and other forms of advanced architectural research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('MSc Thesis Proposal', '601', 'PHARM', 'This course prepares MSc in Pharmacy students to present their research objectives and proposed methods in written and oral form suitable for an MSc program and involves preparation of a written research proposal and oral defence of the proposal. The written proposal and the oral presentation of the proposal will be considered by an examination committee.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Numerical Methods 1', '204A', 'ECE', 'Application of computational methods to engineering problems. Number systems, errors and error propagation. Roots of nonlinear equations. Introduction to numerical linear algebra. Interpolation and numerical integration. [Offered: F, W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Numerical Methods 2', '204B', 'ECE', 'Application of computational methods to engineering problems. Introduction to numerical solutions of ordinary differential equations, optimization. [Offered: F, S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Civil Engineering Concepts', '100', 'CIVE', 'An introduction to the fundamental methods, principles and skills of civil engineering.  Fundamentals of technical communication, the engineering design process and problem solving.  Completion of a pre-design study and report for a civil engineering project. Independent and team work. Fundamentals of engineering computation: units, data collection, measurement, and error analysis. Field surveying (automatic level, engineers transit, differential Global Positioning System (GPS), total station). Laboratory on engineering graphics auto-computer assisted diagnosis (AutoCAD) and computational software (Excel, Matlab). Aspects of the engineering profession (code of ethics, negligence, misconduct, role of the Professional Engineers Ontario (PEO), etc.), diversity in the workplace, and professional development. Preparation for the University of Waterloo co-operative education program (Co-operative Education and Career Action (CECA), résumé writing, job search and interview skills).  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanics 1', '104', 'CIVE', 'Newtonian mechanics. Force systems; vectors, forces and moments; equilibrium and free body diagrams. Mechanics of simple structures. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Mechanics 2', '105', 'CIVE', 'Distributed forces, centroids and moment of inertia. Introduction to foundations and fluid statics. Basic structural analysis. Plane trusses. Beam diagrams. Stress-strain-temperature relationships. Behaviour of members in tension, compression and bending. Thin-walled pressure vessels. Friction. [Offered: W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra', '115', 'CIVE', 'Linear systems of equations, matrices and determinants. Introduction to the eigenvalue problem. Applications. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra', '115', 'ENVE', 'Linear systems of equations, matrices and determinants. Introduction to the eigenvalue problem. Applications. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Linear Algebra', '115', 'GEOE', 'Linear systems of equations, matrices and determinants. Introduction to the eigenvalue problem. Applications. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Methodology for Vision Science', '650', 'OPTOM', 'The course will work through the process of the scientific method using a grant proposal to NSERC as a framework. The scientific method, research and experimental questions, experimental design, sampling, statistical theory & power, and data analysis will be covered. Ethical and budgetary issues will also be discussed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Data Analysis in Vision Science', '651', 'OPTOM', 'Measurements, probability and power; Detecting difference between parametric/non-parametric variables; Analyzing repeated measures designs; Relationships between parametric/non-parametric variables; Between-eye comparison and correlation; Complex regression; Statistical re-sampling techniques; Analysis of clinical images; Accurancy and repeatability of novel clinical and experimental techniques; Introductory analysis of epidemiological and clinical trials data.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visual Psychophysics', '660', 'OPTOM', 'The course will examine how various factors (luminance, contrast, psychophysical method and ocular disease) can influence the outcome of psychophysical tests that are used in both basic and clinical vision research. Consideration will be given to the utility of such tests by considering their reliability, discriminative power and validity.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Systematic Review and Meta-Analysis', '614', 'PHARM', 'This course will provide students with an overview of the rationale and stages involved in the conduct of a formal systematic review and meta-analysis of a well-defined clinical/health research question. The overarching aim is to provide students with the tools to critically appraise and conduct a systematic review and meta-analysis.  Students will largely work in pairs to progress through each step involved (with feedback from instructors at each stage) and to produce a final systematic review and meta-analysis to be presented/submitted at the end of the course.  
Course Objectives:
1.  To demonstrate an understanding of the rationale underlying a systematic review and meta-analysis and relevance to clinical care and health policy;
2.  To critically appraise a systematic review;
3.  To develop a focused research question amenable to a systematic review;
4.  To develop and implement a comprehensive and systematic literature search strategy;
5.  To determine and apply procedures for including/excluding potential studies for a systematic review and meta-analysis;
6.  To develop and implement a data abstraction process and study database;
7.  To demonstrate an understanding of the fundamental statistical/biostatistical issues relevant to the conduct of a formal systematic review and meta-analysis.
8.  To perform statistical analyses for a systematic review and meta-analysis and complete/present a final report demonstrating all stages involved.

Students will need to meet with Course Coordinators and provide of appropriate previous experience with linear and/or logistic regression techniques.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Systematic Review and Meta-Analysis', '814', 'STAT', 'This course will provide students with an overview of the rationale and stages involved in the conduct of a formal systematic review and meta-analysis of a well-defined clinical/health research question. The overarching aim is to provide students with the tools to critically appraise and conduct a systematic review and meta-analysis.  Students will largely work in pairs to progress through each step involved (with feedback from instructors at each stage) and to produce a final systematic review and meta-analysis to be presented/submitted at the end of the course.  
Course Objectives:
1.  To demonstrate an understanding of the rationale underlying a systematic review and meta-analysis and relevance to clinical care and health policy;
2.  To critically appraise a systematic review;
3.  To develop a focused research question amenable to a systematic review;
4.  To develop and implement a comprehensive and systematic literature search strategy;
5.  To determine and apply procedures for including/excluding potential studies for a systematic review and meta-analysis;
6.  To develop and implement a data abstraction process and study database;
7.  To demonstrate an understanding of the fundamental statistical/biostatistical issues relevant to the conduct of a formal systematic review and meta-analysis.
8.  To perform statistical analyses for a systematic review and meta-analysis and complete/present a final report demonstrating all stages involved.

Students will need to meet with Course Coordinators and provide of appropriate previous experience with linear and/or logistic regression techniques.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transport Principles and Applications', '241', 'CIVE', 'Application of scientific principles to the planning, design, maintenance and management of transportation systems. The basic principles of transportation engineering for contemporary urban transportation modes; auto, transit, cycling and walking, and intercity modes; rail and air. Transportation economics, environmental impacts, and demand estimation. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transportation Engineering Applications', '341', 'CIVE', 'Traffic engineering and travel forecasting. Evaluation, design and management of urban transport systems through advanced traffic control techniques. Quantitative methods for evaluating investments in transportation infrastructure or operational changes. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Air Quality Engineering', '277', 'ENVE', 'Quantitative introduction to indoor and outdoor air pollution sources, and major processes including emission rates, atmospheric dispersion, chemistry, and deposition. Air quality standards and regulations. Basic atmospheric science and meteorology to support the fate and transport of air pollutants. Overview of control and treatment methods. Indoor air exposure estimates and the function of the HVAC components. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Energy and the Environment', '279', 'ENVE', 'Conservation of energy, energy balances on closed systems. Steady-state and transient heat transfer via convection, radiation, and conduction. Mechanical and electrical work. Internal energy, enthalpy, and specific heats of solids, liquids and gases. Phase change in natural environmental systems; The basics of heat engines, refrigerators, and heat pumps. Function, evaluation, and design of energy resource technology: wind and hydroelectric turbines, photovoltaics, geothermal energy, biomass and biofuel, natural gas and petroleum extraction, and tidal energy. Renewable energy policy and implications. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Hydrology and Hydraulics', '383', 'ENVE', 'Physical and mathematical models of hydrological processes at the landscape scale, and hydraulic phenomena in channels. Advanced models of evapotranspiration and snow energy balances. Hydrologic modelling: parameterization, boundary conditions, calibration. Simulation of energy losses, backwater effects, and gradually varying flow profiles in open channels. Sediment transport, scour, and erosion. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Strategic Management of Biopharmaceutical Technology', '615', 'PHARM', 'The strategic development and commercialization of novel and incremental bio/pharma technological innovation is rapidly expanding across all industries and with it comes the need for adept management of the process.  Effective management involves ensuring technological precaution such that the risks and ambiguities associated with advanced technologies are minimized.  Additionally, management must be strategic to maximize the successful achievement of corporate objectives based on the alignment of internal resources with the external environment. The transfer of technology from lab to market and successful propagation is usually impeded by a lack of cohesion between scientific understanding and business adeptness.  This course also serves to analyse and connect scientific principles underlying novel and incremental technologies with strategic management and the commercialization process from an entrepreneurial perspective.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Structural Design', '310', 'CIVE', 'Introduction to structural systems. Systems for carrying gravity and lateral loads in buildings. Structural design concepts. Analysis and design of concrete beams, and one-way slabs. Design of steel beams and tension members. Comparison of steel and concrete framing systems. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Hydrology and Open Channel Flow', '382', 'CIVE', 'Introduction to the water cycle, flood frequency analysis, design storms. Analysis of hydrographs and rainfall-runoff response mechanisms in urban and natural systems. Mass continuity and water budgets at the watershed scale. Impact of land use change on hydrologic response. Quantification of open channel flow; subcritical and supercritical flow regimes. Dynamic forces on submerged structures and low/scour beneath bridges. [Offered as: CIVE 382 (W), ENVE 382 (F,W)]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Hydrology and Open Channel Flow', '382', 'ENVE', 'Introduction to the water cycle, flood frequency analysis, design storms. Analysis of hydrographs and rainfall-runoff response mechanisms in urban and natural systems. Mass continuity and water budgets at the watershed scale. Impact of land use change on hydrologic response. Quantification of open channel flow; subcritical and supercritical flow regimes. Dynamic forces on submerged structures and low/scour beneath bridges. [Offered as: CIVE 382 (W), ENVE 382 (F,W)]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics and Life Cycle Cost Analysis', '392', 'CIVE', 'Project financing, life-cycle cost analysis, time value of money, sensitivity analyses, tax, financial implications of infrastructure projects, quantitative decision-making, financial aspects of a business plan. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics and Life Cycle Cost Analysis', '392', 'ENVE', 'Project financing, life-cycle cost analysis, time value of money, sensitivity analyses, tax, financial implications of infrastructure projects, quantitative decision-making, financial aspects of a business plan. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics and Life Cycle Cost Analysis', '392', 'GEOE', 'Project financing, life-cycle cost analysis, time value of money, sensitivity analyses, tax, financial implications of infrastructure projects, quantitative decision-making, financial aspects of a business plan. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Economics and Life Cycle Cost Analysis', '392', 'AE', 'Project financing, life-cycle cost analysis, time value of money, sensitivity analyses, tax, financial implications of infrastructure projects, quantitative decision-making, financial aspects of a business plan. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Environmental Modelling', '225', 'ENVE', 'Modelling of environmental engineering processes via the solution of differential equations. Mass transfer processes in continuum and discrete systems, with applications to natural and engineered systems. Reactor theory. Understanding initial and boundary conditions. Classical and numerical solution techniques for solving differential equations. Volume and surface integration. Reynolds transport theorem. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluid Mechanics', '280', 'ENVE', 'An introduction to fluid mechanics. Fluid properties. Review of fluid statics. Buoyancy. Bernoulli equation. The momentum equation and applications. Laminar and turbulent flow. Dimensionless numbers. Closed conduit flow including friction losses. Pipe network analysis including energy losses and efficiencies. Four lab sessions. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fluid Mechanics', '280', 'GEOE', 'An introduction to fluid mechanics. Fluid properties. Review of fluid statics. Buoyancy. Bernoulli equation. The momentum equation and applications. Laminar and turbulent flow. Dimensionless numbers. Closed conduit flow including friction losses. Pipe network analysis including energy losses and efficiencies. Four lab sessions. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Decision Making for Environmental Engineers', '335', 'ENVE', 'Decision-making, optimization, and assessment of environmental and water resource systems, and the design process in environmental engineering. Multi-criteria decision-making methods for concept and embodiment engineering design phases. Risk-based system performance metrics and trend tests. Uncertainty estimation via First-Order Uncertainty Analysis and Monte Carlo Simulation. Formulation and solution of linear, integer, and nonlinear optimization models. Multi-objective optimization methods. Sensitivity analysis for decision-making and descriptive (numerical) models. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Biological Processes', '376', 'ENVE', 'Common microbial substrates and metabolisms in engineered and natural systems, kinetics of microbial growth, stoichiometry of nutrient uptake, continuous flow stirred tanks with/without recycle, aeration system design, applications to wastewater treatment, solid waste management, groundwater, and soil remediation. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Reading and Listening for Academic Purposes', '104R', 'EMLS', 'Designed specifically for students for whom English is not the first language, this course offers instruction in reading and listening strategies. Students learn macro and micro reading and listening strategies to improve their reading comprehension and speed and their listening and note-taking skills.  Students also build their academic vocabulary. [Note: Not open to fluent speakers or writers of English.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Professional Communications in Statistics and Actuarial Science', '378', 'ENGL', 'This course introduces students to oral and written communication in the fields of statistics and actuarial science. With emphasis on the public presentation of technical knowledge, the ability to give and receive constructive feedback, and communication in a collaborative environment, this course helps students develop proficiencies in critical workplace skills. This course is writing intensive and includes extensive collaborative assignments. [Note: Students are encouraged to complete this course by their 4A term. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Professional Communications in Statistics and Actuarial Science', '300', 'MTHEL', 'This course introduces students to oral and written communication in the fields of statistics and actuarial science. With emphasis on the public presentation of technical knowledge, the ability to give and receive constructive feedback, and communication in a collaborative environment, this course helps students develop proficiencies in critical workplace skills. This course is writing intensive and includes extensive collaborative assignments. [Note: Students are encouraged to complete this course by their 4A term. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Applied Social Marketing', '309', 'ENBUS', 'Social marketing uses social psychology and marketing concepts to promote behavioural change (e.g. anti-smoking campaigns, promotion of household energy conservation, and so on). This course will focus on developing an understanding of social marketing and behavioural change models. These concepts will be applied in the context of sustainability issues, using case studies to demonstrate successful (and not so successful) social marketing programs.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Industrial Ecology: Sustainable Materials', '406', 'ENBUS', 'Sustainability of industrial materials is examined, including resources, production, use, and end-of-life. Different types of industrial materials and major applications (buildings, transportation, communications, etc.) are covered. Industrial ecology and management approaches help restructure production systems and support sustainable development.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Societal and Environmental Impacts of Nanotechnology', '109', 'NE', 'Nanotechnology in society; health and environmental sustainability; introduction to environmental life cycle assessments; engineering ethics, policies, and regulations; Canadian legal system, tort, and intellectual property; examples of nanotechnology innovation and commercialization. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Vision Science Part 1', '670', 'OPTOM', 'A concentrated review of topics relating to principles of and research in the vision sciences. The course is split into 2 segments (OPTOM 670/671) covering ocular anatomy and ultrastructure and ocular physiology. This course is recommended for all graduate students.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Vision Science Part 2', '671', 'OPTOM', 'A concentrated review of topics relating to principles of and research in the vision sciences. The course is split into 2 segments (OPTOM 670/671) covering optics of the eye and binocular vision. This course is recommended for all graduate students.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Comparative Anatomy & Physiology of the Vertebrate Eye', '672', 'OPTOM', 'An evaluation of evolutionary lines of development of visual structures and functions: the vascular system, accessory glands, light control mechanisms, the distribution of visual pigments, accommodation, binocular vision and neural coding.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visual Development', '680', 'OPTOM', 'Consideration of the development of the optical, and sensory motor functions of vision during the early years of life provides the basis upon which this course examines current methods and literature in basic and clinical vision sciences. Course structure will be organized to suit a students background (optometry, psychology, physics or biology) and their special interests.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visual Motor Neuroscience', '682', 'OPTOM', 'This course will examine the behavior and neural substrates of eye movements, specifically covering fixation, saccades, vestibular ocular interactions, smooth pursuit and the near triad synkinesis (vergence, accommodation and pupil responses).  Oculomotor behaviors for each system will be defined with respect to their specific stimuli, their dynamic and steady state responses.  Adaptive process within and across systems will be examined.  Control theory will be introduced as a means to model each system.  The course will allow emphasis to be focused on oculomotor systems pertinent to the students research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Practical Advanced Data Analysis', '661', 'OPTOM', 'Bayesian statistics, questionnaire analysis, advanced linear and non-linear modeling.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ageing and Vision', '681', 'OPTOM', 'Theories of ageing as they relate to vision.  Ageing changes in structure and function of the eye and visual system.  Impact of visual function on functional (everyday) vision.  Interaction between vision and other ageing changes.  Cortical plasticity and ageing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Visual Sensory Neuroscience', '683', 'OPTOM', 'Visual sensory processing of light, form and space; Ocular somatosensory processing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fiction and Film', '275', 'ENGL', 'A study of the relationships between written and cinematic narrative focusing on adaptations of fiction to film and the different narrative techniques of each medium.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First Nations, Metis, and Inuit Literatures', '211', 'ENGL', 'This course examines literary works in a variety of genres by First Nations, Metis, and Inuit authors in Canada. Students will study the literature in relation to key concepts that recur in Indigenous literary criticism such as land, teaching, and respect.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('First Nations, Metis, and Inuit Literatures', '211', 'GSJ', 'This course examines literary works in a variety of genres by First Nations, Metis, and Inuit authors in Canada. Students will study the literature in relation to key concepts that recur in Indigenous literary criticism such as land, teaching, and respect.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Visual and Digital Media', '510', 'ARCH', 'A range of special topics within visual and digital media that include course offerings in parametric design and scripting, advanced visualization, interaction design, and digital fabrication. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Building Technology and Environmental', '570', 'ARCH', 'A range of special topics courses within building technologies, structures, materials and methods and environmental systems including coursework in alternative energy systems, ecological design and design build. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Urbanism and Landscape', '520', 'ARCH', 'A range of special topics courses within urbanism and landscape. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Architectural History and Theory', '540', 'ARCH', 'A range of special topics courses within cultural and architectural history, theory and criticism. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Cities', '429', 'ARCH', 'A global cities course enabling students to travel abroad and study first hand the architecture and urbanism of cities across Europe, Asia and South America. These course offerings are thematically based and involve field trips to specific architectural sites, cities, and regions around the world. [Note: Additional course travel fee applies to this course, please contact the Architecture Student Services Co-ordinator for current fees. Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Fabrication', '212', 'ARCH', 'This course will introduce students to the tools, work-flows, and culture surrounding computer-aided design/computer-aided manufacturing (CAD/CAM) and its creative applications within architecture. Students will learn how to work with CAD/CAM technologies such as laser cutters, three-dimensional (3D) printers and computer numerical control (CNC) routers while expanding their knowledge of two- and three-dimensional CAD geometries that inform the digital fabrication process. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Milestone Planning and Execution', '411', 'BET', 'This course, taken concurrently with a capstone project or entrepreneurial milestone, consists of workshops, seminars, and online content. Topics can include goal setting and project planning, team building, understanding your competitive landscape and customers, patents and patent searches, commercialization options and developing a business model, legal issues, creating a business case for the capstone project, presentation skills and "pitching", post-mortem and root cause analysis. [Note: Students interested in this course are responsible for finding an instructor willing to supervise it. Course plans must be approved by the Entrepreneurship Minor/Option academic co-ordinator.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Topics in Entrepreneurship', '412', 'BET', 'This course will be taken by students in the corporate entrepreneurship track of the Entrepreneurship Minor, and approved by the program co-ordinator in order to meet the experiential component requirements in the minor with special projects as assigned. [Offered: F,W,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Entrepreneurship for Social Impact', '420', 'BET', 'This course explores how innovative approaches can address social problems, create organizations that make positive change, have significant social impact, and be sustainable and economically viable. Topics include the principles of social entrepreneurship, design thinking applied to identifying significant problems, innovative solutions for social impact, and identifying opportunities for social change. Students develop skills and tools for refining and implementing their ideas as well as measuring the success and value of mission-driven organizations. Through case studies, lectures, and workshops, students also develop skills in strategic thinking with a socially conscious business mindset to create high-impact ventures. [Offered: F,S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Games and Gamers', '225', 'SOC', 'This course examines games, gamers, and gaming culture as entry points to understanding the social implications of digital media technologies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Digital Cultures', '324', 'SOC', 'In this course students will develop a sociological lens to examine the intersection of technology and digital cultures. Students will unpack and problematize key terms, ideas, and discourse associated with digital cultures. Learning outcomes include examining how popular technology discourse both structures social action and obscures underlying relations of power and inequality. Sample topics students will work on include critical investigations of "smart" technologies and cities, online communities, maker cultures, sharing and platform societies, as well as automation and gig work.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Surveillance and Society', '413', 'SOC', 'An examination of the way monitoring technologies alter and shape social life in terms of security, fear, control, and vulnerability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Surveillance and Society', '413', 'LS', 'An examination of the way monitoring technologies alter and shape social life in terms of security, fear, control, and vulnerability.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Modern Arab and Muslim Drama', '132R', 'SI', 'The course explores contemporary Arab and Muslim drama in English (1940s-present) from multiple perspectives, including literary, social, economic, and political.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Modern Arab and Muslim Drama', '132R', 'ENGL', 'The course explores contemporary Arab and Muslim drama in English (1940s-present) from multiple perspectives, including literary, social, economic, and political.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Seminar - Child and Adolescent Psychopathology', '451', 'PSYCH', 'This seminar addresses issues in child/adolescent psychopathology such as mood/anxiety disorders, conduct disorder, psychosis, eating disorders, Tourettes Syndrome, and severe behavioural problems. Emphasis is on theories and research concerning social-emotional functioning, diagnosis, therapy, and socio-cultural contexts. The course also considers challenges and barriers encountered when raising and teaching children/adolescents with a mental illness.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sex, Politics, and Religion in the U.S. and Canada', '241', 'RS', 'What are the three topics that should not be talked about in polite company? Sex, politics, and religion. Breaking the taboo, this course examines religious practices and conflicts around sexuality and sexual behaviour in the United States and Canada, and how they have manifested in political contests and public policy. [Note: Formerly RS 115R.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Professional Conservation and  Restoration Practice 2', '342', 'ERS', 'An applied ecology course that is designed for those interested in becoming professional ecologists. In keeping with the Ecological Society of Americas Professional Ecologist Certification and the Society for Ecological Restorations Certification Programme, the core vehicle is a 7-10 day field trip that involves on-site discussions of the successes and challenges of ecological restoration and/or conservation projects with practitioners. Normally, these projects are located in the Carolinian Zone and have some focus on coastal areas of Lake Erie. The course will provide a platform for learning the advanced and professional principles of restoration and conservation ecology and the restoration of ecosystem services.
[Note: Field trip fee normally $750+HST; will not exceed $1000+HST.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Wildlife Ecology', '346', 'ERS', 'This course introduces the main concepts and principles of wildlife ecology. Topics  include: population dynamics, animal behavior, habitats, genetics, predation, and habitat use. The lab component will introduce students to wildlife data collection, analysis, and interpretation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Wildlife Management', '446', 'ERS', 'This course introduces the main concepts and principles for the management of wildlife species. This course builds on ERS 346 and explores the application of the principles from that course to the management of wildlife.  The lab component will build on the skills of wildlife data collection, analysis, and interpretation presented in ERS 346.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Paths to Sustainability', '406', 'ERS', 'Experiential and reflective course examines everyday significance of several metaphors we live by both as citizens and as environmentalists.  Examines links between language, worldview and sustainability by contrasting metaphors used in sustainability discourse, including ecological balance and health, ecosystem services, planetary boundaries, resilience and restoration.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Ecosystem Field Research', '443', 'ERS', 'This field research course is designed to involve students in high level intensive research on the function and/or structure of ecosystems as they change because of successional and human processes. Students will normally undertake an experimental approach to an ecosystem-based problem and evaluation outcomes of their experiment or long-term data sets. The course will be focused on one ecosystem per offering, e.g. marine, aquatic, terrestrial forest, mountain. It is expected that the course will be off-campus at a field station or protected area within North America but there may be opportunities to deliver it outside of North America. When offered, the syllabus will provide details on exact location. [Note: Field trip fee normally $2500+HST; will not exceed $3125+HST.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Immunology', '341', 'BIOL', 'This course will provide students with an understanding of the fundamental concepts of the vertebrate immune system. Topics will focus on the components of the immune system, innate and adaptive immunity, immune cell development, communication, and recognition of non-self leading to an
immune response. [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Politics of South Asia', '359', 'PSCI', 'How do international and domestic politics interact in South Asia? Students will study bilateral relations between countries within South Asia and the involvement of external powers, such as the U.S. and China, and delve into the challenges of political development in the region as they are shaped by class, ethnicity, religion, and gender.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Kings, Generals, and Tyrants', '450', 'PSCI', 'What are the differences between non-democracies like China, hybrid regimes like Russia, one-party states like Singapore, patrimonial and personalist states in Africa, and military dictatorships in Latin America? Students will study how political power is structured in authoritarian regimes and how it shapes the relationship between state and society.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Geographies of Health', '325', 'GEOG', 'A critical examination of major themes related to the distribution, diffusion, determinants and delivery of health and health care with a particular emphasis on the roles of space, place and environment.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Environment', '374', 'ENVS', 'This special topics course covers material relevant to all students in the Faculty of Environment. Course content is not specific to an individual program and is specified by the instructor at the level indicated by the course number.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Environment', '274', 'ENVS', 'This special topics course covers material relevant to all students in the Faculty of Environment. Course content is not specific to an individual program and is specified by the instructor at the level indicated by the course number.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design in Planning', '705', 'PLAN', 'The foundation of this course is the intersection of the strong relation between urban design as the shaping of urban space and urban planning as the strategic and regulatory mediation of urban change. Establishing the basis of this relationship, the course critically explores contemporary issues in urban design and their implications for urban futures, planning, the environment, and the public realm. Case studies drawn from around the globe are featured.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design Studio - Comprehensive Building Design', '691', 'ARCH', 'Through the integration and application of skills and knowledge to a complex building project, students will develop designs to a high level of detail. A concern for technical material, environmental and legal aspects of Architecture will support open speculation and innovative design. Studio fee $25.00.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Steel & Concrete: Design, Structure and Construction', '662', 'ARCH', 'Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural steel and concrete systems. Topics such as tension, flexural and compression members; and connections are studies using calculations, design aids, rules of thumb and the latest CSA design standards. Heldwith ARCH 362- graduate students will be required to complete additional coursework for graduate level course credit.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Thesis Research & Design Studio II', '693', 'ARCH', 'Graduate Thesis Research & Design Studio II guides students through the development of their thesis projects. Students may draw from a wide range of sources both within and beyond the conventional boundaries of the architectural discipline, using methods of exploration that mat employ the tools of architectural production and critique, and that culminate in a written research document and/or design proposal. The goal of the Graduate Thesis is to make an active contribution to the field by opening up new ways of understanding an existing architectural topic. Studio fee $ 25.00.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Global Cities', '629', 'ARCH', 'An elective offering that enables students to travel abroad and study firsthand the architecture and urbanism of cities across Europe, Asia and South America. Sections are thematically based and include field trips to specific architectural sites, cities and regions around the world. Additional travel costs associated with field trips to be determined based on location.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Design Studio', '690', 'ARCH', 'The Design Studio is subdivided into various sections, providing students the opportunity to select an Architectural studio of their interest and undertake a detailed exploration of design project within this theme. Studio fee $25.00.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics in Intercultural Perspectives', '790', 'GER', 'This course is normally taught in Mannheim, Germany.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Apprenticeship I', '823', 'PSYCH', 'Intended for students who will be seeking further studies at the Ph.D. level, this research apprenticeship will provide a student with a more advanced laboratory research project over two terms and requiring approximately an extra 3.5 hours per week in addition to the 10 hours per week required of the Research Lab Internship required of all MASc DCS students. The particular project that the student will be engaged in, and the ultimate end product of the Apprenticeship at the end of the two terms, will be determined by the student and advisor together.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Apprenticeship II', '824', 'PSYCH', 'Intended for students who will be seeking further studies at the Ph.D. level, this research apprenticeship will provide a student with a more advanced laboratory research project over two terms and requiring approximately an extra 3.5 hours per week in addition to the 10 hours per week required of the Research Lab Internship required of all MASc DCS students. The particular project that the student will be engaged in, and the ultimate end project of the Apprenticeship at the end of the two terms, will be determined by the student and advisor together.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Problem Solving and Proof in Geometry', '661', 'MATH', 'This course explores the Euclidean geometry of triangles and circles from elementary to advanced settings. The course also briefly discusses three-dimensional geometry. An emphasis is placed on proof, on problem solving, on problem creation in a geometric context, and on aspects of teaching geometry. Where possible, problems that combine multiple areas of mathematics are used.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computer Programming 1', '105', 'CS', 'An introduction to the fundamentals of computer programming through media computation. Students will learn to write interactive graphical programs. Fundamental language concepts such as variables, conditionals, loops, functions, and arrays. Programming concepts such as coding style, modular design, testing, and debugging. Media concepts such as 2D graphics drawing, input, animation, and image processing.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Computer Programming 2', '106', 'CS', 'A continuation of the introduction to computer programming begun in CS 105. The use of programming, in conjunction with libraries, as a means of solving practical problems in art, design, and data processing. Basic text processing, manipulation of images and sound, handling and visualization of tabular and hierarchical data. Introductions to user interfaces, physical simulation, and object-oriented programming.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Korean Law and Society', '304R', 'EASIA', 'This course examines the legal system, legal culture, and socio-cultural traditions of the Republic of Korea from a comparative legal perspective.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Pre-Modern Japanese Literature', '362R', 'EASIA', 'Students study a selection of works of early Japanese literature from its beginnings to the 17th century and examine a wide variety of genres, including poetry, diaries, military tales, and philosophical tracts composed during that time period.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Early Modern Japanese Literature', '363R', 'EASIA', 'A study of a selection of works of Japanese literature from the 17th century to the early 20th century, examining a wide variety of genres in early modern Japan.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Psychological Interventions', '444R', 'PSYCH', 'This course reviews research showing how brief, non-invasive, low-cost, social psychological treatments can have small but significant benefits months and years later. Topics include school-based interventions, health interventions, psychological well-being interventions, prejudice-reduction interventions, when and why interventions can fail, and the theories behind how interventions work.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organizational Psychology', '875', 'PSYCH', 'This course considers psychological theories and research that focuses on the behavior of people at work. In particular, there will be an emphasis on the psychology of the individual at work (often referred to as the "micro" component of organizational psychology). Accordingly, topics examined will be self-regulation, negotiation, ethics, and citizenship behavior. By necessity, coverage of these topics will not be comprehensive; the primary goal of the course is to simply introduce students to a broad range of theories and topics that are relevant to organizational psychology.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Psychology of Justice in the Workplace', '876', 'PSYCH', 'This doctoral seminar will examine historic and contemporary psychological theories and research on justice with a focus on understanding the dynamics of justice in the work setting.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Honours Seminar - Close Relationships', '459', 'PSYCH', 'This course covers social psychological theories of close relationships with emphasis on reading and discussion of original empirical articles. Topics may include attraction, partner selection, trust and power, attachment, communications, conflict, intimacy, and relationship maintenance and dissolution. Student roles include presenting articles, leading discussions, and submitting a written research proposal.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Academic Writing', '209', 'ENGL', 'This course will explore relationships between audience, situation, purpose, and form in academic writing in the disciplines. Students will explore the rhetorical features of knowledge creation across fields of study. They will practice situated inquiry and argumentation through a variety of research-based written exercises, including a significant research project in a field of their choice.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The First World War', '312', 'HIST', 'This course examines the origins, conduct, and wider international legacy of the First World War. The course has an international outline, but with a particular Canadian focus as it explores tactics, strategy, and the soldiers experience, as well as the Wars lasting economic, political, and cultural impacts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Law and Violence', '386', 'LS', 'Discussions about laws coercive power raise important questions about the legitimacy of state-sanctioned violence. This course uncovers the roots and limits of laws power as it relates to questions of violence, obedience, and dissent.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Law in Popular Culture', '286', 'LS', 'Much is at stake in how law is portrayed in paintings, literature, music, television, and movies. In this course students will draw on a range of disciplines in the social sciences and humanities to study how law is represented in popular culture, and how these representations, in turn, impact how we view law.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organized Crime', '263', 'LS', 'An examination of select criminal organizations in North America. Particular attention will be given to the social history of "the mafia" and the development of legal tools for policing criminal organizations. Additional themes for discussion include enterprise and economic crimes, corruption, and the role of women in organized crime.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Organized Crime', '263', 'SOC', 'An examination of select criminal organizations in North America. Particular attention will be given to the social history of "the mafia" and the development of legal tools for policing criminal organizations. Additional themes for discussion include enterprise and economic crimes, corruption, and the role of women in organized crime.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transnational Organized Crime', '461', 'LS', 'This seminar course examines contemporary legal regimes surrounding transnational organized crime, terrorism and organized crime, and international money laundering.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transnational Organized Crime', '461', 'SOC', 'This seminar course examines contemporary legal regimes surrounding transnational organized crime, terrorism and organized crime, and international money laundering.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Theory and Applications in Program Evaluation', '651', 'HLTH', 'An advanced program and policy evaluation course that provides theoretical knowledge, skills, and application of program evaluation approaches, including organizational and program planning. A more in-depth coverage of topics will be presented, including case studies and the understanding and use of program and policy intervention theory. This includes the creation of logic models, the identification of the purpose of the evaluation, the development of an appropriate evaluation design, and consideration of factors associated with knowledge use.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Qualitative Methods and Analysis', '652', 'HLTH', 'This course explores qualitative method approaches to program evaluation and public health practice. A critical analysis will inform health policy and practice based on methodological approaches most appropriate to the type of evaluation being conducted. Data collection tools, such as focus groups or interviews will be taught, as well as useful techniques for data analysis, interpretation of results, and presentation of findings. Mixed methods are introduced.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Evaluation Practice and Management', '653', 'HLTH', 'Key concepts necessary for successful evaluation practice and management are discussed and applied using case examples. Evaluation practice examples that are discussed include learning to understand and incorporate consideration of diversity, societal, organizational and environmental context in the conduct of evaluations. Key project management concepts relevant to each step of the evaluation of intervention will be covered, including (but not limited to) selecting research questions and focusing design considerations, budgets, timelines, politics, and contingency plans as well as creating and working in teams.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Systems Thinking and Analysis in Health Program Planning and Evaluation', '654', 'HLTH', 'This course provides the theory and tools needed to apply systems thinking and a systems approach to health program evaluation. Course includes application of ethical theory to health reform, systems approaches to health programming planning and evaluation, international comparison of health systems, and an in-depth investigation of health sector subsystems or building blocks.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Health Measurement and Survey Methods', '655', 'HLTH', 'This course provides in-depth coverage of measurement and survey methods needed for evaluation data collection and other research or clinical applications.  Various measurement approaches will be studied, including classical test theory and item response theory. Students will learn how to develop a measure or questionnaire and test its reliability and validity, or to critically appraise an existing measure or questionnaire. Issues and methods related to administering health measures or questionnaires will be covered, including sampling and ethical issues.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Cultural Dimensions in English/Spanish Literary Translation', '395', 'SPAN', 'This course focuses on the actual practice of translating literature, not only as the transformation of textual expressions, but also as a process of cultural interpretation. Besides reinforcing vocabulary and grammar, students will also review and research notions of different literary genres (poetry, narrative, and theatre), and solidify their knowledge of cultural studies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Energy and Environment', '659', 'ME', 'Review Canadas and the worlds energy situation from energy reserve availability, to consumption trends, to environmental impact. Energy resources covered include fossil fuels, nuclear, solar, wind, geothermal, and others. The technological capabilities and challenges associated with, and physics and engineering principles behind, a range of energy conversion systems are covered. The energy conversion systems covered are fossil fuel systems, nuclear fission power plants, photovoltaic and thermal solar collector systems, wind energy systems, and a range of other systems as appropriate from wave and tidal energy to fuel cells. The thermodynamics of energy conversion systems is considered from the perspectives of optimization and improving energy conversion efficiency. May be held with ME 459 (Energy Conversion) but with enhancements for the ME 659 students. Also offered Online.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Design Engineering', '680', 'ME', 'An overview of advanced engineering design, from the establishment of the need to preliminary design, including formal and informal methods to facilitate and encourage innovation. Focus is places on a fundamental understanding of the problem and various theoretical and practical techniques to arrive at feasible solutions. There is a strong focus on practical applications, including case studies, and culminating in a major design project.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamental Fire Dynamics', '671', 'ME', 'Fuels and their chemical structure. Polymers and polymer decomposition, ignition of liquids and solids, geometric and chemical effects. Combustion processes and reaction stoichiometry. Ignition, catalysis and combustion kinetics. Heat of reaction, flame temperature, fire heat release rate. Flammability, evaporation, flashpoint. Conduction, convection and radiation heat transfer in fire analysis. Gas and flame radiation, point source, planar models. Premixed flames, explosions, deflagrations. Suppression, flame arrestors, "explosion proof" equipment. Diffusion and jet flames, fire plumes, flame heights, CO, soot and smoke formation. (Also offered Online)');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Design Engineering- Design Project 1', '681', 'ME', 'In this course students apply design methods, principles and practices learned in the previous course (ME 680) to a highly authentic design project challenge. Students apply methods, principles and practices related largely to proposal refinement, needs assessment, conceptual design, embodiment design, design verification and design project communication. Aspects of design safety, sustainability and professional ethics and emphasized during the realization of the design project. Three graded design project reviews provide formative and summative assessment opportunities, as well as weekly instructor mentorship and assignments, preferably in a studio setting.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Design Engineering- Design Project 2', '682', 'ME', 'In this course students apply design methods, principles and practices learned in the previous courses (ME 680 and ME 681) to a highly authentic design project challenge. Students apply methods, principles and practices related largely to embodiment design, design verification, design implementation and refinement and design project communication. Aspects of design safety, sustainability and professional ethics and emphasized during the realization of the design project. Three graded design project reviews provide formative and summative assessment opportunities, as well as weekly instructor mentorship and assignments, preferably in a studio setting.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fire Modelling', '673', 'ME', 'Introduction to Computational Fluids Dynamics (CFD) simulation of complex, fire-related flows. First-and second-order finite control volume discretization and iterative solution methods. Introduction to finite element analysis. Boundary conditions, turbulence models and grid generation techniques. Assessment of strengths, limitations and uncertainty in simulation of complex applications relevant to fire engineering. Introduction to zone and field models, such as CFAST and FDS, with application to practical fire engineering applications and design. ME 671 recommended but not required. (Also offered Online)');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Fire Dynamics', '672', 'ME', 'Ignitability of materials, flame spread, burning rates. Specification of design fires. Stages of room fires, hot layer development and composition. Thermal stratification, entrainment, ceiling flames and jets, smoke filling. Principles of ventilation, wall and ceiling vents, pressure distributions, smoke control. Wall and ceiling heat transfer, corner effects. Enclosure and zone radiation. Room fire growth and spread, flashover, rapid fire growth phenomena. Use of zone/field models for fire development. (Also offered Online)');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('JFK: The Decision-Maker Behind the Myth', '289', 'HIST', 'This course re-examines John F. Kennedys presidency, refuting myths about his decision-making in war and peace. Students grapple with historical sources in a trans-media platform. They are invited to relate the past to contemporary issues of war and peace in an intensive learning experience.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Genres of Creative Writing', '210C', 'ENGL', 'This course introduces students to both contemporary and historical forms of creative writing. Students will explore genres of poetry, prose, and/or drama through their own writing. Students will also investigate the culture of publishing, learn key revision strategies, and workshop the writing of their peers to develop their critical abilities.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Metaphysics and Epistemology', '251', 'PHIL', 'This course introduces philosophical theories about the nature of reality (metaphysics), how we gain knowledge of it (epistemology), and of why such questions matter. Topics may include skepticism, the nature of rationality, knowledge of other minds, realism and anti-realism, and free will and determinism.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Philosophy of Economics', '205', 'PHIL', 'This course considers conceptual, methodological, foundational, and ethical issues in economic theory and practice. Questions include: What can philosophy teach us about economic methodology and justification? Are economic formalizations useful idealizations of human behaviour? Is economics a science? What role do values play in economic reasoning and policy-making?');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Philosophy of Economics', '261', 'ECON', 'This course considers conceptual, methodological, foundational, and ethical issues in economic theory and practice. Questions include: What can philosophy teach us about economic methodology and justification? Are economic formalizations useful idealizations of human behaviour? Is economics a science? What role do values play in economic reasoning and policy-making?');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Philosophy of Sport', '206', 'PHIL', 'This course uses the tools of philosophy to ask important questions about sport, such as: What is sport? What is the value of sport for participants or fans? Are violent sports unethical? Should performance enhancing drugs be banned? What is luck, and how is it different from skill?');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Philosophy of Sport', '206', 'REC', 'This course uses the tools of philosophy to ask important questions about sport, such as: What is sport? What is the value of sport for participants or fans? Are violent sports unethical? Should performance enhancing drugs be banned? What is luck, and how is it different from skill?');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Introduction to Real Analysis', '333', 'PMATH', 'The purpose of the course is to present the familiar concepts of calculus at a rigorous level and to provide students who took the MATH 137/MATH 138/MATH 237 sequence with the background needed to be successful in PMATH 351 and PMATH 352. Topics discussed include the completeness properties of the reals; the density of the rationals; the topology of real n-dimensional space: open and closed sets, connectedness, compactness (by open covers), the Heine-Borel theorem, completeness; sequences in real n-dimensional space: convergence, Cauchy sequences, subsequences, the Bolzano-Weierstrass theorem; multivariable functions: limits, point-wise and uniform continuity, the extreme value theorem, uniform convergence of sequences of functions, Taylors theorem, term-by-term differentiation of power series; integration in real n-dimensional space: Riemann integrability, Fubinis theorem for continuous functions on rectangles, term-by-term integration of power series.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Issues in Health 1', '173', 'HLTH', 'One or more term courses will be offered from time to time as announced by the School of Public Health and Health Systems. Topics will be dependent upon special research and/or instructional interests of the faculty.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '391', 'MSCI', 'A work-term report is a written report that provides an opportunity for students to effectively communicate, document, and reflect on engineering experience gained during a preceding work term. In the report, students draw connections between the theoretical aspects of engineering taught in the classroom and the practical applications of that theory in the workplace. Reports are due on the 10th day of lectures for the academic term in which the report is required.  [Offered: W]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '392', 'MSCI', 'A work-term report is a written report that provides an opportunity for students to effectively communicate, document, and reflect on engineering experience gained during a preceding work term. In the report, students draw connections between the theoretical aspects of engineering taught in the classroom and the practical applications of that theory in the workplace. Reports are due on the 10th day of lectures for the academic term in which the report is required. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '491', 'MSCI', 'A work-term report is a written report that provides an opportunity for students to effectively communicate, document, and reflect on engineering experience gained during a preceding work term. In the report, students draw connections between the theoretical aspects of engineering taught in the classroom and the practical applications of that theory in the workplace. Reports are due on the 10th day of lectures for the academic term in which the report is required. [Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Archaeologies of Landscape', '415', 'ANTH', 'This course examines the ways in which past peoples experienced, transformed, remembered, represented, and controlled landscapes. Readings and discussions emphasize both contemporary theoretical approaches and case studies from various settings in an effort to illuminate the social, political, economic, and ideological dimensions of landscape.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gender and Global Politics', '420', 'PSCI', 'Does looking at the world through the lens of gender change how we see the state, sovereignty, diplomacy, security, trade, migration, globalization, governance, and other foundational concepts in global politics? Students will review feminist theories of politics, with a particular focus on international relations and global governance; examine how gender shapes the roles and experiences of women and men in global politics; and discuss how to do feminist research.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Media, Images, and Communication', '339', 'COMMST', 'This course investigates how distinct modes of communication impact the creation of messages, dissemination or knowledge, and reception of meaning. Students will examine aural, textual, and visual communication in the context of print, electronic, and digital media.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Power, Agency, Community', '335', 'COMMST', 'This course examines the ways in which individuals and institutions communicate to enact agency and build communities situated within power relations. Students will develop their understanding of what power and agency are, the relationship of each to communication, and how each constitutes community life.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Work-term Report', '100M', 'WKRPT', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Work-term reports are required courses for the degree but their grades are not included in the average. [Note: Credit for WKRPT 100M is granted upon the successful completion of the report component of PD 2 and no additional work is required for WKRPT 100M.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sociology of Activity, Health, and Well-being', '107', 'HEALTH', 'This course will focus on the social and cultural factors that influence leisure, activity, health, and well-being in different settings and among different populations. Sociological literature will be used to examine activity, health, and well-being in the lives of individuals, groups, and society. [Note: Formerly AHS 107]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Foundations of a Healthy Lifestyle', '100', 'HEALTH', 'This course will introduce the student to the basics of health, wellness, and disease, with an emphasis on assessing and evaluating the students own lifestyle and health risk, and committing to a plan to begin or maintain a healthy lifestyle across the life course. [Note: Formerly AHS 100]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Movement Assessment and Exercise Prescription', '204', 'KIN', 'This course is designed to provide students with an understanding of movement competency and performance enhancement of apparently healthy individuals. The breadth of tests and assessment tools to determine capabilities with respect to mobility, movement patterns, physical activity, fitness, and lifestyle will be covered in detail and practiced and applied in KIN 204L.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Research Design and Statistics', '232', 'KIN', 'An introduction to research design, statistical analysis, and their application to kinesiology and clinical health practice. A primary focus is to provide students with the tools to critically evaluate sources of information pertinent to their chosen profession.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Leisure Studies 1', '172', 'REC', 'One or more term courses will be offered from time to time as announced by the Recreation and Leisure Studies Department. Subjects will be dependent upon special research and/or instructional interests of faculty.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Leisure and Social Justice', '201', 'REC', 'This course focuses on what social justice means in leisure studies and explores why social justice matters by introducing issues related to inclusivity, privilege, power, marginalization, and oppression. Areas of exploration may include but are not limited to age, class and poverty, (dis)ability, non-human interactions, race and ethnicity, sexual and gender identities, and status.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Business of Professional Sport', '213', 'REC', 'This course explores the business of professional sport around the world. Topics may include league policy and governance, strategic growth and development, media, team building, analytics, financial management, and business ethics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Entrepreneurship for Change', '218', 'REC', 'This course examines social entrepreneurship as a means for improving individual and community well-being. Focus will be placed on learning and implementing tools which businesses, citizens, and consumers can use to address social issues. Students will complete the course with an understanding of their personal responsibility and role as community/ global citizen which will aid them in their career development. Particular emphasis may be placed on the role of social entrepreneurship in addressing issues related to social justice, inclusion, and sustainable management practices.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Leisure Studies 2', '272', 'REC', 'One or more term courses will be offered from time to time as announced by the Recreation and Leisure Studies Department. Subjects will be dependent upon special research and/or instructional interests of faculty.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Amateur Sport from Playground to Podium', '313', 'REC', 'This course introduces students to the policies, programming, and management strategies involved in supporting the amateur sport system from early sport and recreation participation to the international stage. Students will learn about key actors, sport policy, long-term athlete development, board governance, fundraising and grants, strategic partnerships, and volunteer management. Specific emphasis will be given to critical thinking related to how effective governance systems can be implemented.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Leisure Studies 3', '372', 'REC', 'One or more term courses will be offered from time to time as announced by the Recreation and Leisure Studies Department. Subjects will be dependent upon special research and/or instructional interests of faculty.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Qualitative Approaches to Leisure Research', '373', 'REC', 'An exploration of qualitative methodologies used in the fields of leisure. Philosophies, theoretical orientations, and ethical considerations will be emphasized as students discuss and participate in various approaches to research design, data collection, analysis, and representation.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Quantitative Approaches to Health Science', '204', 'HLTH', 'An introduction to descriptive and basic inferential statistics and their application to health research. Various statistical techniques commonly used in health research are covered and applied to data using software.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Issues in Health 2', '273', 'HLTH', 'One or more term courses will be offered from time to time as announced by the School of Public Health and Health Systems. Topics will be dependent upon special research and/or instructional interests of the faculty.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Contemporary Issues in Health 3', '373', 'HLTH', 'One or more term courses will be offered from time to time as announced by the School of Public Health and Health Systems. Topics will be dependent upon special research and/or instructional interests of the faculty');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Seminar in Cognitive Science', '600', 'PHIL', 'Cognitive Science is the interdisciplinary study of mind and intelligence. This seminar will discuss controversial topics concerning the nature of human and computer intelligence');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Muslim Lives and Practices Worldwide', '312', 'RS', 'This course examines the diversity of Muslim lives and practices worldwide. Selected case studies raise issues such as gender, authority, stereotypes, fundamentalism, and secularism. [Note: This course fulfils an Area 1 requirement for Religious Studies majors.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Gothic Monsters', '208G', 'ENGL', 'A study of monstrosity, fear, terror, and horror in the gothic mode from its origins to the present, with attention to the ways various genres (from the novel to new media) represent gothic sexualities, genders, politics, and aesthetics.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in East Asian Studies', '291R', 'EASIA', 'This course deals with selected topics in East Asian studies. [Note: This is a repeatable course, subject to different content; it may be completed a total of five times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics', '391R', 'CHINA', 'This course deals with advanced topics in Chinese language and literature. Students are expected to have at least an intermediate level of language proficiency. [Note: This is a repeatable course, subject to different content; it may be completed a total of five times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics', '391R', 'JAPAN', 'This course deals with advanced topics in Japanese language and literature. Students are expected to have at least an intermediate level of language proficiency. [Note: This is a repeatable course, subject to different content; it may be completed a total of five times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics', '391R', 'KOREA', 'This course deals with advanced topics in Korean language and literature. Students are expected to have at least an intermediate level of language proficiency. [Note: This is a repeatable course, subject to different content; it may be completed a total of five times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in History', '421', 'HIST', 'A special study of a selected topic in history. Please see course instructor for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in History', '422', 'HIST', 'This seminar is a special study of a selected topic in history. Please see course instructor for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of three times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Making History', '450', 'HIST', 'This course challenges students with an opportunity to synthesize and showcase, at a high level of achievement, the disciplinary skills and knowledge they have gained during the course of their studies in History. It encourages students to pursue individual research interests and presentation formats as limited only by historical methodology, academic rigour, and the consent of the instructor. Course focus may be shaped by instructor expertise. [Note: This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Processes for Technical Report Writing', '11', 'PD', 'This course develops flexible and broadly applicable report writing skills by involving students in various stages of the writing process, from drafting to revision. Students develop context-specific content for their own technical report and learn strategies for peer review.
[Note: Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Critical Reflection for Growth in the Workplace', '12', 'PD', 'This course supports students in strengthening their self-awareness and critical reflection skills as they relate to the workplace. Using real-world examples and authentic assessments, students will develop their critical reflection toolkit and apply it to workplace challenges such as navigating group dynamics, managing failure and building resilience, and self-advocacy. [Note: Course will be graded on a CR/NCR basis.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('How Music Matters', '110', 'MUSIC', 'This course studies music from around the globe and throughout the ages, employing several approaches in studying how music reflects and responds to its diverse social, political, and historical contexts.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music as a Global Phenomenon', '232', 'MUSIC', 'A survey of music studied in a cross-cultural perspective with regard to its sound, performance practices, meanings, and functions from selected world traditions found in the diverse cultures of Africa, Asia, Latin America, North America, Europe, and the Middle East.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Musical Rhythms of the World', '233', 'MUSIC', 'A study of rhythm in selected musics from oral traditions worldwide. The course emphasizes communal spirit, musical interaction, and human experience through theoretical and ethnographic studies, and hands-on workshops. It focuses on the development of rhythm skills, embodiment of new sounds, and acquisition of new tools for teaching, composing, and thinking about music. Experience a unique opportunity to study the rhythms of diverse music cultures, learn from guest artists, and play a range of percussion instruments including drums, bells, shakers, bronze metallophones, and gongs.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music for Vocal Ensemble', '262', 'MUSIC', 'A survey of the great masterpieces for vocal ensemble from Medieval times to the present studied through reading, lecture, and listening. Principle choral genres of each historical era are studied and discussed including works performed by the Grand Philharmonic Choir during the term. [Note: The ability to read music notation is not required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Music and Landscape', '333', 'MUSIC', 'This course explores how landscape is represented in music from Vivaldi and Beethoven to popular genres and Canadian music. Topics include the role of landscape and music in cultural identity, ways in which music evokes space and place, music and the environment, soundscape, and site-specific music.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Perspectives in Music and Peace', '335', 'MUSIC', 'This course examines musics role in peace and conflict from a variety of perspectives through listening, reading, discussion, and using examples from around the world. Topics include the use of music in dissent and resistance, and as an instrument of propaganda, conflict, and peacebuilding.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Perspectives in Music and Peace', '335', 'PACS', 'This course examines musics role in peace and conflict from a variety of perspectives through listening, reading, discussion, and using examples from around the world. Topics include the use of music in dissent and resistance, and as an instrument of propaganda, conflict, and peacebuilding.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Global Music', '392', 'MUSIC', 'A study of a selected topic in global music. Consult department for details. [Note: This is a repeatable course, subject to different content; it may be completed a total of two times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Digital Art Studio', '383', 'FINE', 'An upper-level studio course to create computational projects that function as art works and aesthetic experiences. Students will work in an interdisciplinary environment to combine computer science principles with fine art technical and conceptual skills.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Computational Digital Art Studio', '383', 'CS', 'An upper-level studio course to create computational projects that function as art works and aesthetic experiences. Students will work in an interdisciplinary environment to combine computer science principles with fine art technical and conceptual skills.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Migration, Diaspora, and Exile in Muslim Narratives', '240R', 'SI', 'This course examines Muslim narratives written in the diaspora, such as from North America or the United Kingdom. It investigates the diversity of Islamic culture and expression in diasporic contexts, exploring an array of experiences and issues written from various sociocultural locations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Migration, Diaspora, and Exile in Muslim Narratives', '240R', 'ENGL', 'This course examines Muslim narratives written in the diaspora, such as from North America or the United Kingdom. It investigates the diversity of Islamic culture and expression in diasporic contexts, exploring an array of experiences and issues written from various sociocultural locations.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sacred Spaces and Human Geographies in Muslim Literary Expressions', '241R', 'SI', 'Using the Muslim dimension as a central theme, this course explores the social, cultural, and political implications to be found in a range of postcolonial literatures from Africa, South Asia, and the Middle East. Students investigate issues such as identities, nationalism and politics, cultural memory, and sacred space and place.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sacred Spaces and Human Geographies in Muslim Literary Expressions', '241R', 'ENGL', 'Using the Muslim dimension as a central theme, this course explores the social, cultural, and political implications to be found in a range of postcolonial literatures from Africa, South Asia, and the Middle East. Students investigate issues such as identities, nationalism and politics, cultural memory, and sacred space and place.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The History of Islamic Civilization from Late Antiquity to 1300', '250R', 'SI', 'This course surveys the diverse history of Islamic societies from the seventh to the thirteenth century. The primary focus is on the central Islamic lands from Egypt to Iran. In addition to the standard narrative, the course also examines salient aspects of Islamic civilization on a thematic basis. [Note: SI 121R recommended.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The History of Islamic Civilization from Late Antiquity to 1300', '250R', 'MEDVL', 'This course surveys the diverse history of Islamic societies from the seventh to the thirteenth century. The primary focus is on the central Islamic lands from Egypt to Iran. In addition to the standard narrative, the course also examines salient aspects of Islamic civilization on a thematic basis. [Note: SI 121R recommended.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The History of Islamic Civilization from 1300-1800: The Islamic Gunpowder Empires', '251R', 'SI', 'This course studies in comparative fashion the rise, consolidation, and decline of the three major Islamic empires of the early modern period (c. 1300-1800): the Ottomans in Anatolia, the Balkans, and the Middle East; the Safavids in Iran; and the Mughals in India. [Note: SI 121R recommended.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The History of Islamic Civilization from 1300-1800: The Islamic Gunpowder Empires', '251R', 'MEDVL', 'This course studies in comparative fashion the rise, consolidation, and decline of the three major Islamic empires of the early modern period (c. 1300-1800): the Ottomans in Anatolia, the Balkans, and the Middle East; the Safavids in Iran; and the Mughals in India. [Note: SI 121R recommended.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transcultural Studies', '200', 'GER', 'This course provides an introduction to transcultural views of literature, film, language, and literature and media culture (e.g., fairy tales and their adaptation in other cultures, authors who work in multiple languages, transcultural comics, multilingual and transcultural films, local cultures interaction with global arts and media scenes, etc.). [Note: Lectures and readings in English. No knowledge of German required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Transcultural Studies', '200', 'CI', 'This course provides an introduction to transcultural views of literature, film, language, and literature and media culture (e.g., fairy tales and their adaptation in other cultures, authors who work in multiple languages, transcultural comics, multilingual and transcultural films, local cultures interaction with global arts and media scenes, etc.). [Note: Lectures and readings in English. No knowledge of German required.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Sexualities, Relationships, or Families', '200', 'SMF', 'This course introduces students to special topics in sexualities, relationships, and/or families. [Note: This is a repeatable course, subject to different content; it may be completed a total of five times.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Dynamics of Dating', '211', 'SMF', 'This course examines the dynamics of intimate relationships in the context of the modern, Western construct of dating. Topics explored may include long-distance relationships, polyamorous relationships, online dating, hook-up culture, shifting romantic and family formation dynamics, modern communications and technology, and the role of pop culture in dating trends.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Navigating Sexuality and Relationships in Mid/Later Life', '212', 'SMF', 'This course reviews the process of navigating relationships and sexuality as persons age. Topics may include physical/biological changes, desire, youth-focused culture, relationship dissolution, dating, and technologies.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sexual Health and Well-Being', '213', 'SMF', 'This course offers a theoretical and empirical examination of sexuality from a health perspective. Approaches to counselling, prevention, and treatment will be discussed. Topics may include sexually transmitted infections (STI) and pregnancy prevention, pregnancy and post-partum health, healthy romantic and sexual relationships, chronic and long-term illnesses, and sexual cancers.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Constructing Erotics', '214', 'SMF', 'This course examines the social construction of the erotic, in particular how culture, politics, religion, literature, economics, medicine, or technology interact to regulate the definition and performance of sexuality across cultures. Topics may include the sexual body (e.g., beauty and desire), the regulation of sex (e.g., taboos, laws, religion), and other cultural constructions of sexuality (e.g., sexual education).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sexuality and Popular Culture', '215', 'SMF', 'This course examines how sexuality is depicted in, shaped by, and contested in popular culture. It introduces different theories in the study of popular culture and sexuality through a range of historical and contemporary texts and practices.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Sexual Pleasure', '216', 'SMF', 'This course offers a theoretical and empirical examination of the pleasurable and recreational aspects of human sexuality. Topics may include optimal sexuality, romantic passion, sexual techniques, sex toys, BDSM, and sexual role-playing. A range of pleasure discourses and practices (beyond those focused on the genitalia) are addressed.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Capstone Seminar', '400', 'SMF', 'This seminar invites students to integrate their knowledge in the domains of sexuality, relationships, and families and make connections among theories, research, and practices. The capstone focus provides students the opportunity for critical self-reflection on their university experience. [Note: Normally students will take SMF 400 in their last term of study. Offered: Winter. SMF 220 equivalent courses: KIN 330, PSCI 315, PSYCH 291, REC 270, SDS/SWREN 251R, SOC/LS 221. SMF 230 equivalent courses: ARTS 280, ECON 221, ENVS 278, KIN 222, PSCI 314, PSYCH 292, REC 371, SDS/SWREN 250R, SOC/LS 280, STAT 202, 206, 211, 221, 231, 241.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Thesis - Part 1', '499A', 'SMF', 'Under supervision of a faculty member, students normally will review literature, design a study, present a research proposal, analyze data, write a scholarly research paper, and deliver an oral presentation based on the paper (e.g., at the SMF Research Symposium). [Note: SMF 499A and SMF 499B can be taken independently of the SMF Intensive Research Specialization. A grade for SMF 499A will be submitted only after completion of SMF 499B. SMF 499A and 499B cannot be taken in the same term. The two courses must be taken in consecutive terms. SMF 220 equivalent courses: KIN 330, PSCI 315, PSYCH 291, REC 270, SDS/SWREN 251R, SOC/LS 221. SMF 230 equivalent courses: ARTS 280, ECON 221, ENVS 278, KIN 222, PSCI 314, PSYCH 292, REC 371, SDS/SWREN 250R, SOC/LS 280, STAT 202, 206, 211, 221, 231, 241.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Thesis - Part 2', '499B', 'SMF', 'Continuation of SMF 499A. [Note: SMF 499A and SMF 499B can be taken independently of the SMF Intensive Research Specialization. A grade for SMF 499A will be submitted only after completion of SMF 499B. SMF 499A and 499B cannot be taken in the same term. The two courses must be taken in consecutive terms.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Tolkien: From Book to Film', '108T', 'ENGL', 'A study of J.R.R. Tolkiens The Hobbit (1937) and The Lord of the Rings (1954-55), and their film adaptations by Peter Jackson (2001-03, 2012-14).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Popular Potter', '108P', 'ENGL', 'This course examines all seven of J.K. Rowlings Harry Potter novels.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Social Science Advanced Research Methods Topics', '389', 'PSYCH', 'Topics explore social science research methods in Psychology. Consult departmental listings for upcoming topics, which may include naturalistic observation, factorial experiment design, behavioural coding, survey/questionnaire construction, interviewing, and/or linguistic analysis. Activities may include research proposals, group and/or individual projects (e.g., lab experience, data collection), research reports, critiques of published/proposed research, and student presentations. [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Natural Science Advanced Research Methods Topics', '390', 'PSYCH', 'Topics explore natural science research methods in Psychology. Consult departmental listings for upcoming topics, which may include methods for studying psychophysiology, neuroanatomy, and/or cognitive processes (e.g., perception, attention, memory, information-processing, decision-making). Activities may include research proposals, group and/or individual projects (e.g., lab experience, data collection), research reports, critiques of published/proposed research, and student presentations. [Offered: W and/or S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Special Topics in Applied Psychology', '470', 'PSYCH', 'Topics cover practical applications of theory and research in one or more of the six areas of psychology. Consult departmental listings for upcoming topics. Topics may include applications of psychology to health, well-being, law, education, policy analysis, management, marketing, regulation, systems design, community organizing, conflict resolution, and/or communications.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Grief and Palliative Care in Social Work', '653R', 'SWK', 'Grief is a part of the human experience that often occurs when a person dies. Social workers should be familiar with the concept for all aspects of practice. Palliative care is a model of health care for people with terminal illnesses, along with their families, in order to relieve suffering and improve quality of life. Palliative care is delivered by an interprofessional team which is most often comprised of medicine, nursing, social work and chaplaincy; other professions are often involved such as pharmacy, occupational therapy and others. This course addresses the social work role in grief and palliative care. (Note: This is an online course).');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('An Introduction to Accounting for Income Taxes', '614', 'TAX', 'The goal of financial accounting is to reflect the economic activities of the firm in its financial statements. This course introduces students to the accounting for income taxes with a particular focus on analyzing differences between accounting and tax treatments, computing tax provisions, and disclosing tax information in corporate financial statements. The course also provides exposure to the both internal and external users of tax accounting disclosures. In doing so, the course builds a solid grounding in the preparation of accounting information, but also helps students gain an appreciation for the role financial accounting in tax planning and compliance decisions.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics for Natural Science Elective Courses Taken on Exchange by Civil Engineering Students', '23K', 'GENE', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. To earn credit the exchange courses will include the exploration of the physical and chemical interactions of the natural world, and the systematic observation and understanding of physical and natural phenomena through analytical and/or experimental techniques. These courses are treated as natural science electives in the students plan. Such courses are reported on the students transcript with their original titles in English showing as the topic. [Note: The grades for these courses will be either CR or NCR.]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Topics for Natural Science Elective Courses Taken on Exchange by Electrical Engineering Students', '23E', 'GENE', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. To earn credit the exchange courses will include the exploration of the physical and chemical interactions of the natural world, and the systematic observation and understanding of physical and natural phenomena through analytical and/or experimental techniques. These courses are treated as natural science electives in the students plan. Such courses are reported on the students transcript with their original titles in English showing as the topic. [Note: The grades for these courses will be either CR or NCR.]');
