INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Model Building and Response Surface Methodology', '621', 'CHE', 'Process/product optimization based on design of experiments (DOE), empirical modelling, and non-linear mechanistic models. Response surface methods for investigating process/product performance, determining optimal conditions and designs.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Ecology and Evolution', '451', 'BIOL', 'Students will use scientific literature to explore and analyze a range of advanced topics in ecology and evolution at the individual, population, community, and ecosystem scales. Recent advances in understanding of fundamental concepts as well as current frontiers in the field will be examined through written reports, essays, and oral presentations.  [Offered: F]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('The Natural History of Aquatic Organisms', '312', 'BIOL', 'This course will introduce students to the diversity of freshwater organisms and their habitats. Emphasis will be placed on sampling techniques, taxonomic identification, life histories, and adaptations to aquatic environments. The course will introduce students to the use of aquatic organisms in environmental impact and biodiversity assessments. Students must be prepared to work outdoors in potentially inclement weather. [Note: A field trip fee of $60 is required toward the cost of transportation; Offered: S]');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Systems Design Graduate Workshop 1 - AI and Machine Learning', '660A', 'SYDE', 'Engineering Design Project course where students work individually or in small groups applying the principles of engineering problem solving, research methods, systems analysis including modelling, simulation, optimization and design. There is a strong focus on a major AI and Machine Learning design project with regular mentorship, project update reports & presentations, project reviews, and design embodiment demonstrations.');
