INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Systems Design Graduate Workshop 2', '662', 'SYDE', 'This course builds upon SYDE660, SYDE660A, SYDE660B, SYDE660C, SYDE660D, or SYDE660E. The project undertaken in this course can either be a continuation of the project in one of these courses or a new project. Expectations in this course are that the design undertaken also includes prototype testing, evaluation, and final design specification.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Advanced Distributed Systems', '754', 'CS', 'This course provides a broad discussion of advanced topics in modern distributed systems. The course will discuss the state of the art of these systems and how and why they reached there. The course will discuss current data center hardware and software architecture, communication middleware, and how modern systems can tolerate failures, maintain data consistency, scale, leverage the new hardware capabilities, and exploit highly concurrent hardware.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Communication Security', '716', 'ECE', 'This course introduces some timely topics in communications security. It covers the advanced topics on cryptography, encryption and authentication, homomorphic encryption, cryptanalysis, provable security, network security mechanisms and protocols, network access authentication, radio air link protection, privacy in radio frequency identification systems, game theory and security in wireless spectrum sharing, attacks, and hardware protections.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Fundamentals of Astrophysics II: Observational Techniques and Data Analysis', '782', 'PHYS', 'Astronomical observations, including telescopes and instrumentation, data processing and calibration, photometry, astrometry and spectroscopy. Measurement of luminosity functions, spectral energy distributions and other fundamental astrophysical quantities. Data analysis techniques: statistical modelling, hypothesis testing, Bayesian analysis, selection effects, correlations, archives and databases.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
VALUES ('Master of Engineering Project 1', '699A', 'ECE', 'This is a project course open to all ECE MEng students who have completed at least three courses and have a cumulative average of at least 80%. The MEng student will carry out a 4-month research project under the direct supervision of an ECE faculty member. Faculty members will post the projects offered and interview candidates. At the end of the term, a written project report will be submitted, evaluated, and graded. MEng students must obtain a permission number to register for this course. ECE research students (MASc and PhD) are NOT eligible to enroll in this course nor is this course transferable to either the ECE MASc or PhD program.');
INSERT INTO class_profile (class_name, course_code, faculty, description)
